INSERT INTO `scene_script_text` VALUES (14912, '[1] Mage - Puzzle Room - Hex Interactions - DGK / (RKS)', 'the hex is already flagged\n	if (hex.flagged) then\n		FlagHex ( hex )\n	end\n	-- Grant the player Bonus Time if this is a bonus time hex\n	if (hex.bonusTime) then\n		scene:TriggerServerEvent(\"BONUSTIME\")\n		hex.bonustime = false\n		--hex:ClearSpellStateVisual( 62657, hex.bonusTimeAura )\n		hex:ClearSpellVisual(hex.bonusTimeAura)\n	end\n	-- Make the hex uninteractible\n	hex:SetInteractible ( false )\n	--Print (\"X = \"..hex.x..\"   Y = \"..hex.y)\nend\n\n-- Add a Mine Co-Routine\nfunction AddHexMineCoroutine(hex, mineRange, frequency)\n	scene:AddCoroutineWithParams(hexMineCoroutine, hex, mineRange, frequency)\n	--print (\"MINE ADDED\")\nend\n\n-- Add an Open Space Count Co-Routine\nfunction AddHexCountCoroutine(hex, countRange, frequency)\n	scene:AddCoroutineWithParams(hexCountCoroutine, hex, countRange, frequency)\n	--print (\"MINE ADDED\")\nend\n\n-- Hex Land Mine\nhexMineCoroutine = function(hex, mineRange, frequency)\n	local timer\n	local explode = false\n	--print (\"Mine Initialized\")\n\n	while (true) do\n		if (not hex) then\n			-- something else despawned me\n			--print(\"externally despawned\")\n			return\n		end\n\n		if (not hex.mined) then\n			-- Something removed the mine (Likely the player won the game)\n			return\n		end\n\n		local myPos = hex:GetPosition()\n		local playerDisplay = scene:GetActivePlayerDisplay()\n		if (playerDisplay) then\n			local playerPos = playerDisplay:GetPosition()\n			local offset = myPos - playerPos\n			local distance = offset:Magnitude()\n			--print(\"distance: \"..distance)\n			if (distance < mineRange) and not (hex.flagged == true) then\n				explode = true\n			end\n		else\n			--print(\"no player display\")\n		end\n\n		if (explode == true) then\n			if (winner) then -- The player just won the game, don\'t kill them now!\n				return\n			end\n			--hex:PlaySpellCastVisual( 19350 )\n			--hex:ClearSpellStateVisual( 62543, hex.hexAura )\n			hex:ClearSpellVisual(hex.hexAura)\n			--hex:ClearSpellStateVisual(62543, hexVisual)\n			--Squish(critter)\n			--Despawn(critter)\n			--print(\"BOOM!\")\n			scene:TriggerServerEvent(\"MINEEXPLODE\")\n			complete=true\n			return\n		end\n\n		if (not hex) then\n			-- something else depspawned me\n			--print(\"externally despawned\")\n			return\n		end\n\n\n		local scene = hex:GetScene()\n		if (not timer) then\n			timer = scene:Timer(frequency)\n		else\n			timer:Reset()\n		end\n		scene:Wait(timer)\n		--hex:PlaySpellCastVisual( 19350 )\n		--print(\"Merp!\")\n	end\nend\n\n-- Hex Open Space Count\nhexCountCoroutine = function(hex, countRange, frequency)\n	local timer\n	local reveal = false\n	--print (\"Mine Initialized\")\n\n	while (true) do\n		if (not hex) then\n			-- something else despawned me\n			--print(\"externally despawned\")\n			return\n		end\n\n		if (hex.revealed) then\n			-- Something revealed me (Probably a hint at beginning of level)\n			return\n		end\n\n		local myPos = hex:GetPosition()\n		local playerDisplay = scene:GetActivePlayerDisplay()\n		if (playerDisplay) then\n			local playerPos = playerDisplay:GetPosition()\n			local offset = myPos - playerPos\n			local distance = offset:Magnitude()\n			--print(\"distance: \"..distance)\n			if (distance < countRange) and not (hex.flagged == true) then\n				reveal = true\n			end\n		else\n			--print(\"no player display\")\n		end\n\n		if (reveal == true) then\n			DisplayCount ( hex )\n			Wait(1)\n			return\n		end\n\n		if (not hex) then\n			-- something else despawned me\n			--print(\"externally despawned\")\n			return\n		end\n\n		local scene = hex:GetScene()\n		if (not timer) then\n			timer = scene:Timer(frequency)\n		else\n			timer:Reset()\n		end\n		scene:Wait(timer)\n		--hex:PlaySpellCastVisual( 19350 )\n		--print(\"Merp!\")\n	end\nend', 34963);
