INSERT INTO `scene_script_text` VALUES (19472, '8.0.1 [MHI] Conditionals - IGC', '------------------------------------------------------------\n--Copied from IGC Framework Candidates - Conditionals\n\n-- Alliance\nraceHuman 			= scene:EvalPlayerCondition( 47454 )	\nraceDwarf  			= scene:EvalPlayerCondition( 34465 )\nraceNightelf  		= scene:EvalPlayerCondition( 47458 )\nraceGnome  			= scene:EvalPlayerCondition( 33812 )\nraceDraenei  		= scene:EvalPlayerCondition( 34463 ) \nraceWorgen	 		= scene:EvalPlayerCondition( 47456 ) \n\n--------------------------------------------------------\n-- Horde\nraceOrc 			= scene:EvalPlayerCondition( 34464 ) \nraceUndead 			= scene:EvalPlayerCondition( 47453 )\nraceTauren	 		= scene:EvalPlayerCondition( 34467 ) \nraceTroll	 		= scene:EvalPlayerCondition( 34462 ) \nraceBloodelf 		= scene:EvalPlayerCondition( 47452 ) \nraceGoblin 			= scene:EvalPlayerCondition( 34466 )\n\n\n--------------------------------------------------------\n-- Allied Races\nraceLightforged 	= scene:EvalPlayerCondition( 54202 )\nraceVoidelf 		= scene:EvalPlayerCondition( 54203 )\nraceDarkIron 		= scene:EvalPlayerCondition( 61660 )\nraceHighmountain 	= scene:EvalPlayerCondition( 54204 )\nraceNightborne 		= scene:EvalPlayerCondition( 54205 )\nraceMaghar 			= scene:EvalPlayerCondition( 61661 )\nraceKultiran		= scene:EvalPlayerCondition( 66693 )\nraceZandalari		= scene:EvalPlayerCondition( 64968 )\nraceVulpera 		= scene:EvalPlayerCondition( 74393 )\n\n--------------------------------------------------------\n-- Neutral\nracePandaren 		= scene:EvalPlayerCondition( 34468 )\n\n\n\n\n------------------------------------------------------------\n\nfunction GetPlayerScale ()\n	PlayerScale = 1.5\n	PlayerAnim = Animations.Ready1H\n	\n	if 	raceDraenei then\n		\n		PlayerScale = 1.2;\n\n	elseif 	raceLightforged then\n		\n		PlayerScale = 1.2;\n\n	elseif racePandaren and genderMale then\n		PlayerScale = 1.35;\n		PlayerAnim = 860; -- sha ready spell cast\n\n	elseif racePandaren and genderFemale then\n		PlayerScale = 1.35;\n		PlayerAnim = 860; -- sha ready spell cast\n	\n\n	elseif raceGnome then\n	\n		PlayerScale = 2.3;\n		PlayerAnim = Animations.ReadyUnarmed;\n		\n		\n	elseif raceGoblin then\n	\n		PlayerScale = 2.1;\n				\n		\n	elseif raceTauren then\n		PlayerScale = 1;\n\n	elseif raceOrc then\n		PlayerScale = 1.35;\n		\n	elseif raceNightborne then\n		PlayerScale = 1.35;\n		\n	elseif raceTroll then\n		PlayerScale = 1.3;\n		\n	elseif raceVulpera then\n		PlayerScale = 2.3;\n		\n	elseif raceZandalari then\n		PlayerScale = 1.15;\n\n	elseif raceKultiran then\n		PlayerScale = 1.15;		\n	\n	elseif raceDwarf and genderMale then\n\n		PlayerAnim = Animations.ReadyFist1H;\n		PlayerScale = 1.75;\n	\n\n	elseif raceDwarf and genderFemale then\n		PlayerScale = 1.75;\n\n	elseif raceNightelf and genderMale then\n		PlayerAnim = 940; -- dru ready spell cast\n		PlayerScale = 1.2;\n\n	elseif raceNightelf and genderFemale then\n		PlayerAnim = 940; -- dru ready spell cast\n		PlayerScale = 1.2;\n\n	elseif raceWorgen and genderFemale then\n		PlayerScale = 1.25;\n		PlayerAnim = Animations.ReadyThrown;\n\n	elseif raceWorgen and genderMale then\n\n		PlayerScale = 1.25;\n		PlayerAnim = 860; -- sha ready spell cast\n\n\n\n				\n	end\n\nend\n\n------------------------------------------------------------\nGetPlayerScale ()\n', 34963);
