INSERT INTO `scene_script_text` VALUES (24369, '8.3 [MRE] Conditionals - IGC', '------------------------------------------------------------\n\nscene:AddFadeRegion(Vector:New(4022.36, 1908.85, 358.84), 60.0, true)\n\nscene:RemoveFadeRegionExcludedCreature(110152)\nscene:RemoveFadeRegionExcludedCreature(110153)\nscene:RemoveFadeRegionExcludedCreature(110154)\nscene:RemoveFadeRegionExcludedCreature(110155)\nscene:RemoveFadeRegionExcludedCreature(110156)\nscene:RemoveFadeRegionExcludedCreature(110157)\n\nscene:RemoveFadeRegionExcludedGameObject(1346340)\nscene:RemoveFadeRegionExcludedGameObject(1346341)\nscene:RemoveFadeRegionExcludedGameObject(1346342)\nscene:RemoveFadeRegionExcludedGameObject(1346343)\nscene:RemoveFadeRegionExcludedGameObject(1346344)\nscene:RemoveFadeRegionExcludedGameObject(1346345)\n\n------------------------------------------------------------\n\ncamCamPos = nil\n\n------------------------------------------------------------\n\n--Alliance\nplayerHuM	=	scene:EvalPlayerCondition(34987)\nplayerHuF	=	scene:EvalPlayerCondition(34988)\nplayerDwM	=	scene:EvalPlayerCondition(34991) --Includes Dark Iron\nplayerDwF	=	scene:EvalPlayerCondition(34992) --Includes Dark Iron\nplayerNiM	=	scene:EvalPlayerCondition(34993) --Includes Nightborne\nplayerNiF	=	scene:EvalPlayerCondition(34994) --Includes Nightborne\nplayerGnM	=	scene:EvalPlayerCondition(34999)\nplayerGnF	=	scene:EvalPlayerCondition(35000)\nplayerDrM	=	scene:EvalPlayerCondition(35007) --Includes Lightforged\nplayerDrF	=	scene:EvalPlayerCondition(35008) --Includes Lightforged\nplayerWoM	=	scene:EvalPlayerCondition(44885) --Worgen Transformed\nplayerWoF	=	scene:EvalPlayerCondition(44886) --Worgen Transformed\nplayerGiM	=	scene:EvalPlayerCondition(44884) --Gilnean\nplayerGiF	=	scene:EvalPlayerCondition(44887) --Gilnean\nplayerKtM	=	scene:EvalPlayerCondition(67699)\nplayerKtF	=	scene:EvalPlayerCondition(67700)\nplayerMeM	=	scene:EvalPlayerCondition(76806)\nplayerMeF	=	scene:EvalPlayerCondition(76808)\n\n--Horde\nplayerOrM	=	scene:EvalPlayerCondition(34989) --Includes Mag\'har\nplayerOrF	=	scene:EvalPlayerCondition(34990) --Includes Mag\'har\nplayerScM	=	scene:EvalPlayerCondition(34995)\nplayerScF	=	scene:EvalPlayerCondition(34996)\nplayerTaM	=	scene:EvalPlayerCondition(64341) --Includes Highmountain\nplayerTaF	=	scene:EvalPlayerCondition(64342) --Includes Highmountain\nplayerTrM	=	scene:EvalPlayerCondition(35001)\nplayerTrF	=	scene:EvalPlayerCondition(35002)\nplayerBeM	=	scene:EvalPlayerCondition(35005) --Includes Void Elf\nplayerBeF	=	scene:EvalPlayerCondition(35006) --Includes Void Elf\nplayerGoM	=	scene:EvalPlayerCondition(35003)\nplayerGoF	=	scene:EvalPlayerCondition(35004)\nplayerZaM	=	scene:EvalPlayerCondition(68507)\nplayerZaF	=	scene:EvalPlayerCondition(68508)\nplayerVuM	=	scene:EvalPlayerCondition(76804)\nplayerVuF	=	scene:EvalPlayerCondition(76805)\n\n--Pandaren\nplayerPaM	=	scene:EvalPlayerCondition(35012)\nplayerPaF	=	scene:EvalPlayerCondition(35013)\n\n------------------------------------------------------------\n\nfunction playerCheck()\n	if 		(playerTaM) or --Super tall folks\n			(playerZaM) or\n			(playerZaF) or\n			(playerTaF) then\n		camCamPos = [[8.3 [MRE] Magni Reconnects the Engine - IGC\\8.3 [MRE] 1030\\CamEye_1030_ExtraTall]]\n	elseif 	(playerDrM) or --Tall\n			(playerDrF) or\n			(playerKtM) or\n			(playerKtF) or\n			(playerNiM) or\n			(playerNiF) or\n			(playerTrM) or\n			(playerTrF) or\n			(playerWoM) or\n			(playerWoF) or \n			(playerPaM) then \n		camCamPos = [[8.3 [MRE] Magni Reconnects the Engine - IGC\\8.3 [MRE] 1030\\CamEye_1030_Tallbois]]\n	elseif 	(playerGnM) or --Itty Bitty\n			(playerGnF) or \n			(playerGoM) or \n			(playerGoF) or\n			(playerMeM) or\n			(playerMeF) or\n			(playerVuM) or\n			(playerVuF) then\n		camCamPos = [[8.3 [MRE] Magni Reconnects the Engine - IGC\\8.3 [MRE] 1030\\CamEye_1030_Shorties]]\n	else\n		camCamPos = [[8.3 [MRE] Magni Reconnects the Engine - IGC\\8.3 [MRE] 1030\\CamEye_1030]] --Normal\n		', 34963);
