INSERT INTO `scene_script_text` VALUES (24535, 'World Generic - Hide Other Players - Client Scene [40 yards]', 'KEEP_SCENE_RUNNING = 1\n\n--Hides all other players from the current player.\nscene:SetFadeRegionExcludesAllNonPlayers(true)\nscene:SetFadeRegion(40, false)\n\n\n--Will be hiding other players constantly in background.\nwhile KEEP_SCENE_RUNNING == 1 do\n		scene:SetFadeRegionExcludesAllNonPlayers(true)\n		Wait(0.5)\n	end\n\nif KEEP_SCENE_RUNNING == 0 then\n	scene: EndScene()\nend ', 34963);

