INSERT INTO `creature_text` VALUES (20159, 0, 0, 'If you think I\'ll betray my friend that easily, think again!  Face me, you scoundrel!', 12, 1, 100, 0, 0, 0, 17758, 0, 'Magister Aledis');
INSERT INTO `creature_text` VALUES (20159, 1, 0, 'Spare my life!  I will tell you about Arelion\'s secret.', 12, 1, 100, 0, 0, 0, 17755, 0, 'Magister Aledis');
INSERT INTO `creature_text` VALUES (20234, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 4583, 0, 'Runetog Wildhammer');
INSERT INTO `creature_text` VALUES (20242, 0, 0, 'The best selection of smuggled goods is available right here, $n.  Great for the whole family!  Avoid goblin taxation and naaru prohibition - the Consortium is here to fill your every shopping need!', 12, 0, 100, 1, 0, 0, 17916, 0, 'Karaaz');
INSERT INTO `creature_text` VALUES (20610, 0, 0, '%s calls for the aid of a nearby male!', 16, 0, 100, 0, 0, 0, 19207, 0, 'Talbuk Doe');
