INSERT INTO `creature_text` VALUES (21291, 0, 0, 'I\'m through asking nicely, fish-man.', 12, 0, 100, 25, 0, 0, 18954, 0, 'Grom\'tor, Son of Oronok');
INSERT INTO `creature_text` VALUES (21291, 1, 0, 'Where is the Cipher of Damnation? I know a third of it is in this village.', 12, 0, 100, 6, 0, 0, 18955, 0, 'Grom\'tor, Son of Oronok');
INSERT INTO `creature_text` VALUES (21291, 2, 0, 'No... Of course not. What was I thinking? Why would you have a third of the Cipher of Damnation?', 12, 0, 100, 274, 0, 0, 18958, 0, 'Grom\'tor, Son of Oronok');
INSERT INTO `creature_text` VALUES (21291, 3, 0, 'I feel so stupid. Maybe I should unchain you and set you free?', 12, 0, 100, 11, 0, 0, 18959, 0, 'Grom\'tor, Son of Oronok');
INSERT INTO `creature_text` VALUES (21291, 4, 0, 'DO I HONESTLY LOOK THAT STUPID TO YOU, NAGA? NOW YOU FEEL PAIN!', 12, 0, 100, 5, 0, 0, 18960, 0, 'Grom\'tor, Son of Oronok');
INSERT INTO `creature_text` VALUES (21291, 5, 0, '%s spits on the Coilskar naga.', 16, 0, 100, 5, 0, 0, 18962, 0, 'Grom\'tor, Son of Oronok');
INSERT INTO `creature_text` VALUES (21291, 6, 0, 'GET UP YOU ROTTEN PIECE OF TRASH! GET UP BEFORE I END YOU!', 12, 0, 100, 5, 0, 0, 18963, 0, 'Grom\'tor, Son of Oronok');
INSERT INTO `creature_text` VALUES (21291, 7, 0, 'I\'m listening...', 12, 0, 100, 273, 0, 0, 18966, 0, 'Grom\'tor, Son of Oronok');
INSERT INTO `creature_text` VALUES (21295, 0, 0, 'Whhaaat... are you sssspeaking of, ssssoft-sskin?', 12, 0, 100, 1, 0, 0, 18956, 0, 'Coilskar Commander');
INSERT INTO `creature_text` VALUES (21295, 1, 0, 'The naga do nottt... have thissss... cipherrr...', 12, 0, 100, 1, 0, 0, 18957, 0, 'Coilskar Commander');
INSERT INTO `creature_text` VALUES (21295, 2, 0, 'Yesss... Yesss... Ssset me freee...', 12, 0, 100, 1, 0, 0, 18961, 0, 'Coilskar Commander');
INSERT INTO `creature_text` VALUES (21295, 3, 0, '%s regains consciousness.', 16, 0, 100, 0, 0, 0, 25036, 0, 'Coilskar Commander');
INSERT INTO `creature_text` VALUES (21295, 4, 0, 'Do not beat the Coilsssskaarrr commander anymore, orc! I will tell you! The cipherrr... It is hidden in one of the chests at Coilssskarrr Point... The naga there hold the keysss...', 12, 0, 100, 1, 0, 0, 18965, 0, 'Coilskar Commander');
INSERT INTO `creature_text` VALUES (21295, 5, 0, 'COILSSSKARRR!', 12, 0, 100, 15, 0, 0, 18967, 0, 'Coilskar Commander');
