INSERT INTO `creature_text` VALUES (23861, 0, 2, 'It is too late for Jarl...its hold is too strong...', 12, 0, 50, 1, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` VALUES (23861, 0, 3, 'The darkness...the corruption...they came too quickly for anyone to know...', 12, 0, 50, 1, 0, 0, 0, 0, 'say Text');
