INSERT INTO `creature_text` VALUES (23975, 3, 0, 'Thank you, spirits.', 12, 7, 100, 2, 0, 0, 22670, 0, 'Thoralius the Wise');
INSERT INTO `creature_text` VALUES (24032, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 4583, 0, 'Celea Frozenmane');
INSERT INTO `creature_text` VALUES (24035, 0, 0, 'Avenge my friends who have already been sacrificed!', 12, 0, 100, 0, 0, 0, 22647, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 1, 'I don\'t want to be sacrificed!', 12, 0, 100, 0, 0, 0, 22641, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 2, 'I don\'t want to be taken down into the catacombs!', 12, 0, 100, 0, 0, 0, 22640, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 3, 'I think I saw a key on one of those Gjalerbron vrykul up top.', 12, 0, 100, 0, 0, 0, 23649, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 4, 'I think I saw a key on one of those vrykul.', 12, 0, 100, 0, 0, 0, 22639, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 5, 'Is there no hero to rescue me?', 12, 0, 100, 0, 0, 0, 22643, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 6, 'My thanks to you, hero!', 12, 0, 100, 0, 0, 0, 22649, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 7, 'Oh, thank you, thank you!', 12, 0, 100, 0, 0, 0, 22645, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 8, 'Please, let me out of here!', 12, 0, 100, 0, 0, 0, 22637, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 9, 'Thank goodness for you!', 12, 0, 100, 0, 0, 0, 22652, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 10, 'Thank you.  I will not forget this!', 12, 0, 100, 0, 0, 0, 22646, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 11, 'The key... you have to find the key!', 12, 0, 100, 0, 0, 0, 22638, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 12, 'The vrykul are evil and must be destroyed!', 12, 0, 100, 0, 0, 0, 22650, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 13, 'They\'re performing unholy rites down in the catacombs, and I\'m about to be the sacrifice!', 12, 0, 100, 0, 0, 0, 22642, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 14, 'This is the end.', 12, 0, 100, 0, 0, 0, 22644, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 15, 'You have to put a stop to this madness before it\'s too late!', 12, 0, 100, 0, 0, 0, 22648, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 16, 'You saved my life!', 12, 0, 100, 0, 0, 0, 22651, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24052, 0, 0, 'We don\'t shoe no mules here. \'Sides, it\'s strictly expedition business right now.', 12, 7, 100, 1, 0, 0, 23562, 0, 'Eldrim Mounder');
INSERT INTO `creature_text` VALUES (24052, 1, 0, 'Not my problem! If yer so antsy to get yerself a house built, do it \'ere in the fort and quit yer whinin\'!', 12, 7, 100, 274, 0, 0, 23564, 0, 'Eldrim Mounder');
INSERT INTO `creature_text` VALUES (24052, 2, 0, 'Now, if you\'d be so kind as to get your ass away from my forge...', 12, 7, 100, 25, 0, 0, 23566, 0, 'Eldrim Mounder');
INSERT INTO `creature_text` VALUES (24061, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 4583, 0, 'James Ormsby');
INSERT INTO `creature_text` VALUES (24155, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 4583, 0, 'Tobias Sarkhoff');
INSERT INTO `creature_text` VALUES (24178, 0, 0, 'Arrrrrhhhhhhhh!!!', 1, 0, 100, 1, 0, 0, 0, 0, 'Inner Turmoil text 0');
INSERT INTO `creature_text` VALUES (35229, 0, 0, 'Now you will die ...!', 16, 0, 100, 0, 0, 0, 7798, 0, 'Bloodfang Stalker');
INSERT INTO `creature_text` VALUES (24211, 0, 0, 'Blessings of the spirits of the land and air upon you.', 12, 1, 100, 1, 0, 0, 22947, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24211, 0, 1, 'I must return. Good hunting to you.', 12, 1, 100, 1, 0, 0, 22946, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24211, 0, 2, 'I owe you a life debt, stranger.', 12, 1, 100, 1, 0, 0, 22952, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24211, 0, 3, 'Strange ghosts walk the land. Be careful!', 12, 1, 100, 1, 0, 0, 22950, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24211, 0, 4, 'Thank you, stranger.', 12, 1, 100, 1, 0, 0, 22945, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24211, 0, 5, 'They took us one by one. I\'m sure there are other survivors.', 12, 1, 100, 1, 0, 0, 22951, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24211, 0, 6, 'We should never have traveled through the Rivenwood. Everyone knows this!', 12, 1, 100, 1, 0, 0, 22949, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24211, 0, 7, 'You saved me from certain death. I owe you.', 12, 1, 100, 1, 0, 0, 22948, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24238, 2, 1, 'Fight without fear and the Val\'kyr will reward you!', 14, 0, 100, 0, 0, 0, 23063, 0, 'Bjorn Halgurdsson');
INSERT INTO `creature_text` VALUES (24238, 2, 0, 'Drive the invaders out!  For Queen Angerboda!', 14, 0, 100, 0, 0, 0, 23062, 0, 'Bjorn Halgurdsson');
INSERT INTO `creature_text` VALUES (24238, 1, 0, 'No one speaks of my mother in that manner!', 14, 0, 100, 0, 0, 0, 23009, 0, 'Bjorn Halgurdsson');
INSERT INTO `creature_text` VALUES (24238, 0, 0, '[Crude Vrykul] Bjorns mottar haelgond sygar!', 14, 0, 100, 0, 0, 0, 23008, 0, 'Bjorn Halgurdsson');
INSERT INTO `creature_text` VALUES (24321, 0, 0, '%s motions for silence.', 16, 0, 100, 397, 0, 0, 23130, 0, 'King Ymiron emote');
INSERT INTO `creature_text` VALUES (24321, 1, 0, 'Vrykul, your king implores you to listen!', 14, 0, 100, 22, 0, 0, 23134, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 2, 0, 'The crowd gasps in horror.', 16, 0, 100, 53, 0, 0, 23136, 0, 'King Ymiron emote');
INSERT INTO `creature_text` VALUES (24321, 3, 0, 'Even now, in our darkest hour, they mock us!', 14, 0, 100, 22, 0, 0, 23137, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 4, 0, 'Where are the titans in our time of greatest need? Our women birth abberations - disfigured runts unable to even stand on their own! Weak and ugly... Useless...', 14, 0, 100, 22, 0, 0, 23138, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 5, 0, 'Ymiron has toiled. Long have I sat upon my throne and thought hard of our plight. There is only one answer... one reason...', 14, 0, 100, 22, 0, 0, 23139, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 6, 0, 'For who but the titans themselves could bestow such a curse? What could have such power?', 14, 0, 100, 1, 0, 0, 23140, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 7, 0, 'And the answer is nothing... For it is the titans that have cursed us!', 14, 0, 100, 53, 0, 0, 23141, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 8, 0, 'The crowd cheers.', 16, 0, 100, 0, 0, 0, 23144, 0, 'King Ymiron');
INSERT INTO `creature_text` VALUES (24321, 9, 0, 'On this day all Vrykul will shed their old beliefs! We denounce our old gods! All Vrykul will pledge their allegiance to Ymiron! Ymiron will protect our noble race!', 14, 0, 100, 53, 0, 0, 23143, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 10, 0, 'And now my first decree upon the Vrykul! All malformed infants born of Vrykul mother and father are to be destroyed upon birth! Our blood must remain pure always! Those found in violation of Ymiron\'s decree will be taken to Gjalerbron for execution!', 14, 0, 100, 1, 0, 0, 23145, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24322, 0, 0, 'Silence!', 14, 1, 100, 1, 0, 0, 32240, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24322, 1, 0, 'Show them mercy, my king! They are of our flesh and blood!', 14, 0, 100, 53, 0, 0, 23128, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24322, 2, 0, 'All hail our glorious king, Ymiron!', 14, 0, 100, 53, 0, 0, 23123, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24323, 0, 0, 'Show the abberations no mercy, Ymiron!', 14, 0, 100, 1, 0, 0, 23125, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24323, 1, 0, 'They weaken us! Our strength is dilluted by their very existence! Destroy them all!', 14, 0, 100, 113, 0, 0, 23129, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24366, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 4583, 0, 'Nizzle');
INSERT INTO `creature_text` VALUES (24528, 0, 0, 'How much\'ll it cost me to get my mules shod?', 12, 7, 100, 6, 0, 0, 23561, 0, 'Brune Grayblade');
INSERT INTO `creature_text` VALUES (24528, 1, 0, 'You don\'t understand! If I\'m not ready to go soon, all the good homesteading land will be taken!', 12, 7, 100, 1, 0, 0, 23563, 0, 'Brune Grayblade');
INSERT INTO `creature_text` VALUES (24528, 2, 0, 'That, sir, is a mule!', 12, 7, 100, 25, 0, 0, 23567, 0, 'Brune Grayblade');
INSERT INTO `creature_text` VALUES (24531, 2, 0, 'I\'d rather listen to his braying than yours, dear.', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 3');
INSERT INTO `creature_text` VALUES (24531, 1, 0, 'Yes, darling. You realize you\'ll be eating dire ram mutton for dinner until the crops are planted...', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 2');
INSERT INTO `creature_text` VALUES (24531, 0, 0, 'Of course, darling.', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 1');
INSERT INTO `creature_text` VALUES (24532, 2, 0, ' I\'d sooner butcher that mule of yours! Why did you bring that beast in here, anyway?', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 3');
INSERT INTO `creature_text` VALUES (24532, 1, 0, 'And I don\'t want our land to have any of those horrible wolves, or giants, or ugly rams!', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 2');
INSERT INTO `creature_text` VALUES (24532, 0, 0, 'Remember, honey, we need to get a plot of land with a nice hot spring.', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 1');
INSERT INTO `creature_text` VALUES (24534, 6, 0, 'Now, my loyal custo... err, friends, go forth and conquer this land for our people!', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 7');
INSERT INTO `creature_text` VALUES (24534, 5, 0, 'There are more than enough to go around. Should you need other goods, don\'t hesitate to ask!', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 6');
INSERT INTO `creature_text` VALUES (24534, 4, 0, 'Axes, picks, seed, nails, food, blankets, water... It\'s all there, waiting for you. I think you\'ll find my prices quite reasonable, too.', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 5');
INSERT INTO `creature_text` VALUES (24534, 3, 0, 'But, you will not be alone out there. My men and I have prepared pack mules carrying the supplies you\'ll need most.', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 4');
INSERT INTO `creature_text` VALUES (24534, 2, 0, 'We will win this land with the sword, and break it with the plow! You are the men and women who will be remembered for taming the wild continent!', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 3');
INSERT INTO `creature_text` VALUES (24534, 1, 0, 'True, Northend is a hard land, but our people are strong, hardy, and equal to the task!', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 2');
INSERT INTO `creature_text` VALUES (24534, 0, 0, 'Welcome to Fort Wildervar, brave homesteaders! There\'s a whole continent out there just waiting to be claimed!', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 1');
INSERT INTO `creature_text` VALUES (24639, 0, 0, 'Keep quiet, will you?  If anyone catches on, we\'re both dead.', 15, 0, 100, 0, 0, 0, 23818, 0, 'Olga, the Scalawag Wench');
INSERT INTO `creature_text` VALUES (24639, 1, 0, 'All right, fellas!  Who ordered the spiced rum?  Was it you, Jackie boy?', 12, 0, 100, 0, 0, 0, 23819, 0, 'Olga, the Scalawag Wench');
INSERT INTO `creature_text` VALUES (24639, 2, 0, 'It\'s ok, sweetheart.  This one\'s on the house.', 12, 0, 100, 0, 0, 0, 23820, 0, 'Olga, the Scalawag Wench');
INSERT INTO `creature_text` VALUES (24710, 0, 0, 'Right!  Let\'s try this so-called dire brew...', 12, 0, 100, 0, 0, 0, 27418, 0, 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES (24710, 1, 0, 'By Bronzebeard\'s beard!  What a drink!', 12, 0, 100, 0, 0, 0, 27419, 0, 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES (24710, 2, 0, '$n, this beer... well, it might be the best of Brewfest!', 12, 0, 100, 15, 0, 0, 27421, 0, 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES (24710, 3, 0, 'It is a shame Coren\'s Dire Brew can\'t quench more thirsting throats!  It\'s blasphemy, I know, but  I envy those Dark Iron dwarves!', 12, 0, 100, 274, 0, 0, 27420, 0, 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES (24710, 4, 0, 'Ho there, and welcome to Brewfest!', 12, 7, 100, 3, 0, 0, 23691, 0, 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES (24710, 4, 1, 'Hello, friend! Welcome to the Brewfest Grounds!', 12, 7, 100, 3, 0, 0, 23692, 0, 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES (24710, 4, 2, 'Hail, good $c! Welcome to Ironforge\'s annual Brewfest celebration!', 12, 7, 100, 3, 0, 0, 23693, 0, 'Ipfelkofer Ironkeg');
INSERT INTO `creature_text` VALUES (24711, 0, 0, 'Well time\'s money, and time\'s wasting!  Let\'s have that drink...', 12, 0, 100, 1, 0, 0, 27435, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24711, 1, 0, 'Wow!  This really greases the steamplugs!', 12, 0, 100, 1, 0, 0, 27436, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24711, 2, 0, 'If I were an honest goblin, I\'d say this was the best beer of the festival...', 12, 0, 100, 1, 0, 0, 27437, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24711, 3, 0, 'Too bad Coren\'s dead.  If I could buy that brew from him and resell it -- watered down and rebottled under my own label, of course -- I\'d be rich!', 12, 0, 100, 1, 0, 0, 27438, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24711, 4, 0, 'Hey there, and welcome to Brewfest. Come in and have a drink or three!', 12, 0, 100, 3, 0, 0, 23698, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24711, 4, 1, 'What\'s up, friend? Welcome to the Brewfest Grounds! Now go buy some drinks!', 12, 0, 100, 3, 0, 0, 23700, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24711, 4, 2, 'Hey hey, it\'s another $c! Welcome to my annual Brewfest celebration... now go feast and drink some!', 12, 0, 100, 3, 0, 0, 23701, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24779, 0, 0, 'Tonight we feast on you!', 12, 0, 100, 0, 0, 0, 23806, 0, 'Dragonflayer Ambusher');
INSERT INTO `creature_text` VALUES (24779, 0, 1, 'Such easy prey...', 12, 0, 100, 0, 0, 0, 23805, 0, 'Dragonflayer Ambusher');
INSERT INTO `creature_text` VALUES (24779, 0, 2, 'Who\'s hunting who?', 12, 0, 100, 0, 0, 0, 23804, 0, 'Dragonflayer Ambusher');
INSERT INTO `creature_text` VALUES (24788, 0, 0, 'Sure thing, love.  Put it on Harry\'s tab, will ya?  He owes me a drink!', 12, 0, 100, 0, 0, 0, 23830, 0, 'Jack Adams');
INSERT INTO `creature_text` VALUES (24788, 1, 0, 'Free rum?  Why... that\'s me favorite kind!', 12, 0, 100, 0, 0, 0, 23821, 0, 'Jack Adams');
INSERT INTO `creature_text` VALUES (24788, 2, 0, 'Sweet Neptulon!  That was... one drink... too many!', 12, 0, 100, 0, 0, 0, 23822, 0, 'Jack Adams');
INSERT INTO `creature_text` VALUES (25288, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 4583, 0, 'Turida Coldwind');
INSERT INTO `creature_text` VALUES (25321, 0, 0, 'Thank you for freeing me! May the tides always favor you.', 12, 0, 100, 1, 0, 0, 24596, 0, 'Kaskala Craftman');
INSERT INTO `creature_text` VALUES (25321, 0, 1, 'Do not allow Kaskala to forget what has happened here.', 12, 0, 100, 1, 0, 0, 24598, 0, 'Kaskala Craftman');
INSERT INTO `creature_text` VALUES (25322, 0, 0, 'May the ancestors always aid you, $c, as you have aided me.', 12, 0, 100, 1, 0, 0, 24601, 0, 'Kaskala Shaman');
INSERT INTO `creature_text` VALUES (25322, 0, 1, 'Thank you, $c. May the winds and seas always deliver you safely.', 12, 0, 100, 1, 0, 0, 24599, 0, 'Kaskala Shaman');
