INSERT INTO `creature_text` VALUES (26420, 0, 0, 'Ah, yes. Loken is well known to me.', 12, 0, 100, 0, 0, 0, 26267, 0, 'Gavrock');
INSERT INTO `creature_text` VALUES (26420, 1, 0, 'It is he who commands the sons of iron in their war against us.', 12, 0, 100, 0, 0, 0, 26268, 0, 'Gavrock');
INSERT INTO `creature_text` VALUES (26420, 2, 0, 'From his hiding place, he oversees their preparations for war with the goal of exterminating the stone giants!', 12, 0, 100, 0, 0, 0, 26269, 0, 'Gavrock');
INSERT INTO `creature_text` VALUES (26484, 0, 0, 'He\'s out to get me, too! I\'d be careful around here if I was you, traveler. You never know which tree he\'s hiding behind!', 12, 0, 100, 0, 0, 0, 26266, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26484, 1, 0, 'That\'s not something Limpy Joe would ask! But yeah, I know Loken.', 12, 0, 100, 0, 0, 0, 26265, 0, 'Hugh Glass');
