INSERT INTO `creature_text` VALUES (26917, 0, 0, 'Mortal champions have long used these weapons to combat evil. I see no reason to keep the swords from them in this battle.', 12, 0, 100, 1, 0, 0, 37781, 0, 'Alexstrasza the Life-Binder');
INSERT INTO `creature_text` VALUES (27113, 0, 0, 'Thank you for rescuing me, but the others are still held on the tiers above!', 12, 0, 100, 1, 0, 0, 26189, 0, 'Prospector Gann');
INSERT INTO `creature_text` VALUES (27114, 0, 0, 'Thank goodness! I was worried they were going to press me into service in their war against the stone giants!', 12, 0, 100, 1, 0, 0, 26190, 0, 'Prospector Torgan');
INSERT INTO `creature_text` VALUES (27115, 0, 0, 'These irons are crazy! They keep talking about serving their \'master Loken\' but from what I can tell, only a few receive orders from him.', 12, 0, 100, 1, 0, 0, 26191, 0, 'Prospector Varana');
INSERT INTO `creature_text` VALUES (27234, 0, 0, 'You are being misled! The Onslaught is all lies! The Scourge and the Forsaken are not our enemies! Wake up!', 14, 0, 100, 5, 0, 0, 26596, 0, 'Blacksmith Goodman');
INSERT INTO `creature_text` VALUES (27235, 0, 0, 'Renounce the Scarlet Onslaught! Don\'t listen to the lies of the high general and the grand admiral any longer!', 14, 0, 100, 5, 0, 0, 26598, 0, 'Lead Cannoneer Zierhut');
INSERT INTO `creature_text` VALUES (27236, 0, 0, 'Abbendis is nothing but a harlot and Grand Admiral Westwind is selling her cheap like he sold us out!', 14, 0, 100, 5, 0, 0, 26599, 0, 'Stable Master Mercer');
INSERT INTO `creature_text` VALUES (27237, 0, 0, 'High General Abbendis personally told me that it was a mistake to come north and that we\'re doomed! I urge you all to lay down your weapons and leave before it\'s too late!', 14, 0, 100, 5, 0, 0, 26597, 0, 'Commander Jordan');
INSERT INTO `creature_text` VALUES (27316, 0, 0, 'Stand back, $n, the beast might lash out and harm you.', 12, 7, 100, 396, 0, 0, 26800, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 1, 0, 'GODFREY! Hear me, fiend! Hear me, for I am the Light, here to deliver you from evil!', 12, 7, 100, 5, 0, 0, 26801, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 2, 0, 'Good. I have your attention, then, Godfrey?', 12, 7, 100, 396, 0, 0, 26803, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 3, 0, 'We can do this in one of two ways, Godfrey. First, I will simply ask you to tell me what the unholy markings etched upon the pages of this tome mean. What say you?', 12, 7, 100, 1, 0, 0, 26804, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 4, 0, 'Then it is option two.', 12, 7, 100, 1, 0, 0, 26809, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 5, 0, 'I say a prayer for you now, lost soul. May the Light take you gracefully.', 12, 7, 100, 68, 4000, 0, 26810, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 6, 0, 'Let the sermon begin.', 12, 7, 100, 1, 0, 0, 26811, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 7, 0, 'LIGHT TAKE YOU, BEAST!', 12, 7, 100, 0, 0, 0, 26812, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 8, 0, 'BURN IN HOLY FIRE!', 12, 7, 100, 0, 0, 0, 26816, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 9, 0, 'LET THE LIGHT IN, GODFREY GOODARD!', 12, 7, 100, 0, 0, 0, 26814, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 10, 0, 'May the power of Light compel you!', 12, 7, 100, 0, 0, 0, 26813, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 11, 0, 'I thought you would see the Light, Godfrey... Now speak quickly, fiend. What does it say?', 12, 7, 100, 396, 0, 0, 26824, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 12, 0, 'RUBBISH! Godfrey do you take me for a fool? Do not spew your Scourge propaganda at a man of the Light, heathen! Speak now or I will have the peasants craft a holy water bath and dip you into it like a dog with fleas!', 12, 7, 100, 6, 0, 0, 26826, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 13, 0, 'What? There is a mausoleum beneath the old village? What fools we are to not have noticed! We will find and strike down your master now, Godfrey. We will end this nightmare.', 12, 7, 100, 6, 0, 0, 26829, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 14, 0, 'You let us worry about how we kill the monster, Godfrey.', 12, 7, 100, 396, 0, 0, 26830, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 15, 0, 'Return to Halford with the information that the good mayor was kind enough to submit, $n. I will finish here and squeeze whatever else this wretch might know about Thel\'zan. Now, I will show Godfrey the rarely seen \"option three.\"', 12, 7, 100, 1, 0, 0, 26833, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 16, 0, 'Let\'s you and I have a chat about some things, Godfrey.', 12, 7, 100, 1, 0, 0, 26834, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27344, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 4583, 0, 'Bat Handler Adeline');
INSERT INTO `creature_text` VALUES (27430, 0, 0, 'The Destructive Ward gains in power.', 41, 0, 100, 0, 0, 0, 26601, 0, 'Destructive Ward');
INSERT INTO `creature_text` VALUES (27430, 1, 0, 'The Destructive Ward is fully charged!', 41, 0, 100, 0, 0, 0, 26602, 0, 'Destructive Ward');
INSERT INTO `creature_text` VALUES (27447, 0, 0, 'There will be no mercy!', 14, 0, 0, 0, 0, 13649, 31813, 0, '');
INSERT INTO `creature_text` VALUES (27447, 1, 1, 'Blast them! Destroy them!', 14, 0, 0, 0, 0, 13650, 31814, 0, '');
INSERT INTO `creature_text` VALUES (27447, 2, 2, '%s calls an Azure Ring Captain!', 41, 0, 0, 0, 0, 0, 33077, 0, '');
INSERT INTO `creature_text` VALUES (27447, 3, 3, 'They are... too strong! Underestimated their... fortitude.', 14, 0, 0, 0, 0, 13655, 31819, 0, '');
INSERT INTO `creature_text` VALUES (27577, 0, 0, '%s growls.', 16, 0, 100, 0, 0, 409, 26802, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 1, 0, '%s hisses!', 16, 0, 100, 0, 0, 410, 26805, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 2, 0, 'Tell you nothing, preacher.', 12, 0, 100, 1, 0, 0, 26806, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 3, 0, 'What can you do to me that Kel\'Thuzad has not? That the Lich King will not?', 12, 0, 100, 1, 0, 0, 26807, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 4, 0, 'The book is your salvation, yes... but nothing will you know! NOTHING I SAY! NOTHING! ', 12, 0, 100, 1, 0, 0, 26808, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 5, 0, 'No more LIGHT! NO MORE! I BEG YOU!', 12, 0, 100, 0, 0, 0, 26822, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 6, 0, 'Never felt hurt like this!', 12, 0, 100, 0, 0, 0, 26823, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 7, 0, 'It tells of the coming apocalypse. How this world will burn and be reborn from death.', 12, 0, 100, 1, 0, 0, 26825, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 8, 0, 'NOOOO!!! I tell you! I tell you everything!', 12, 0, 100, 39, 0, 0, 26827, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 9, 0, 'Humans... Beneath the earth of Wintergarde Village you built a mausoleum! Why do you think Naxxramas attacked that spot? Thel\'zan hides deep in your own crypt and sends a thousand-thousand corpses at you! Perish you will...', 12, 0, 100, 1, 0, 0, 26828, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 10, 0, 'How? Humans truly are stupid, yes? Thel\'zan the Duskbringer! Thel\'zan the Lich! He who is born of human flesh and bone, sacrificed all for power, protected by the Lich King!', 12, 0, 100, 1, 0, 0, 26831, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 11, 0, 'You cannot stop Thel\'zan! He bears the dark gift, bestowed upon him by the Lich King himself!', 12, 0, 100, 1, 0, 0, 26832, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27656, 1, 0, 'You brash interlopers are out of your element! I will ground you!', 14, 0, 100, 0, 0, 13623, 31873, 0, 'Ley-Guardian Eregos - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (27656, 0, 0, 'Simpletons! You cannot comprehend the forces you have set in motion. The ley line conduit will not be disrupted! Your defeat shall be absolute!', 14, 0, 100, 0, 0, 13622, 31841, 3, 'Ley-Guardian Eregos - SAY_SPAWN');
INSERT INTO `creature_text` VALUES (27842, 0, 0, 'Bat gizzards again for the gnomes tonight...', 12, 1, 100, 1, 0, 0, 27129, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 0, 1, 'What do they expect, making the bats come in at that angle? Broken necks and gamey bat stew, that\'s what they get.', 12, 1, 100, 1, 0, 0, 27127, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 0, 2, '\'We like the trees, Fenrick. They provide cover.\' They won\'t let me chop them down, either.', 12, 1, 100, 1, 0, 0, 27126, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 0, 3, 'I wonder how many reinforcements need to suffer injury before they allow us to chop down these idiotic trees. They\'re costing us a fortune in bats. Maybe I\'ll rig a harness or two...', 12, 33, 100, 1, 0, 0, 27128, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 1, 0, 'Guards help!', 14, 0, 100, 0, 0, 0, 30163, 0, 'Common Horde Flight Master');
INSERT INTO `creature_text` VALUES (27915, 0, 0, 'Good work with the crates! Come talk to me in front of Stratholme for your next assignment!', 15, 0, 0, 0, 0, 0, 30509, 3, 'Chromie - SAY_EVENT_START');
INSERT INTO `creature_text` VALUES (27990, 0, 0, 'You\'re too late, $n. Another visitor from Dalaran came asking after information about the same prismatic dragon blades.', 12, 0, 100, 1, 0, 0, 37769, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 1, 0, 'From your description, I\'m certain that the book I loaned our visitor could allow you to easily identify the weapon.', 12, 0, 100, 25, 0, 0, 37770, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 2, 0, 'I\'m afraid you\'ll have to ask the -- Well, perhaps Kalecgos can help.', 12, 0, 100, 1, 0, 0, 37772, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 3, 0, '$n may have found the remains of a prismatic blade, Kalecgos. Will you offer your help to our visitor?', 12, 0, 100, 1, 0, 0, 37773, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 4, 0, 'You believe our allies will not be able to control the power of the swords?', 12, 0, 100, 1, 0, 0, 37778, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 5, 0, 'As will we all.', 12, 0, 100, 25, 0, 0, 37785, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 6, 0, 'Please, mortal, speak with Arcanist Tybalin in Dalaran. He may be able to negotiate with the Sunreavers for access to the book.', 12, 0, 100, 1, 0, 0, 37873, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 7, 0, 'Please, mortal, seek out Magister Hathorel in Dalaran. He might be able to negotiate with the Silver Covenant for access to the book.', 12, 0, 100, 1, 0, 0, 37874, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (28012, 0, 0, 'The trickster Mage-Lord Urom protects the third ring.  He will appear alone and defenseless, but do not be fooled by appearances!  Urom is a powerful conjurer who commands a menagerie of Phantasmal creatures.  Seek him out above.', 12, 0, 0, 0, 0, 0, 28079, 0, '');
INSERT INTO `creature_text` VALUES (28012, 1, 1, 'Your greatest challenge lies ahead.  Ley-Guardian Eregos is a Blue dragon of immense power. You will find him flying above the uppermost ring.', 12, 0, 0, 0, 0, 0, 27389, 0, '');
INSERT INTO `creature_text` VALUES (28179, 0, 0, 'The Lich King reacted swiftly to the breach. Faster than I anticipated.', 12, 0, 100, 1, 0, 0, 31004, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (28179, 1, 0, 'What would you have me do, Darion?', 12, 0, 100, 6, 0, 0, 31008, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (28179, 2, 0, 'Choose your words wisely, death knight. You stand amidst the company of the devoted.', 12, 0, 100, 1, 0, 0, 31010, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (28179, 3, 0, 'We will do this with honor, Darion. We will not sink to the levels of the Scourge to be victorious. To do so would make us no better than the monster that we fight to destroy!', 12, 0, 100, 1, 0, 0, 31016, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (28518, 0, 0, 'Not sure how ol\' Nass always finds his way back here. Must be by smell....', 12, 0, 100, 0, 0, 0, 28440, 0, 'Stefan Vadu');
INSERT INTO `creature_text` VALUES (28521, 0, 0, 'Let\'s go to Zul\'Drak he says. We can get to the bottom of this mess, he says...', 12, 0, 33, 0, 0, 0, 28463, 0, 'Nass');
INSERT INTO `creature_text` VALUES (28521, 0, 1, 'One more lock of scourge hair gained, one more shred of dignity lost....', 12, 0, 33, 0, 0, 0, 28458, 0, 'Nass');
INSERT INTO `creature_text` VALUES (28521, 0, 2, 'Are we finished yet?', 12, 0, 33, 0, 0, 0, 28457, 0, 'Nass');
INSERT INTO `creature_text` VALUES (28532, 0, 0, 'Indeed. Watch this, $r.', 12, 0, 100, 1, 0, 0, 28760, 0, 'Bloodrose Datura');
INSERT INTO `creature_text` VALUES (28532, 1, 0, 'Here, troll... a gift!', 12, 0, 100, 0, 0, 0, 28505, 0, 'Bloodrose Datura');
INSERT INTO `creature_text` VALUES (28532, 2, 0, 'Ugh... disgusting!', 12, 0, 100, 0, 0, 0, 28509, 0, 'Bloodrose Datura');
INSERT INTO `creature_text` VALUES (28541, 0, 0, 'For me? Really, mon?', 12, 0, 100, 6, 0, 0, 28511, 0, 'Captured Drakkari Scout');
INSERT INTO `creature_text` VALUES (28541, 1, 0, 'It....it be beautiful!', 12, 0, 100, 5, 0, 0, 28512, 0, 'Captured Drakkari Scout');
INSERT INTO `creature_text` VALUES (28546, 0, 0, 'You wish to confront the master? You must first weather the storm!', 14, 0, 100, 0, 0, 14453, 30528, 0, 'Ionar');
INSERT INTO `creature_text` VALUES (28546, 1, 0, 'The slightest spark shall be your undoing.', 14, 0, 100, 0, 0, 14454, 30529, 0, 'Ionar');
INSERT INTO `creature_text` VALUES (28546, 1, 1, 'No one is safe!', 14, 0, 100, 0, 0, 14455, 30530, 0, 'Ionar');
INSERT INTO `creature_text` VALUES (28546, 2, 0, 'Shocking, I know.', 14, 0, 100, 0, 0, 14456, 30531, 0, 'Ionar');
INSERT INTO `creature_text` VALUES (28546, 2, 1, 'You attempt the impossible.', 14, 0, 100, 0, 0, 14457, 30532, 0, 'Ionar');
INSERT INTO `creature_text` VALUES (28546, 2, 2, 'Your spark of life is... extinguished.', 14, 0, 100, 0, 0, 14458, 30533, 0, 'Ionar');
INSERT INTO `creature_text` VALUES (28546, 3, 0, 'Master... you have guests.', 14, 0, 100, 0, 0, 14459, 30534, 0, 'Ionar');
INSERT INTO `creature_text` VALUES (28563, 0, 0, 'The structure above begins to hum with energy.', 42, 0, 0, 0, 0, 0, 28536, 0, 'Freya\'s Presence - On Call of the Lifewarden cast');
INSERT INTO `creature_text` VALUES (28566, 0, 0, 'Beginning the distillation in 5 seconds.', 12, 0, 100, 0, 0, 0, 28514, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 1, 0, 'Add another orange! Quickly!', 12, 0, 100, 25, 0, 0, 28515, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 2, 0, 'Put a papaya in the still!', 12, 0, 100, 25, 0, 0, 28517, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 3, 0, 'Add bananas!', 12, 0, 100, 25, 0, 0, 28516, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 4, 0, 'Pressure\'s too high! Open the pressure valve!', 12, 0, 100, 5, 0, 0, 28519, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 5, 0, 'The still needs heat! Light the brazier!', 12, 0, 100, 5, 0, 0, 28518, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 6, 0, 'Good job! Keep your eyes open, now.', 12, 0, 100, 4, 0, 0, 28534, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 6, 1, 'That\'ll do. Never know what it\'ll need next...', 12, 0, 100, 4, 0, 0, 28535, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 6, 2, 'Nicely handled! Stay on your toes!', 12, 0, 100, 4, 0, 0, 28533, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 6, 3, 'Well done! Be ready for anything!', 12, 0, 100, 4, 0, 0, 28524, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 7, 0, 'It\'s no good! I\'m shutting it down...', 12, 0, 100, 0, 0, 0, 28523, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 8, 0, 'We\'ve done it! Come get the cask.', 12, 0, 100, 0, 0, 0, 28521, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28586, 0, 0, 'I am the greatest of my father\'s sons! Your end has come!', 14, 0, 100, 0, 0, 14149, 31407, 0, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 1, 0, 'Give me your worst!', 14, 0, 100, 0, 0, 14150, 31408, 0, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 2, 0, 'Defend yourself, for all the good it will do!', 14, 0, 100, 0, 0, 14151, 31409, 0, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 3, 0, 'GRAAAAAH! Behold the fury of iron and steel!', 14, 0, 100, 0, 0, 14152, 31410, 0, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 4, 0, 'So ends your curse.', 14, 0, 100, 0, 0, 14153, 31411, 0, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 4, 1, 'Flesh... is... weak!', 14, 0, 100, 0, 0, 14154, 31412, 0, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 4, 2, '...', 14, 0, 100, 0, 0, 14155, 38461, 0, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 5, 0, 'How can it be...? Flesh is not... stronger!', 14, 0, 100, 0, 0, 14156, 31414, 0, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 6, 0, '%s switches to Defensive Stance!', 41, 0, 100, 0, 0, 0, 29834, 0, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 7, 0, '%s switches to Battle Stance!', 41, 0, 100, 0, 0, 0, 29832, 0, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 8, 0, '%s switches to Berserker Stance!', 41, 0, 100, 0, 0, 0, 29833, 0, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28587, 0, 0, 'It is you who have destroyed my children? You... shall... pay!', 14, 0, 100, 0, 0, 13960, 31415, 0, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 1, 0, 'Life from lifelessness... death for you.', 14, 0, 100, 0, 0, 13961, 31416, 0, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 1, 1, 'Nothing is wasted in the process. You will see....', 14, 0, 100, 0, 0, 13962, 31417, 0, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 2, 0, 'I will crush you beneath my boots!', 14, 0, 100, 0, 0, 13963, 31418, 0, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 2, 1, 'All my work... undone!', 14, 0, 100, 0, 0, 13964, 31419, 0, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 3, 0, 'The armies of iron will conquer all!', 14, 0, 100, 0, 0, 13965, 31420, 0, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 3, 1, 'Feh! Pathetic!', 14, 0, 100, 0, 0, 13966, 31421, 0, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 3, 2, 'You have cost me too much work!', 14, 0, 100, 0, 0, 13967, 31422, 0, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 4, 0, 'The master was right... to be concerned.', 14, 0, 100, 0, 0, 13968, 31423, 0, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 5, 0, '%s runs to his anvil!', 41, 0, 100, 0, 0, 0, 32214, 0, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 6, 0, '%s prepares to shatter his Brittle Golems!', 41, 0, 100, 0, 0, 0, 29823, 0, 'Volkhan');
INSERT INTO `creature_text` VALUES (28601, 0, 0, 'Fool!  You led us to the only being that could stand up to our armies!  You will never bring the Etymidian back to Northrend!', 12, 0, 100, 25, 0, 0, 29968, 0, 'High Cultist Herenn');
INSERT INTO `creature_text` VALUES (28859, 14, 0, 'I am unstoppable!', 14, 0, 100, 0, 2000, 14533, 31783, 0, 'Malygos - Buffed by a spark');
INSERT INTO `creature_text` VALUES (28859, 13, 0, 'The powers at work here exceed anything you could possibly imagine!', 14, 0, 100, 0, 5000, 14532, 31782, 0, 'Malygos - Surge of Power');
INSERT INTO `creature_text` VALUES (28859, 12, 0, '%s takes a deep breath...', 41, 0, 100, 0, 10000, 0, 33657, 0, 'Malygos - Surge of Power warning (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 10, 0, 'Now your benefactors make their appearance, but they are too late! The powers contained here are sufficient to destroy the world ten times over. What do you think they will do to you?', 14, 0, 100, 0, 13000, 14530, 31779, 0, 'Intro Phase 3');
INSERT INTO `creature_text` VALUES (28859, 11, 0, 'Submit!', 14, 0, 100, 0, 1000, 14531, 31780, 0, 'Malygos - Start phase 3');
INSERT INTO `creature_text` VALUES (28859, 9, 0, 'ENOUGH! If you intend to reclaim Azeroth\'s magic, then you shall have it!', 14, 0, 100, 0, 7000, 14529, 31778, 0, 'Malygos - End Phase 2');
INSERT INTO `creature_text` VALUES (28859, 8, 2, 'Your spirit will linger here forever!', 14, 0, 100, 0, 3800, 14528, 31777, 0, 'Malygos - Killed Player (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 8, 1, 'I am the Spell-Weaver! My power is infinite!', 14, 0, 100, 0, 5200, 14527, 31776, 0, 'Malygos - Killed Player (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 8, 0, 'Your energy will be put to good use.', 14, 0, 100, 0, 2000, 14526, 31775, 0, 'Malygos - Killed Player (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 7, 0, 'Watch helplessly as your hopes are swept away!', 14, 0, 100, 0, 4000, 14525, 31774, 0, 'Malygos - Magic Blast');
INSERT INTO `creature_text` VALUES (28859, 5, 0, 'Few have experienced the pain I will now inflict upon you!', 14, 0, 100, 0, 5500, 14523, 31765, 0, 'Malygos - Start phase 2');
INSERT INTO `creature_text` VALUES (28859, 6, 0, 'I will teach you ignorant children just how little you know of magic.', 14, 0, 100, 0, 7000, 14524, 31773, 0, 'Malygos - Anti-Magic Shell');
INSERT INTO `creature_text` VALUES (28859, 4, 0, 'I had hoped to end your lives quickly, but you have proven more... resilient than I anticipated. Nonetheless, your efforts are in vain. It is you reckless, careless mortals who are to blame for this war. I do what I must, and if it means your extinction.... then SO BE IT!!', 14, 0, 100, 0, 22900, 14522, 31772, 0, 'Malygos - End Phase One');
INSERT INTO `creature_text` VALUES (28859, 3, 2, 'How very naive.', 14, 0, 100, 0, 4800, 14521, 31769, 0, 'Malygos - Killed Player (Phase 1)');
INSERT INTO `creature_text` VALUES (28859, 3, 1, 'More artifacts to confiscate.', 14, 0, 100, 0, 2800, 14520, 31768, 0, 'Malygos - Killed Player (Phase 1)');
INSERT INTO `creature_text` VALUES (28859, 3, 0, 'Your stupidity has finally caught up with you!', 14, 0, 100, 0, 3250, 14519, 31767, 0, 'Malygos - Killed Player (Phase 1)');
INSERT INTO `creature_text` VALUES (28859, 2, 0, 'You will not succeed while I draw breath!', 14, 0, 100, 0, 3000, 14518, 31781, 0, 'Malygos - Begin to cast Deep Breath');
INSERT INTO `creature_text` VALUES (28914, 0, 0, 'Make haste, $n. Use the portal!', 12, 0, 100, 25, 0, 0, 29443, 0, 'Orbaz Bloodlane');
INSERT INTO `creature_text` VALUES (28923, 0, 0, 'I have witnessed the rise and fall of empires... the birth and extinction of entire species... Over countless millennia the foolishness of mortals has remained the only constant. Your presence here confirms this.', 14, 0, 100, 0, 0, 14160, 30536, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 1, 0, 'My master has shown me the future, and you have no place in it. Azeroth will be reborn in darkness. Yogg-Saron shall be released! The Pantheon shall fall!', 14, 0, 100, 0, 0, 14161, 30537, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 2, 0, 'What hope is there for you? None!', 14, 0, 100, 0, 0, 14162, 30535, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 3, 0, 'You cannot hide from fate!', 14, 0, 100, 0, 0, 14163, 30538, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 3, 1, 'Come closer. I will make it quick.', 14, 0, 100, 0, 0, 14164, 30539, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 3, 2, 'Your flesh cannot hold out for long.', 14, 0, 100, 0, 0, 14165, 30540, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 4, 0, 'Only mortal...', 14, 0, 100, 0, 0, 14166, 30541, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 4, 1, 'I... am... FOREVER!', 14, 0, 100, 0, 0, 14167, 30542, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 4, 2, 'What little time you had, you wasted!', 14, 0, 100, 0, 0, 14168, 30543, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 5, 0, 'You stare blindly into the abyss!', 14, 0, 100, 0, 0, 14169, 30545, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 6, 0, 'Your ignorance is profound. Can you not see where this path leads?', 14, 0, 100, 0, 0, 14170, 30546, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 7, 0, 'You cross the precipice of oblivion!', 14, 0, 100, 0, 0, 14171, 30547, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 8, 0, 'My death... heralds the end of this world.', 14, 0, 100, 0, 0, 14172, 30544, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 9, 0, '%s begins to cast Lightning Nova!', 41, 0, 100, 0, 0, 0, 29865, 0, 'Loken');
INSERT INTO `creature_text` VALUES (29280, 0, 0, 'Your sacrifice is a testament to your obedience.  Indeed you are worthy of this charge. Arise, and forever be known as Svala Sorrowgrave!', 14, 0, 0, 0, 0, 14732, 29688, 0, 'Image of Arthas SAY_DIALOG_OF_ARTHAS_1');
INSERT INTO `creature_text` VALUES (29280, 1, 0, 'Your first test awaits you. Destroy our uninvited guests.', 14, 0, 0, 0, 0, 14733, 29689, 0, 'Image of Arthas SAY_DIALOG_OF_ARTHAS_2');
INSERT INTO `creature_text` VALUES (29281, 0, 0, 'My liege! I have done as you asked, and now beseech you for your blessing!', 14, 0, 0, 0, 0, 13856, 29684, 0, 'Svala SAY_DIALOG_WITH_ARTHAS_1');
INSERT INTO `creature_text` VALUES (29327, 0, 0, 'It\'s an angry male!', 42, 0, 100, 0, 0, 0, 33191, 0, 'Male Frost Leopard');
