INSERT INTO `creature_text` VALUES (30190, 3, 0, 'The ore used in this sample originated deep within the crust of Azeroth and was recently unearthed.', 12, 0, 100, 275, 0, 0, 30850, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 4, 0, 'The saronite in this sample is nearly pure. Ores like this do not occur naturally on the surface of Azeroth.', 12, 0, 100, 0, 0, 0, 30851, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 5, 0, 'This ore can only have been created by a powerful, malevolent force. Attempting to access databanks for more information ...', 12, 0, 100, 1, 0, 0, 30854, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 6, 0, 'Access denied?!', 12, 0, 100, 6, 0, 0, 30859, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 7, 0, 'Further information can only be accessed from the archives within Ulduar, by order of Keeper Loken.', 12, 0, 100, 1, 0, 0, 30860, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 8, 0, 'But one thing is for certain: this plate\'s size, thickness, and bolt holes point to a use as armor for a colossal structure.', 12, 0, 100, 1, 0, 0, 30867, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 9, 0, 'Directive completed. Returning to standby mode.', 12, 0, 100, 1, 0, 0, 30861, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30224, 0, 0, 'What\'s on yer mind, lad?', 12, 0, 100, 6, 0, 0, 31065, 0, 'Crusader Lord Dalfors');
