INSERT INTO `creature_text` VALUES (40352, 2, 0, 'Our leader, Vol\'jin, son of Sen\'jin, issued de call to all Darkspears: reclaim de Echo Isles for our tribe.', 12, 0, 100, 1, 0, 0, 40389, 0, 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES (40352, 3, 0, 'Spirits! I offer me own body to you! Speak through me! Is de time right for mighty Vol\'jin\'s undertaking?', 12, 0, 100, 5, 0, 0, 40390, 0, 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES (40352, 4, 0, 'De ancestors hear ya, witch doctor!', 12, 0, 100, 1, 0, 0, 40391, 0, 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES (40352, 5, 0, 'Know dat your plans please us, Darkspears. De son of Sen\'jin walks de right path.', 12, 0, 100, 1, 0, 0, 40392, 0, 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES (40352, 6, 0, 'De task in front of ya will not be easy, but ya have our blessin\'.', 12, 0, 100, 1, 0, 0, 40393, 0, 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES (40352, 7, 0, 'Ya gave up your home an\' ya gave up de loas of your ancestors when ya left de Echo Isles. Dey will not be pleased dat you been ignorin\' dem.', 12, 0, 100, 1, 0, 0, 40394, 0, 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES (40352, 8, 0, 'Ya must make amends wit\' Bwonsamdi, de guardian of de dead, if ya hope to defeat Zalazane. It be de only way. Tell de son of Sen\'jin dese things.', 12, 0, 100, 1, 0, 0, 40395, 0, 'Witch Doctor Hez\'tok');
INSERT INTO `creature_text` VALUES (40352, 9, 0, 'De spirits have blessed us wit\' an answer! De Echo Isles will be ours again!', 12, 0, 100, 5, 0, 0, 40396, 0, 'Witch Doctor Hez\'tok');
