INSERT INTO `creature_text` VALUES (26902, 0, 0, 'How dare you summon me without an offering!', 42, 0, 0, 0, 0, 0, 26098, 0, 'Essence of Warlord Jin\'arrak');
INSERT INTO `creature_text` VALUES (36494, 0, 0, 'Tiny creatures under feet, you bring Garfrost something good to eat!', 14, 0, 100, 0, 0, 16912, 37218, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (36494, 1, 0, 'Axe too weak. Garfrost make better and CRUSH YOU.', 14, 0, 100, 0, 0, 16916, 36445, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (36494, 2, 0, 'Garfrost tired of puny mortals. Now your bones will freeze!', 14, 0, 100, 0, 0, 16917, 36444, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (36494, 3, 0, 'Garfrost hope giant underpants clean. Save boss great shame. For later.', 14, 0, 100, 0, 0, 16915, 37221, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (36494, 4, 0, 'Will save for snack. For later.', 14, 0, 100, 0, 0, 16913, 37219, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (36494, 4, 1, 'That one maybe not so good to eat now. Stupid Garfrost! BAD! BAD!', 14, 0, 100, 0, 0, 16914, 37220, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (36494, 5, 0, '%s hurls a massive saronite boulder at you!', 42, 0, 100, 0, 0, 0, 37438, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (36494, 6, 0, '%s casts |cFF00AACCDeep Freeze|r at $n.', 41, 0, 100, 0, 0, 0, 37260, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (33134, 5, 1, 'Powerless to act....', 14, 0, 100, 0, 0, 15778, 34349, 0, 'Sara - SAY_SARA_KILL');
INSERT INTO `creature_text` VALUES (33134, 5, 0, 'Could they have been saved?', 14, 0, 100, 0, 0, 15779, 34350, 0, 'Sara - SAY_SARA_KILL');
INSERT INTO `creature_text` VALUES (33134, 4, 0, 'Let hatred and rage guide your blows!', 14, 0, 100, 0, 0, 15774, 34345, 0, 'SAY_SARA_BLESSING_HIT');
INSERT INTO `creature_text` VALUES (33134, 3, 0, 'Yes! YES! Show them no mercy! Give no pause to your attacks!', 14, 0, 100, 0, 0, 15773, 34344, 0, 'Sara - SAY_SARA_FERVOR_HIT');
INSERT INTO `creature_text` VALUES (33134, 2, 0, 'The time to strike at the head of the beast will soon be upon us! Focus your anger and hatred on his minions!', 14, 0, 100, 457, 0, 15775, 34346, 0, 'Sara - SAY_SARA_AGGRO');
INSERT INTO `creature_text` VALUES (33134, 1, 0, 'What do you want from me? Leave me alone!', 14, 0, 100, 0, 0, 15772, 34343, 0, 'Sara - SAY_SARA_ULDUAR_SCREAM_2');
INSERT INTO `creature_text` VALUES (33134, 0, 0, 'HELP ME! Please get them off of me!', 14, 0, 100, 0, 0, 15771, 34342, 0, 'Sara - SAY_SARA_ULDUAR_SCREAM_1');
