INSERT INTO `creature_text` VALUES (19216, 0, 0, 'Friends, it is my great pleasure to welcome you to the temple today.', 12, 0, 100, 1, 0, 0, 16735, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 1, 0, 'A\'dal has been most generous in his revelations to me, a humble servant of the Light.', 12, 0, 100, 0, 0, 0, 16740, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 2, 0, 'In accordance with my divine calling, I must share these revealed truths to all who would hear.', 12, 0, 100, 0, 0, 0, 16741, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 3, 0, 'It has been made known to me that inside each of us, the Light resides...', 12, 0, 100, 0, 0, 0, 16742, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 4, 0, '...that it is a gift, given freely to all naturally born beings.', 12, 0, 100, 0, 0, 0, 16743, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 5, 0, 'It manifests itself as a feeling, small at first and easily ignored, that confirms truths and subtly prods one to do good.', 12, 0, 100, 0, 0, 0, 16752, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 6, 0, 'Simple kindnesses, charitable deeds, service to those in need. These are all fruits of the Light.', 12, 0, 100, 0, 0, 0, 16753, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 7, 0, 'It rewards those who heed its promptings with blessings, both seen and unseen.', 12, 0, 100, 1, 0, 0, 16754, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 8, 0, 'Personal reservoirs of hope and faith are strengthened, and one\'s capacity for greater light increases.', 12, 0, 100, 0, 0, 0, 16755, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 9, 0, 'Over the course of time, through obedience to the Light\'s guidance, one becomes more sensitive to its voice, and its power.', 12, 0, 100, 273, 0, 0, 16756, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 10, 0, 'Great is the healing and blessing power of the Light\'s most diligent followers.', 12, 0, 100, 0, 0, 0, 16757, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 11, 0, 'Sadly, there are those who wander through mortality in defiance of the Light.', 12, 0, 100, 1, 0, 0, 16761, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 12, 0, 'From these the Light withdraws, until only darkness remains.', 12, 0, 100, 0, 0, 0, 16762, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 13, 0, 'Where there is no light, despair, loathing and rage thrive.', 12, 0, 100, 5, 0, 0, 16763, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 14, 0, 'We are born into a perilous age, where the forces of darkness are determined to bring about our destruction.', 12, 0, 100, 1, 0, 0, 16764, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 15, 0, 'These are the times to find yourselves in unison with the Light.', 12, 0, 100, 5, 0, 0, 16765, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 16, 0, 'Hear my words, and let the Light inside of you confirm the truth of them.', 12, 0, 100, 0, 0, 0, 16766, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 17, 0, 'Embrace the light that is in you, my friends! Deny yourself all darkness...', 12, 0, 100, 5, 0, 0, 16775, 0, 'Grand Anchorite Almonen');
INSERT INTO `creature_text` VALUES (19216, 18, 0, 'We must endure these trying times faithfully, and one day we will find ourselves victorious.', 12, 0, 100, 5, 0, 0, 16776, 0, 'Grand Anchorite Almonen');
