INSERT INTO `creature_text` VALUES (23602, 0, 2, 'Wouldn\'t you like to bring those days back? I have some friends who know just the way to do that...', 12, 7, 100, 1, 0, 0, 22052, 0, 'Deserter Agitator');
INSERT INTO `creature_text` VALUES (23602, 1, 0, 'These people will know Admiral Proudmoore for the true hero he was!', 12, 7, 100, 5, 0, 0, 22048, 0, 'Deserter Agitator');
INSERT INTO `creature_text` VALUES (23602, 1, 1, 'You can\'t stop us from exposing the truth about Jaina\'s cowardice!', 12, 7, 100, 5, 0, 0, 22047, 0, 'Deserter Agitator');
INSERT INTO `creature_text` VALUES (23602, 1, 2, 'If you think you can stop us, you\'re mistaken, \'friend.\'', 12, 7, 100, 5, 0, 0, 22046, 0, 'Deserter Agitator');
INSERT INTO `creature_text` VALUES (23602, 1, 3, 'It\'s people like you who weaken the Alliance and invite the Horde to take away all we\'ve fought for!', 12, 7, 100, 5, 0, 0, 22049, 0, 'Deserter Agitator');
INSERT INTO `creature_text` VALUES (17808, 5, 0, 'The clock... is still... ticking.', 14, 0, 100, 0, 0, 10982, 0, 0, 'on Death Text');
INSERT INTO `creature_text` VALUES (17808, 4, 2, 'Pity, no time for a slow death!', 14, 0, 50, 0, 0, 11039, 0, 0, 'on Player Death Text');
INSERT INTO `creature_text` VALUES (17808, 4, 1, 'Scream for me!', 14, 0, 50, 0, 0, 11038, 0, 0, 'on Player Death Text');
INSERT INTO `creature_text` VALUES (17808, 4, 0, 'Your hopes are lost!', 14, 0, 50, 0, 0, 10981, 0, 0, 'on Player Death Text');
INSERT INTO `creature_text` VALUES (17808, 3, 1, 'Earth and sky shall burn!', 14, 0, 50, 0, 0, 11036, 0, 0, 'on Spell Cast Text');
INSERT INTO `creature_text` VALUES (17808, 3, 0, 'Let fire rain from above!', 14, 0, 50, 0, 0, 10980, 0, 0, 'on Spell Cast Text');
INSERT INTO `creature_text` VALUES (17808, 2, 1, 'Sweet dreams...', 14, 0, 50, 0, 0, 11545, 0, 0, 'on Spell Cast Text');
INSERT INTO `creature_text` VALUES (17808, 2, 0, 'You look tired...', 14, 0, 50, 0, 0, 10978, 0, 0, 'on Spell Cast Text');
INSERT INTO `creature_text` VALUES (17808, 1, 1, 'Pestilence upon you!', 14, 0, 50, 0, 0, 11037, 0, 0, 'on Spell Cast Text');
INSERT INTO `creature_text` VALUES (17808, 1, 0, 'The swarm is eager to feed!', 14, 0, 50, 0, 0, 10979, 0, 0, 'on Spell Cast Text');
INSERT INTO `creature_text` VALUES (17808, 0, 0, 'You are defenders of a doomed world! Flee here, and perhaps you will prolong your pathetic lives!', 14, 0, 100, 0, 0, 10977, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (17842, 3, 0, 'Your time is almost... up.', 14, 0, 100, 0, 0, 11002, 0, 0, 'on Death Text');
INSERT INTO `creature_text` VALUES (17842, 2, 2, 'No one is going to save you.', 14, 0, 50, 0, 0, 11048, 0, 0, 'on Player Kill Text');
INSERT INTO `creature_text` VALUES (17842, 2, 1, 'Don\'t fight it.', 14, 0, 50, 0, 0, 11047, 0, 0, 'on Player Kill Text');
INSERT INTO `creature_text` VALUES (17842, 2, 0, 'Reesh, hokta!', 14, 0, 50, 0, 0, 11001, 0, 0, 'on Player Kill Text');
INSERT INTO `creature_text` VALUES (17842, 1, 1, 'Suffer you despicable insect!', 14, 0, 50, 0, 0, 11000, 0, 0, 'on Spell Cast Text');
INSERT INTO `creature_text` VALUES (17842, 1, 0, 'Just a taste... of what awaits you.', 14, 0, 50, 0, 0, 11046, 0, 0, 'on Spell Cast Text');
INSERT INTO `creature_text` VALUES (17842, 0, 0, 'Abandon all hope! The Legion has returned to finish what was begun so many years ago. This time, there will be no escape!!', 14, 0, 100, 0, 0, 10999, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (17888, 2, 1, 'Miserable nuisance!', 14, 0, 50, 0, 0, 11054, 0, 0, 'on Player Kill Text');
INSERT INTO `creature_text` VALUES (17888, 2, 0, 'You... are nothing!', 14, 0, 50, 0, 0, 11053, 0, 0, 'on Player Kill Text');
INSERT INTO `creature_text` VALUES (17888, 1, 1, 'You... are marked.', 14, 0, 50, 0, 0, 11052, 0, 0, 'on Spell Cast Text');
INSERT INTO `creature_text` VALUES (17888, 1, 0, 'Your death will be a painful one.', 14, 0, 50, 0, 0, 11016, 0, 0, 'on Spell Cast Text');
INSERT INTO `creature_text` VALUES (17888, 0, 0, 'Cry for mercy! Your meaningless lives will soon be forfeit!', 14, 0, 100, 0, 0, 11015, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (17767, 3, 2, 'Your world is ours now.', 14, 0, 50, 0, 0, 11056, 0, 0, 'on Player Kill Text');
INSERT INTO `creature_text` VALUES (17767, 3, 1, 'Victory to the Legion!', 14, 0, 50, 0, 0, 11057, 0, 0, 'on Player Kill Text');
INSERT INTO `creature_text` VALUES (17767, 3, 0, 'All life must perish!', 14, 0, 50, 0, 0, 11025, 0, 0, 'on Player Kill Text');
INSERT INTO `creature_text` VALUES (17767, 2, 1, 'Ashes to ashes, dust to dust.', 14, 0, 50, 0, 0, 11055, 0, 0, 'on Spell Cast Text');
INSERT INTO `creature_text` VALUES (17767, 2, 0, 'Crumble and rot!', 14, 0, 50, 0, 0, 11023, 0, 0, 'on Spell Cast Text');
INSERT INTO `creature_text` VALUES (17767, 1, 1, 'It will be much colder in your grave.', 14, 0, 50, 0, 0, 11058, 0, 0, 'on Spell Cast Text');
INSERT INTO `creature_text` VALUES (17767, 1, 0, 'Succumb to the icy chill... of death!', 14, 0, 50, 0, 0, 11024, 0, 0, 'on Spell Cast Text');
INSERT INTO `creature_text` VALUES (17767, 0, 0, 'The Legion\'s final conquest has begun! Once again the subjugation of this world is within our grasp. Let none survive!', 14, 0, 100, 0, 0, 11022, 0, 0, 'on Spawn Text');
INSERT INTO `creature_text` VALUES (17968, 1, 0, 'Your resistance is insignificant.', 14, 0, 100, 0, 0, 10987, 18257, 0, 'Archimonde - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (17968, 2, 0, 'This world will burn!', 14, 0, 100, 0, 0, 10990, 18262, 0, 'Archimonde - SAY_DOOMFIRE1');
INSERT INTO `creature_text` VALUES (17968, 2, 1, 'Manach sheek-thrish!', 14, 0, 100, 0, 0, 11041, 18263, 0, 'Archimonde - SAY_DOOMFIRE2');
INSERT INTO `creature_text` VALUES (17968, 3, 0, 'A-kreesh!', 14, 0, 100, 0, 0, 11041, 18260, 0, 'Archimonde - SAY_AIR_BURST1');
INSERT INTO `creature_text` VALUES (17968, 3, 1, 'Away, vermin!', 14, 0, 100, 0, 0, 10989, 18261, 0, 'Archimonde - SAY_AIR_BURST2');
INSERT INTO `creature_text` VALUES (17968, 4, 0, 'All creation will be devoured.', 14, 0, 100, 0, 0, 11044, 18265, 0, 'Archimonde - SAY_SLAY1');
INSERT INTO `creature_text` VALUES (17968, 4, 1, 'Your soul will languish for eternity.', 14, 0, 100, 0, 0, 10991, 18264, 0, 'Archimonde - SAY_SLAY12');
INSERT INTO `creature_text` VALUES (17968, 4, 2, 'I am the coming of the end!', 14, 0, 100, 0, 0, 11045, 18266, 0, 'Archimonde - SAY_SLAY3');
INSERT INTO `creature_text` VALUES (17968, 5, 0, 'At last it is here! Mourn and lament the passing of all you have ever known, and all that would have been! Hach min corai!', 14, 0, 100, 0, 0, 10993, 14990, 0, 'Archimonde - SAY_ENRAGE');
INSERT INTO `creature_text` VALUES (17968, 6, 0, 'No, it cannot be! Nooo!', 14, 0, 100, 0, 0, 10992, 14991, 0, 'Archimonde - SAY_DEATH');
INSERT INTO `creature_text` VALUES (17968, 7, 0, 'You are mine now.', 14, 0, 100, 0, 0, 10988, 18258, 0, 'Archimonde - SAY_SOUL_CHARGE1');
INSERT INTO `creature_text` VALUES (17968, 7, 1, 'Bow to my will.', 14, 0, 100, 0, 0, 11042, 18259, 0, 'Archimonde - SAY_SOUL_CHARGE2');
INSERT INTO `creature_text` VALUES (30840, 1, 1, 'Say hello to my little friend!', 12, 0, 100, 0, 0, 0, 31486, 0, 'Munch arrival');
INSERT INTO `creature_text` VALUES (30840, 2, 2, 'Arise, Melt!', 12, 0, 100, 0, 0, 0, 31487, 0, 'Summon Melts');
INSERT INTO `creature_text` VALUES (30840, 3, 3, 'We\'re done here. I\'m out.', 12, 0, 100, 0, 0, 0, 31488, 0, 'Munch oppening portal');
INSERT INTO `creature_text` VALUES (30839, 1, 1, 'Too weak to finish your own battles? Very well then...', 12, 0, 100, 0, 0, 0, 31489, 0, 'Jayde arrival');
INSERT INTO `creature_text` VALUES (30839, 2, 2, 'Anything else you need help with before I go? Right, I thought not...', 12, 0, 100, 0, 0, 0, 31490, 0, 'Oppens portal and dissapears');
INSERT INTO `creature_text` VALUES (30838, 1, 1, 'You challenge death itself! Come then, \"King.\" Face the might of Mograine!', 12, 0, 100, 0, 0, 0, 31455, 0, 'Mograine\"s arrival');
INSERT INTO `creature_text` VALUES (30838, 2, 1, 'I will see you back at the Valley of Echoes. Suffer well, ally.', 12, 0, 100, 0, 0, 0, 31456, 0, 'Opens portal');
INSERT INTO `creature_text` VALUES (27003, 0, 0, 'I know why you\'ve come - one of those foolish Magnataur on the plains meddled and managed to get the dragons involved.  Do you enjoy serving them like a dog?', 14, 0, 100, 0, 0, 0, 26544, 0, 'Dregmar Runebrand - yell');
INSERT INTO `creature_text` VALUES (27003, 1, 0, 'You seek their leader... little thing, you wage war against the clans of Grom\'thar the Thunderbringer himself.  Don\'t be so eager to rush to your death.', 14, 0, 100, 0, 0, 0, 26186, 0, 'Dregmar Runebrand yell');
INSERT INTO `creature_text` VALUES (27003, 2, 0, 'Hah!  So be it.  Blow the horn of a magnataur leader at the ring of torches south of the Blue Dragonshrine.  Make peace with your gods... Grom\'thar will come.  ', 14, 0, 100, 0, 0, 0, 26187, 0, 'Dregmar Runebrand yell');
INSERT INTO `creature_text` VALUES (27482, 0, 0, 'I\'d nearly given up. You\'ve given me new life!', 12, 0, 50, 0, 0, 0, 26684, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (27482, 0, 1, 'Bless you, friend. I nearly expired....', 12, 0, 50, 0, 0, 0, 26682, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (27482, 0, 2, 'Without your help, I surely would have died....', 12, 0, 50, 0, 0, 0, 26685, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (27482, 0, 3, 'Thank you, $r!', 12, 0, 50, 0, 0, 0, 26683, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (28041, 0, 0, 'Careful here, $c. These trolls killed their own snake god!', 15, 0, 50, 0, 0, 0, 27449, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 1, 'Watch your back. These Drakkari are a nasty lot.', 15, 0, 50, 0, 0, 0, 27448, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 2, 'These Drakkari are just bad news. We need to leave and head to Justice Keep!', 15, 0, 50, 0, 0, 0, 27452, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 3, 'See you around.', 15, 0, 50, 0, 0, 0, 27447, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 4, 'I wonder where we\'re headed to. And who\'s going to deal with these guys?', 15, 0, 50, 0, 0, 0, 27451, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 5, 'Right. I\'d better get back to the sergeant then.', 15, 0, 50, 0, 0, 0, 27446, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 6, '$n? I heard you were dead.', 15, 0, 50, 0, 0, 0, 35005, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (22019, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Enrage');
INSERT INTO `creature_text` VALUES (26076, 0, 0, 'What is the meaning of this?! I have not yet finished my feast!', 14, 0, 100, 1, 1000, 0, 24586, 0, 'Nafarset on activation');
INSERT INTO `creature_text` VALUES (30461, 0, 0, '%s lets out a bellowing roar as she descends upon the nest.', 16, 0, 100, 15, 0, 0, 31158, 0, 'Veranus');
INSERT INTO `creature_text` VALUES (30462, 0, 0, 'Look out below!', 14, 0, 100, 0, 0, 0, 31159, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (30462, 1, 0, 'Easy there, girl!  Don\'t you recognize your old master?', 12, 0, 100, 0, 0, 0, 31160, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (30462, 2, 0, 'I will see you at the Temple of Storms.  Looks like I\'m going to have to break her in again.', 12, 0, 100, 0, 0, 0, 31161, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (2768, 9, 0, 'Doctor Draxlegauge will give you further instructions, $n. Many thanks for your help!', 12, 0, 100, 0, 0, 0, 890, 0, 'Professor Phizzlethorpe - SAY_PROGRESS_9');
INSERT INTO `creature_text` VALUES (34996, 1, 0, 'Hailing from the deepest, darkest caverns of the Storm Peaks, Gormok the Impaler! Battle on, heroes!', 14, 0, 100, 0, 0, 16038, 35710, 0, 'Highlord Tirion Fordring - Summing Gormok the Impaler');
INSERT INTO `creature_text` VALUES (34996, 2, 0, 'Steel yourselves, heroes, for the twin terrors, Acidmaw and Dreadscale, enter the arena!', 14, 0, 100, 0, 0, 16039, 35711, 0, 'Highlord Tirion Fordring - Summing Acidmaw and Dreadscale');
INSERT INTO `creature_text` VALUES (34996, 3, 0, 'The air itself freezes with the introduction of our next combatant, Icehowl! Kill or be killed, champions!', 14, 0, 100, 0, 0, 16040, 35712, 0, 'Highlord Tirion Fordring Summing Icehowl');
INSERT INTO `creature_text` VALUES (34996, 4, 0, 'The monstrous menagerie has been vanquished!', 14, 0, 100, 0, 0, 16041, 35713, 0, 'Highlord Tirion Fordring - Northrend Beasts Done');
INSERT INTO `creature_text` VALUES (34996, 5, 0, 'Tragic... They fought valiantly, but the beasts of Northrend triumphed. Let us observe a moment of silence for our fallen heroes.', 14, 0, 0, 0, 0, 16042, 35714, 0, 'Highlord Tirion Fordring - Northrend Beasts FAIL');
INSERT INTO `creature_text` VALUES (34996, 6, 0, 'Grand Warlock Wilfred Fizzlebang will summon forth your next challenge. Stand by for his entry.', 14, 0, 100, 0, 0, 16043, 35715, 0, 'Highlord Tirion Fordring - Summing Wilfred Fizzlebang');
INSERT INTO `creature_text` VALUES (34996, 7, 0, 'Quickly, heroes, destroy the demon lord before it can open a portal to its twisted demonic realm!', 14, 0, 100, 5, 0, 16044, 35716, 0, 'Highlord Tirion Fordring to Wilfred Fizzlebang - Lord Jaraxxus Intro');
INSERT INTO `creature_text` VALUES (34996, 8, 0, 'The loss of Wilfred Fizzlebang, while unfortunate, should be a lesson to those that dare dabble in dark magic. Alas, you are victorious and now must face the next challenge.', 14, 0, 100, 0, 0, 16045, 35717, 0, 'Highlord Tirion Fordring - Lord Jaraxxus Outro');
INSERT INTO `creature_text` VALUES (34996, 9, 0, 'Everyone calm down! Compose yourselves! There is no conspiracy at play here! The warlock acted on his own volition, outside of influences from the Alliance. The tournament must go on!', 14, 0, 100, 5, 0, 16046, 35718, 0, 'Highlord Tirion Fordring - Lord Jaraxxus Outro');
INSERT INTO `creature_text` VALUES (34996, 10, 0, 'The next battle will be against the Argent Crusade\'s most powerful knights! Only by defeating them will you be deemed worthy...', 14, 0, 100, 0, 0, 16047, 35719, 0, 'Highlord Tirion Fordring - Faction Champions Intro');
INSERT INTO `creature_text` VALUES (34996, 11, 0, 'Very well. I will allow it. Fight with honor!', 14, 0, 100, 1, 0, 16048, 35720, 0, 'Highlord Tirion Fordring - Faction Champions Intro');
INSERT INTO `creature_text` VALUES (34996, 12, 0, 'A shallow and tragic victory. We are weaker as a whole from the losses suffered today. Who but the Lich King could benefit from such foolishness? Great warriors have lost their lives. And for what? The true threat looms ahead - the Lich King awaits us all in death.', 14, 0, 100, 0, 0, 16049, 35721, 0, 'Highlord Tirion Fordring - Faction Champions Outro');
INSERT INTO `creature_text` VALUES (34996, 13, 0, 'Only by working together will you overcome the final challenge. From the depths of Icecrown come two of the Scourge\'s most powerful lieutenants: fearsome val\'kyr, winged harbingers of the Lich King!', 14, 0, 100, 0, 0, 16050, 35722, 0, 'Highlord Tirion Fordring - Twin Val\'kyr Intro');
INSERT INTO `creature_text` VALUES (34996, 14, 0, 'Let the games begin!', 14, 0, 100, 0, 0, 16037, 35709, 0, 'Highlord Tirion Fordring - Twin Val\'kyr Intro');
INSERT INTO `creature_text` VALUES (34996, 15, 0, 'A mighty blow has been dealt to the Lich King! You have proven yourselves as able bodied champions of the Argent Crusade. Together we will strike against Icecrown Citadel and destroy what remains of the Scourge! There is no challenge that we cannot face united!', 14, 0, 100, 5, 0, 16051, 35723, 0, 'Highlord Tirion Fordring - Anub\'arak Intro');
INSERT INTO `creature_text` VALUES (34996, 16, 0, 'Arthas! You are hopelessly outnumbered! Lay down Frostmourne and I will grant you a just death.', 14, 0, 100, 25, 0, 16052, 35724, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (34995, 3, 0, 'Show them no mercy, Horde champions! LOK\'TAR OGAR! ', 14, 0, 0, 0, 0, 16022, 35701, 0, 'Garrosh - Faction Champions Intro');
INSERT INTO `creature_text` VALUES (34995, 2, 0, 'The Horde demands justice! We challenge the Alliance! Allow us to battle in place of your knights, paladin. We will show these dogs what it means to insult the Horde.', 14, 0, 100, 1, 0, 16023, 35703, 0, 'Garrosh Hellscream - Faction Champions Intro');
INSERT INTO `creature_text` VALUES (34995, 1, 0, 'Treacherous Alliance dogs! You summon a demon lord against warriors of the Horde? Your deaths will be swift!', 14, 0, 100, 5, 0, 16021, 35699, 0, 'Garrosh Hellscream - Lord Jaraxxus Outro');
INSERT INTO `creature_text` VALUES (34995, 0, 0, 'I\'ve seen more worthy challenges in the Ring of Blood. You waste our time, paladin.', 14, 0, 100, 1, 0, 16026, 35707, 0, 'Garrosh Hellscream  - Northrend Beasts Outro');
INSERT INTO `creature_text` VALUES (34990, 6, 3, 'Worthless scrub.', 14, 0, 100, 25, 0, 16062, 35686, 0, 'King Varian Wrynn  - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES (34990, 6, 2, 'Is this the best the Horde has to offer?', 14, 0, 100, 6, 0, 16063, 35687, 0, 'King Varian Wrynn  - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES (34990, 6, 1, 'HAH!', 14, 0, 100, 5, 0, 16060, 35684, 0, 'King Varian Wrynn - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES (34990, 6, 0, 'Hardly a challenge.', 14, 0, 100, 274, 0, 16061, 35685, 0, 'King Varian Wrynn - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES (34990, 4, 0, 'GLORY TO THE ALLIANCE!', 14, 0, 100, 0, 0, 16067, 35691, 0, 'King Varian Wrynn - Victory');
INSERT INTO `creature_text` VALUES (34990, 5, 0, 'Not even the Lich King\'s most powerful minions can stand against the Alliance. All hail our victors!', 14, 0, 0, 0, 0, 16068, 35692, 0, 'King Varian Wrynn - Faction Champions Outro');
INSERT INTO `creature_text` VALUES (34990, 3, 0, 'Fight for the glory of the Alliance, heroes! Honor your king and your people!', 14, 0, 100, 5, 0, 16065, 35689, 0, 'King Varian Wrynn - Faction Champions Intro');
INSERT INTO `creature_text` VALUES (34990, 2, 0, 'Our honor has been besmirched! They make wild claims and false accusations against us. I demand justice! Allow my champions to fight in place of your knights, Tirion. We challenge the Horde!', 14, 0, 100, 5, 0, 16066, 35690, 0, 'King Varian Wrynn - Faction Champions Intro');
INSERT INTO `creature_text` VALUES (34990, 1, 0, 'The Alliance doesn\'t need the help of a demon lord to deal with Horde filth! Come, pig!', 14, 0, 100, 5, 0, 16064, 35688, 0, 'King Varian Wrynn - Lord Jaraxxus Outro');
INSERT INTO `creature_text` VALUES (34990, 0, 0, 'Your beasts will be no match for my champions, Tirion!', 14, 0, 0, 0, 0, 16069, 35693, 0, 'King Varian Wrynn - Northrend Beasts Outro');
INSERT INTO `creature_text` VALUES (36095, 0, 0, 'Champions, you\'re alive! Not only have you defeated every challenge of the Trial of the Crusader, but also thwarted Arthas\' plans! Your skill and cunning will prove to be a powerful weapon against the Scourge. Well done! Allow one of the Crusade\'s mages to transport you to the surface!', 14, 0, 100, 5, 0, 16053, 36129, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (36095, 1, 0, 'Let me hand you the chests as a reward, and let its contents will serve you faithfully in the campaign against Arthas in the heart of the Icecrown Citadel!', 41, 0, 0, 0, 0, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (34796, 0, 0, 'My slaves! Destroy the enemy!', 41, 0, 0, 0, 0, 0, 0, 0, 'Gormok the Impaler - Snowball');
INSERT INTO `creature_text` VALUES (35144, 0, 0, 'Upon seeing its companion perish, %s becomes enraged!', 41, 0, 100, 0, 0, 0, 36147, 0, 'Acidmaw to Beasts Controller - Enrage');
INSERT INTO `creature_text` VALUES (34799, 0, 0, 'Upon seeing its companion perish, %s becomes enraged!', 41, 0, 100, 0, 0, 0, 36147, 0, 'Dreadscale to Beasts Controller - Enrage');
INSERT INTO `creature_text` VALUES (34797, 0, 0, '%s glares at $n and lets out a bellowing roar!', 41, 0, 100, 0, 0, 0, 35675, 0, 'Icehowl - Start');
INSERT INTO `creature_text` VALUES (34797, 1, 0, '%s crashes into the Coliseum wall and is stunned!', 41, 0, 100, 0, 0, 0, 35317, 0, 'Icehowl - Crash');
INSERT INTO `creature_text` VALUES (34797, 2, 0, 'Trampling combatants underfoot, %s goes into a frothing rage!', 41, 0, 100, 0, 0, 0, 35318, 0, 'Icehowl - Fail');
INSERT INTO `creature_text` VALUES (35458, 0, 0, 'Thank you, Highlord. Now, challengers, I will begin the ritual of summoning. When I am done a fearsome doomguard will appear!', 14, 0, 100, 2, 0, 16268, 35726, 0, 'Wilfred Fizzlebang - Intro');
INSERT INTO `creature_text` VALUES (35458, 1, 0, 'Prepare for oblivion!', 14, 0, 100, 0, 0, 16269, 35727, 0, 'Wilfred Fizzlebang - Intro');
INSERT INTO `creature_text` VALUES (35458, 2, 0, 'A-HA! I\'ve done it! Behold the absolute power of Wilfred Fizzlebang, master summoner! You are bound to me, demon!', 14, 0, 100, 5, 0, 16270, 35728, 0, 'Wilfred Fizzlebang to Wilfred Fizzlebang - Intro');
INSERT INTO `creature_text` VALUES (35458, 3, 0, 'But I\'m in charge here...', 14, 0, 100, 5, 0, 16271, 35729, 0, 'Wilfred Fizzlebang to Wilfred Fizzlebang - Death');
INSERT INTO `creature_text` VALUES (34780, 0, 0, 'Trifling gnome! Your arrogance will be your undoing!', 14, 0, 100, 397, 0, 16143, 35730, 0, 'Lord Jaraxxus to Wilfred Fizzlebang - Intro');
INSERT INTO `creature_text` VALUES (34780, 1, 0, 'You face Jaraxxus, Eredar Lord of the Burning Legion!', 14, 0, 100, 0, 0, 16144, 35731, 0, 'Lord Jaraxxus - Aggro');
INSERT INTO `creature_text` VALUES (34780, 2, 0, '$n has |cFFFF0000Legion Flames!|r\n', 41, 0, 100, 0, 0, 0, 35194, 0, 'Lord Jaraxxus - Legion Flame');
INSERT INTO `creature_text` VALUES (34780, 3, 0, '%s creates a Nether Portal!', 41, 0, 100, 0, 0, 16150, 35190, 0, 'Lord Jaraxxus - Summing Nether Portal');
INSERT INTO `creature_text` VALUES (34780, 4, 0, 'Come forth, sister! Your master calls!', 14, 0, 100, 0, 0, 16150, 35736, 0, 'Lord Jaraxxus - Summoning Mistress of Pain');
INSERT INTO `creature_text` VALUES (34780, 5, 0, '$n has |cFF00FFFFIncinerate Flesh!|r', 41, 0, 100, 0, 0, 16149, 35195, 0, 'Lord Jaraxxus - Incinerate Flesh');
