INSERT INTO `creature_text` VALUES (7998, 0, 0, 'With your help, I can evaluate these tunnels.', 12, 0, 100, 0, 0, 0, 4050, 0, 'SAY_BLASTMASTER_0');
INSERT INTO `creature_text` VALUES (7998, 1, 0, 'Let\'s see if we can find out where these Troggs are coming from.... and put a stop to the invasion!', 12, 0, 100, 0, 0, 0, 4051, 0, 'SAY_BLASTMASTER_1');
INSERT INTO `creature_text` VALUES (7998, 2, 0, 'Such devastation... what a horrible mess...', 12, 0, 100, 0, 0, 0, 4052, 0, 'SAY_BLASTMASTER_2');
INSERT INTO `creature_text` VALUES (7998, 3, 0, 'It\'s quiet here....', 12, 0, 100, 0, 0, 0, 4129, 0, 'SAY_BLASTMASTER_3');
INSERT INTO `creature_text` VALUES (7998, 4, 0, '...too quiet.', 12, 0, 100, 0, 0, 0, 4130, 0, 'SAY_BLASTMASTER_4');
INSERT INTO `creature_text` VALUES (7998, 5, 0, 'Look! Over there at the tunnel wall!', 12, 0, 100, 0, 0, 0, 4131, 0, 'SAY_BLASTMASTER_5');
INSERT INTO `creature_text` VALUES (7998, 6, 0, 'Trogg incursion! Defend me while I blast the hole closed!', 12, 0, 100, 0, 0, 0, 4132, 0, 'SAY_BLASTMASTER_6');
INSERT INTO `creature_text` VALUES (7998, 7, 0, 'I don\'t think one charge is going to cut it. Keep fending them off!', 12, 0, 100, 0, 0, 0, 4133, 0, 'SAY_BLASTMASTER_7');
INSERT INTO `creature_text` VALUES (7998, 8, 0, 'The charges are set. Get back before they blow!', 12, 0, 100, 0, 0, 0, 4134, 0, 'SAY_BLASTMASTER_8');
INSERT INTO `creature_text` VALUES (7998, 9, 0, 'Incoming blast in 10 seconds!', 14, 0, 100, 0, 0, 0, 4135, 0, 'SAY_BLASTMASTER_9');
INSERT INTO `creature_text` VALUES (7998, 10, 0, 'Incoming blast in 5 seconds. Clear the tunnel! Stay back!', 14, 0, 100, 0, 0, 0, 4136, 0, 'SAY_BLASTMASTER_10');
INSERT INTO `creature_text` VALUES (7998, 11, 0, 'Fire in the hole!', 14, 1, 100, 0, 0, 0, 32326, 0, 'SAY_BLASTMASTER_11');
INSERT INTO `creature_text` VALUES (7998, 12, 0, 'Well done! Without your help I would have never been able to thwart that wave of troggs.', 12, 0, 100, 0, 0, 0, 4206, 0, 'SAY_BLASTMASTER_12');
INSERT INTO `creature_text` VALUES (7998, 13, 0, 'Did you hear something?', 12, 0, 100, 0, 0, 0, 27926, 0, 'SAY_BLASTMASTER_13');
INSERT INTO `creature_text` VALUES (7998, 14, 0, 'I heard something over there.', 12, 0, 100, 0, 0, 0, 4208, 0, 'SAY_BLASTMASTER_14');
INSERT INTO `creature_text` VALUES (7998, 15, 0, 'More troggs! Ward them off as I prepare the explosives!', 12, 0, 100, 0, 0, 0, 4209, 0, 'SAY_BLASTMASTER_15');
INSERT INTO `creature_text` VALUES (7998, 16, 0, 'The final charge is set. Stand back!', 12, 0, 100, 0, 0, 0, 4325, 0, 'SAY_BLASTMASTER_16');
INSERT INTO `creature_text` VALUES (7998, 17, 0, '10 seconds to blast! Stand back!!!', 14, 0, 100, 0, 0, 0, 4326, 0, 'SAY_BLASTMASTER_17');
INSERT INTO `creature_text` VALUES (7998, 18, 0, '5 seconds until detonation!!!!!', 14, 0, 100, 0, 0, 0, 4327, 0, 'SAY_BLASTMASTER_18');
INSERT INTO `creature_text` VALUES (7998, 19, 0, 'Superb! Because of your help, my people stand a chance of re-taking our beloved city. Three cheers to you!', 12, 0, 100, 0, 0, 0, 4446, 0, 'SAY_BLASTMASTER_19');
INSERT INTO `creature_text` VALUES (7361, 0, 0, 'We come from below! You can never stop us!', 14, 0, 100, 0, 0, 0, 4328, 0, 'SAY_GRUBBIS');
INSERT INTO `creature_text` VALUES (33533, 1, 0, 'Look!', 12, 0, 0, 0, 0, 0, 34620, 0, '');
INSERT INTO `creature_text` VALUES (33533, 2, 0, 'We can dance too!', 12, 0, 0, 0, 0, 0, 34618, 0, '');
INSERT INTO `creature_text` VALUES (33533, 3, 0, 'We made a new friend!', 12, 0, 0, 0, 0, 0, 34615, 0, '');
INSERT INTO `creature_text` VALUES (33533, 4, 0, 'We here! Only, if this is world tree, how come it broke when they tried to put whole world in?', 12, 0, 0, 0, 0, 0, 34576, 0, '');
INSERT INTO `creature_text` VALUES (33533, 5, 0, 'Look! Is that us? We think it\'s us. A bit older, maybe?', 12, 0, 0, 0, 0, 0, 34606, 0, '');
INSERT INTO `creature_text` VALUES (33533, 6, 0, 'Looks like we blessed by Great Ones! Shrines give magic, make us high-oracle someday? Maybe?', 12, 0, 0, 0, 0, 0, 34607, 0, '');
INSERT INTO `creature_text` VALUES (33533, 7, 0, 'Wow! A real Great One? We don\'t even think Soo-say has met a real Great One!', 12, 0, 0, 0, 0, 0, 34672, 0, '');
INSERT INTO `creature_text` VALUES (33533, 8, 0, 'Um, hello, Mr. Great One. We are honored to meet you.', 12, 0, 0, 0, 0, 0, 34673, 0, '');
INSERT INTO `creature_text` VALUES (33533, 9, 0, 'Maybe you\'re asleep, Mr. Great One? We understand. Must be hard working being Great One, collecting all the shinies we leave for you.', 12, 0, 0, 0, 0, 0, 34674, 0, '');
INSERT INTO `creature_text` VALUES (33533, 10, 0, 'We brought you a gift, Great One. Maybe you see it when you wake up. Maybe you remember we came to see you, oki?', 12, 0, 0, 0, 0, 0, 34675, 0, '');
INSERT INTO `creature_text` VALUES (33533, 11, 0, 'So that\'s the queen of the dragons? Hmm... we thought she\'d be bigger. Yes, we did.', 12, 0, 0, 0, 0, 0, 34631, 0, '');
INSERT INTO `creature_text` VALUES (33533, 12, 0, 'How come she doesn\'t look like the other dragons? Could she turn into a big ol\' dragon if she wanted to?', 12, 0, 0, 0, 0, 0, 34632, 0, '');
INSERT INTO `creature_text` VALUES (33533, 13, 0, 'Um... Your Majesty, would you turn into a dragon for us?', 12, 0, 0, 0, 0, 0, 34633, 0, '');
INSERT INTO `creature_text` VALUES (33533, 14, 0, 'Oki... How about now?', 12, 0, 0, 0, 0, 0, 34634, 0, '');
INSERT INTO `creature_text` VALUES (33532, 1, 0, 'Look!', 12, 0, 0, 0, 0, 0, 34620, 0, '');
INSERT INTO `creature_text` VALUES (33532, 2, 0, 'Got you back!', 12, 0, 0, 0, 0, 0, 34622, 0, '');
INSERT INTO `creature_text` VALUES (33532, 3, 0, 'Good snowball fight!', 12, 0, 0, 0, 0, 0, 34623, 0, '');
INSERT INTO `creature_text` VALUES (33532, 4, 0, 'Look! That must be Kekek as a mighty warrior! See? What Kekek tell you?', 12, 0, 0, 0, 0, 0, 34610, 0, '');
INSERT INTO `creature_text` VALUES (33532, 5, 0, 'Hmm... that Kekek very old. Must be elder, leader of Frenzyheart. This good. Kekek have good future as warrior and leader. Kekek very happy you bring him here. Orphan-lady never do that for Kekek.', 12, 0, 0, 0, 0, 0, 34636, 0, '');
INSERT INTO `creature_text` VALUES (33532, 6, 0, 'Wow. At last, Kekek get to meet Hemet Nesingwary!', 12, 0, 0, 0, 0, 0, 34685, 0, '');
INSERT INTO `creature_text` VALUES (33532, 7, 0, 'Even Frenzyheart know all about you after you come to Sholazar. Maybe you teach Kekek your tricks?', 12, 0, 0, 0, 0, 0, 34686, 0, '');
INSERT INTO `creature_text` VALUES (33532, 8, 0, 'Not sure how reading helps. Kekek can\'t read anyway, but if Hemet Nesingwary say, Kekek try.', 12, 0, 0, 0, 0, 0, 34688, 0, '');
INSERT INTO `creature_text` VALUES (33532, 9, 0, 'Maybe give up and throw book at animals, but try.', 12, 0, 0, 0, 0, 0, 34689, 0, '');
INSERT INTO `creature_text` VALUES (33532, 10, 0, 'You think maybe one day, Kekek lead Frenzyheart home, like bear-men fight to get tree-city back? Kekek want own home, not city with purple men, not jungle with big-tongues.', 12, 0, 0, 0, 0, 0, 34603, 0, '');
INSERT INTO `creature_text` VALUES (33532, 11, 0, 'Kekek thought we go see dragon queen. This just some elf-lady with funny horns.', 12, 0, 0, 0, 0, 0, 34655, 0, '');
INSERT INTO `creature_text` VALUES (33532, 12, 0, 'But why? Dragons big and powerful. Elf-lady weak. Kekek want to see dragon queen\'s real form.', 12, 0, 0, 0, 0, 0, 34664, 0, '');
INSERT INTO `creature_text` VALUES (33532, 13, 0, 'Oh! Now Kekek understand. Not want to be crushed by big dragon-lady. Maybe come back sometime when not so crowded. Nice to meet you, dragon-lady.', 12, 0, 0, 0, 0, 0, 34666, 0, '');
INSERT INTO `creature_text` VALUES (34387, 1, 0, 'No worry, Kekek. You grow up, be strong for Frenzyheart. You lead people back to old home, where there no big-tongue babies.', 12, 0, 0, 0, 0, 0, 35493, 0, '');
INSERT INTO `creature_text` VALUES (26917, 2, 0, 'If I was in my dragon form, there wouldn\'t be any room for anyone else, little one.', 12, 0, 0, 0, 0, 0, 34635, 0, '');
INSERT INTO `creature_text` VALUES (27990, 8, 0, 'Rest assured, Kekek, she is the queen of the dragons. We often take the forms of smaller beings when we work with them.', 12, 0, 0, 0, 0, 0, 34665, 0, '');
INSERT INTO `creature_text` VALUES (34386, 1, 0, 'We remember this visit, yes we do. Never forget what you learn from your new friend, little Roo. You got great future ahead.', 12, 0, 0, 0, 0, 0, 35492, 0, '');
INSERT INTO `creature_text` VALUES (27986, 0, 0, 'Aye, I\'ll try it.', 12, 0, 100, 273, 0, 0, 28549, 0, 'Hemet Nesingwary');
INSERT INTO `creature_text` VALUES (27986, 1, 0, 'That\'s exactly what I needed!', 12, 0, 100, 5, 0, 0, 28550, 0, 'Hemet Nesingwary');
INSERT INTO `creature_text` VALUES (34490, 1, 0, 'Better watch out!', 12, 0, 0, 0, 0, 0, 34624, 0, '');
INSERT INTO `creature_text` VALUES (34490, 2, 0, 'Got you good!', 12, 0, 0, 0, 0, 0, 34621, 0, '');
