INSERT INTO `creature_text` VALUES (17535, 1, 0, 'Don\'t let them hurt us Tito! Oh, you won\'t, will you?', 14, 0, 100, 0, 0, 9191, 15066, 0, 'dorothee SAY_DOROTHEE_SUMMON');
INSERT INTO `creature_text` VALUES (17535, 2, 0, 'Tito! Oh Tito, no!', 14, 0, 100, 0, 0, 9192, 15069, 0, 'dorothee SAY_DOROTHEE_TITO_DEATH');
INSERT INTO `creature_text` VALUES (17535, 3, 0, 'Oh Tito, we simply must find a way home! The old wizard could be our only hope! Strawman, Roar, Tinhead, will you - wait... oh golly, look we have visitors!', 14, 0, 100, 0, 0, 9195, 15067, 0, 'dorothee SAY_DOROTHEE_AGGRO');
INSERT INTO `creature_text` VALUES (17546, 0, 0, 'I\'m not afraid a\' you! Do you wanna\' fight? Huh, do ya\'? C\'mon! I\'ll fight ya\' with both paws behind my back!', 14, 0, 100, 0, 0, 9227, 19276, 0, 'roar SAY_ROAR_AGGRO');
INSERT INTO `creature_text` VALUES (17546, 1, 0, 'You didn\'t have to go and do that!', 14, 0, 100, 0, 0, 9229, 15133, 0, 'roar SAY_ROAR_DEATH');
INSERT INTO `creature_text` VALUES (17546, 2, 0, 'I think I\'m going to go take fourty winks.', 14, 0, 100, 0, 0, 9230, 0, 0, 'roar SAY_ROAR_SLAY');
INSERT INTO `creature_text` VALUES (17543, 0, 0, 'Now what should I do with you? I simply can\'t make up my mind.', 14, 0, 100, 0, 0, 9254, 19277, 0, 'strawman SAY_STRAWMAN_AGGRO');
INSERT INTO `creature_text` VALUES (17543, 1, 0, 'Don\'t let them make... a mattress outta\' me.', 14, 0, 100, 0, 0, 9256, 15135, 0, 'strawman SAY_STRAWMAN_DEATH');
INSERT INTO `creature_text` VALUES (17543, 2, 0, 'I guess I\'m not a failure after all!', 14, 0, 100, 0, 0, 9257, 15136, 0, 'strawman SAY_STRAWMAN_SLAY');
INSERT INTO `creature_text` VALUES (17547, 0, 0, 'I could really use a heart. Say, can I have yours?', 14, 0, 100, 0, 0, 9268, 19278, 0, 'tinhead SAY_TINHEAD_AGGRO');
INSERT INTO `creature_text` VALUES (17547, 1, 0, 'Back to being an old rust bucket.', 14, 0, 100, 0, 0, 9270, 15155, 0, 'tinhead SAY_TINHEAD_DEATH');
INSERT INTO `creature_text` VALUES (17547, 2, 0, 'Guess I\'m not so rusty after all.', 14, 0, 100, 0, 0, 9271, 15156, 0, 'tinhead SAY_TINHEAD_SLAY');
INSERT INTO `creature_text` VALUES (17547, 3, 0, '%s begins to rust!', 16, 0, 100, 0, 0, 0, 14361, 0, 'tinhead EMOTE_RUST');
INSERT INTO `creature_text` VALUES (18168, 0, 0, 'Woe to each and every one of you, my pretties!', 14, 0, 100, 0, 0, 9179, 15050, 0, 'crone SAY_CRONE_AGGRO');
INSERT INTO `creature_text` VALUES (18168, 0, 1, 'It will all be over soon!', 14, 0, 100, 0, 0, 9307, 15332, 0, 'crone SAY_CRONE_AGGRO2');
INSERT INTO `creature_text` VALUES (18168, 1, 0, 'How could you? What a cruel, cruel world...', 14, 0, 100, 0, 0, 9178, 15052, 0, 'crone SAY_CRONE_DEATH');
INSERT INTO `creature_text` VALUES (18168, 2, 0, 'Fixed you, didn\'t I?', 14, 0, 100, 0, 0, 9180, 15051, 0, 'crone SAY_CRONE_SLAY');
INSERT INTO `creature_text` VALUES (17521, 0, 0, 'The better to own you with!', 14, 0, 100, 0, 0, 9276, 14212, 0, 'wolf SAY_WOLF_AGGRO');
INSERT INTO `creature_text` VALUES (17521, 1, 0, 'Mmmm... delicious.', 14, 0, 100, 0, 0, 9277, 15153, 0, 'SAY_WOLF_SLAY');
INSERT INTO `creature_text` VALUES (17521, 2, 0, 'Run away little girl, run away!', 14, 0, 100, 0, 0, 9278, 14213, 0, 'SAY_WOLF_HOOD');
INSERT INTO `creature_text` VALUES (17534, 0, 0, 'What devil art thou, that dost torment me thus?', 14, 0, 100, 0, 0, 9196, 15070, 0, 'julianne SAY_JULIANNE_AGGRO');
INSERT INTO `creature_text` VALUES (17534, 1, 0, 'Where is my lord? Where is my Romulo?', 14, 0, 100, 0, 0, 9199, 0, 0, 'julianne SAY_JULIANNE_ENTER');
INSERT INTO `creature_text` VALUES (17534, 2, 0, 'Romulo, I come! Oh... this do I drink to thee!', 14, 0, 100, 0, 0, 9199, 15072, 0, 'julianne SAY_JULIANNE_DEATH01');
INSERT INTO `creature_text` VALUES (17534, 3, 0, 'O happy dagger! This is thy sheath; there rust, and let me die!', 14, 0, 100, 0, 0, 9310, 15074, 0, 'julianne SAY_JULIANNE_DEATH02');
INSERT INTO `creature_text` VALUES (17534, 4, 0, 'Come, gentle night; and give me back my Romulo!', 14, 0, 100, 0, 0, 9200, 15073, 0, 'julianne SAY_JULIANNE_RESURRECT');
INSERT INTO `creature_text` VALUES (17534, 5, 0, 'Parting is such sweet sorrow.', 14, 0, 100, 0, 0, 9201, 15071, 0, 'julianne SAY_JULIANNE_SLAY');
INSERT INTO `creature_text` VALUES (17533, 0, 0, 'Wilt thou provoke me? Then have at thee, boy!', 14, 0, 100, 0, 0, 9233, 15075, 0, 'romulo SAY_ROMULO_AGGRO');
INSERT INTO `creature_text` VALUES (17533, 1, 0, 'Thou smilest... upon the stroke that... murders me.', 14, 0, 100, 0, 0, 9235, 15078, 0, 'romulo SAY_ROMULO_DEATH');
INSERT INTO `creature_text` VALUES (17533, 2, 0, 'This day\'s black fate on more days doth depend. This but begins the woe. Others must end.', 14, 0, 100, 0, 0, 9236, 0, 0, 'romulo SAY_ROMULO_ENTER');
INSERT INTO `creature_text` VALUES (17533, 3, 0, 'Thou detestable maw, thou womb of death; I enforce thy rotten jaws to open!', 14, 0, 100, 0, 0, 9237, 15077, 0, 'romulo SAY_ROMULO_RESURRECT');
INSERT INTO `creature_text` VALUES (17533, 4, 0, 'How well my comfort is revived by this!', 14, 0, 100, 0, 0, 9238, 15076, 0, 'romulo SAY_ROMULO_SLAY');
INSERT INTO `creature_text` VALUES (16812, 0, 0, 'Welcome ladies and gentlemen, to this evening\'s presentation!', 14, 0, 100, 0, 0, 9174, 14122, 0, 'barnes OZ1');
INSERT INTO `creature_text` VALUES (16812, 1, 0, 'Tonight we plumb the depths of the human soul as we join a lost, lonely girl trying desperately--with the help of her loyal companions--to find her way home!', 14, 0, 100, 0, 0, 9338, 15362, 0, 'barnes OZ2');
INSERT INTO `creature_text` VALUES (16812, 2, 0, 'But she is pursued... by a wicked, malevolent crone!', 14, 0, 100, 0, 0, 9339, 15363, 0, 'barnes OZ3');
INSERT INTO `creature_text` VALUES (16812, 3, 0, 'Will she survive? Will she prevail? Only time will tell. And now... on with the show!', 14, 0, 100, 0, 0, 9340, 15364, 0, 'barnes OZ4');
INSERT INTO `creature_text` VALUES (16812, 4, 0, 'Good evening ladies and gentlemen, welcome to this evening\'s presentation!', 14, 0, 100, 0, 0, 9175, 14057, 0, 'barnes HOOD1');
INSERT INTO `creature_text` VALUES (16812, 5, 0, 'Tonight, things are not what they seem... for tonight your eyes may not be trusted!', 14, 0, 100, 0, 0, 9335, 15359, 0, 'barnes HOOD2');
INSERT INTO `creature_text` VALUES (16812, 6, 0, 'Take for instance this quiet elderly woman waiting for a visit from her granddaughter... surely there is nothing to fear from this sweet, gray-haired old lady!', 14, 0, 100, 0, 0, 9336, 15360, 0, 'barnes HOOD3');
INSERT INTO `creature_text` VALUES (16812, 7, 0, 'But don\'t let me pull the wool over your eyes! See for yourself what lies beneath those covers! And now... on with the show!', 14, 0, 100, 0, 0, 9337, 15361, 0, 'barnes HOOD4');
INSERT INTO `creature_text` VALUES (16812, 8, 0, 'Welcome ladies and gentlemen, to this evening\'s presentation!', 14, 0, 100, 0, 0, 9176, 14122, 0, 'barnes RAJ1');
INSERT INTO `creature_text` VALUES (16812, 9, 0, 'Tonight... we explore a tale of forbidden love!', 14, 0, 100, 0, 0, 9341, 15365, 0, 'barnes RAJ2');
INSERT INTO `creature_text` VALUES (16812, 10, 0, 'But beware, for not all love stories end happily, as you may find out. Sometimes, love pricks like a thorn!', 14, 0, 100, 0, 0, 9342, 15366, 0, 'barnes RAJ3');
INSERT INTO `creature_text` VALUES (16812, 11, 0, 'But don\'t take it from me; see for yourself what tragedy lies ahead when the paths of star crossed lovers meet! And now... on with the show!', 14, 0, 100, 0, 0, 9343, 15367, 0, 'barnes RAJ4');
INSERT INTO `creature_text` VALUES (15690, 0, 0, 'Madness has brought you here to me. I shall be your undoing!', 14, 0, 100, 0, 0, 9218, 15127, 0, 'malchezaar SAY_AGGRO');
INSERT INTO `creature_text` VALUES (15690, 1, 0, 'Simple fools! Time is the fire in which you\'ll burn!', 14, 0, 100, 0, 0, 9220, 15341, 0, 'malchezaar SAY_AXE_TOSS1');
INSERT INTO `creature_text` VALUES (15690, 2, 0, 'I see the subtlety of conception is beyond primitives such as you.', 14, 0, 100, 0, 0, 9317, 0, 0, 'malchezaar SAY_AXE_TOSS2');
INSERT INTO `creature_text` VALUES (15690, 3, 0, 'Who knows what secrets hide in the dark.', 14, 0, 100, 0, 0, 9223, 0, 0, 'malchezaar SAY_SPECIAL1');
INSERT INTO `creature_text` VALUES (15690, 4, 0, 'The cerestial forces are mine to manipulate.', 14, 0, 100, 0, 0, 9320, 0, 0, 'malchezaar SAY_SPECIAL2');
INSERT INTO `creature_text` VALUES (15690, 5, 0, 'How can you hope to stand against such overwhelming power?', 14, 0, 100, 0, 0, 9321, 15342, 0, 'malchezaar SAY_SPECIAL3');
INSERT INTO `creature_text` VALUES (15690, 6, 0, 'Surely you did not think you could win.', 14, 0, 100, 0, 0, 9222, 15124, 0, 'malchezaar SAY_SLAY1');
INSERT INTO `creature_text` VALUES (15690, 6, 1, 'Your greed, your foolishness has brought you to this end.', 14, 0, 100, 0, 0, 9318, 15125, 0, 'malchezaar SAY_SLAY2');
INSERT INTO `creature_text` VALUES (15690, 6, 2, 'You are but a plaything. Unfit even to amuse.', 14, 0, 100, 0, 0, 9319, 15126, 0, 'malchezaar SAY_SLAY3');
INSERT INTO `creature_text` VALUES (15690, 7, 0, 'All realities, all dimensions are open to me!', 14, 0, 100, 0, 0, 9224, 15343, 0, 'malchezaar SAY_SUMMON1');
INSERT INTO `creature_text` VALUES (15690, 7, 1, 'You face not Malchezaar alone, but the legions I command!', 14, 0, 100, 0, 0, 9322, 15344, 0, 'malchezaar SAY_SUMMON2');
INSERT INTO `creature_text` VALUES (15690, 8, 0, 'I refuse to concede defeat! I am a prince of the Eredar! I... am...', 14, 0, 100, 0, 0, 9221, 15123, 0, 'malchezaar SAY_DEATH');
INSERT INTO `creature_text` VALUES (16524, 0, 0, 'Please, no more! My son... he\'s gone mad!', 14, 0, 100, 0, 0, 9241, 13529, 0, 'aran SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (16524, 0, 1, 'I\'ll not be tortured again!', 14, 0, 100, 0, 0, 9323, 15353, 0, 'aran SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (16524, 0, 2, 'Who are you? What do you want? Stay away from me!', 14, 0, 100, 0, 0, 9324, 13530, 0, 'aran SAY_AGGRO3');
INSERT INTO `creature_text` VALUES (16524, 1, 0, 'I\'ll show you: this beaten dog still has some teeth!', 14, 0, 100, 0, 0, 9245, 13519, 0, 'aran SAY_FLAMEWREATH1');
INSERT INTO `creature_text` VALUES (16524, 1, 1, 'Burn, you hellish fiends!', 14, 0, 100, 0, 0, 9326, 13520, 0, 'aran SAY_FLAMEWREATH2');
INSERT INTO `creature_text` VALUES (16524, 2, 0, 'I\'ll freeze you all!', 14, 0, 100, 0, 0, 9246, 13522, 0, 'aran SAY_BLIZZARD1');
INSERT INTO `creature_text` VALUES (16524, 2, 1, 'Back to the cold dark with you!', 14, 7, 100, 0, 0, 9327, 22914, 0, 'aran SAY_BLIZZARD2');
INSERT INTO `creature_text` VALUES (16524, 3, 0, 'Yes, yes my son is quite powerful... but I have powers of my own!', 14, 0, 100, 0, 0, 9242, 13517, 0, 'aran SAY_EXPLOSION1');
INSERT INTO `creature_text` VALUES (16524, 3, 1, 'I am not some simple jester! I am Nielas Aran!', 14, 0, 100, 0, 0, 9325, 13518, 0, 'aran SAY_EXPLOSION2');
INSERT INTO `creature_text` VALUES (16524, 4, 0, 'Surely you wouldn\'t deny an old man a replenishing drink? No, no, I thought not.', 14, 0, 100, 0, 0, 9248, 13514, 0, 'aran SAY_DRINK');
INSERT INTO `creature_text` VALUES (16524, 5, 0, 'I\'m not finished yet! No, I have a few more tricks up my sleeve...', 12, 0, 100, 0, 0, 9251, 13513, 0, 'aran SAY_ELEMENTALS');
INSERT INTO `creature_text` VALUES (16524, 6, 0, 'I want this nightmare to be over!', 14, 0, 100, 0, 0, 9250, 13526, 0, 'aran SAY_KILL1');
INSERT INTO `creature_text` VALUES (16524, 6, 1, 'Torment me no more!', 14, 0, 100, 0, 0, 9328, 13527, 0, 'aran SAY_KILL2');
INSERT INTO `creature_text` VALUES (16524, 7, 0, 'You\'ve wasted enough of my time... let these games be finished!', 14, 0, 100, 0, 0, 9247, 13569, 0, 'aran SAY_TIMEOVER');
INSERT INTO `creature_text` VALUES (16524, 8, 0, 'At last the nightmare is over...', 14, 0, 100, 0, 0, 9244, 15345, 0, 'aran SAY_DEATH');
INSERT INTO `creature_text` VALUES (16524, 9, 0, 'Where did you get that? Did HE send you?', 14, 0, 100, 0, 0, 9249, 13532, 0, 'aran SAY_ATIESH');
INSERT INTO `creature_text` VALUES (15688, 0, 0, 'Your blood will anoint my circle!', 14, 0, 100, 0, 0, 9264, 15145, 0, 'terestian SAY_SLAY1');
INSERT INTO `creature_text` VALUES (15688, 0, 1, 'The great one will be pleased.', 14, 0, 100, 0, 0, 9329, 15146, 0, 'terestian SAY_SLAY2');
INSERT INTO `creature_text` VALUES (15688, 1, 0, 'My life is yours, oh great one...', 14, 0, 100, 0, 0, 9262, 15137, 0, 'terestian SAY_DEATH');
INSERT INTO `creature_text` VALUES (15688, 2, 0, 'Ah, you\'re just in time. The rituals are about to begin!', 14, 0, 100, 0, 0, 9260, 15141, 0, 'terestian SAY_AGGRO');
INSERT INTO `creature_text` VALUES (15688, 3, 0, 'Please accept this humble offering, oh great one...', 14, 0, 100, 0, 0, 9263, 15147, 0, 'terestian SAY_SACRIFICE1');
INSERT INTO `creature_text` VALUES (15688, 3, 1, 'Let this sacrifice serve as testament to my fealty.', 14, 0, 100, 0, 0, 9330, 15148, 0, 'terestian SAY_SACRIFICE2');
INSERT INTO `creature_text` VALUES (15688, 4, 0, 'Come, you dwellers in the dark! Rally to my call!', 14, 0, 100, 0, 0, 9265, 15150, 0, 'terestian SAY_SUMMON1');
INSERT INTO `creature_text` VALUES (15688, 4, 1, 'Gather, my pets... there is plenty for all!', 14, 0, 100, 0, 0, 9331, 15151, 0, 'terestian SAY_SUMMON2');
INSERT INTO `creature_text` VALUES (15687, 0, 0, 'Hm, unannounced visitors. Preparations must be made...', 14, 0, 100, 0, 0, 9211, 15021, 0, 'moroes SAY_AGGRO');
INSERT INTO `creature_text` VALUES (15687, 1, 0, 'Now, where was I? Oh, yes...', 14, 0, 100, 0, 0, 9215, 15339, 0, 'moroes SAY_SPECIAL_1');
INSERT INTO `creature_text` VALUES (15687, 1, 1, 'You rang?', 14, 0, 100, 0, 0, 9316, 15340, 0, 'moroes SAY_SPECIAL_2');
INSERT INTO `creature_text` VALUES (15687, 2, 0, 'One more for dinner this evening.', 14, 0, 100, 0, 0, 9214, 13461, 0, 'moroes SAY_KILL_1');
INSERT INTO `creature_text` VALUES (15687, 2, 1, 'Time... never enough time.', 14, 0, 100, 0, 0, 9314, 15085, 0, 'moroes SAY_KILL_2');
INSERT INTO `creature_text` VALUES (15687, 2, 2, 'Oh, I\'ve gone and made a mess...', 14, 0, 100, 0, 0, 9315, 15086, 0, 'moroes SAY_KILL_3');
INSERT INTO `creature_text` VALUES (15687, 3, 0, 'How terribly clumsy of me.', 14, 0, 100, 0, 0, 9213, 15022, 0, 'moroes SAY_DEATH');
INSERT INTO `creature_text` VALUES (15691, 0, 0, 'The Menagerie is for guests only.', 14, 0, 100, 0, 0, 9183, 15055, 0, 'curator SAY_AGGRO');
INSERT INTO `creature_text` VALUES (15691, 1, 0, 'Gallery rules will be strictly enforced.', 14, 0, 100, 0, 0, 9188, 15337, 0, 'curator SAY_SUMMON1');
INSERT INTO `creature_text` VALUES (15691, 1, 1, 'This Curator is equipped for gallery protection.', 14, 0, 100, 0, 0, 9309, 15338, 0, 'curator SAY_SUMMON2');
INSERT INTO `creature_text` VALUES (15691, 2, 0, 'Your request cannot be processed.', 14, 0, 100, 0, 0, 9186, 15059, 0, 'curator SAY_EVOCATE');
INSERT INTO `creature_text` VALUES (15691, 3, 0, 'Failure to comply will result in offensive action.', 14, 0, 100, 0, 0, 9185, 15060, 0, 'curator SAY_ENRAGE');
INSERT INTO `creature_text` VALUES (15691, 4, 0, 'Do not touch the displays.', 14, 0, 100, 0, 0, 9187, 15335, 0, 'curator SAY_KILL1');
INSERT INTO `creature_text` VALUES (15691, 4, 1, 'You are not a guest.', 14, 0, 100, 0, 0, 9308, 15336, 0, 'curator SAY_KILL2');
INSERT INTO `creature_text` VALUES (15691, 5, 0, 'Curator is no longer operation-a-l.', 14, 0, 100, 0, 0, 9184, 15057, 0, 'curator SAY_DEATH');
INSERT INTO `creature_text` VALUES (16151, 1, 1, 'Who dares attack the steed of the Huntsman?', 14, 0, 100, 0, 0, 9298, 15378, 0, 'attumen SAY_APPEAR2');
INSERT INTO `creature_text` VALUES (16151, 1, 0, 'Cowards! Wretches!', 14, 0, 100, 0, 0, 9167, 13459, 0, 'attumen SAY_APPEAR1');
INSERT INTO `creature_text` VALUES (16151, 0, 0, 'Well done, Midnight!', 14, 0, 100, 0, 0, 9173, 15334, 0, 'attumen SAY_MIDNIGHT_KILL');
INSERT INTO `creature_text` VALUES (15550, 3, 1, 'Amateurs! Do not think you can best me! I kill for a living.', 14, 0, 100, 0, 0, 9304, 0, 0, 'attumen SAY_RANDOM2');
INSERT INTO `creature_text` VALUES (15550, 3, 0, 'Such easy sport.', 14, 0, 100, 0, 0, 9170, 0, 0, 'attumen SAY_RANDOM1');
INSERT INTO `creature_text` VALUES (15550, 2, 0, 'Always knew... someday I would become... the hunted.', 14, 0, 100, 0, 0, 9165, 13462, 0, 'attumen SAY_DEATH');
INSERT INTO `creature_text` VALUES (15550, 1, 0, 'Weapons are merely a convenience for a warrior of my skill!', 14, 0, 100, 0, 0, 9166, 13490, 0, 'attumen SAY_DISARMED');
INSERT INTO `creature_text` VALUES (15550, 0, 1, 'Another trophy to add to my collection!', 14, 0, 100, 0, 0, 9300, 15333, 0, 'attumen SAY_KILL2');
INSERT INTO `creature_text` VALUES (15550, 0, 0, 'It was... inevitable.', 14, 0, 100, 0, 0, 9169, 13460, 0, 'attumen SAY_KILL1');
INSERT INTO `creature_text` VALUES (16457, 0, 0, 'Your behavior will not be tolerated.', 14, 0, 100, 0, 0, 9204, 15079, 0, 'maiden SAY_AGGRO');
INSERT INTO `creature_text` VALUES (16457, 1, 0, 'Ah ah ah...', 14, 0, 100, 0, 0, 9207, 15082, 0, 'maiden SAY_SLAY1');
INSERT INTO `creature_text` VALUES (16457, 1, 1, 'This is for the best.', 14, 0, 100, 0, 0, 9312, 15084, 0, 'maiden SAY_SLAY2');
INSERT INTO `creature_text` VALUES (16457, 1, 2, 'Impure thoughts lead to profane actions.', 14, 0, 100, 0, 0, 9311, 15083, 0, 'maiden SAY_SLAY3');
INSERT INTO `creature_text` VALUES (16457, 2, 0, 'Cast out your corrupt thoughts.', 14, 0, 100, 0, 0, 9313, 15080, 0, 'maiden SAY_REPENTANCE1');
INSERT INTO `creature_text` VALUES (16457, 2, 1, 'Your impurity must be cleansed.', 14, 0, 100, 0, 0, 9208, 13396, 0, 'maiden SAY_REPENTANCE2');
INSERT INTO `creature_text` VALUES (16457, 3, 0, 'Death comes. Will your conscience be clear?', 14, 0, 100, 0, 0, 9206, 15081, 0, 'maiden SAY_DEATH');
INSERT INTO `creature_text` VALUES (3974, 0, 0, 'Release the hounds!', 14, 0, 100, 0, 0, 5841, 20395, 0, 'loksey SAY_AGGRO');
INSERT INTO `creature_text` VALUES (23775, 0, 0, 'So eager you are, for my blood to spill. Yet to vanquish me, \'tis my head you must kill!', 14, 0, 100, 0, 0, 11969, 22757, 0, 'headless horseman SAY_LOST_HEAD');
INSERT INTO `creature_text` VALUES (23775, 1, 0, 'Your body lies beaten, battered and broken! Let my curse be your own, fate has spoken!', 14, 0, 100, 0, 0, 11962, 40546, 0, 'headless horseman SAY_PLAYER_DEATH');
INSERT INTO `creature_text` VALUES (23682, 0, 0, 'It is over, your search is done. Let fate choose now, the righteous one.', 14, 0, 100, 0, 0, 11961, 22261, 0, 'headless horseman SAY_ENTRANCE');
INSERT INTO `creature_text` VALUES (23682, 1, 0, 'Here\'s my body, fit and pure!  Now, your blackened souls I\'ll cure!', 14, 0, 100, 0, 0, 12567, 22271, 0, 'headless horseman SAY_REJOINED');
INSERT INTO `creature_text` VALUES (23682, 2, 0, 'Harken, cur! Tis you I spurn! Now feel... the burn!', 12, 0, 100, 0, 0, 0, 22587, 0, 'headless horseman SAY_CONFLAGRATION');
INSERT INTO `creature_text` VALUES (23682, 3, 0, 'Soldiers arise, stand and fight! Bring victory at last to this fallen knight!', 14, 0, 100, 0, 0, 11963, 23861, 0, 'headless horseman SAY_SPROUTING_PUMPKINS');
INSERT INTO `creature_text` VALUES (23682, 4, 0, 'This end have I reached before.  What new adventure lies in store?', 14, 0, 100, 0, 0, 11964, 23455, 0, 'headless horseman SAY_DEATH');
INSERT INTO `creature_text` VALUES (3975, 0, 0, 'Ah - I\'ve been waiting for a real challenge!', 14, 0, 100, 0, 0, 5830, 6194, 0, 'herod SAY_AGGRO');
INSERT INTO `creature_text` VALUES (3975, 1, 0, 'Blades of light!', 14, 0, 100, 0, 0, 5832, 6534, 0, 'herod SAY_WHIRLWIND');
INSERT INTO `creature_text` VALUES (3975, 2, 0, 'Light, give me strength!', 14, 0, 100, 0, 0, 5833, 6195, 0, 'herod SAY_ENRAGE');
INSERT INTO `creature_text` VALUES (3975, 3, 0, 'Is that all?', 14, 0, 100, 0, 0, 5831, 6196, 0, 'herod SAY_KILL');
INSERT INTO `creature_text` VALUES (3975, 4, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 24144, 0, 'herod EMOTE_ENRAGE');
INSERT INTO `creature_text` VALUES (6487, 0, 0, 'You will not defile these mysteries!', 14, 0, 100, 0, 0, 5842, 6199, 0, 'doan SAY_AGGRO');
INSERT INTO `creature_text` VALUES (6487, 1, 0, 'Burn in righteous fire!', 14, 0, 100, 0, 0, 5843, 6200, 0, 'doan SAY_SPECIALAE');
INSERT INTO `creature_text` VALUES (3976, 0, 0, 'Infidels! They must be purified!', 14, 0, 100, 0, 0, 5835, 2847, 0, 'mograine SAY_MO_AGGRO');
INSERT INTO `creature_text` VALUES (3976, 1, 0, 'Unworthy!', 14, 0, 100, 0, 0, 5836, 35740, 0, 'mograine SAY_MO_KILL');
INSERT INTO `creature_text` VALUES (3976, 2, 0, 'At your side, milady!', 14, 0, 100, 0, 0, 5837, 18026, 0, 'mograine SAY_MO_RESSURECTED');
INSERT INTO `creature_text` VALUES (3977, 0, 0, 'Mograine has fallen! You shall pay for this treachery! Arise, my champion! Arise!', 14, 0, 100, 0, 0, 5838, 18023, 0, 'whitemane SAY_WH_INTRO');
INSERT INTO `creature_text` VALUES (3977, 1, 0, 'The Light has spoken!', 14, 0, 100, 0, 0, 5839, 6198, 0, 'whitemane SAY_WH_KILL');
INSERT INTO `creature_text` VALUES (3977, 2, 0, 'Arise, my champion!', 14, 0, 100, 0, 0, 5840, 6532, 0, 'whitemane SAY_WH_RESSURECT');
INSERT INTO `creature_text` VALUES (4543, 0, 0, 'We hunger for vengeance.', 14, 0, 100, 0, 0, 5844, 6201, 0, 'thalnos SAY_AGGRO');
INSERT INTO `creature_text` VALUES (4543, 1, 0, 'No rest... for the angry dead!', 14, 0, 100, 0, 0, 5846, 6202, 0, 'thalnos SAY_HEALTH');
INSERT INTO `creature_text` VALUES (4543, 2, 0, 'More... More souls!', 14, 0, 100, 0, 0, 5845, 6203, 0, 'thalnos SAY_KILL');
INSERT INTO `creature_text` VALUES (3983, 0, 0, 'Tell me... tell me everything!', 14, 0, 100, 0, 0, 5847, 6204, 0, 'vishas SAY_AGGRO');
INSERT INTO `creature_text` VALUES (3983, 1, 0, 'Naughty secrets!', 14, 0, 100, 0, 0, 5849, 6206, 0, 'vishas SAY_HEALTH1');
