INSERT INTO `creature_text` VALUES (28016, 10, 0, 'Forgive my deception, mon. It all been for your own good.', 12, 0, 100, 396, 0, 14015, 28420, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 11, 0, 'Ya needed ta prove yer worthiness....', 12, 0, 100, 396, 0, 14016, 28421, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 12, 0, 'I be needin\' worthy underlings in Zul\'Drak, mon. Ones ready to wield real power.', 12, 0, 100, 396, 0, 14017, 28422, 0, 'Drakuru');
