INSERT INTO `creature_text` VALUES (13020, 0, 0, 'Too late, friends! Nefarius\' corruption has taken hold...I cannot...control myself.\n', 14, 0, 100, 0, 0, 8281, 9886, 0, 'vaelastrasz SAY_LINE1');
INSERT INTO `creature_text` VALUES (13020, 1, 0, 'I beg you, mortals - FLEE! Flee before I lose all sense of control! The black fire rages within my heart! I MUST- release it! ', 14, 0, 100, 0, 0, 8282, 9887, 0, 'vaelastrasz SAY_LINE2');
