INSERT INTO `creature_text` VALUES (29032, 33, 0, 'Think, $n. Think back. Try and remember the hills and valleys of Elwynn, where you were born. Remember the splendor of life, $g brother:sister;. You were a champion of the Alliance once! This isn\'t you.', 12, 0, 100, 0, 0, 0, 29315, 0, 'special_surprise SAY_EXEC_THINK_7');
