INSERT INTO `creature_text` VALUES (29070, 29, 0, 'Think, $n. Think back. Try and remember the majestic halls of Silvermoon City, where you were born. Remember the splendor of life, $g brother:sister;. You were a champion of the sin\'dorei once! This isn\'t you.', 12, 0, 100, 0, 0, 0, 29374, 0, 'special_surprise SAY_EXEC_THINK_3');
INSERT INTO `creature_text` VALUES (29070, 30, 0, 'Think, $n. Think back. Try and remember the proud mountains of Argus, where you were born. Remember the splendor of life, $g brother:sister;. You were a champion of the draenei once! This isn\'t you.', 12, 0, 100, 0, 0, 0, 29362, 0, 'special_surprise SAY_EXEC_THINK_4');
