INSERT INTO `creature_text` VALUES (29070, 32, 0, 'Think, $n. Think back. Try and remember Gnomeregan before those damned troggs! Remember the feel of an arclight spanner, $g brother:sister;. You were a champion of gnome-kind once! This isn\'t you.', 12, 0, 100, 0, 0, 0, 29352, 0, 'special_surprise SAY_EXEC_THINK_6');
INSERT INTO `creature_text` VALUES (29070, 33, 0, 'Think, $n. Think back. Try and remember the hills and valleys of Elwynn, where you were born. Remember the splendor of life, $g brother:sister;. You were a champion of the Alliance once! This isn\'t you.', 12, 0, 100, 0, 0, 0, 29315, 0, 'special_surprise SAY_EXEC_THINK_7');
