INSERT INTO `creature_text` VALUES (349, 2, 0, 'The Blackrock infestation is thick in these parts.  I will do my best to keep the pace.  Let\'s go!', 12, 7, 100, 0, 0, 0, 27, 0, 'corporal keeshan SAY_CORPORAL_3');
INSERT INTO `creature_text` VALUES (349, 3, 0, 'Marshal Marris, sir.  Corporal Keeshan of the 12th Sabre Regiment returned from battle and reporting for duty!', 12, 7, 100, 0, 0, 0, 29, 0, 'corporal keeshan SAY_CORPORAL_4');
INSERT INTO `creature_text` VALUES (349, 4, 0, 'Brave adventurer, thank you for rescuing me! I am sure Marshal Marris will reward your kind deed.', 12, 7, 100, 0, 0, 0, 30, 0, 'corporal keeshan SAY_CORPORAL_5');
INSERT INTO `creature_text` VALUES (17238, 0, 0, 'Beware!  We are attacked!', 12, 7, 100, 0, 0, 0, 13616, 0, 'anchorite truuen SAY_WP_0');
INSERT INTO `creature_text` VALUES (17238, 1, 0, 'It must be the purity of the Mark of the Lightbringer that is drawing forth the Scourge to us.  We must proceed with caution lest we be overwhelmed!', 12, 7, 100, 0, 0, 0, 13617, 0, 'anchorite truuen SAY_WP_1');
INSERT INTO `creature_text` VALUES (17238, 2, 0, 'This land truly needs to be cleansed by the Light!  Let us continue on to the tomb.  It isn\'t far now.', 12, 7, 100, 0, 0, 0, 13619, 0, 'anchorite truuen SAY_WP_2');
INSERT INTO `creature_text` VALUES (1854, 0, 0, 'Be welcome, friends!', 12, 7, 100, 0, 0, 0, 13621, 0, 'anchorite truuen SAY_WP_3');
INSERT INTO `creature_text` VALUES (17233, 0, 0, 'Thank you for coming here in remembrance of me.  Your efforts in recovering that symbol, while unnecessary, are certainly touching to an old man\'s heart.', 12, 0, 100, 0, 0, 0, 13622, 0, 'anchorite truuen SAY_WP_4');
INSERT INTO `creature_text` VALUES (17233, 1, 0, 'Please, rise my friend.  Keep the Blessing as a symbol of the strength of the Light and how heroes long gone might once again rise in each of us to inspire.', 12, 0, 100, 0, 0, 0, 13623, 0, 'anchorite truuen SAY_WP_5');
INSERT INTO `creature_text` VALUES (17233, 2, 0, 'Thank you my friend for making this possible.  This is a day that I shall never forget!  I think I will stay a while.  Please return to High Priestess MacDonnell at the camp.  I know that she\'ll be keenly interested to know of what has transpired here.', 12, 7, 100, 0, 0, 0, 13624, 0, 'anchorite truuen SAY_WP_6');
