INSERT INTO `creature_text` VALUES (9999, 1, 2, 'Uuuuuuggggghhhhh....', 12, 0, 100, 0, 0, 0, 5394, 0, 'ringo SAY_FAINT_3');
INSERT INTO `creature_text` VALUES (9999, 1, 3, 'I\'m not feeling so well...', 12, 0, 100, 0, 0, 0, 5395, 0, 'ringo SAY_FAINT_4');
INSERT INTO `creature_text` VALUES (9999, 2, 0, 'Where... where am I?', 12, 0, 100, 0, 0, 0, 26240, 0, 'ringo SAY_WAKE_1');
INSERT INTO `creature_text` VALUES (9999, 2, 1, 'I am feeling a little better now, thank you.', 12, 0, 100, 0, 0, 0, 5399, 0, 'ringo SAY_WAKE_2');
INSERT INTO `creature_text` VALUES (9999, 2, 2, 'Yes, I must go on.', 12, 0, 100, 0, 0, 0, 5398, 0, 'ringo SAY_WAKE_3');
INSERT INTO `creature_text` VALUES (9999, 2, 3, 'How am I feeling? Quite soaked, thank you.', 12, 0, 100, 0, 0, 0, 5401, 0, 'ringo SAY_WAKE_4');
INSERT INTO `creature_text` VALUES (9999, 3, 0, 'Spraggle! I didn\'t think I\'d make it back!', 12, 0, 100, 0, 0, 0, 5402, 0, 'ringo SAY_RIN_END_1');
INSERT INTO `creature_text` VALUES (9997, 0, 0, 'Ringo! You\'re okay!', 12, 0, 100, 0, 0, 0, 5405, 0, 'ringo SAY_SPR_END_2');
INSERT INTO `creature_text` VALUES (9999, 4, 0, 'Oh... I\'m feeling faint...', 12, 0, 100, 0, 0, 0, 5403, 0, 'ringo SAY_RIN_END_3');
INSERT INTO `creature_text` VALUES (9999, 5, 0, '%s faints and falls onto the ground.', 16, 0, 100, 0, 0, 0, 5393, 0, 'ringo EMOTE_RIN_END_4');
INSERT INTO `creature_text` VALUES (9999, 6, 0, '%s stands up after a short pause.', 16, 0, 100, 0, 0, 0, 0, 0, 'ringo EMOTE_RIN_END_5');
INSERT INTO `creature_text` VALUES (9999, 7, 0, 'Ugh.', 12, 0, 100, 0, 0, 0, 5404, 0, 'ringo SAY_RIN_END_6');
INSERT INTO `creature_text` VALUES (9997, 1, 0, 'Ringo? Wake up! Don\'t worry, I\'ll take care of you.', 12, 0, 100, 0, 0, 0, 5406, 0, 'ringo SAY_SPR_END_7');
INSERT INTO `creature_text` VALUES (9999, 8, 0, '%s fades away after a long pause.', 16, 0, 100, 0, 0, 0, 0, 0, 'ringo EMOTE_RIN_END_8');
INSERT INTO `creature_text` VALUES (3850, 3, 0, 'For once I agree with you... scum.', 12, 7, 100, 1, 0, 0, 1329, 0, 'prisoner ashcrombe SAY_BOSS_DIE_AS');
INSERT INTO `creature_text` VALUES (7766, 1, 0, 'That\'s it! That\'s what you were waiting for! KILL THEM!', 14, 0, 100, 0, 0, 0, 4613, 0, 'lord gregor lescovar SAY_TYRION_2');
INSERT INTO `creature_text` VALUES (26935, 0, 0, 'I\'m old enough to shoot beasts like you right between the eyes... at twice this distance.', 12, 0, 100, 0, 0, 0, 26110, 0, 'Sasha');
INSERT INTO `creature_text` VALUES (26935, 1, 0, 'Don\'t you dare talk about my father, monster.  He was twice the man you\'ll ever be.', 12, 0, 100, 0, 0, 0, 26113, 0, 'Sasha');
INSERT INTO `creature_text` VALUES (26935, 2, 0, 'I\'d rather be dead than be one of you!  You think you\'re still human?  You\'re... animals!', 12, 0, 100, 0, 0, 0, 26112, 0, 'Sasha');
INSERT INTO `creature_text` VALUES (26935, 3, 0, 'Surprise, you scum!  You\'re going to tell me where my sister is or I\'ll put a bucketful of truesilver bullets through your wife\'s heart.', 12, 0, 100, 0, 0, 0, 26884, 0, 'Sasha');
INSERT INTO `creature_text` VALUES (26935, 4, 0, 'Where is Anya?', 12, 0, 100, 0, 0, 0, 26889, 0, 'Sasha');
INSERT INTO `creature_text` VALUES (26935, 5, 0, 'There is one last thing.  I need to know where Arugal is.', 12, 0, 100, 0, 0, 0, 26890, 0, 'Sasha');
INSERT INTO `creature_text` VALUES (26971, 0, 0, 'How old are you, lass?', 12, 0, 100, 0, 0, 0, 26107, 0, 'Anatoly');
INSERT INTO `creature_text` VALUES (26971, 1, 0, 'You won\'t get away with this, you know?  You\'re just a kid.', 12, 0, 100, 0, 0, 0, 26106, 0, 'Anatoly');
INSERT INTO `creature_text` VALUES (26971, 2, 0, 'Your father was weak, Sasha... he didn\'t have the guts to do what had to be done.', 12, 0, 100, 0, 0, 0, 26109, 0, 'Anatoly');
INSERT INTO `creature_text` VALUES (26971, 3, 0, 'We\'ll all end up serving the Lich King, kid.  Better this way than becoming a rotten corpse.', 12, 0, 100, 0, 0, 0, 26108, 0, 'Anatoly');
INSERT INTO `creature_text` VALUES (26971, 4, 0, 'Stop!  Do not shoot!  Do not hurt Tatjana!', 12, 0, 100, 0, 0, 0, 26885, 0, 'Anatoly');
INSERT INTO `creature_text` VALUES (26971, 5, 0, 'The brat\'s held prisoner in the wolf den on the other side of the mountain.  She was to be taken to Arugal.  Are we free to go now?', 12, 0, 100, 0, 0, 0, 26886, 0, 'Anatoly');
INSERT INTO `creature_text` VALUES (26971, 6, 0, 'Forgive me, Tatjana... ', 12, 0, 100, 0, 0, 0, 26887, 0, 'Anatoly');
INSERT INTO `creature_text` VALUES (26971, 7, 0, 'Nothing you can do can compare to what Arugal can do to us!  I will tear you apart myself!', 12, 0, 100, 0, 0, 0, 26888, 0, 'Anatoly');
INSERT INTO `creature_text` VALUES (26935, 6, 0, 'Anya!!  You\'re all right!', 12, 0, 100, 0, 0, 0, 26915, 0, 'Sasha');
INSERT INTO `creature_text` VALUES (26935, 7, 0, 'Badmoon?  You mean Bloodmoon?', 12, 0, 100, 0, 0, 0, 26916, 0, 'Sasha');
INSERT INTO `creature_text` VALUES (26935, 8, 0, 'Don\'t worry, sister... no one can hurt you now.', 12, 0, 100, 0, 0, 0, 26917, 0, 'Sasha');
INSERT INTO `creature_text` VALUES (27646, 0, 0, 'You won\'t eat me, will you?', 12, 0, 100, 0, 0, 0, 26914, 0, 'Anya');
INSERT INTO `creature_text` VALUES (27646, 1, 0, 'Sister!  The mean men were going to take me to Badmoon Isle.  They wanted to turn me into one of them.', 12, 0, 100, 0, 0, 0, 26918, 0, 'Anya');
INSERT INTO `creature_text` VALUES (27646, 2, 0, 'That\'s what I said!  Badmoon!  Sasha... don\'t ever leave me again!  First they took papa, then they took you... I don\'t want to be alone with them!', 12, 0, 100, 0, 0, 0, 26919, 0, 'Anya');
INSERT INTO `creature_text` VALUES (25610, 0, 0, 'Don\'t let them turn me into one of those aberrations!', 12, 1, 100, 1, 0, 0, 24828, 0, 'Scourge Prisoner');
INSERT INTO `creature_text` VALUES (25610, 0, 1, 'Kill me... Kill me now!', 12, 1, 100, 1, 0, 0, 24829, 0, 'Scourge Prisoner');
INSERT INTO `creature_text` VALUES (25610, 0, 2, 'Somebody please... Help...', 12, 1, 100, 1, 0, 0, 24827, 0, 'Scourge Prisoner');
INSERT INTO `creature_text` VALUES (25610, 0, 3, 'Don\'t let them turn me into one of those aberrations!', 12, 1, 100, 1, 0, 0, 24828, 0, 'Scourge Prisoner');
INSERT INTO `creature_text` VALUES (25610, 1, 0, 'Freedom at last! I must return to Warsong Hold at once!', 12, 1, 100, 1, 0, 0, 24832, 0, 'Scourge Prisoner');
INSERT INTO `creature_text` VALUES (25610, 1, 1, 'I am forever indebted to you, friend.', 12, 1, 100, 1, 0, 0, 24834, 0, 'Scourge Prisoner');
INSERT INTO `creature_text` VALUES (25610, 1, 2, 'Thank you, friend.', 12, 1, 100, 1, 0, 0, 24831, 0, 'Scourge Prisoner');
INSERT INTO `creature_text` VALUES (25610, 1, 3, 'You have my thanks, stranger.', 12, 1, 100, 1, 0, 0, 24833, 0, 'Scourge Prisoner');
INSERT INTO `creature_text` VALUES (23905, 0, 0, 'Marksmen, form up and take your positions!', 14, 0, 100, 0, 0, 0, 22447, 0, 'Major Mills');
INSERT INTO `creature_text` VALUES (30086, 0, 0, 'Taste my steel, little $g boy : girl;!', 12, 0, 100, 0, 0, 0, 30750, 0, 'say');
INSERT INTO `creature_text` VALUES (30086, 1, 0, 'You fight well, little one. I am bested for now. We will meet again I assure you. When we do I will be better prepared!', 12, 0, 100, 66, 0, 0, 30821, 0, 'say');
INSERT INTO `creature_text` VALUES (30081, 0, 0, 'You dare to touch ME?! You won\'t keep me from proving myself to the Lich King!', 12, 0, 100, 0, 0, 0, 30746, 0, 'say');
INSERT INTO `creature_text` VALUES (30180, 0, 0, 'Onu\'zun gonna kill you dead!', 12, 0, 100, 0, 0, 0, 30856, 0, 'say');
INSERT INTO `creature_text` VALUES (30162, 0, 0, 'Grrrrrrrr! I\'ll kneecap ya!', 12, 0, 100, 0, 0, 0, 30822, 0, 'say');
INSERT INTO `creature_text` VALUES (30162, 1, 0, 'WHEE!!!', 12, 0, 100, 0, 0, 0, 30828, 0, 'say');
INSERT INTO `creature_text` VALUES (36642, 1, 0, 'The style of this blade and its markings hinted that it might be a dragonforged sword. At last, we will have our answer.', 12, 0, 100, 1, 0, 16745, 37469, 0, 'Myralion Sunblaze - Say 1');
INSERT INTO `creature_text` VALUES (36642, 2, 0, 'After all these years, can it really be --', 12, 0, 100, 6, 0, 16746, 37470, 0, 'Myralion Sunblaze - Say 2');
INSERT INTO `creature_text` VALUES (36642, 3, 0, 'It falls to us to see Quel\'Delar restored for the Sunreavers.', 12, 0, 100, 0, 0, 0, 37514, 0, 'Myralion Sunblaze - Say 3');
INSERT INTO `creature_text` VALUES (36624, 1, 0, 'I knew this was a dragonforged blade when I first laid eyes on it.', 12, 0, 100, 1, 0, 16604, 37465, 0, 'Caladis Brightspear - Say 1');
INSERT INTO `creature_text` VALUES (36624, 2, 0, 'But can it be? Is this really --', 12, 0, 100, 6, 0, 16605, 37466, 0, 'Caladis Brightspear - Say 2');
INSERT INTO `creature_text` VALUES (36624, 3, 0, 'I vow that the Silver Covenant will see Quel\'Delar restored.', 12, 0, 100, 0, 0, 0, 37518, 0, 'Caladis Brightspear - Say 3');
INSERT INTO `creature_text` VALUES (36656, 1, 0, 'You are not welcome here, minion of the Lich King!', 12, 0, 100, 0, 0, 0, 37887, 0, 'Silver Covenant Sentinel - Say 1');
INSERT INTO `creature_text` VALUES (37846, 1, 0, 'Quel\'Delar.', 12, 0, 100, 1, 0, 16808, 37484, 0, 'Blood Queen Lana\'thel - Say 1');
INSERT INTO `creature_text` VALUES (37846, 2, 0, 'Quiet, fools. If I had wished to kill you, you\'d already be dead.', 12, 0, 100, 0, 0, 0, 37894, 0, 'Blood Queen Lana\'thel - Say 2');
INSERT INTO `creature_text` VALUES (37846, 3, 0, 'As Quel\'Serrar was forged by the dragons and given to the kaldorei, its twin, Quel\'Delar, was given to my people.', 12, 0, 100, 1, 0, 16809, 37485, 0, 'Blood Queen Lana\'thel - Say 3');
INSERT INTO `creature_text` VALUES (37846, 4, 0, 'The king bestowed the blade upon my friend, Thalorien Dawnseeker.', 12, 0, 100, 1, 0, 16800, 37486, 0, 'Blood Queen Lana\'thel - Say 4');
INSERT INTO `creature_text` VALUES (37846, 5, 0, 'But even Thalorien\'s skill and Quel\'Delar\'s magic could not save Silvermoon from the might of the Scourge.', 12, 0, 100, 1, 0, 16801, 37487, 0, 'Blood Queen Lana\'thel - Say 5');
INSERT INTO `creature_text` VALUES (37846, 6, 0, 'Thalorien fell before the gates of the Sunwell, buying time for others to escape.', 12, 0, 100, 1, 0, 16802, 37488, 0, 'Blood Queen Lana\'thel - Say 6');
INSERT INTO `creature_text` VALUES (37846, 7, 0, 'After the battle, I recovered the sword from the field. I bore it to Northrend in the service of my prince, seeking to avenge our people\'s defeat.', 12, 0, 100, 16, 0, 16803, 37490, 0, 'Blood Queen Lana\'thel - Say 7');
INSERT INTO `creature_text` VALUES (37846, 8, 0, 'Arthas shattered our forces and took the most powerful of us into his service as the San\'layn.', 12, 0, 100, 1, 0, 16804, 37491, 0, 'Blood Queen Lana\'thel - Say 8');
INSERT INTO `creature_text` VALUES (37846, 9, 0, 'My memories of Thalorien went cold and so did his blade.', 12, 0, 100, 1, 0, 16805, 37492, 0, 'Blood Queen Lana\'thel - Say 9');
INSERT INTO `creature_text` VALUES (37846, 10, 0, 'It was I who brought Quel\'Delar here, to return it to its makers. In breaking the weapon, so did I break its power.', 12, 0, 100, 25, 0, 16806, 37493, 0, 'Blood Queen Lana\'thel - Say 10');
INSERT INTO `creature_text` VALUES (37846, 11, 0, 'Quel\'Delar will never serve another!', 12, 0, 100, 5, 0, 16807, 37494, 0, 'Blood Queen Lana\'thel - Say 11');
INSERT INTO `creature_text` VALUES (30891, 0, 0, 'The Blessed Banner of the Crusade has been planted.\nDefend the banner from all attackers!', 41, 0, 100, 0, 0, 0, 31539, 0, 'Blessed Banner of the Crusade');
INSERT INTO `creature_text` VALUES (31003, 0, 0, 'BY THE LIGHT! Those damned monsters! Look at what they\'ve done to our people!', 12, 0, 100, 5, 0, 0, 31560, 0, 'Crusader Lord Dalfors');
INSERT INTO `creature_text` VALUES (31003, 1, 0, 'Burn it down, boys. Burn it all down.', 12, 0, 100, 1, 0, 0, 31561, 0, 'Crusader Lord Dalfors');
INSERT INTO `creature_text` VALUES (31003, 2, 0, 'Let \'em come. They\'ll pay for what they\'ve done!', 12, 0, 100, 1, 0, 0, 31562, 0, 'Crusader Lord Dalfors');
INSERT INTO `creature_text` VALUES (31003, 3, 0, 'We\'ve done it, lads! We\'ve taken the pinnacle from the Scourge! Report to Father Gustav at once and tell him the good news! We\'re gonna get to buildin\' and settin\' up! Go!', 14, 0, 100, 71, 0, 0, 31573, 0, 'Crusader Lord Dalfors');
INSERT INTO `creature_text` VALUES (31013, 0, 0, 'Leave no survivors!', 14, 0, 100, 0, 0, 14793, 31567, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (31013, 1, 0, 'Cower before my terrible creations!', 14, 0, 100, 0, 0, 14800, 31568, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (31013, 2, 0, 'Feast my children! Feast upon the flesh of the living!', 14, 0, 100, 0, 0, 14801, 31569, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (31013, 3, 0, 'Lay down your arms and surrender your souls!', 14, 0, 100, 0, 0, 14802, 31570, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (15989, 0, 0, 'Sapphiron lifts off into the air!', 41, 0, 100, 0, 0, 0, 32801, 3, 'Sapphiron');
INSERT INTO `creature_text` VALUES (15989, 1, 0, 'Sapphiron resumes his attacks!', 41, 0, 100, 0, 0, 0, 32802, 3, 'Sapphiron');
INSERT INTO `creature_text` VALUES (15989, 2, 0, '%s takes a deep breath.', 41, 0, 100, 0, 0, 0, 24548, 3, 'Sapphiron');
INSERT INTO `creature_text` VALUES (15989, 3, 0, '%s enrages!', 41, 0, 100, 0, 0, 0, 0, 3, 'Sapphiron');
INSERT INTO `creature_text` VALUES (32867, 0, 0, 'You will not defeat the Assembly of Iron so easily, invaders!', 14, 0, 100, 0, 0, 15674, 34321, 0, 'Steelbreaker - Aggro');
INSERT INTO `creature_text` VALUES (32867, 1, 0, 'So fragile and weak!', 14, 0, 100, 0, 0, 15675, 34322, 0, 'Steelbreaker - Slay 1');
INSERT INTO `creature_text` VALUES (32867, 1, 1, 'Flesh... such a hindrance.', 14, 0, 100, 0, 0, 15676, 34323, 0, 'Steelbreaker - Slay 2');
INSERT INTO `creature_text` VALUES (32867, 2, 0, 'You seek the secrets of Ulduar? Then take them!', 14, 0, 100, 0, 0, 15677, 34324, 0, 'Steelbreaker - Power');
INSERT INTO `creature_text` VALUES (32867, 3, 0, 'My death only serves to hasten your demise.', 14, 0, 100, 0, 0, 15678, 34325, 0, 'Steelbreaker - Death');
INSERT INTO `creature_text` VALUES (32867, 4, 0, 'Impossible...', 14, 0, 100, 0, 0, 15679, 38080, 0, 'Steelbreaker - Encounter defeated');
INSERT INTO `creature_text` VALUES (32867, 5, 0, 'This meeting of the Assembly of Iron is adjourned!', 14, 0, 100, 0, 0, 15680, 34335, 0, 'Steelbreaker - Berserk');
INSERT INTO `creature_text` VALUES (32927, 0, 0, 'Nothing short of total decimation will suffice.', 14, 0, 100, 0, 0, 15657, 34328, 0, 'Runemaster Molgeim - Aggro');
INSERT INTO `creature_text` VALUES (32927, 1, 0, 'The world suffers yet another insignificant loss.', 14, 0, 100, 0, 0, 15658, 34329, 0, 'Runemaster Molgeim - Slay 1');
INSERT INTO `creature_text` VALUES (32927, 1, 1, 'Death is the price of your arrogance.', 14, 0, 100, 0, 0, 15659, 34330, 0, 'Runemaster Molgeim - Slay 2');
INSERT INTO `creature_text` VALUES (32927, 2, 0, 'Decipher this!', 14, 0, 100, 0, 0, 15660, 34331, 0, 'Runemaster Molgeim - Rune of Death');
INSERT INTO `creature_text` VALUES (32927, 3, 0, 'Face the lightning\'s surge!', 14, 0, 100, 0, 0, 15661, 34332, 0, 'Runemaster Molgeim - Rune of Summon');
INSERT INTO `creature_text` VALUES (32927, 4, 0, 'The legacy of storms shall not be undone.', 14, 0, 100, 0, 0, 15662, 34333, 0, 'Runemaster Molgeim - Death');
INSERT INTO `creature_text` VALUES (32927, 5, 0, 'What have you gained from my defeat? You are no less doomed, mortals.', 14, 0, 100, 0, 0, 15663, 34334, 0, 'Runemaster Molgeim - Encounter defeated');
INSERT INTO `creature_text` VALUES (32927, 6, 0, 'This meeting of the Assembly of Iron is adjourned!', 14, 0, 100, 0, 0, 15664, 34335, 0, 'Runemaster Molgeim - Berserk');
INSERT INTO `creature_text` VALUES (32857, 0, 0, 'Whether the world\'s greatest gnats or the world\'s greatest heroes, you are still only mortal.', 14, 0, 100, 0, 0, 15684, 34314, 0, 'Brundir - Aggro');
INSERT INTO `creature_text` VALUES (32857, 1, 0, 'A merciful kill!', 14, 0, 100, 0, 0, 15685, 34315, 0, 'Brundir Slay 1');
INSERT INTO `creature_text` VALUES (32857, 1, 1, 'HAH!', 14, 0, 100, 0, 0, 15686, 35684, 0, 'Brundir - Slay 2');
INSERT INTO `creature_text` VALUES (32857, 2, 0, 'Stand still and stare into the light!', 14, 0, 100, 0, 0, 15687, 33962, 0, 'Brundir - Special');
INSERT INTO `creature_text` VALUES (32857, 3, 0, 'Let the storm clouds rise and rain down death from above!', 14, 0, 100, 0, 0, 15688, 34317, 0, 'Brundir - Flight');
INSERT INTO `creature_text` VALUES (32857, 4, 0, 'The power of the storm lives on.', 14, 0, 100, 0, 0, 15689, 34318, 0, 'Brundir - Death');
INSERT INTO `creature_text` VALUES (32857, 5, 0, 'You rush headlong into the maw of madness!', 14, 0, 100, 0, 0, 15690, 34319, 0, 'Brundir - Encounter defeated');
INSERT INTO `creature_text` VALUES (32857, 6, 0, 'This meeting of the Assembly of Iron is adjourned!', 14, 0, 100, 0, 0, 15691, 34335, 0, 'Brundir - Berserk');
INSERT INTO `creature_text` VALUES (32857, 7, 0, '%s begins to Overload!', 41, 0, 100, 0, 0, 0, 34004, 0, 'Brundir - Overload');
INSERT INTO `creature_text` VALUES (11596, 0, 0, 'Ah...the wondrous sound of kodos.  I love the way they make the ground shake... inspect the beast for me.', 12, 0, 100, 0, 0, 0, 7303, 0, 'kodo round SAY_SMEED_HOME_1');
INSERT INTO `creature_text` VALUES (11596, 0, 1, 'Hey, look out with that kodo! You had better inspect that beast before I give you credit!', 12, 0, 100, 0, 0, 0, 7301, 0, 'kodo round SAY_SMEED_HOME_2');
INSERT INTO `creature_text` VALUES (11596, 0, 2, 'That kodo sure is a beauty.  Wait a minute, where are my bifocals?  Perhaps you should inspect the beast for me.', 12, 0, 100, 0, 0, 0, 7302, 0, 'kodo round SAY_SMEED_HOME_3');
INSERT INTO `creature_text` VALUES (10556, 0, 0, 'Ow!  OK, I\'ll get back to work, $n!', 12, 1, 100, 0, 0, 0, 5774, 0, 'lazy peon SAY_WP_0');
INSERT INTO `creature_text` VALUES (7358, 0, 0, 'You\'ll never leave this place alive.', 14, 0, 100, 0, 0, 5825, 6187, 0, 'amnennar SAY_AGGRO');
INSERT INTO `creature_text` VALUES (7358, 1, 0, 'To me, my servants!', 14, 0, 100, 0, 0, 5828, 6189, 0, 'amnennar SAY_SUMMON60');
INSERT INTO `creature_text` VALUES (7358, 2, 0, 'Come, spirits - attend your master!', 14, 0, 100, 0, 0, 5829, 6190, 0, 'amnennar SAY_SUMMON30');
INSERT INTO `creature_text` VALUES (7358, 3, 0, 'I am the hand of the Lich King!', 14, 0, 100, 0, 0, 5827, 6192, 0, 'amnennar SAY_HP');
INSERT INTO `creature_text` VALUES (7358, 4, 0, 'Too easy.', 12, 0, 100, 0, 0, 5826, 6188, 0, 'amnennar SAY_KILL');
INSERT INTO `creature_text` VALUES (22916, 0, 0, 'Lurosa, I am entrusting the Relics of Aviana to $n, who will take them to Morthis Whisperwing. I must return completely to the Emerald Dream now. Do not let $n fail!', 12, 0, 100, 0, 0, 0, 20601, 0, 'clintar SAY_END');
INSERT INTO `creature_text` VALUES (3465, 0, 0, 'Stay close, $n. I\'ll need all the help I can get to break out of here. Let\'s go!', 12, 0, 100, 0, 0, 0, 1065, 0, 'gilthares SAY_GIL_START');
INSERT INTO `creature_text` VALUES (3465, 1, 0, 'At last! Free from Northwatch Hold! I need a moment to catch my breath....', 12, 0, 100, 0, 0, 0, 1066, 0, 'gilthares SAY_GIL_AT_LAST');
INSERT INTO `creature_text` VALUES (3465, 2, 0, 'Now I feel better. Let\'s get back to Ratchet. Come on, $n.', 12, 0, 100, 0, 0, 0, 1067, 0, 'gilthares SAY_GIL_PROCEED');
INSERT INTO `creature_text` VALUES (3465, 3, 0, 'Looks like the Southsea Freebooters are heavily entrenched on the coast. This could get rough.', 12, 0, 100, 0, 0, 0, 1068, 0, 'gilthares SAY_GIL_FREEBOOTERS');
INSERT INTO `creature_text` VALUES (3465, 4, 0, 'Help! $n attacking!', 12, 0, 100, 0, 0, 0, 1074, 0, 'gilthares SAY_GIL_AGGRO_1');
INSERT INTO `creature_text` VALUES (3465, 4, 1, '$n heading this way fast! Time for revenge!', 12, 0, 100, 0, 0, 0, 1075, 0, 'gilthares SAY_GIL_AGGRO_2');
INSERT INTO `creature_text` VALUES (3465, 4, 2, '$n coming right at us!', 12, 0, 100, 0, 0, 0, 1072, 0, 'gilthares SAY_GIL_AGGRO_3');
INSERT INTO `creature_text` VALUES (3465, 4, 3, 'Get this $n off of me!', 12, 0, 100, 0, 0, 0, 1073, 0, 'gilthares SAY_GIL_AGGRO_4');
INSERT INTO `creature_text` VALUES (3465, 5, 0, 'Almost back to Ratchet! Let\'s keep up the pace...', 12, 0, 100, 0, 0, 0, 1069, 0, 'gilthares SAY_GIL_ALMOST');
INSERT INTO `creature_text` VALUES (3465, 6, 0, 'Ah, the sweet salt air of Ratchet.', 12, 0, 100, 0, 0, 0, 1070, 0, 'gilthares SAY_GIL_SWEET');
INSERT INTO `creature_text` VALUES (3465, 7, 0, 'Captain Brightsun, $n here has freed me! $n, I am certain the Captain will reward your bravery.', 12, 0, 100, 0, 0, 0, 1071, 0, 'gilthares SAY_GIL_FREED');
INSERT INTO `creature_text` VALUES (6238, 0, 0, 'Ready when you are, warrior.', 12, 0, 100, 0, 0, 0, 2421, 0, 'big will SAY_BIG_WILL_READY');
INSERT INTO `creature_text` VALUES (6248, 0, 0, 'The Affray has begun.  $n, get ready to fight!', 12, 0, 100, 0, 0, 0, 2301, 0, 'twiggy SAY_TWIGGY_BEGIN');
INSERT INTO `creature_text` VALUES (6248, 1, 0, 'You!  Enter the fray!', 12, 0, 100, 0, 0, 0, 2318, 0, 'twiggy SAY_TWIGGY_FRAY');
INSERT INTO `creature_text` VALUES (6248, 2, 0, 'Challenger is down!', 12, 0, 100, 0, 0, 0, 2355, 0, 'twiggy SAY_TWIGGY_DOWN');
INSERT INTO `creature_text` VALUES (6248, 3, 0, 'The Affray is over!', 12, 0, 100, 0, 0, 0, 2320, 0, 'twiggy SAY_TWIGGY_OVER');
INSERT INTO `creature_text` VALUES (3439, 0, 0, 'Alright, alright! I think I can figure out how to operate this thing...', 12, 0, 100, 0, 0, 0, 1031, 0, 'wizzlecrank SAY_START');
INSERT INTO `creature_text` VALUES (3439, 1, 0, 'Arrrgh! This isn\'t right!', 12, 0, 100, 0, 0, 0, 1039, 0, 'wizzlecrank SAY_STARTUP1');
INSERT INTO `creature_text` VALUES (3439, 2, 0, 'Okay, I think I\'ve got it, now. Follow me, $n!', 12, 0, 100, 0, 0, 0, 1032, 0, 'wizzlecrank SAY_STARTUP2');
INSERT INTO `creature_text` VALUES (3282, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` VALUES (3439, 3, 0, 'Looks like we\'re out of the woods, eh? Wonder what this does...', 12, 0, 100, 0, 0, 0, 1033, 0, 'wizzlecrank SAY_PROGRESS_1');
INSERT INTO `creature_text` VALUES (3439, 4, 0, 'Come on, don\'t break down on me now!', 12, 0, 100, 0, 0, 0, 1043, 0, 'wizzlecrank SAY_PROGRESS_2');
INSERT INTO `creature_text` VALUES (3439, 5, 0, 'That was a close one! Well, let\'s get going, we\'re almost there.', 12, 0, 100, 0, 0, 0, 1041, 0, 'wizzlecrank SAY_PROGRESS_3');
INSERT INTO `creature_text` VALUES (3439, 6, 0, 'Hm... I don\'t think this blinking red light is a good thing...', 12, 0, 100, 0, 0, 0, 1044, 0, 'wizzlecrank SAY_END');
INSERT INTO `creature_text` VALUES (12717, 0, 0, 'Are you sure that you are ready? If we do not have a group of your allies to aid us, we will surely fail.', 12, 0, 100, 0, 0, 0, 8566, 0, 'muglash SAY_MUG_START1');
INSERT INTO `creature_text` VALUES (12717, 1, 0, 'This will be a tough fight, $n. Follow me closely!', 12, 0, 100, 0, 0, 0, 8555, 0, 'muglash SAY_MUG_START2');
INSERT INTO `creature_text` VALUES (12717, 2, 0, 'This is the brazier, $n. Put it out. Vorsha is a beast, worthy of praise from no one!', 12, 0, 100, 0, 0, 0, 8556, 0, 'muglash SAY_MUG_BRAZIER');
INSERT INTO `creature_text` VALUES (12717, 3, 0, 'Now we must wait. It won\'t be long before the naga realize what we have done.', 12, 0, 100, 0, 0, 0, 8410, 0, 'muglash SAY_MUG_BRAZIER_WAIT');
INSERT INTO `creature_text` VALUES (12717, 4, 0, 'Be on your guard, $n!', 12, 0, 100, 0, 0, 0, 8412, 0, 'muglash SAY_MUG_ON_GUARD');
INSERT INTO `creature_text` VALUES (12717, 5, 0, 'Perhaps we will get a moment to rest. But I will not give up until we have faced off against Vorsha!', 12, 0, 100, 0, 0, 0, 8413, 0, 'muglash SAY_MUG_REST');
INSERT INTO `creature_text` VALUES (12717, 6, 0, 'We have done it!', 12, 0, 100, 0, 0, 0, 8569, 0, 'muglash SAY_MUG_DONE');
INSERT INTO `creature_text` VALUES (12717, 7, 0, 'You have my deepest gratitude. I thank you.', 12, 0, 100, 0, 0, 0, 8558, 0, 'muglash SAY_MUG_GRATITUDE');
INSERT INTO `creature_text` VALUES (12717, 8, 0, 'I am going to patrol the area for a while longer and ensure that things are truly safe.', 12, 0, 100, 0, 0, 0, 8564, 0, 'muglash SAY_MUG_PATROL');
INSERT INTO `creature_text` VALUES (12717, 9, 0, 'Please return to Zoram\'gar and report our success to the Warsong Runner.', 12, 0, 100, 0, 0, 0, 8565, 0, 'muglash SAY_MUG_RETURN');
INSERT INTO `creature_text` VALUES (17772, 0, 0, 'I\'m in jeopardy! Help me if you can!', 14, 0, 100, 0, 0, 11007, 18537, 0, 'jaina hyjal ATTACKED 1');
INSERT INTO `creature_text` VALUES (17772, 0, 1, 'They\'ve broken through!', 14, 0, 100, 0, 0, 11049, 18538, 0, 'jaina hyjal ATTACKED 2');
INSERT INTO `creature_text` VALUES (17772, 1, 0, 'Don\'t give up! We must prevail!', 14, 0, 100, 0, 0, 11006, 18540, 0, 'jaina hyjal BEGIN');
INSERT INTO `creature_text` VALUES (17772, 2, 0, 'Stay alert! Another wave approaches.', 14, 0, 100, 0, 0, 11008, 0, 0, 'jaina hyjal INCOMING');
INSERT INTO `creature_text` VALUES (17772, 3, 0, 'Hold them back as long as possible!', 14, 0, 100, 0, 0, 11050, 18541, 0, 'jaina hyjal RALLY 1');
INSERT INTO `creature_text` VALUES (17772, 3, 1, 'We must hold strong!', 14, 0, 100, 0, 0, 11051, 18542, 0, 'jaina hyjal RALLY 2');
INSERT INTO `creature_text` VALUES (17772, 4, 0, 'We are lost! Fall back!', 14, 0, 100, 0, 0, 11009, 18543, 0, 'jaina hyjal FAILURE');
INSERT INTO `creature_text` VALUES (17772, 5, 0, 'We have won valuable time. Now we must pull back.', 14, 0, 100, 0, 0, 11011, 15309, 0, 'jaina hyjal SUCCESS');
INSERT INTO `creature_text` VALUES (17772, 6, 0, 'I did... my best.', 14, 0, 100, 0, 0, 11010, 18539, 0, 'jaina hyjal DEATH');
INSERT INTO `creature_text` VALUES (17852, 0, 0, 'I will lay down for no one!', 14, 0, 100, 0, 0, 11031, 18544, 0, 'thrall hyjal ATTACKED 1');
INSERT INTO `creature_text` VALUES (17852, 0, 1, 'Bring the fight to me and pay with your lives!', 14, 0, 100, 0, 0, 11061, 18545, 0, 'thrall hyjal ATTACKED 2');
INSERT INTO `creature_text` VALUES (17852, 1, 0, 'Hold them back! Do not falter!', 14, 0, 100, 0, 0, 11030, 18731, 0, 'thrall hyjal BEGIN');
INSERT INTO `creature_text` VALUES (17852, 2, 0, 'Make ready for another wave! LOK-TAR OGAR!', 14, 0, 100, 0, 0, 11032, 0, 0, 'thrall hyjal INCOMING');
INSERT INTO `creature_text` VALUES (17852, 3, 0, 'Victory or Death!', 14, 0, 100, 0, 0, 11059, 18732, 0, 'thrall hyjal RALLY 1');
INSERT INTO `creature_text` VALUES (17852, 3, 1, 'Do not give an inch of ground!', 14, 0, 100, 0, 0, 11060, 18733, 0, 'thrall hyjal RALLY 2');
INSERT INTO `creature_text` VALUES (17852, 4, 0, 'It is over! Withdraw! We have failed....', 14, 0, 100, 0, 0, 11033, 18734, 0, 'thrall hyjal FAILURE');
INSERT INTO `creature_text` VALUES (17852, 5, 0, 'We have played our part, and done well at that. It is up to the others now.', 14, 0, 100, 0, 0, 11035, 15312, 0, 'thrall hyjal SUCCESS');
INSERT INTO `creature_text` VALUES (17852, 6, 0, 'Uraaa...', 14, 0, 100, 0, 0, 11034, 0, 0, 'thrall hyjal DEATH');
INSERT INTO `creature_text` VALUES (17848, 0, 0, 'You there, fetch water quickly! Get these flames out before they spread to the rest of the keep! Hurry, damn you!', 14, 0, 100, 0, 0, 10428, 15737, 0, 'lieutenant drake SAY_ENTER');
INSERT INTO `creature_text` VALUES (17848, 1, 0, 'I know what you\'re up to, and I mean to put an end to it, permanently!', 14, 0, 100, 0, 0, 10429, 16650, 0, 'lieutenant drake SAY_AGGRO');
INSERT INTO `creature_text` VALUES (17848, 2, 0, 'No more middling for you.', 14, 0, 100, 0, 0, 10432, 0, 0, 'lieutenant drake SAY_SLAY1');
INSERT INTO `creature_text` VALUES (17848, 2, 1, 'You will not interfere!', 14, 0, 100, 0, 0, 10433, 16663, 0, 'lieutenant drake SAY_SLAY2');
INSERT INTO `creature_text` VALUES (17848, 3, 0, 'Time to bleed!', 14, 0, 100, 0, 0, 10430, 16659, 0, 'lieutenant drake SAY_MORTAL');
INSERT INTO `creature_text` VALUES (17848, 4, 0, 'Run, you blasted cowards!', 14, 0, 100, 0, 0, 10431, 16660, 0, 'lieutenant drake SAY_SHOUT');
INSERT INTO `creature_text` VALUES (17848, 5, 0, 'Thrall... must not... go free.', 14, 0, 100, 0, 0, 10434, 16664, 0, 'lieutenant drake SAY_DEATH');
INSERT INTO `creature_text` VALUES (17876, 0, 0, 'Very well then. Let\'s go!', 14, 0, 100, 0, 0, 10465, 17622, 0, 'thrall hillsbrad SAY_TH_START_EVENT_PART1');
INSERT INTO `creature_text` VALUES (17876, 1, 0, 'As long as we\'re going with a new plan, I may as well pick up a weapon and some armor.', 12, 0, 100, 0, 0, 0, 15893, 0, 'thrall hillsbrad SAY_TH_ARMORY');
INSERT INTO `creature_text` VALUES (17876, 2, 0, 'A rider approaches.', 12, 0, 100, 0, 0, 10466, 15908, 0, 'thrall hillsbrad SAY_TH_SKARLOC_MEET');
INSERT INTO `creature_text` VALUES (17876, 3, 0, 'I\'ll never be chained again!', 14, 0, 100, 0, 0, 10467, 17623, 0, 'thrall hillsbrad SAY_TH_SKARLOC_TAUNT');
INSERT INTO `creature_text` VALUES (17876, 4, 0, 'Very well. Tarren Mill lies just west of here. Since time is of the essence...', 14, 0, 100, 0, 0, 10468, 15916, 0, 'thrall hillsbrad SAY_TH_START_EVENT_PART2');
INSERT INTO `creature_text` VALUES (17876, 5, 0, 'Let\'s ride!', 12, 0, 100, 0, 0, 10469, 15948, 0, 'thrall hillsbrad SAY_TH_MOUNTS_UP');
INSERT INTO `creature_text` VALUES (17876, 6, 0, 'Taretha must be in the inn. Let\'s go.', 12, 0, 100, 0, 0, 0, 15977, 0, 'thrall hillsbrad SAY_TH_CHURCH_END');
INSERT INTO `creature_text` VALUES (17876, 7, 0, 'Taretha! What foul magic is this?', 12, 0, 100, 0, 0, 0, 15978, 0, 'thrall hillsbrad SAY_TH_MEET_TARETHA');
INSERT INTO `creature_text` VALUES (17876, 8, 0, 'Who or what was that?', 14, 0, 100, 0, 0, 10470, 16017, 0, 'thrall hillsbrad SAY_TH_EPOCH_WONDER');
INSERT INTO `creature_text` VALUES (17876, 9, 0, 'No!', 14, 0, 100, 0, 0, 10471, 35155, 0, 'thrall hillsbrad SAY_TH_EPOCH_KILL_TARETHA');
INSERT INTO `creature_text` VALUES (17876, 10, 0, 'Goodbye, Taretha. I will never forget your kindness.', 14, 0, 100, 0, 0, 10472, 16089, 0, 'thrall hillsbrad SAY_TH_EVENT_COMPLETE');
INSERT INTO `creature_text` VALUES (17876, 11, 0, 'Things are looking grim....', 14, 0, 100, 0, 0, 10458, 17610, 0, 'thrall hillsbrad SAY_TH_RANDOM_LOW_HP1');
INSERT INTO `creature_text` VALUES (17876, 11, 1, 'I\'ll fight... to the last!', 14, 0, 100, 0, 0, 10459, 17611, 0, 'thrall hillsbrad SAY_TH_RANDOM_LOW_HP2');
INSERT INTO `creature_text` VALUES (17876, 12, 0, 'Taretha....', 14, 0, 100, 0, 0, 10460, 17612, 0, 'thrall hillsbrad SAY_TH_RANDOM_DIE1');
INSERT INTO `creature_text` VALUES (17876, 12, 1, 'A good day... to die.', 14, 0, 100, 0, 0, 10461, 17613, 0, 'thrall hillsbrad SAY_TH_RANDOM_DIE2');
INSERT INTO `creature_text` VALUES (17876, 13, 0, 'I have earned my freedom!', 14, 0, 100, 0, 0, 10448, 17600, 0, 'thrall hillsbrad SAY_TH_RANDOM_AGGRO1');
INSERT INTO `creature_text` VALUES (17876, 13, 1, 'This day is long overdue! Out of my way!', 14, 0, 100, 0, 0, 10449, 17601, 0, 'thrall hillsbrad SAY_TH_RANDOM_AGGRO2');
INSERT INTO `creature_text` VALUES (17876, 13, 2, 'I am a slave no longer!', 14, 0, 100, 0, 0, 10450, 17602, 0, 'thrall hillsbrad SAY_TH_RANDOM_AGGRO3');
INSERT INTO `creature_text` VALUES (17876, 13, 3, 'Blackmoore has much to answer for!', 14, 0, 100, 0, 0, 10451, 17603, 0, 'thrall hillsbrad SAY_TH_RANDOM_AGGRO4');
INSERT INTO `creature_text` VALUES (17876, 14, 0, 'You have forced my hand!', 14, 0, 100, 0, 0, 10452, 17604, 0, 'thrall hillsbrad SAY_TH_RANDOM_KILL1');
INSERT INTO `creature_text` VALUES (17876, 14, 1, 'It should not have come to this!', 14, 0, 100, 0, 0, 10453, 17605, 0, 'thrall hillsbrad SAY_TH_RANDOM_KILL2');
INSERT INTO `creature_text` VALUES (17876, 14, 2, 'I did not ask for this!', 14, 0, 100, 0, 0, 10454, 17606, 0, 'thrall hillsbrad SAY_TH_RANDOM_KILL3');
INSERT INTO `creature_text` VALUES (17876, 15, 0, 'I am truly in your debt, strangers.', 14, 0, 100, 0, 0, 10455, 17607, 0, 'thrall hillsbrad SAY_TH_LEAVE_COMBAT1');
INSERT INTO `creature_text` VALUES (17876, 15, 1, 'Thank you, strangers. You have given me hope.', 14, 0, 100, 0, 0, 10456, 17608, 0, 'thrall hillsbrad SAY_TH_LEAVE_COMBAT2');
INSERT INTO `creature_text` VALUES (17876, 15, 2, 'I will not waste this chance. I will seek out my destiny.', 14, 0, 100, 0, 0, 10457, 17609, 0, 'thrall hillsbrad SAY_TH_LEAVE_COMBAT3');
INSERT INTO `creature_text` VALUES (18887, 0, 0, 'I\'m free! Thank you all!', 12, 0, 100, 0, 0, 0, 16082, 0, 'taretha SAY_TA_FREE');
INSERT INTO `creature_text` VALUES (18887, 1, 0, 'Thrall, you escaped!', 12, 0, 100, 0, 0, 0, 15979, 0, 'taretha SAY_TA_ESCAPED');
INSERT INTO `creature_text` VALUES (17862, 0, 0, 'Thrall! You didn\'t really think you would escape, did you?  You and your allies shall answer to Blackmoore... after I\'ve had my fun.', 14, 0, 100, 0, 0, 10406, 15909, 0, 'skarloc SAY_ENTER');
INSERT INTO `creature_text` VALUES (17862, 1, 0, 'You\'re a slave! That\'s all you\'ll ever be!', 14, 0, 100, 0, 0, 10407, 16673, 0, 'skarloc SAY_TAUNT1');
INSERT INTO `creature_text` VALUES (17862, 2, 0, 'I don\'t know what Blackmoore sees in you. For my money you\'re just another ignorant savage!', 14, 0, 100, 0, 0, 10408, 16674, 0, 'skarloc SAY_TAUNT2');
INSERT INTO `creature_text` VALUES (17862, 3, 0, 'Thrall will never be free!', 14, 0, 100, 0, 0, 10409, 16675, 0, 'skarloc SAY_SLAY1');
INSERT INTO `creature_text` VALUES (17862, 3, 1, 'Did you really think you would leave here alive?', 14, 0, 100, 0, 0, 10410, 16676, 0, 'skarloc SAY_SLAY2');
INSERT INTO `creature_text` VALUES (17862, 4, 0, 'Guards... guards!', 14, 0, 100, 0, 0, 10411, 16677, 0, 'skarloc SAY_DEATH');
INSERT INTO `creature_text` VALUES (18096, 0, 0, 'Thrall! Come outside and face your fate!', 14, 0, 100, 0, 0, 10418, 16016, 0, 'epoch hunter SAY_ENTER1');
INSERT INTO `creature_text` VALUES (18096, 0, 1, 'Taretha\'s life hangs in the balance. Surely you care for her. Surely you wish to save her... ', 14, 0, 100, 0, 0, 10419, 16019, 0, 'epoch hunter SAY_ENTER2');
INSERT INTO `creature_text` VALUES (18096, 0, 2, 'Ah, there you are. I had hoped to accomplish this with a bit of subtlety, but I suppose direct confrontation was inevitable. Your future, Thrall, must not come to pass and so... you and your troublesome friends must die!', 14, 0, 100, 0, 0, 10420, 16021, 0, 'epoch hunter SAY_ENTER3');
INSERT INTO `creature_text` VALUES (18096, 1, 0, 'Enough, I will erase your very existence!', 14, 0, 100, 0, 0, 10421, 16023, 0, 'epoch hunter SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (18096, 1, 1, 'You cannot fight fate!', 14, 0, 100, 0, 0, 10422, 16693, 0, 'epoch hunter SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (18096, 2, 0, 'You are... irrelevant.', 14, 0, 100, 0, 0, 10425, 16694, 0, 'epoch hunter SAY_SLAY1');
INSERT INTO `creature_text` VALUES (18096, 2, 1, 'Thrall will remain a slave. Taretha will die. You have failed.', 14, 0, 100, 0, 0, 10426, 16695, 0, 'epoch hunter SAY_SLAY2');
INSERT INTO `creature_text` VALUES (18096, 3, 0, 'Not so fast!', 14, 0, 100, 0, 0, 10423, 16691, 0, 'epoch hunter SAY_BREATH1');
INSERT INTO `creature_text` VALUES (18096, 3, 1, 'Struggle as much as you like...', 14, 0, 100, 0, 0, 10424, 16692, 0, 'epoch hunter SAY_BREATH2');
INSERT INTO `creature_text` VALUES (18096, 4, 0, 'No! The master will... not be... pleased.', 14, 0, 100, 0, 0, 10427, 16696, 0, 'epoch hunter SAY_DEATH');
INSERT INTO `creature_text` VALUES (26529, 0, 0, 'Play time!', 14, 0, 100, 0, 0, 13428, 27716, 0, 'meathook SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26529, 1, 0, 'Boring...', 14, 0, 100, 0, 0, 13430, 29943, 0, 'meathook SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (26529, 1, 1, 'Why you stop moving?', 14, 0, 100, 0, 0, 13431, 29944, 0, 'meathook SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (26529, 1, 2, 'Get up! Me not done!', 14, 0, 100, 0, 0, 13432, 29945, 0, 'meathook SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (26529, 2, 0, 'New toys!', 14, 0, 100, 0, 0, 13429, 29942, 0, 'meathook SAY_SPAWN');
INSERT INTO `creature_text` VALUES (26529, 3, 0, 'This not fun...', 14, 0, 100, 0, 0, 13433, 29941, 0, 'meathook SAY_DEATH');
INSERT INTO `creature_text` VALUES (26532, 0, 0, 'Prince Arthas Menethil, on this day, a powerful darkness has taken hold of your soul. The death you are destined to visit upon others will this day be your own.', 14, 0, 100, 0, 0, 13408, 27911, 0, 'epoch SAY_INTRO | culling SAY_PHASE314');
INSERT INTO `creature_text` VALUES (26532, 1, 0, 'We\'ll see about that, young prince.', 14, 0, 100, 0, 0, 13409, 27920, 0, 'epoch SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26532, 2, 0, 'Tick tock, tick tock...', 14, 0, 100, 0, 0, 13410, 30451, 0, 'epoch SAY_TIME_WARP_1');
INSERT INTO `creature_text` VALUES (26532, 2, 1, 'Not quick enough!', 14, 0, 100, 0, 0, 13411, 30452, 0, 'epoch SAY_TIME_WARP_2');
INSERT INTO `creature_text` VALUES (26532, 2, 2, 'Let\'s get this over with.', 14, 0, 100, 0, 0, 13412, 30453, 0, 'epoch SAY_TIME_WARP_3');
INSERT INTO `creature_text` VALUES (26532, 3, 0, 'There is no future for you.', 14, 0, 100, 0, 0, 13413, 30454, 0, 'epoch SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (26532, 3, 1, 'This is the hour of our greatest triumph!', 14, 0, 100, 0, 0, 13414, 30455, 0, 'epoch SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (26532, 3, 2, 'You were destined to fail.', 14, 0, 100, 0, 0, 13415, 30456, 0, 'epoch SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (26532, 4, 0, '', 12, 0, 100, 0, 0, 13416, 0, 0, 'epoch SAY_DEATH');
INSERT INTO `creature_text` VALUES (26530, 0, 0, 'Ah, the entertainment has arrived!', 14, 0, 100, 0, 0, 13472, 29947, 0, 'salramm SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26530, 1, 0, 'You are too late, champion of Lordaeron. The dead shall have their day.', 14, 0, 100, 0, 0, 13471, 27717, 0, 'salramm SAY_SPAWN');
INSERT INTO `creature_text` VALUES (26530, 2, 0, 'The fun is just beginning!', 14, 0, 100, 0, 0, 13473, 29948, 0, 'salramm SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (26530, 2, 1, 'Ah! Quality materials.', 14, 0, 100, 0, 0, 13474, 29949, 0, 'salramm SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (26530, 2, 2, 'Don\'t worry; I\'ll make good use of you!', 14, 0, 100, 0, 0, 13475, 29950, 0, 'salramm SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (26530, 3, 0, 'You only advance... the master\'s plan...', 14, 0, 100, 0, 0, 13483, 29946, 0, 'salramm SAY_DEATH');
INSERT INTO `creature_text` VALUES (26530, 4, 0, 'BOOM! Hahahahah...', 14, 0, 100, 0, 0, 13478, 29953, 0, 'salramm SAY_EXPLODE_GHOUL_1');
INSERT INTO `creature_text` VALUES (26530, 4, 1, 'Blood... destruction... EXHILARATING!', 14, 0, 100, 0, 0, 13479, 29954, 0, 'salramm SAY_EXPLODE_GHOUL_2');
INSERT INTO `creature_text` VALUES (26530, 5, 0, 'I want a sample...', 14, 0, 100, 0, 0, 13480, 29955, 0, 'salramm SAY_STEAL_FLESH_1');
INSERT INTO `creature_text` VALUES (26530, 5, 1, 'Such strength... it must be mine!', 14, 0, 100, 0, 0, 13481, 29956, 0, 'salramm SAY_STEAL_FLESH_2');
INSERT INTO `creature_text` VALUES (26530, 5, 2, 'Your flesh betrays you.', 14, 0, 100, 0, 0, 13482, 29957, 0, 'salramm SAY_STEAL_FLESH_3');
INSERT INTO `creature_text` VALUES (26530, 6, 0, 'Say hello to some friends of mine.', 14, 0, 100, 0, 0, 13476, 29951, 0, 'salramm SAY_SUMMON_GHOULS_1');
INSERT INTO `creature_text` VALUES (26530, 6, 1, 'Come, citizens of Stratholme! Meet your \"saviors.\"', 14, 0, 100, 0, 0, 13477, 29952, 0, 'salramm SAY_SUMMON_GHOULS_2');
INSERT INTO `creature_text` VALUES (26499, 0, 0, 'Glad you could make it, Uther.', 12, 0, 100, 0, 0, 12828, 27314, 0, 'culling SAY_PHASE101');
INSERT INTO `creature_text` VALUES (26499, 1, 0, 'As if I could forget. Listen, Uther, there\'s something about the plague you should know...', 12, 0, 100, 0, 0, 12829, 27316, 0, 'culling SAY_PHASE103');
INSERT INTO `creature_text` VALUES (26499, 2, 0, 'Oh, no. We\'re too late. These people have all been infected! They may look fine now, but it\'s just a matter of time before they turn into the undead!', 12, 0, 100, 0, 0, 12830, 27317, 0, 'culling SAY_PHASE104');
INSERT INTO `creature_text` VALUES (26499, 3, 0, 'This entire city must be purged.', 12, 0, 100, 0, 0, 12831, 27319, 0, 'culling SAY_PHASE106');
INSERT INTO `creature_text` VALUES (26499, 4, 0, 'Damn it, Uther! As your future king, I order you to purge this city!', 12, 0, 100, 0, 0, 12832, 27321, 0, 'culling SAY_PHASE108');
INSERT INTO `creature_text` VALUES (26499, 5, 0, 'Then I must consider this an act of treason.', 12, 0, 100, 0, 0, 12833, 27323, 0, 'culling SAY_PHASE110');
INSERT INTO `creature_text` VALUES (26499, 6, 0, 'Have I? Lord Uther, by my right of succession and the sovereignty of my crown, I hereby relieve you of your command and suspend your paladins from service.', 12, 0, 100, 0, 0, 12834, 27325, 0, 'culling SAY_PHASE112');
INSERT INTO `creature_text` VALUES (26499, 7, 0, 'It\'s done! Those of you who have the will to save this land, follow me! The rest of you... get out of my sight!', 12, 0, 100, 0, 0, 12835, 27327, 0, 'culling SAY_PHASE114');
INSERT INTO `creature_text` VALUES (26499, 8, 0, 'Jaina?', 12, 0, 100, 0, 0, 12836, 27329, 0, 'culling SAY_PHASE116');
INSERT INTO `creature_text` VALUES (26499, 9, 0, 'Take position here, and I will lead a small force inside Stratholme to begin the culling. We must contain and purge the infected for the sake of all of Lordaeron!', 14, 0, 100, 0, 0, 14327, 27466, 0, 'culling SAY_PHASE118');
INSERT INTO `creature_text` VALUES (26499, 10, 0, 'Everyone looks ready. Remember, these people are all infected with the plague and will die soon. We must purge Stratholme to protect the remainder of Lordaeron from the Scourge. Let\'s go.', 12, 0, 100, 0, 0, 14293, 27535, 0, 'culling SAY_PHASE201');
INSERT INTO `creature_text` VALUES (26499, 11, 0, 'I can only help you with a clean death.', 12, 0, 100, 0, 0, 14294, 27548, 0, 'culling SAY_PHASE203');
INSERT INTO `creature_text` VALUES (26499, 12, 0, 'That was just the beginning.', 12, 0, 100, 0, 0, 14295, 27553, 0, 'culling SAY_PHASE205');
INSERT INTO `creature_text` VALUES (26499, 13, 0, 'I won\'t allow it, Mal\'Ganis! Better that these people die by my hand than serve as your slaves in death!', 14, 0, 100, 0, 0, 14296, 27577, 0, 'culling SAY_PHASE208');
INSERT INTO `creature_text` VALUES (26499, 14, 0, 'Mal\'Ganis will send out some of his Scourge minions to interfere with us. Those of you with the strongest steel and magic shall go forth and destroy them. I will lead the rest of my forces in purging Stratholme of the infected.', 14, 0, 100, 0, 0, 14885, 27584, 0, 'culling SAY_PHASE209');
INSERT INTO `creature_text` VALUES (26499, 15, 0, 'Champions, meet me at the Town Hall at once. We must take the fight to Mal\'Ganis.', 14, 0, 100, 0, 0, 14297, 28890, 0, 'culling SAY_PHASE210');
INSERT INTO `creature_text` VALUES (26499, 16, 0, 'Follow me, I know the way through.', 12, 0, 100, 0, 0, 14298, 27800, 0, 'culling SAY_PHASE301');
INSERT INTO `creature_text` VALUES (26499, 17, 0, 'Yes, I\'m glad I could get to you before the plague.', 12, 0, 100, 0, 0, 14299, 27812, 0, 'culling SAY_PHASE303');
INSERT INTO `creature_text` VALUES (26499, 18, 0, 'What is this sorcery?', 12, 0, 100, 0, 0, 14300, 27814, 0, 'culling SAY_PHASE304');
INSERT INTO `creature_text` VALUES (26499, 19, 0, 'Mal\'Ganis appears to have more than Scourge in his arsenal. We should make haste.', 12, 0, 100, 0, 0, 14301, 27840, 0, 'culling SAY_PHASE306. NEEDS VERIFICATION');
INSERT INTO `creature_text` VALUES (26499, 20, 0, 'More vile sorcery! Be ready for anything!', 12, 0, 100, 0, 0, 14302, 27882, 0, 'culling SAY_PHASE307');
INSERT INTO `creature_text` VALUES (26499, 21, 0, 'Let\'s move on.', 12, 0, 100, 0, 0, 14303, 36010, 0, 'culling SAY_PHASE308');
INSERT INTO `creature_text` VALUES (26499, 22, 0, 'Watch your backs: they have us surrounded in this hall.', 12, 0, 100, 0, 0, 14304, 27901, 0, 'culling SAY_PHASE309');
INSERT INTO `creature_text` VALUES (26499, 23, 0, 'One less obstacle to deal with.', 12, 0, 100, 0, 0, 0, 31356, 0, 'culling SAY_PHASE310. NEEDS VERIFICATION');
INSERT INTO `creature_text` VALUES (26499, 24, 0, 'Mal\'Ganis is not making this easy.', 12, 0, 100, 0, 0, 14305, 27902, 0, 'culling SAY_PHASE311');
INSERT INTO `creature_text` VALUES (26499, 25, 0, 'They\'re very persistent.', 12, 0, 100, 0, 0, 14306, 27904, 0, 'culling SAY_PHASE312');
INSERT INTO `creature_text` VALUES (26499, 26, 0, 'What else can he put in my way?', 12, 0, 100, 0, 0, 14307, 27905, 0, 'culling SAY_PHASE313');
INSERT INTO `creature_text` VALUES (26499, 27, 0, 'I do what I must for Lordaeron, and neither your words nor your actions will stop me.', 12, 0, 100, 0, 0, 14309, 27916, 0, 'culling SAY_PHASE315');
INSERT INTO `creature_text` VALUES (26499, 28, 0, 'The quickest path to Mal\'Ganis lies behind that bookshelf ahead.', 12, 0, 100, 0, 0, 14308, 27906, 0, 'culling SAY_PHASE401');
INSERT INTO `creature_text` VALUES (26499, 29, 0, 'This will only take a moment.', 12, 0, 100, 0, 0, 14310, 28040, 0, 'culling SAY_PHASE402');
INSERT INTO `creature_text` VALUES (26499, 30, 0, 'I\'m relieved this secret passage still works.', 12, 0, 100, 0, 0, 14311, 28041, 0, 'culling SAY_PHASE403');
INSERT INTO `creature_text` VALUES (26499, 31, 0, 'Let\'s move through here as quickly as possible. If the undead don\'t kill us, the fires might.', 12, 0, 100, 0, 0, 14312, 28562, 0, 'culling SAY_PHASE404');
INSERT INTO `creature_text` VALUES (26499, 32, 0, 'Rest a moment and clear your lungs, but we must move again soon.', 12, 0, 100, 0, 0, 14313, 28045, 0, 'culling SAY_PHASE405');
INSERT INTO `creature_text` VALUES (26499, 33, 0, 'That\'s enough; we must move again. Mal\'Ganis awaits.', 12, 0, 100, 0, 0, 14314, 28047, 0, 'culling SAY_PHASE406');
INSERT INTO `creature_text` VALUES (26499, 34, 0, 'At last some good luck. Market Row has not caught fire yet. Mal\'Ganis is supposed to be in Crusaders\' Square, which is just ahead. Tell me when you\'re ready to move forward.', 12, 0, 100, 0, 0, 14315, 28048, 0, 'culling SAY_PHASE407');
INSERT INTO `creature_text` VALUES (26499, 35, 0, 'Justice will be done.', 12, 0, 100, 0, 0, 14316, 28563, 0, 'culling SAY_PHASE501');
INSERT INTO `creature_text` VALUES (26499, 36, 0, 'We\'re going to finish this right now, Mal\'Ganis. Just you... and me.', 12, 0, 100, 0, 0, 14317, 28520, 0, 'culling SAY_PHASE502');
INSERT INTO `creature_text` VALUES (26499, 37, 0, 'I\'ll hunt you to the ends of the earth if I have to! Do you hear me? To the ends of the earth!', 12, 0, 100, 0, 0, 14318, 28529, 0, 'culling SAY_PHASE503');
INSERT INTO `creature_text` VALUES (26499, 38, 0, 'You performed well this day. Anything that Mal\'Ganis has left behind is yours. Take it as your reward. I must now begin plans for an expedition to Northrend.', 12, 0, 100, 0, 0, 14319, 28530, 0, 'culling SAY_PHASE504');
INSERT INTO `creature_text` VALUES (26528, 0, 0, 'Watch your tone with me, boy. You may be the prince, but I\'m still your superior as a paladin!', 12, 0, 100, 0, 0, 12839, 27315, 0, 'culling SAY_PHASE102');
INSERT INTO `creature_text` VALUES (26528, 1, 0, 'What?', 12, 0, 100, 0, 0, 12840, 38871, 0, 'culling SAY_PHASE105');
INSERT INTO `creature_text` VALUES (26528, 2, 0, 'How can you even consider that? There\'s got to be some other way.', 12, 0, 100, 0, 0, 12841, 27320, 0, 'culling SAY_PHASE107');
INSERT INTO `creature_text` VALUES (26528, 3, 0, 'You are not my king yet, boy! Nor would I obey that command even if you were!', 12, 0, 100, 0, 0, 12842, 27322, 0, 'culling SAY_PHASE109');
INSERT INTO `creature_text` VALUES (26528, 4, 0, 'Treason? Have you lost your mind, Arthas?', 12, 0, 100, 0, 0, 12843, 27324, 0, 'culling SAY_PHASE111');
INSERT INTO `creature_text` VALUES (26528, 5, 0, 'You\'ve just crossed a terrible threshold, Arthas.', 12, 0, 100, 0, 0, 12844, 27328, 0, 'culling SAY_PHASE115');
INSERT INTO `creature_text` VALUES (26497, 0, 0, 'Arthas! You can\'t just--', 12, 0, 100, 0, 0, 12837, 27326, 0, 'culling SAY_PHASE113');
INSERT INTO `creature_text` VALUES (26497, 1, 0, 'I\'m sorry, Arthas. I can\'t watch you do this.', 12, 0, 100, 0, 0, 12838, 27330, 0, 'culling SAY_PHASE117');
INSERT INTO `creature_text` VALUES (28167, 0, 0, 'Prince Arthas, may the light be praised! Many people in the town have begun to fall seriously ill, can you help us?', 12, 0, 100, 0, 0, 0, 27547, 0, 'culling SAY_PHASE202');
INSERT INTO `creature_text` VALUES (28169, 0, 0, 'What? This can\'t be!', 12, 0, 100, 0, 0, 0, 27549, 0, 'culling SAY_PHASE204');
INSERT INTO `creature_text` VALUES (31126, 0, 0, 'Ah, you\'ve finally arrived Prince Arthas. You\'re here just in the nick of time.', 12, 0, 100, 0, 0, 0, 27811, 0, 'culling SAY_PHASE302');
INSERT INTO `creature_text` VALUES (31126, 1, 0, 'There\'s no need for you to understand, Arthas. All you need to do is die.', 12, 0, 100, 0, 0, 0, 27815, 0, 'culling SAY_PHASE305');
INSERT INTO `creature_text` VALUES (26533, 0, 0, 'Yes, this is the beginning. I\'ve been waiting for you, young prince. I am Mal\'Ganis.', 14, 0, 100, 0, 0, 14410, 27574, 0, 'malganis SAY_INTRO_1 | culling SAY_PHASE206');
INSERT INTO `creature_text` VALUES (26533, 1, 0, 'As you can see, your people are now mine. I will now turn this city household by household, until the flame of life has been snuffed out... forever.', 14, 0, 100, 0, 0, 14411, 27575, 0, 'malganis SAY_INTRO_2 | culling SAY_PHASE207');
INSERT INTO `creature_text` VALUES (26533, 2, 0, 'This will be a fine test, Prince Arthas.', 14, 0, 100, 0, 0, 14413, 31344, 0, 'malganis SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26533, 3, 0, 'All too easy.', 14, 0, 100, 0, 0, 14416, 31347, 0, 'malganis SAY_KILL_1');
INSERT INTO `creature_text` VALUES (26533, 3, 1, 'The Dark Lord is displeased with your interference.', 14, 0, 100, 0, 0, 14417, 31348, 0, 'malganis SAY_KILL_2');
INSERT INTO `creature_text` VALUES (26533, 3, 2, 'It is Prince Arthas I want, not you!', 14, 0, 100, 0, 0, 14418, 31349, 0, 'malganis SAY_KILL_3');
INSERT INTO `creature_text` VALUES (26533, 4, 0, 'Anakh kyree!', 14, 0, 100, 0, 0, 14422, 30412, 0, 'malganis SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (26533, 4, 1, 'My Onslaught will wash over the Lich King\'s forces!', 14, 0, 100, 0, 0, 14423, 30413, 0, 'malganis SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (26533, 4, 2, 'Your death is in vain, tiny mortal.', 14, 0, 100, 0, 0, 14424, 30414, 0, 'malganis SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (26533, 4, 3, 'Your time has come to an end.', 14, 0, 100, 0, 0, 14425, 30415, 0, 'malganis SAY_SLAY_4');
INSERT INTO `creature_text` VALUES (26533, 5, 0, 'Time out.', 14, 0, 100, 0, 0, 14414, 31345, 0, 'malganis SAY_SLEEP_1');
INSERT INTO `creature_text` VALUES (26533, 5, 1, 'You seem tired.', 14, 0, 100, 0, 0, 14415, 31346, 0, 'malganis SAY_SLEEP_2');
INSERT INTO `creature_text` VALUES (26533, 6, 0, 'Gah! I spent too much time in that weak little shell.', 14, 0, 100, 0, 0, 14426, 30275, 0, 'malganis SAY_30HEALTH');
INSERT INTO `creature_text` VALUES (26533, 7, 0, 'Kirel narak! I am Mal\'Ganis. I AM ETERNAL!', 14, 0, 100, 0, 0, 14427, 30276, 0, 'malganis SAY_15HEALTH');
INSERT INTO `creature_text` VALUES (26533, 8, 0, 'ENOUGH! I waste my time here. I must gather my strength on the homeworld.', 14, 0, 100, 0, 0, 14428, 30277, 0, 'malganis SAY_ESCAPE_SPEECH_1');
INSERT INTO `creature_text` VALUES (26533, 9, 0, 'You\'ll never defeat the Lich King without my forces. I\'ll have my revenge... on him AND you!', 14, 0, 100, 0, 0, 14429, 30278, 0, 'malganis SAY_ESCAPE_SPEECH_2');
INSERT INTO `creature_text` VALUES (26533, 10, 0, 'Your journey has just begun, young prince. Gather your forces and meet me in the arctic land of Northrend. It is there that we shall settle the score between us. It is there that your true destiny will unfold.', 14, 0, 100, 0, 0, 14412, 28528, 0, 'malganis SAY_OUTRO');
INSERT INTO `creature_text` VALUES (17879, 0, 0, 'Why do you aid the Magus? Just think of how many lives could be saved if the portal is never opened, if the resulting wars could be erased....', 14, 0, 100, 0, 0, 10412, 16814, 0, 'chrono lord deja SAY_ENTER');
INSERT INTO `creature_text` VALUES (17879, 1, 0, 'If you will not cease this foolish quest, then you will die!', 14, 0, 100, 0, 0, 10414, 16815, 0, 'chrono lord deja SAY_AGGRO');
INSERT INTO `creature_text` VALUES (17879, 2, 0, 'You have outstayed your welcome, Keeper. Be gone!', 14, 0, 100, 0, 0, 10413, 16817, 0, 'chrono lord deja SAY_BANISH');
INSERT INTO `creature_text` VALUES (17879, 3, 0, 'I told you it was a fool\'s quest!', 14, 0, 100, 0, 0, 10415, 16818, 0, 'chrono lord deja SAY_SLAY1');
INSERT INTO `creature_text` VALUES (17879, 3, 1, 'Leaving so soon?', 14, 0, 100, 0, 0, 10416, 16819, 0, 'chrono lord deja SAY_SLAY2');
INSERT INTO `creature_text` VALUES (17879, 4, 0, 'Time... is on our side.', 14, 0, 100, 0, 0, 10417, 16816, 0, 'chrono lord deja SAY_DEATH');
INSERT INTO `creature_text` VALUES (17881, 0, 0, 'The time has come to shatter this clockwork universe forever! Let us no longer be slaves of the hourglass! I warn you: those who do not embrace this greater path shall become victims of its passing!', 14, 0, 100, 0, 0, 10400, 16827, 0, 'aeonus SAY_ENTER');
INSERT INTO `creature_text` VALUES (17881, 1, 0, 'Let us see what fate has in store....', 14, 0, 100, 0, 0, 10402, 16828, 0, 'aeonus SAY_AGGRO');
INSERT INTO `creature_text` VALUES (17881, 2, 0, 'Your time is up, slave of the past.', 14, 0, 100, 0, 0, 10401, 16829, 0, 'aeonus SAY_BANISH');
INSERT INTO `creature_text` VALUES (17881, 3, 0, 'One less obstacle in our way!', 14, 0, 100, 0, 0, 10403, 16831, 0, 'aeonus SAY_SLAY1');
INSERT INTO `creature_text` VALUES (17881, 3, 1, 'No one will stop us!', 14, 0, 100, 0, 0, 10404, 16832, 0, 'aeonus SAY_SLAY2');
INSERT INTO `creature_text` VALUES (17881, 4, 0, 'It is only a matter...of time.', 14, 0, 100, 0, 0, 10405, 0, 0, 'aeonus SAY_DEATH');
INSERT INTO `creature_text` VALUES (17881, 5, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 10645, 0, 'aeonus EMOTE_FRENZY');
INSERT INTO `creature_text` VALUES (15608, 0, 0, 'The time has come! Gul\'dan, order your warlocks to double their efforts! Moments from now the gateway will open and your Horde will be released upon this ripe, unsuspecting world!', 14, 0, 100, 0, 0, 10435, 16787, 0, 'medivh SAY_ENTER');
INSERT INTO `creature_text` VALUES (15608, 1, 0, 'What is this? Champions, coming to my aid? I sense the hand of the dark one in this. Truly this sacred event bears his blessing?', 14, 0, 100, 0, 0, 10436, 0, 0, 'medivh SAY_INTRO');
INSERT INTO `creature_text` VALUES (15608, 2, 0, 'Champions! My shield grows weak!', 14, 0, 100, 0, 0, 10437, 16792, 0, 'medivh SAY_WEAK75');
INSERT INTO `creature_text` VALUES (15608, 3, 0, 'My powers must be concentrated on the portal! I do not have time to hold the shield!', 14, 0, 100, 0, 0, 10438, 16794, 0, 'medivh SAY_WEAK50');
INSERT INTO `creature_text` VALUES (15608, 4, 0, 'The shield is nearly gone! All that I have worked for is in danger!', 14, 0, 100, 0, 0, 10439, 16795, 0, 'medivh SAY_WEAK25');
INSERT INTO `creature_text` VALUES (15608, 5, 0, 'No! Damn this feeble, mortal coil!', 14, 0, 100, 0, 0, 10441, 16790, 0, 'medivh SAY_DEATH');
INSERT INTO `creature_text` VALUES (15608, 6, 0, 'I am grateful for your aid, champions. Now, Gul\'dan\'s Horde will sweep across this world like a locust swarm, and all my designs, all my carefully-laid plans will at last fall into place.', 14, 0, 100, 0, 0, 10440, 16796, 0, 'medivh SAY_WIN');
INSERT INTO `creature_text` VALUES (15608, 7, 0, 'Orcs of the Horde! This portal is the gateway to your new destiny! Azeroth lies before you, ripe for the taking!', 14, 0, 100, 0, 0, 0, 19909, 0, 'medivh SAY_ORCS_ENTER');
INSERT INTO `creature_text` VALUES (15608, 8, 0, 'Gul\'dan speaks the truth! We should return at once to tell our brothers of the news! Retreat back through the portal!', 14, 0, 100, 0, 0, 0, 19911, 0, 'medivh SAY_ORCS_ANSWER');
INSERT INTO `creature_text` VALUES (17880, 0, 0, 'Why do you persist? Surely you can see the futility of it all. It is not too late! You may still leave with your lives...', 14, 0, 100, 0, 0, 10442, 16821, 0, 'temporus SAY_ENTER');
INSERT INTO `creature_text` VALUES (17880, 1, 0, 'So be it... you have been warned.', 14, 0, 100, 0, 0, 10444, 16822, 0, 'temporus SAY_AGGRO');
INSERT INTO `creature_text` VALUES (17880, 2, 0, 'Keeper! The sands of time have run out for you.', 14, 0, 100, 0, 0, 10443, 16824, 0, 'temporus SAY_BANISH');
INSERT INTO `creature_text` VALUES (17880, 3, 0, 'You should have left when you had the chance.', 14, 0, 100, 0, 0, 10445, 16825, 0, 'temporus SAY_SLAY1');
INSERT INTO `creature_text` VALUES (17880, 3, 1, 'Your days are done.', 14, 0, 100, 0, 0, 10446, 16826, 0, 'temporus SAY_SLAY2');
INSERT INTO `creature_text` VALUES (17880, 4, 0, 'My death means... little.', 14, 0, 100, 0, 0, 10447, 16823, 0, 'temporus SAY_DEATH');
INSERT INTO `creature_text` VALUES (23002, 0, 0, 'You, there! Hand over that moonstone and nobody gets hurt!', 14, 0, 100, 0, 0, 0, 20715, 0, 'sprysprocket SAY_START');
INSERT INTO `creature_text` VALUES (23002, 1, 0, 'Just chill!', 15, 0, 100, 0, 0, 0, 21320, 0, 'sprysprocket SAY_WHISPER_CHILL');
INSERT INTO `creature_text` VALUES (23002, 2, 0, 'All right, you win! I surrender! Just don\'t hurt me!', 14, 0, 100, 0, 0, 0, 20720, 0, 'sprysprocket SAY_END');
INSERT INTO `creature_text` VALUES (23002, 3, 0, '%s takes the Southfury moonstone and escapes into the river. Follow her!', 41, 0, 100, 0, 0, 0, 20708, 0, 'sprysprocket EMOTE_START');
INSERT INTO `creature_text` VALUES (3568, 0, 0, '%s growls in acknowledgement before straightening and making her way off into the forest.', 16, 0, 100, 0, 0, 0, 1340, 0, 'Mist');
INSERT INTO `creature_text` VALUES (3519, 0, 0, 'Mist! I feared I would never see you again! Yes, I am well, do not worry for me. You must rest and recover your health.', 12, 7, 100, 0, 0, 0, 1330, 0, 'Sentinel Arynia Cloudsbreak');
INSERT INTO `creature_text` VALUES (15381, 0, 0, 'We must act quickly or all shall be lost!', 12, 0, 100, 0, 0, 0, 10909, 0, 'anachronos the ancient ANACHRONOS_SAY_1');
INSERT INTO `creature_text` VALUES (15381, 1, 0, 'NOW, STAGHELM! WE GO NOW! Prepare your magic!', 12, 0, 100, 0, 0, 0, 10915, 0, 'anachronos the ancient ANACHRONOS_SAY_2');
INSERT INTO `creature_text` VALUES (15381, 2, 0, 'Stay close...', 12, 0, 100, 0, 0, 0, 10917, 0, 'anachronos the ancient ANACHRONOS_SAY_3');
INSERT INTO `creature_text` VALUES (15381, 3, 0, 'The sands of time will halt, but only for a moment! I will now conjure the barrier.', 12, 0, 100, 0, 0, 0, 10930, 0, 'anachronos the ancient ANACHRONOS_SAY_4');
INSERT INTO `creature_text` VALUES (15381, 4, 0, 'FINISH THE SPELL, STAGHELM! I CANNOT HOLD THE GLYPHS OF WARDING IN PLACE MUCH LONGER! CALL FORTH THE ROOTS!', 12, 0, 100, 0, 0, 0, 10920, 0, 'anachronos the ancient ANACHRONOS_SAY_5');
INSERT INTO `creature_text` VALUES (15381, 5, 0, 'It... It is over, Lord Staghelm. We are victorious. Albeit the cost for this victory was great.', 12, 0, 100, 0, 0, 0, 10923, 0, 'anachronos the ancient ANACHRONOS_SAY_6');
INSERT INTO `creature_text` VALUES (15381, 6, 0, 'There is but one duty that remains...', 12, 0, 100, 0, 0, 0, 10924, 0, 'anachronos the ancient ANACHRONOS_SAY_7');
INSERT INTO `creature_text` VALUES (15381, 7, 0, 'Before I leave this place, I make one final offering to you, Lord Staghelm. Should a time arise in which you must gain entry to this accursed fortress, use the Scepter of the Shifting Sands on the sacred gong. The magic holding the barrier together will dissipate and the horrors of Ahn\'Qiraj will be unleashed upon the world once more.', 12, 0, 100, 0, 0, 0, 10925, 0, 'anachronos the ancient ANACHRONOS_SAY_8');
INSERT INTO `creature_text` VALUES (15381, 8, 0, 'Lord Staghelm, where are you going? You would shatter our bond for the sake of pride?\n', 12, 0, 100, 0, 0, 0, 10931, 0, 'anachronos the ancient ANACHRONOS_SAY_9');
INSERT INTO `creature_text` VALUES (15381, 9, 0, 'And now you know all that there is to know, mortal... ', 12, 0, 100, 0, 0, 0, 10935, 0, 'anachronos the ancient ANACHRONOS_SAY_10');
INSERT INTO `creature_text` VALUES (15381, 10, 0, '%s hands the Scepter of the Shifting Sands to Fandral Staghelm.', 16, 0, 100, 0, 0, 0, 10926, 0, 'anachronos the ancient ANACHRONOS_EMOTE_1');
INSERT INTO `creature_text` VALUES (15381, 11, 0, '%s shakes his head in disappointment.', 16, 0, 100, 0, 0, 0, 10933, 0, 'anachronos the ancient ANACHRONOS_EMOTE_2');
INSERT INTO `creature_text` VALUES (15381, 12, 0, '%s kneels down to pick up the fragments of the shattered scepter.', 16, 0, 100, 0, 0, 0, 10934, 0, 'anachronos the ancient ANACHRONOS_EMOTE_3');
INSERT INTO `creature_text` VALUES (15382, 0, 0, 'My forces cannot overcome the Qiraji defenses. We will not be able to get close enough to place your precious barrier, dragon.', 12, 0, 100, 0, 0, 0, 10910, 0, 'anachronos the ancient FANDRAL_SAY_1');
INSERT INTO `creature_text` VALUES (15382, 1, 0, 'It is done, dragon. Lead the way.', 12, 0, 100, 0, 0, 0, 10916, 0, 'anachronos the ancient FANDRAL_SAY_2');
INSERT INTO `creature_text` VALUES (15382, 2, 0, 'Ancient ones guide my hand... Wake from your slumber! WAKE AND SEAL THIS CURSED PLACE!', 12, 0, 100, 0, 0, 0, 10921, 0, 'anachronos the ancient FANDRAL_SAY_3');
INSERT INTO `creature_text` VALUES (15382, 3, 0, 'After the savagery that my people have witnessed and felt, you expect me to accept another burden, dragon? Surely you are mad.', 12, 0, 100, 0, 0, 0, 10927, 0, 'anachronos the ancient FANDRAL_SAY_4');
INSERT INTO `creature_text` VALUES (15382, 4, 0, 'I want nothing to do with Silithus, the Qiraji and least of all, any damned dragons!', 12, 0, 100, 0, 0, 0, 10928, 0, 'anachronos the ancient FANDRAL_SAY_5');
INSERT INTO `creature_text` VALUES (15382, 5, 0, 'My son\'s soul will find no comfort in this hollow victory, dragon. I will have him back. Though it takes a millennia, I WILL have my son back!  ', 12, 0, 100, 0, 0, 0, 10932, 0, 'anachronos the ancient FANDRAL_SAY_6');
INSERT INTO `creature_text` VALUES (15382, 6, 0, '%s falls to one knee - exhausted.', 16, 0, 100, 0, 0, 0, 10922, 0, 'anachronos the ancient FANDRAL_EMOTE_1');
INSERT INTO `creature_text` VALUES (15382, 7, 0, '%s hurls the Scepter of the Shifting Sands into the barrier, shattering it.', 16, 0, 100, 0, 0, 0, 10929, 0, 'anachronos the ancient FANDRAL_EMOTE_2');
INSERT INTO `creature_text` VALUES (15379, 0, 0, 'Aye, Fandral, remember these words: Let not your grief guide your faith. These thoughts you hold... dark places you go, night elf. Absolution cannot be had through misguided vengeance.', 12, 0, 100, 0, 0, 0, 10914, 0, 'anachronos the ancient CAELESTRASZ_SAY_1');
INSERT INTO `creature_text` VALUES (15379, 1, 0, 'Do not forget the sacrifices made on this day, night elf. We have all suffered immensely at the hands of these beasts.', 12, 0, 100, 0, 0, 0, 10907, 0, 'anachronos the ancient CAELESTRASZ_SAY_2');
INSERT INTO `creature_text` VALUES (15379, 2, 0, 'Alexstrasza grant me the resolve to drive our enemies back!', 14, 0, 100, 0, 0, 0, 10902, 0, 'anachronos the ancient CAELESTRASZ_YELL_1');
