INSERT INTO `creature_text` VALUES (32865, 6, 0, 'Can\'t you at least put up a fight!?', 14, 0, 100, 0, 0, 15739, 34239, 0, 'Thorim SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (32865, 6, 1, 'Pathetic.', 14, 0, 100, 0, 0, 15740, 35768, 0, 'Thorim SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (32865, 5, 0, 'Impertinent whelps, you dare challenge me atop my pedestal? I will crush you myself!', 14, 0, 100, 0, 0, 15738, 33148, 0, 'Thorim SAY_JUMPDOWN');
INSERT INTO `creature_text` VALUES (32865, 4, 0, 'Have you begun to regret your intrusion?', 14, 0, 100, 0, 0, 15737, 0, 0, 'Thorim SAY_SPECIAL_3');
INSERT INTO `creature_text` VALUES (32865, 3, 0, 'Do not hold back! Destroy them!', 14, 0, 100, 0, 0, 15736, 34241, 0, 'Thorim SAY_SPECIAL_2');
INSERT INTO `creature_text` VALUES (32865, 2, 0, 'Behold the power of the storms and despair!', 14, 0, 100, 0, 0, 15735, 0, 0, 'Thorim SAY_SPECIAL_1');
INSERT INTO `creature_text` VALUES (32865, 0, 0, 'Interlopers! You mortals who dare to interfere with my sport will pay... Wait--you...', 14, 0, 100, 0, 0, 15733, 33145, 0, 'Thorim SAY_AGGRO_1');
INSERT INTO `creature_text` VALUES (32865, 1, 0, 'I remember you... In the mountains... But you... what is this? Where am--', 14, 0, 100, 0, 0, 15734, 33270, 0, 'Thorim SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES (16483, 0, 0, 'The last thing I remember is the ship falling and us getting into the pods.  I\'ll go see how I can help.  Thank you!', 12, 35, 100, 0, 0, 0, 13408, 0, 'draenei survivor SAY_HEAL1');
INSERT INTO `creature_text` VALUES (16483, 0, 1, 'Where am I?  Who are you?  Oh no!  What happened to the ship?', 12, 35, 100, 0, 0, 0, 13409, 0, 'draenei survivor SAY_HEAL2');
INSERT INTO `creature_text` VALUES (16483, 0, 2, '$C, you saved me!  I owe you a debt that I can never repay.  I\'ll go see if I can help the others.', 12, 35, 100, 0, 0, 0, 12451, 0, 'draenei survivor SAY_HEAL3');
INSERT INTO `creature_text` VALUES (16483, 0, 3, 'Ugh... what is this place?  Is that all that\'s left of the ship over there?', 12, 35, 100, 0, 0, 0, 13410, 0, 'draenei survivor SAY_HEAL4');
INSERT INTO `creature_text` VALUES (16483, 1, 0, 'Oh, the pain...', 12, 35, 100, 0, 0, 0, 13440, 0, 'draenei survivor SAY_HELP1');
INSERT INTO `creature_text` VALUES (16483, 1, 1, 'Everything hurts. Please, make it stop...', 12, 35, 100, 0, 0, 0, 13441, 0, 'draenei survivor SAY_HELP2');
INSERT INTO `creature_text` VALUES (16483, 1, 2, 'Ughhh... I hurt.  Can you help me?', 12, 35, 100, 0, 0, 0, 13411, 0, 'draenei survivor SAY_HELP3');
INSERT INTO `creature_text` VALUES (16483, 1, 3, 'I don\'t know if I can make it. Please help me...', 12, 35, 100, 0, 0, 0, 13442, 0, 'draenei survivor SAY_HELP4');
INSERT INTO `creature_text` VALUES (17243, 3, 0, 'What\'s the big idea? You nearly blew my cover, idiot! I told you to put the compass and navigation maps somewhere safe - not out in the open for any fool to discover.', 12, 0, 100, 0, 0, 0, 13777, 0, 'geezle SPARK_SAY_2');
INSERT INTO `creature_text` VALUES (17318, 0, 0, 'What\'s the big idea, Spark? Why\'d you call for this meeting?', 12, 0, 100, 0, 0, 0, 13776, 0, 'geezle GEEZLE_SAY_1');
INSERT INTO `creature_text` VALUES (17312, 0, 0, 'Our house is this way, through the thicket.', 12, 7, 100, 0, 0, 0, 13744, 0, 'magwin SAY_START');
INSERT INTO `creature_text` VALUES (17312, 1, 0, 'Help me!', 12, 0, 100, 0, 0, 0, 27791, 0, 'magwin SAY_AGGRO');
INSERT INTO `creature_text` VALUES (17312, 2, 0, 'My poor family. Everything has been destroyed.', 12, 7, 100, 0, 0, 0, 13745, 0, 'magwin SAY_PROGRESS');
INSERT INTO `creature_text` VALUES (17312, 3, 0, 'Father! Father! You\'re alive!', 12, 7, 100, 0, 0, 0, 13746, 0, 'magwin SAY_END1');
INSERT INTO `creature_text` VALUES (17312, 4, 0, 'You can thank $n for getting me back here safely, father.', 12, 7, 100, 0, 0, 0, 13748, 0, 'magwin SAY_END2');
INSERT INTO `creature_text` VALUES (17312, 5, 0, '%s hugs her father.', 16, 7, 100, 0, 0, 0, 13747, 0, 'magwin EMOTE_HUG');
INSERT INTO `creature_text` VALUES (17243, 4, 0, 'The Master has gone to great lengths to secure information about the whereabouts of the Exodar. You could have blown the entire operation, including the cover of our spy on the inside.', 12, 0, 100, 0, 0, 0, 13778, 0, 'geezle SPARK_SAY_3');
INSERT INTO `creature_text` VALUES (17318, 1, 0, 'Relax, Spark! I have it all under control. We\'ll strip mine the Exodar right out from under \'em - making both you and I very, very rich in the process.', 12, 0, 100, 0, 0, 0, 13779, 0, 'geezle GEEZLE_SAY_4');
INSERT INTO `creature_text` VALUES (17243, 5, 0, 'Relax? Do you know what Kael\'thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool.', 12, 0, 100, 0, 0, 0, 13780, 0, 'geezle SPARK_SAY_5');
INSERT INTO `creature_text` VALUES (17243, 6, 0, 'Our Bloodmyst scouts have located our contact. The fool, Velen, will soon leave himself open and defenseless -- long enough for us to strike! Now get out of my sight before I vaporize you...', 12, 0, 100, 0, 0, 0, 13781, 0, 'geezle SPARK_SAY_6');
INSERT INTO `creature_text` VALUES (17318, 2, 0, 'Yes, sir. It won\'t happen again...', 12, 0, 100, 0, 0, 0, 13782, 0, 'geezle GEEZLE_SAY_7');
INSERT INTO `creature_text` VALUES (17375, 0, 0, '[Furbolg] The Stillpine furbolgs will not soon forget your bravery!', 12, 0, 100, 0, 0, 0, 13978, 0, 'stillpine capitive CAPITIVE_SAY_1');
INSERT INTO `creature_text` VALUES (17375, 0, 1, '[Furbolg] Thank you, $n!', 12, 0, 100, 0, 0, 0, 13837, 0, 'stillpine capitive CAPITIVE_SAY_2');
INSERT INTO `creature_text` VALUES (17375, 0, 2, '[Furbolg] Those remaining at Stillpine Hold will welcome you as a hero!', 12, 0, 100, 0, 0, 0, 13979, 0, 'stillpine capitive CAPITIVE_SAY_3');
INSERT INTO `creature_text` VALUES (6729, 0, 0, 'Aku\'mai is dead! At last, I can leave this wretched place.', 14, 0, 100, 0, 0, 0, 2848, 0, 'morridune SAY_MORRIDUNE_1');
INSERT INTO `creature_text` VALUES (6729, 1, 0, 'Speak with me to hear my tale.', 12, 0, 100, 0, 0, 0, 2849, 0, 'morridune SAY_MORRIDUNE_2');
INSERT INTO `creature_text` VALUES (17682, 0, 0, 'Thank you for saving me, $N! My father will be delighted!', 12, 0, 100, 0, 0, 0, 14323, 0, 'Princess Stillpine');
INSERT INTO `creature_text` VALUES (11218, 0, 0, 'Liladris has been waiting for me at Maestra\'s Post, so we should make haste, $n.', 12, 0, 100, 0, 0, 6209, 6540, 0, 'kerlonian SAY_KER_START');
INSERT INTO `creature_text` VALUES (11218, 1, 0, '%s looks very sleepy...', 16, 0, 100, 0, 0, 0, 6811, 0, 'kerlonian EMOTE_KER_SLEEP_1');
INSERT INTO `creature_text` VALUES (11218, 1, 1, '%s suddenly falls asleep.', 16, 0, 100, 0, 0, 0, 6542, 0, 'kerlonian EMOTE_KER_SLEEP_2');
INSERT INTO `creature_text` VALUES (11218, 1, 2, '%s begins to drift off...', 16, 0, 100, 0, 0, 0, 6541, 0, 'kerlonian EMOTE_KER_SLEEP_3');
INSERT INTO `creature_text` VALUES (11218, 2, 0, 'This looks like the perfect place for a nap...', 12, 0, 100, 0, 0, 0, 6813, 0, 'kerlonian SAY_KER_SLEEP_1');
INSERT INTO `creature_text` VALUES (11218, 2, 1, 'Yaaaaawwwwwnnnn...', 12, 0, 100, 0, 0, 0, 6543, 0, 'kerlonian SAY_KER_SLEEP_2');
INSERT INTO `creature_text` VALUES (11218, 2, 2, 'Oh, I am so tired...', 12, 0, 100, 0, 0, 0, 6544, 0, 'kerlonian SAY_KER_SLEEP_3');
INSERT INTO `creature_text` VALUES (11218, 2, 3, 'You don\'t mind if I stop here for a moment, do you?', 12, 0, 100, 0, 0, 0, 6545, 0, 'kerlonian SAY_KER_SLEEP_4');
INSERT INTO `creature_text` VALUES (11218, 3, 0, 'Be on the alert! The Blackwood furbolgs are numerous in this area...', 12, 0, 100, 0, 0, 0, 6867, 0, 'kerlonian SAY_KER_ALERT_1');
INSERT INTO `creature_text` VALUES (11218, 3, 1, 'It\'s quiet... Too quiet...', 12, 0, 100, 0, 0, 0, 6868, 0, 'kerlonian SAY_KER_ALERT_2');
INSERT INTO `creature_text` VALUES (11218, 4, 0, 'Oh, I can see Liladris from here... Tell her I\'m here, won\'t you?', 12, 0, 100, 0, 0, 0, 6643, 0, 'kerlonian SAY_KER_END');
INSERT INTO `creature_text` VALUES (11218, 5, 0, '%s wakes up!', 16, 0, 100, 0, 0, 0, 6612, 0, 'kerlonian EMOTE_KER_AWAKEN');
INSERT INTO `creature_text` VALUES (2917, 0, 0, 'Ok, $n, let\'s go find where I left that mysterious fossil. Follow me!', 12, 7, 100, 0, 0, 0, 925, 0, 'remtravel SAY_REM_START');
INSERT INTO `creature_text` VALUES (2917, 1, 0, 'Now where did I put that mysterious fossil? Ah, maybe up there...', 12, 7, 100, 0, 0, 0, 926, 0, 'remtravel SAY_REM_RAMP1_1');
INSERT INTO `creature_text` VALUES (2917, 2, 0, 'Hrm, nothing up here.', 12, 7, 100, 0, 0, 0, 927, 0, 'remtravel SAY_REM_RAMP1_2');
INSERT INTO `creature_text` VALUES (2917, 3, 0, 'No mysterious fossil here... Ah, but my copy of Green Hills of Stranglethorn.  What a good book!', 12, 7, 100, 0, 0, 0, 928, 0, 'remtravel SAY_REM_BOOK');
INSERT INTO `creature_text` VALUES (2917, 4, 0, 'I bet you I left it in the tent!', 12, 7, 100, 0, 0, 0, 929, 0, 'remtravel SAY_REM_TENT1_1');
INSERT INTO `creature_text` VALUES (2917, 5, 0, 'Oh wait, that\'s Hollee\'s tent... and it\'s empty.', 12, 7, 100, 0, 0, 0, 930, 0, 'remtravel SAY_REM_TENT1_2');
INSERT INTO `creature_text` VALUES (2917, 6, 0, 'Interesting... I hadn\'t noticed this earlier...', 12, 7, 100, 0, 0, 0, 931, 0, 'remtravel SAY_REM_MOSS');
INSERT INTO `creature_text` VALUES (2917, 7, 0, '%s inspects the ancient, mossy stone.', 16, 0, 100, 0, 0, 0, 932, 0, 'remtravel EMOTE_REM_MOSS');
INSERT INTO `creature_text` VALUES (2917, 8, 0, 'Oh wait! I\'m supposed to be looking for that mysterious fossil!', 12, 7, 100, 0, 0, 0, 933, 0, 'remtravel SAY_REM_MOSS_PROGRESS');
INSERT INTO `creature_text` VALUES (2917, 9, 0, 'Nope, didn\'t leave the fossil back here!', 12, 7, 100, 0, 0, 0, 935, 0, 'remtravel SAY_REM_PROGRESS');
INSERT INTO `creature_text` VALUES (2917, 10, 0, 'Ah, I remember now! I gave the mysterious fossil to Hollee! Check with her.', 12, 7, 100, 0, 0, 0, 936, 0, 'remtravel SAY_REM_REMEMBER');
INSERT INTO `creature_text` VALUES (2917, 11, 0, '%s goes back to work, oblivious to everything around him.', 16, 0, 100, 0, 0, 0, 937, 0, 'remtravel EMOTE_REM_END');
INSERT INTO `creature_text` VALUES (2917, 12, 0, 'Something tells me this $n wants the mysterious fossil too. Help!', 12, 7, 100, 0, 0, 0, 941, 0, 'remtravel SAY_REM_AGGRO');
INSERT INTO `creature_text` VALUES (4966, 0, 0, 'Why don\'t we deal with you now, Hendel? Lady Proudmoore will speak to you back in the tower.', 12, 7, 100, 0, 0, 0, 1663, 0, 'hendel SAY_PROGRESS_1_TER');
INSERT INTO `creature_text` VALUES (4966, 1, 0, 'Please... please... Miss Proudmore. I didn\'t mean to...', 12, 7, 100, 0, 0, 0, 1759, 0, 'hendel SAY_PROGRESS_2_HEN');
INSERT INTO `creature_text` VALUES (4966, 2, 0, 'I apologize for taking so long to get here. I wanted Lady Proudmoore to be present also.', 12, 7, 100, 0, 0, 0, 1761, 0, 'hendel SAY_PROGRESS_3_TER');
INSERT INTO `creature_text` VALUES (4966, 3, 0, 'We can only stay a few moments before returning to the tower. If you wish to speak to us more you may find us there.', 12, 7, 100, 0, 0, 0, 1808, 0, 'hendel SAY_PROGRESS_4_TER');
INSERT INTO `creature_text` VALUES (4966, 4, 0, '%s, too injured, gives up the chase.', 16, 0, 100, 0, 0, 0, 8224, 0, 'hendel EMOTE_SURRENDER');
INSERT INTO `creature_text` VALUES (23864, 0, 0, 'This land was mine long before your wretched kind set foot here.', 14, 0, 100, 0, 0, 0, 22370, 0, 'zelfrax SAY_ZELFRAX1');
INSERT INTO `creature_text` VALUES (23864, 1, 0, 'All who venture here belong to me, including you!', 14, 0, 100, 0, 0, 0, 22371, 0, 'zelfrax SAY_ZELFRAX2');
INSERT INTO `creature_text` VALUES (7807, 0, 0, 'Emergency power activated!  Initializing ambulatory motor!  CLUCK!', 12, 0, 100, 0, 0, 0, 3794, 0, 'oox SAY_OOX_START');
INSERT INTO `creature_text` VALUES (7807, 1, 0, 'Physical threat detected!  Evasive action!  CLUCK!', 12, 0, 100, 0, 0, 0, 3795, 0, 'oox SAY_OOX_AGGRO1');
INSERT INTO `creature_text` VALUES (7807, 1, 1, 'Threat analyzed!  Activating combat plan beta!  CLUCK!', 12, 0, 100, 0, 0, 0, 3796, 0, 'oox SAY_OOX_AGGRO2');
INSERT INTO `creature_text` VALUES (7807, 2, 0, 'CLUCK!  Sensors detect spatial anomaly -- danger imminent!  CLUCK!', 12, 0, 100, 0, 0, 0, 3797, 0, 'oox SAY_OOX_AMBUSH');
INSERT INTO `creature_text` VALUES (7807, 3, 0, 'Flight systems online!  CLUCK!  Engaging rockets for transport to Booty Bay!', 12, 0, 100, 0, 0, 0, 3816, 0, 'oox SAY_OOX_END');
INSERT INTO `creature_text` VALUES (23616, 0, 0, '%s howls in delight at the sight of his lunch!', 16, 0, 100, 393, 0, 9036, 22068, 0, 'kyle EMOTE_SEE_LUNCH');
INSERT INTO `creature_text` VALUES (23616, 1, 0, '%s eats his lunch.', 16, 0, 100, 35, 0, 0, 22069, 0, 'kyle EMOTE_EAT_LUNCH');
INSERT INTO `creature_text` VALUES (23622, 2, 0, '%s thanks you with a special dance.', 16, 0, 100, 0, 0, 0, 22070, 0, 'kyle EMOTE_DANCE');
INSERT INTO `creature_text` VALUES (4508, 0, 0, 'Woo hoo! Finally getting out of here. It\'s going to be rough though. Keep your eyes peeled for trouble.', 12, 0, 100, 0, 0, 0, 1482, 0, 'willix SAY_READY');
INSERT INTO `creature_text` VALUES (4508, 1, 0, 'Up there is where Charlga Razorflank resides. Blasted old crone.', 12, 0, 100, 0, 0, 0, 1483, 0, 'willix SAY_POINT');
INSERT INTO `creature_text` VALUES (4508, 2, 0, 'Help! Get this $n off of me!', 12, 0, 100, 0, 0, 0, 1547, 0, 'willix SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (4508, 3, 0, 'There\'s blueleaf tuber in this trench! It\'s like gold waiting to be mined I tell you!', 12, 0, 100, 0, 0, 0, 1484, 0, 'willix SAY_BLUELEAF');
INSERT INTO `creature_text` VALUES (4508, 4, 0, 'There could be danger around every corner here.', 12, 0, 100, 0, 0, 0, 1485, 0, 'willix SAY_DANGER');
INSERT INTO `creature_text` VALUES (4508, 5, 0, 'I don\'t see how these foul animals live in this place... sheesh it smells!', 12, 0, 100, 0, 0, 0, 1486, 0, 'willix SAY_BAD');
INSERT INTO `creature_text` VALUES (4508, 6, 0, 'I think I see a way for us to get out of this big twisted mess of a bramble.', 12, 0, 100, 0, 0, 0, 1487, 0, 'willix SAY_THINK');
INSERT INTO `creature_text` VALUES (4508, 7, 0, 'Glad to be out of that wretched trench. Not much nicer up here though!', 12, 0, 100, 0, 0, 0, 1488, 0, 'willix SAY_SOON');
INSERT INTO `creature_text` VALUES (4508, 8, 0, 'Finally! I\'ll be glad to get out of this place.', 12, 0, 100, 0, 0, 0, 1490, 0, 'willix SAY_FINALY');
INSERT INTO `creature_text` VALUES (4508, 9, 0, 'I think I\'ll rest a moment and catch my breath before heading back to Ratchet. Thanks for all the help!', 12, 0, 100, 0, 0, 0, 1493, 0, 'willix SAY_WIN');
INSERT INTO `creature_text` VALUES (4508, 10, 0, 'Well, I\'m off to Ratchet now! Best of luck to you!', 12, 0, 100, 0, 0, 0, 1494, 0, 'willix SAY_END');
INSERT INTO `creature_text` VALUES (6669, 0, 0, '*Threshwackonator First Mate unit prepared to follow*', 16, 0, 100, 0, 0, 0, 3012, 0, 'threshwackonator EMOTE_START');
INSERT INTO `creature_text` VALUES (6669, 1, 0, 'YARRR! Swabie, what have ye done?! He\'s gone mad! Baton down the hatches! Hoist the mast! ARRRR! Every man for hi\'self!', 12, 0, 100, 0, 0, 0, 2704, 0, 'threshwackonator SAY_AT_CLOSE');
INSERT INTO `creature_text` VALUES (9453, 0, 0, 'That kodo sure is a beauty.  Wait a minute, where are my bifocals?  Perhaps you should inspect the beast for me.', 12, 0, 100, 0, 0, 0, 7302, 0, 'kodo round SAY_SMEED_HOME_3');
INSERT INTO `creature_text` VALUES (7784, 0, 0, 'Emergency power activated!  Initializing ambulatory motor!  CLUCK!', 12, 0, 100, 0, 0, 0, 3794, 0, 'oox SAY_OOX_START');
INSERT INTO `creature_text` VALUES (7784, 1, 0, 'Physical threat detected!  Evasive action!  CLUCK!', 12, 0, 100, 0, 0, 0, 3795, 0, 'oox SAY_OOX_AGGRO1');
INSERT INTO `creature_text` VALUES (7784, 1, 1, 'Threat analyzed!  Activating combat plan beta!  CLUCK!', 12, 0, 100, 0, 0, 0, 3796, 0, 'oox SAY_OOX_AGGRO2');
INSERT INTO `creature_text` VALUES (7784, 2, 0, 'CLUCK!  Sensors detect spatial anomaly -- danger imminent!  CLUCK!', 12, 0, 100, 0, 0, 0, 3797, 0, 'oox SAY_OOX_AMBUSH');
INSERT INTO `creature_text` VALUES (7805, 0, 0, 'No one challenges the Wastewander nomads -- not even robotic chickens!  ATTACK!', 12, 0, 100, 0, 0, 0, 3812, 0, 'oox SAY_OOX17_AMBUSH_REPLY');
INSERT INTO `creature_text` VALUES (7784, 3, 0, 'Flight systems online!  CLUCK!  Engaging rockets for transport to Booty Bay!', 12, 0, 100, 0, 0, 0, 3816, 0, 'oox SAY_OOX_END');
INSERT INTO `creature_text` VALUES (5955, 0, 0, 'I\'m thirsty.', 12, 0, 100, 0, 0, 0, 2222, 0, 'tooga SAY_TOOG_THIRST');
INSERT INTO `creature_text` VALUES (5955, 0, 1, 'Torta must be so worried.', 12, 0, 100, 0, 0, 0, 2228, 0, 'tooga SAY_TOOG_WORRIED');
INSERT INTO `creature_text` VALUES (5955, 1, 0, 'Torta, my love! I have returned at long last.', 12, 0, 100, 0, 0, 0, 2137, 0, 'tooga SAY_TOOG_POST_1');
INSERT INTO `creature_text` VALUES (6015, 0, 0, 'You have any idea how long I\'ve been waiting here? And where\'s dinner? All that time gone and nothing to show for it?', 12, 0, 100, 0, 0, 0, 2138, 0, 'tooga SAY_TORT_POST_2');
INSERT INTO `creature_text` VALUES (5955, 2, 0, 'My dearest Torta, I have been gone for so long. Finally we are reunited. At long last our love can blossom again.', 12, 0, 100, 0, 0, 0, 2139, 0, 'tooga SAY_TOOG_POST_3');
INSERT INTO `creature_text` VALUES (6015, 1, 0, 'Enough with the rambling. I am starving! Now, get your dusty shell into that ocean and bring momma some grub.', 12, 0, 100, 0, 0, 0, 2140, 0, 'tooga SAY_TORT_POST_4');
INSERT INTO `creature_text` VALUES (5955, 3, 0, 'Yes, Torta. Whatever your heart desires...', 12, 0, 100, 0, 0, 0, 2141, 0, 'tooga SAY_TOOG_POST_5');
INSERT INTO `creature_text` VALUES (6015, 2, 0, 'And try not to get lost this time....', 12, 0, 100, 0, 0, 0, 2145, 0, 'tooga SAY_TORT_POST_6');
INSERT INTO `creature_text` VALUES (15727, 0, 0, '%s is weakened!', 16, 0, 100, 0, 0, 0, 11476, 0, 'cthun EMOTE_WEAKENED');
INSERT INTO `creature_text` VALUES (15509, 0, 0, '%s goes into a killing frenzy!', 16, 0, 100, 0, 0, 0, 24143, 0, 'huhuran EMOTE_FRENZY_KILL');
INSERT INTO `creature_text` VALUES (15509, 1, 0, '%s goes into a berserker rage!', 16, 0, 100, 0, 0, 0, 34057, 0, 'huhuran EMOTE_BERSERK');
INSERT INTO `creature_text` VALUES (15516, 0, 0, 'You will be judged for defiling these sacred grounds!  The laws of the Ancients will not be challenged!  Trespassers will be annihilated!\n', 14, 0, 100, 0, 0, 8646, 11442, 0, 'sartura SAY_AGGRO');
INSERT INTO `creature_text` VALUES (15516, 1, 0, 'I sentence you to death!', 14, 0, 100, 0, 0, 8647, 11443, 0, 'sartura SAY_SLAY');
INSERT INTO `creature_text` VALUES (15516, 2, 0, 'I serve to the last!', 14, 0, 100, 0, 0, 8648, 11444, 0, 'sartura SAY_DEATH');
INSERT INTO `creature_text` VALUES (15263, 0, 0, 'Are you so eager to die? I will be happy to accommodate you...', 14, 0, 100, 0, 0, 8615, 11445, 0, 'skeram SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (15263, 0, 1, 'Cower mortals! The age of darkness is at hand.', 14, 0, 100, 0, 0, 8616, 0, 0, 'skeram SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (15263, 0, 2, 'Tremble! The end is upon you.', 14, 0, 100, 0, 0, 8621, 0, 0, 'skeram SAY_AGGRO3');
INSERT INTO `creature_text` VALUES (15263, 1, 0, 'Let your death serve as an example!', 14, 0, 100, 0, 0, 8617, 11446, 0, 'skeram SAY_SLAY1');
INSERT INTO `creature_text` VALUES (15263, 1, 1, 'Spineless wretches! You will drown in rivers of blood!', 14, 0, 100, 0, 0, 8619, 0, 0, 'skeram SAY_SLAY2');
INSERT INTO `creature_text` VALUES (15263, 1, 2, 'The screams of the dying will fill the air. A symphony of terror is about to begin!', 14, 0, 100, 0, 0, 8620, 0, 0, 'skeram SAY_SLAY3');
INSERT INTO `creature_text` VALUES (15263, 2, 0, 'Prepare for the return of the ancient ones!', 14, 0, 100, 0, 0, 8618, 0, 0, 'skeram SAY_SPLIT');
INSERT INTO `creature_text` VALUES (15263, 3, 0, 'You only delay the inevitable!', 14, 0, 100, 0, 0, 8622, 11447, 0, 'skeram SAY_DEATH');
INSERT INTO `creature_text` VALUES (10638, 0, 0, 'Here they come! Defend yourselves!', 12, 0, 100, 0, 0, 0, 6149, 0, 'kanati SAY_KAN_START');
INSERT INTO `creature_text` VALUES (10646, 0, 0, 'Be on guard... Arnak has some strange power over the Grimtotem... they will not be happy to see me escape.', 12, 0, 100, 0, 0, 0, 5926, 0, 'lakota SAY_LAKO_START');
INSERT INTO `creature_text` VALUES (10646, 1, 0, 'Look out, the Grimtotem are upon us!', 12, 0, 100, 0, 0, 0, 5927, 0, 'lakota SAY_LAKO_LOOK_OUT');
INSERT INTO `creature_text` VALUES (10646, 2, 0, 'Here they come.', 12, 0, 100, 0, 0, 0, 5928, 0, 'lakota SAY_LAKO_HERE_COME');
INSERT INTO `creature_text` VALUES (10646, 3, 0, 'More Grimtotems are coming this way!', 12, 0, 100, 0, 0, 0, 5929, 0, 'lakota SAY_LAKO_MORE');
INSERT INTO `creature_text` VALUES (10646, 4, 0, 'Finally, free at last... I must be going now, thanks for helping me escape. I can get back to Freewind Post by myself.', 12, 0, 100, 0, 0, 0, 5930, 0, 'lakota SAY_LAKO_END');
INSERT INTO `creature_text` VALUES (10427, 0, 0, 'Lets go, $n. I am ready to reach Whitereach Post.', 12, 1, 100, 0, 0, 0, 5648, 0, 'paoka SAY_START');
INSERT INTO `creature_text` VALUES (10427, 1, 0, 'Now this looks familiar. If we keep heading east, I think we can... Ahh! Wyvern on the attack!', 12, 1, 100, 0, 0, 0, 5654, 0, 'paoka SAY_WYVERN');
INSERT INTO `creature_text` VALUES (10427, 2, 0, 'Thanks a bunch... I can find my way back to Whitereach Post from here. Be sure to talk with Motega Firemane; perhaps you can keep him from sending me home.', 12, 1, 100, 0, 0, 0, 5683, 0, 'paoka SAY_COMPLETE');
INSERT INTO `creature_text` VALUES (3678, 0, 0, 'At last! Naralex can be awakened! Come aid me, brave adventurers!', 14, 0, 100, 0, 0, 0, 2101, 0, 'disciple SAY_AT_LAST');
INSERT INTO `creature_text` VALUES (3678, 1, 0, 'I must make the necessary preparations before the awakening ritual can begin. You must protect me!', 12, 0, 100, 0, 0, 0, 1255, 0, 'disciple SAY_MAKE_PREPARATIONS');
INSERT INTO `creature_text` VALUES (3678, 2, 0, 'These caverns were once a temple of promise for regrowth in the Barrens. Now, they are the halls of nightmares.', 12, 0, 100, 0, 0, 0, 1256, 0, 'disciple SAY_TEMPLE_OF_PROMISE');
INSERT INTO `creature_text` VALUES (3678, 3, 0, 'Come. We must continue. There is much to be done before we can pull Naralex from his nightmare.', 12, 0, 100, 0, 0, 0, 1257, 0, 'disciple SAY_MUST_CONTINUE');
INSERT INTO `creature_text` VALUES (3678, 4, 0, 'Within this circle of fire I must cast the spell to banish the spirits of the slain Fanglords.', 12, 0, 100, 0, 0, 0, 1258, 0, 'disciple SAY_BANISH_THE_SPIRITS');
INSERT INTO `creature_text` VALUES (3678, 5, 0, 'The caverns have been purified. To Naralex\'s chamber we go!', 12, 0, 100, 0, 0, 0, 1259, 0, 'disciple SAY_CAVERNS_PURIFIED');
INSERT INTO `creature_text` VALUES (3678, 6, 0, 'Beyond this corridor, Naralex lies in fitful sleep. Let us go awaken him before it is too late.', 12, 0, 100, 0, 0, 0, 1263, 0, 'disciple SAY_BEYOND_THIS_CORRIDOR');
INSERT INTO `creature_text` VALUES (3678, 7, 0, 'Protect me brave souls as I delve into the Emerald Dream to rescue Naralex and put an end to this corruption!', 12, 0, 100, 0, 0, 0, 1264, 0, 'disciple SAY_EMERALD_DREAM');
INSERT INTO `creature_text` VALUES (3678, 8, 0, '%s begins to perform the awakening ritual on Naralex.', 16, 0, 100, 0, 0, 0, 1265, 0, 'disciple EMOTE_AWAKENING_RITUAL');
INSERT INTO `creature_text` VALUES (3679, 0, 0, '%s tosses fitfully in troubled sleep.', 16, 0, 100, 0, 0, 0, 1268, 0, 'naralex EMOTE_TROUBLED_SLEEP');
INSERT INTO `creature_text` VALUES (3679, 1, 0, '%s writhes in agony. The Disciple seems to be breaking through.', 16, 0, 100, 0, 0, 0, 1269, 0, 'naralex EMOTE_WRITHE_IN_AGONY');
INSERT INTO `creature_text` VALUES (3679, 2, 0, '%s dreams up a horrendous vision. Something stirs beneath the murky waters.', 16, 0, 100, 0, 0, 0, 1270, 0, 'naralex EMOTE_HORRENDOUS_VISION');
INSERT INTO `creature_text` VALUES (3678, 9, 0, 'This $n is a minion from Naralex\'s nightmare no doubt!', 12, 0, 100, 0, 0, 0, 1276, 0, 'disciple SAY_MUTANUS_THE_DEVOURER');
INSERT INTO `creature_text` VALUES (3679, 3, 0, 'I AM AWAKE, AT LAST!', 14, 0, 100, 0, 0, 0, 1271, 0, 'naralex SAY_I_AM_AWAKE');
INSERT INTO `creature_text` VALUES (3678, 10, 0, 'At last! Naralex awakes from the nightmare.', 12, 0, 100, 0, 0, 0, 1267, 0, 'disciple SAY_NARALEX_AWAKES');
INSERT INTO `creature_text` VALUES (3679, 4, 0, 'Ah, to be pulled from the dreaded nightmare! I thank you, my loyal Disciple, along with your brave companions.', 12, 0, 100, 0, 0, 0, 1272, 0, 'naralex SAY_THANK_YOU');
INSERT INTO `creature_text` VALUES (3679, 5, 0, 'We must go and gather with the other Disciples. There is much work to be done before I can make another attempt to restore the Barrens. Farewell, brave souls!', 12, 0, 100, 0, 0, 0, 2103, 0, 'naralex SAY_FAREWELL');
INSERT INTO `creature_text` VALUES (3678, 11, 0, 'Attacked! Help get this $n off of me!', 12, 0, 100, 0, 0, 0, 1273, 0, 'disciple SAY_ATTACKED');
INSERT INTO `creature_text` VALUES (7607, 0, 0, 'Oh no!  Here they come!', 12, 0, 100, 0, 0, 0, 3744, 0, 'weegli blastfuse SAY_WEEGLI_OHNO');
INSERT INTO `creature_text` VALUES (7607, 1, 0, 'Ok, here I go!', 12, 0, 100, 0, 0, 0, 3785, 0, 'weegli blastfuse SAY_WEEGLI_OK_I_GO');
INSERT INTO `creature_text` VALUES (29120, 0, 0, 'Eternal agony awaits you!', 14, 0, 100, 0, 0, 14054, 32308, 0, 'anub arak SAY_AGGRO');
INSERT INTO `creature_text` VALUES (29120, 1, 0, 'Soon the master\'s voice will call to you.', 14, 0, 100, 0, 0, 14057, 32311, 0, 'anub arak SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (29120, 1, 1, 'You have made your choice.', 14, 0, 100, 0, 0, 14056, 32310, 0, 'anub arak SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (29120, 1, 2, 'You shall experience my torment first hand!', 14, 0, 100, 0, 0, 14055, 32309, 0, 'anub arak SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (29120, 2, 0, 'Ahhh... RAAAAAGH! Never thought... I would be free of him....', 14, 0, 100, 0, 0, 14069, 32317, 0, 'anub arak SAY_DEATH');
INSERT INTO `creature_text` VALUES (29120, 3, 0, 'Your armor is useless against my locusts!', 14, 0, 100, 0, 0, 14060, 32314, 0, 'anub arak SAY_LOCUST_1');
INSERT INTO `creature_text` VALUES (29120, 3, 1, 'Uunak-hissss tik-k-k-k-k!', 14, 0, 100, 0, 0, 14067, 32315, 0, 'anub arak SAY_LOCUST_2');
INSERT INTO `creature_text` VALUES (29120, 3, 2, 'A pestilence upon you!', 14, 0, 100, 0, 0, 14068, 32316, 0, 'anub arak SAY_LOCUST_3');
INSERT INTO `creature_text` VALUES (29120, 4, 0, 'Auum na-l ak-k-k-k, isshhh.', 14, 0, 100, 0, 0, 14058, 32312, 0, 'anub arak SAY_SUBMERGE_1');
INSERT INTO `creature_text` VALUES (29120, 4, 1, 'Come forth, my brethren! Feast on their flesh!', 14, 0, 100, 0, 0, 14059, 32313, 0, 'anub arak SAY_SUBMERGE_2');
INSERT INTO `creature_text` VALUES (29120, 5, 0, 'I was king of this empire once, long ago. In life I stood as champion. In death I returned as conqueror. Now I protect the kingdom once more. Ironic, yes? ', 14, 0, 100, 0, 0, 14053, 0, 0, 'anub arak SAY_INTRO');
INSERT INTO `creature_text` VALUES (28684, 0, 0, 'This kingdom belongs to the Scourge. Only the dead may enter!', 14, 0, 100, 0, 0, 14075, 32271, 0, 'krik thir SAY_AGGRO');
INSERT INTO `creature_text` VALUES (28684, 1, 0, 'You were foolish to come.', 14, 0, 100, 0, 0, 14077, 32273, 0, 'krik thir SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (28684, 1, 1, 'As Anub\'arak commands!', 14, 0, 100, 0, 0, 14078, 32274, 0, 'krik thir SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (28684, 2, 0, 'I should be grateful... but I long ago lost the capacity....', 14, 0, 100, 0, 0, 14087, 32283, 0, 'krik thir SAY_DEATH');
INSERT INTO `creature_text` VALUES (28684, 3, 0, 'They hunger....', 14, 0, 100, 0, 0, 14085, 32281, 0, 'krik thir SAY_SWARM_1');
INSERT INTO `creature_text` VALUES (28684, 3, 1, 'Dinner time, my pets!', 14, 0, 100, 0, 0, 14086, 32282, 0, 'krik thir SAY_SWARM_2');
INSERT INTO `creature_text` VALUES (28684, 4, 0, 'Keep an eye on that tunnel. We must not let anyone through!', 14, 0, 100, 0, 0, 14082, 32278, 0, 'krik thir SAY_PREFIGHT_1');
INSERT INTO `creature_text` VALUES (28684, 4, 1, 'I hear footsteps. Be on your guard.', 14, 0, 100, 0, 0, 14083, 32279, 0, 'krik thir SAY_PREFIGHT_2');
INSERT INTO `creature_text` VALUES (28684, 4, 2, 'I sense the living. Be ready.', 14, 0, 100, 0, 0, 14084, 32280, 0, 'krik thir SAY_PREFIGHT_3');
INSERT INTO `creature_text` VALUES (28684, 5, 0, 'We are besieged! Strike out and bring back their corpses!', 14, 0, 100, 0, 0, 14079, 32275, 0, 'krik thir SAY_SEND_GROUP_1');
INSERT INTO `creature_text` VALUES (28684, 5, 1, 'We must hold the gate. Attack! Tear them limb from limb!', 14, 0, 100, 0, 0, 14080, 32276, 0, 'krik thir SAY_SEND_GROUP_2');
INSERT INTO `creature_text` VALUES (28684, 5, 2, 'The gate must be protected at all costs. Rip them to shreds!', 14, 0, 100, 0, 0, 14081, 32277, 0, 'krik thir SAY_SEND_GROUP_3');
INSERT INTO `creature_text` VALUES (28189, 0, 0, 'My liege, the infiltration and control of the Alliance power structure by our cultists is well underway.', 12, 0, 100, 0, 0, 14211, 25357, 0, 'thassarian SAY_TALBOT_1');
INSERT INTO `creature_text` VALUES (28189, 1, 0, 'The power you\'ve bestowed upon me has granted me great mental influence over human minds.  I bear these offerings as proof of my progress.', 12, 0, 100, 0, 0, 14212, 25358, 0, 'thassarian SAY_TALBOT_2');
INSERT INTO `creature_text` VALUES (28189, 2, 0, 'Allow me to take care of the intruders, lord.  I will feed their entrails to the maggots.', 12, 0, 100, 0, 0, 14213, 25361, 0, 'thassarian SAY_TALBOT_3');
INSERT INTO `creature_text` VALUES (28189, 3, 0, 'Yes, my lord!', 12, 0, 100, 0, 0, 14214, 25365, 0, 'thassarian SAY_TALBOT_4');
INSERT INTO `creature_text` VALUES (26203, 0, 0, 'Your progress in this region has been impressive, blood prince.  I am pleased.', 12, 0, 100, 0, 0, 14756, 25362, 0, 'thassarian SAY_LICH_1');
INSERT INTO `creature_text` VALUES (26203, 1, 0, 'Now this is a surprise, Thassarian.  I hadn\'t heard from Mograine or the other death knights for months.  You\'ve come to rejoin the Scourge, I take it?', 12, 0, 100, 0, 0, 14757, 25363, 0, 'thassarian SAY_LICH_2');
INSERT INTO `creature_text` VALUES (26203, 2, 0, 'Do not fail me, San\'layn.  Return to Icecrown with this fool\'s head or do not bother to return.', 12, 0, 100, 0, 0, 14758, 25364, 0, 'thassarian SAY_LICH_3');
INSERT INTO `creature_text` VALUES (26170, 0, 0, 'Leryssa!', 14, 0, 100, 0, 0, 14664, 25359, 0, 'thassarian SAY_THASSARIAN_1');
INSERT INTO `creature_text` VALUES (26170, 1, 0, 'What have you done to my sister, you motherless elf scum!?', 14, 0, 100, 0, 0, 14665, 25360, 0, 'thassarian SAY_THASSARIAN_2');
INSERT INTO `creature_text` VALUES (26170, 2, 0, 'I would sooner slit my own throat.  You will pay for what you did to your own men, Arthas... for what you did to me!  I swear it.', 12, 0, 100, 0, 0, 14666, 25366, 0, 'thassarian SAY_THASSARIAN_3');
INSERT INTO `creature_text` VALUES (26170, 3, 0, 'Leryssa... you... you\'re all right!', 12, 0, 100, 0, 0, 14667, 25844, 0, 'thassarian SAY_THASSARIAN_4');
INSERT INTO `creature_text` VALUES (26170, 4, 0, 'I cannot return home with you just yet, Leryssa.  I am not quite done with the Scourge.', 12, 0, 100, 0, 0, 14668, 25845, 0, 'thassarian SAY_THASSARIAN_5');
INSERT INTO `creature_text` VALUES (26170, 5, 0, 'You might be right, sister.  My obligations to my land and King have been fulfilled.  But there is something that I still owe to myself.', 12, 0, 100, 0, 0, 14669, 25846, 0, 'thassarian SAY_THASSARIAN_6');
INSERT INTO `creature_text` VALUES (26170, 6, 0, 'Do not worry, Leryssa.  I will come back to you when I am done.  Nothing in the world will stop me from coming home to the only family that I have left.', 12, 0, 100, 0, 0, 14670, 25850, 0, 'thassarian SAY_THASSARIAN_7');
INSERT INTO `creature_text` VALUES (25250, 0, 0, 'What... what happened to me?', 12, 7, 100, 0, 0, 14196, 25841, 0, 'thassarian SAY_ARLOS_1');
INSERT INTO `creature_text` VALUES (25250, 1, 0, 'Ugh!  My head won\'t stop spinning...', 12, 7, 100, 0, 0, 14197, 25847, 0, 'thassarian SAY_ARLOS_2');
INSERT INTO `creature_text` VALUES (25251, 0, 0, 'Thassarian!  You\'re alive!', 14, 0, 100, 0, 0, 14489, 25842, 0, 'thassarian SAY_LERYSSA_1');
INSERT INTO `creature_text` VALUES (25251, 1, 0, 'I thought... I thought you were... dead.', 12, 0, 100, 0, 0, 14490, 25843, 0, 'thassarian SAY_LERYSSA_2');
INSERT INTO `creature_text` VALUES (25251, 2, 0, 'Don\'t leave me again!  You want to fight for your country, but they don\'t even want you!  They sent you here to die!', 12, 0, 100, 0, 0, 14491, 25848, 0, 'thassarian SAY_LERYSSA_3');
INSERT INTO `creature_text` VALUES (25251, 3, 0, 'I know that look in your eye... I\'m not going to be able to talk you out of this.  If you die on me again...', 12, 0, 100, 0, 0, 14492, 25849, 0, 'thassarian SAY_LERYSSA_4');
INSERT INTO `creature_text` VALUES (25504, 0, 0, 'My father\'s aura is quite strong,he cannot be far. Could you be a doll and fight off the monsters wandering throught the mist?', 12, 0, 100, 0, 0, 0, 24747, 0, 'mootoo the younger SAY_1');
INSERT INTO `creature_text` VALUES (25504, 1, 0, 'Watch out for the monsters!Which way should we go first? Let\'s try this way...', 12, 0, 100, 0, 0, 0, 27243, 0, 'mootoo the younger SAY_2');
INSERT INTO `creature_text` VALUES (25504, 2, 0, 'What could this be?', 12, 0, 100, 0, 0, 0, 27245, 0, 'mootoo the younger SAY_3');
INSERT INTO `creature_text` VALUES (25504, 3, 0, 'There\'s no sign of it ending! Where could my father be?', 12, 0, 100, 0, 0, 0, 24748, 0, 'mootoo the younger SAY_4');
INSERT INTO `creature_text` VALUES (25504, 4, 0, 'Father! You\'re safe!', 12, 0, 100, 0, 0, 0, 24749, 0, 'mootoo the younger SAY_5');
INSERT INTO `creature_text` VALUES (25478, 0, 0, 'Pathetic fool! A servant of Malygos would sooner die than aid an enemy....', 12, 0, 100, 0, 0, 0, 24735, 0, 'imprisoned beryl sorcerer SAY_IMPRISIONED_BERYL_1');
INSERT INTO `creature_text` VALUES (25478, 1, 0, 'Aargh! Do your worst, $c! I\'ll tell you NOTHING!', 12, 0, 100, 0, 0, 0, 24736, 0, 'imprisoned beryl sorcerer SAY_IMPRISIONED_BERYL_2');
INSERT INTO `creature_text` VALUES (25478, 2, 0, 'Aahhhh! Release me! I am of no use to you. I swear it!', 12, 0, 100, 0, 0, 0, 24737, 0, 'imprisoned beryl sorcerer SAY_IMPRISIONED_BERYL_3');
INSERT INTO `creature_text` VALUES (25478, 3, 0, 'Stop! I beg you, please stop. Please....', 12, 0, 100, 0, 0, 0, 24738, 0, 'imprisoned beryl sorcerer SAY_IMPRISIONED_BERYL_4');
INSERT INTO `creature_text` VALUES (25478, 4, 0, 'Alright! I am beaten. Your precious archmage is held in a prison, elevated and sealed. Even if you manage to reach her, Salrand herself holds the key. Your mission is folly!', 12, 0, 100, 0, 0, 0, 24739, 0, 'imprisoned beryl sorcerer SAY_IMPRISIONED_BERYL_5');
INSERT INTO `creature_text` VALUES (25478, 5, 0, 'I\'ve given you the information, $r! You\'re wasting your time....', 12, 0, 100, 0, 0, 0, 24756, 0, 'imprisoned beryl sorcerer SAY_IMPRISIONED_BERYL_6');
INSERT INTO `creature_text` VALUES (25478, 6, 0, 'Noooo! This torture is inhumane! You have what you want... why don\'t you just kill me?', 12, 0, 100, 0, 0, 0, 24755, 0, 'imprisoned beryl sorcerer SAY_IMPRISIONED_BERYL_7');
INSERT INTO `creature_text` VALUES (25589, 0, 0, 'I AM NOT AN APPETIZER!', 12, 7, 100, 0, 0, 0, 24780, 0, 'bonker togglevolt');
INSERT INTO `creature_text` VALUES (25589, 1, 0, 'I\'ll make you a deal: if you get me out of here alive, you\'ll get a reward larger than you can imagine!', 12, 7, 100, 0, 0, 0, 24768, 0, 'bonker togglevolt');
INSERT INTO `creature_text` VALUES (25827, 0, 0, 'Well...', 12, 0, 100, 0, 0, 0, 25243, 0, 'hidden cultist SAY_HIDDEN_CULTIST_1');
INSERT INTO `creature_text` VALUES (25827, 1, 0, 'Finally!  This charade is over... Arthas give me strength!', 12, 0, 100, 0, 0, 0, 25245, 0, 'hidden cultist SAY_HIDDEN_CULTIST_2');
INSERT INTO `creature_text` VALUES (25827, 2, 0, 'You don\'t know who you\'re messing with, $c!  Death beckons!', 12, 0, 100, 0, 0, 0, 25246, 0, 'hidden cultist SAY_HIDDEN_CULTIST_3');
INSERT INTO `creature_text` VALUES (25827, 3, 0, 'I suppose this is it, then?  I won\'t go down quietly!', 12, 0, 100, 0, 0, 0, 25244, 0, 'hidden cultist SAY_HIDDEN_CULTIST_4');
INSERT INTO `creature_text` VALUES (25248, 0, 0, 'Well...', 12, 0, 100, 0, 0, 0, 25243, 0, 'hidden cultist SAY_HIDDEN_CULTIST_1');
