INSERT INTO `creature_text` VALUES (25828, 2, 0, 'You don\'t know who you\'re messing with, $c!  Death beckons!', 12, 0, 100, 0, 0, 0, 25246, 0, 'hidden cultist SAY_HIDDEN_CULTIST_3');
