INSERT INTO `creature_text` VALUES (4832, 2, 0, 'Just... Dust...', 14, 0, 100, 0, 0, 5803, 0, 0, 'combat Say');
INSERT INTO `creature_text` VALUES (4832, 1, 0, 'Who dares disturb my meditation!', 14, 0, 100, 0, 0, 5802, 0, 0, 'combat Say');
INSERT INTO `creature_text` VALUES (4832, 0, 0, 'Sleep...', 1, 0, 100, 0, 0, 5804, 0, 0, 'combat Say');
INSERT INTO `creature_text` VALUES (4979, 0, 0, 'Hey, thanks.', 12, 0, 100, 0, 0, 0, 22111, 0, 'theramore guard SAY_QUEST1');
INSERT INTO `creature_text` VALUES (4979, 1, 0, '...receive 50 percent off deserter undergarments? What is this garbage?', 12, 0, 100, 0, 0, 0, 22555, 0, 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES (4979, 1, 1, '...to create a fund for the purchase of hair gel? I like my hair the way it is, thanks!', 12, 0, 100, 0, 0, 0, 22562, 0, 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES (4979, 1, 2, '...the deserters seek a Theramore where citizens will be free to juggle at all hours of the day? What is this nonsense?', 12, 0, 100, 0, 0, 0, 22559, 0, 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES (4979, 1, 3, '...to establish the chicken as the official symbol of Theramore?! These guys are nuts!', 12, 0, 100, 0, 0, 0, 22561, 0, 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES (4979, 1, 4, '...as a deserter, you\'ll enjoy activities like tethered swimming and dog grooming? How ridiculous!', 12, 0, 100, 0, 0, 0, 22556, 0, 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES (4979, 2, 0, 'This... this is a joke, right?', 12, 0, 100, 0, 0, 0, 22113, 0, 'theramore guard SAY_QUEST3');
INSERT INTO `creature_text` VALUES (4979, 2, 1, 'I\'d never join anything like this. Better keep this, though. It\'ll come in handy in the privy...', 12, 0, 100, 0, 0, 0, 22116, 0, 'theramore guard SAY_QUEST3');
INSERT INTO `creature_text` VALUES (4979, 2, 2, 'What a bunch of lunatics! You actually believe this stuff?', 12, 0, 100, 0, 0, 0, 22115, 0, 'theramore guard SAY_QUEST3');
INSERT INTO `creature_text` VALUES (28860, 0, 0, 'It is my charge to watch over these eggs. I will see you burn before any harm comes to them!', 14, 0, 100, 0, 0, 14093, 31400, 0, 'sartharion SAY_SARTHARION_AGGRO');
INSERT INTO `creature_text` VALUES (28860, 1, 0, 'This pathetic siege ends NOW!', 14, 0, 100, 0, 0, 14103, 31368, 0, 'sartharion SAY_SARTHARION_BERSERK');
INSERT INTO `creature_text` VALUES (28860, 2, 0, 'Burn, you miserable wretches!', 14, 0, 100, 0, 0, 14098, 31907, 0, 'sartharion SAY_SARTHARION_BREATH');
INSERT INTO `creature_text` VALUES (28860, 3, 0, 'Shadron! Come to me! All is at risk!', 14, 0, 100, 0, 0, 14105, 31318, 0, 'sartharion SAY_SARTHARION_CALL_SHADRON');
INSERT INTO `creature_text` VALUES (28860, 4, 0, 'Tenebron! The eggs are yours to protect as well!', 14, 0, 100, 0, 0, 14106, 31319, 0, 'sartharion SAY_SARTHARION_CALL_TENEBRON');
INSERT INTO `creature_text` VALUES (28860, 5, 0, 'Vesperon, the clutch is in danger! Assist me!', 14, 0, 100, 0, 0, 14104, 31320, 0, 'sartharion SAY_SARTHARION_CALL_VESPERON');
INSERT INTO `creature_text` VALUES (28860, 6, 0, 'Such is the price... of failure....', 14, 0, 100, 0, 0, 14107, 31341, 0, 'sartharion SAY_SARTHARION_DEATH');
INSERT INTO `creature_text` VALUES (28860, 7, 0, 'Such flammable little insects....', 14, 0, 100, 0, 0, 14099, 31363, 0, 'sartharion SAY_SARTHARION_SPECIAL_1');
INSERT INTO `creature_text` VALUES (28860, 7, 1, 'Your charred bones will litter the floor!', 14, 0, 100, 0, 0, 14100, 31904, 0, 'sartharion SAY_SARTHARION_SPECIAL_2');
INSERT INTO `creature_text` VALUES (28860, 7, 2, 'How much heat can you take?', 14, 0, 100, 0, 0, 14101, 31905, 0, 'sartharion SAY_SARTHARION_SPECIAL_3');
INSERT INTO `creature_text` VALUES (28860, 7, 3, 'All will be reduced to ash!', 14, 0, 100, 0, 0, 14102, 31906, 0, 'sartharion SAY_SARTHARION_SPECIAL_4');
INSERT INTO `creature_text` VALUES (28860, 8, 0, 'You will make a fine meal for the hatchlings.', 14, 0, 100, 0, 0, 14094, 31908, 0, 'sartharion SAY_SARTHARION_SLAY_1');
INSERT INTO `creature_text` VALUES (28860, 8, 1, 'You are at a grave disadvantage.', 14, 0, 100, 0, 0, 14096, 31909, 0, 'sartharion SAY_SARTHARION_SLAY_2');
