INSERT INTO `creature_text` VALUES (38160, 0, 0, 'Excellent work, champions! We shall set up our base camp in these chambers. My magi will get the Scourge transport device working shortly. Step inside it when you are ready for your next mission. I will meet you on the other side.', 14, 0, 100, 0, 0, 16625, 37369, 0, 'jaina SAY_JAINA_OUTRO');
INSERT INTO `creature_text` VALUES (38161, 0, 0, 'Excellent work, champions! We shall set up our base camp in these chambers. My magi will get the Scourge transport device working shortly. Step inside when you are ready for your next mission. I will meet you on the other side.', 14, 0, 100, 0, 0, 17044, 37387, 0, 'sylvanas SAY_SYLVANAS_OUTRO');
INSERT INTO `creature_text` VALUES (25208, 3, 0, 'Thank you for rescuing me, $r. Please tell the king that I am back.', 12, 0, 100, 0, 0, 0, 24313, 0, 'Lurgglbr');
INSERT INTO `creature_text` VALUES (25208, 2, 0, 'This is far enough. I can make it on my own from here.', 12, 0, 100, 0, 0, 0, 24312, 0, 'Lurgglbr');
INSERT INTO `creature_text` VALUES (25208, 0, 0, 'Together we will fight our way out of here. Are you ready?', 12, 0, 100, 0, 0, 0, 24310, 0, 'Lurgglbr');
INSERT INTO `creature_text` VALUES (25208, 1, 0, 'You can\'t hold me against my will! You\'ve all been brainwashed!', 12, 0, 100, 0, 0, 0, 24316, 0, 'Lurgglbr');
INSERT INTO `creature_text` VALUES (37597, 0, 0, 'Thank the Light for seeing you here safely! We have much work to do if we are to defeat the Lich King and put an end to the Scourge.', 12, 0, 100, 0, 0, 16617, 37370, 0, 'jaina SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (37597, 1, 0, 'Our allies within the Argent Crusade and the Knights of the Ebon Blade have broken through the front gate of Icecrown and are attempting to establish a foothold within the citadel. ', 12, 0, 100, 0, 0, 16618, 37371, 0, 'jaina SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (37597, 2, 0, 'Their success hinges upon what we discover within these cursed halls. Although our mission is fraught with peril, we must persevere!', 12, 0, 100, 0, 0, 16619, 37372, 0, 'jaina SAY_INTRO_3');
INSERT INTO `creature_text` VALUES (37597, 3, 0, 'With the attention of the Lich King drawn towards the front gate, we will be working our way through the side in search of information that will enable us to defeat the Scourge once and for all.', 12, 0, 100, 0, 0, 16620, 37373, 0, 'jaina SAY_INTRO_4');
INSERT INTO `creature_text` VALUES (37597, 4, 0, 'King Varian\'s SI:7 agents have gathered information about a private sanctum of the Lich King\'s deep within a place called the Halls of Reflection. ', 12, 0, 100, 0, 0, 16621, 37374, 0, 'jaina SAY_INTRO_5');
INSERT INTO `creature_text` VALUES (37597, 5, 0, 'We will carve a path through this wretched place and find a way to enter the Halls of Reflection. I sense powerful magic hidden away within those halls. Magic that could be the key to destroying the Scourge!', 12, 0, 100, 0, 0, 16622, 37375, 0, 'jaina SAY_INTRO_6');
INSERT INTO `creature_text` VALUES (37597, 6, 0, 'Your first mission is to destroy the machines of death within this malevolent engine of souls and clear a path for our soldiers. ', 12, 0, 100, 0, 0, 16623, 37376, 0, 'jaina SAY_INTRO_7');
INSERT INTO `creature_text` VALUES (37597, 7, 0, 'Make haste, champions! I will prepare the troops to fall in behind you.', 12, 0, 100, 0, 0, 16624, 37377, 0, 'jaina SAY_INTRO_8');
INSERT INTO `creature_text` VALUES (37596, 0, 0, 'The Argent Crusade and the Knights of the Ebon Blade have assaulted the gates of the Icecrown Citadel and are preparing for a massive attack upon the Scourge. Our mission is a bit more subtle, but equally as important.', 12, 0, 100, 0, 0, 17038, 37380, 0, 'sylvanas SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (37596, 1, 0, 'With the attention of the Lich King turned towards the front gate, we\'ll be working our way through the side in search of information that will enable us to defeat him - once and for all.', 12, 0, 100, 0, 0, 17039, 37381, 0, 'sylvanas SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (37596, 2, 0, 'Our scouts have reported that the Lich King has a private chamber, outside of the Frozen Throne, deep within a place called the Halls of Reflection. That is our target, champions. ', 12, 0, 100, 0, 0, 17040, 37382, 0, 'sylvanas SAY_INTRO_3');
INSERT INTO `creature_text` VALUES (37596, 3, 0, 'We will cut a swath of destruction through this cursed place and find a way to enter the Halls of Reflection. If there is anything of value to be found here, it will be found in the halls.', 12, 0, 100, 0, 0, 17041, 37383, 0, 'sylvanas SAY_INTRO_4');
INSERT INTO `creature_text` VALUES (37596, 4, 0, 'Your first mission is to destroy the machines of death within this malevolent engine of souls and clear a path for our soldiers. ', 12, 0, 100, 0, 0, 17042, 37376, 0, 'sylvanas SAY_INTRO_5');
INSERT INTO `creature_text` VALUES (37596, 5, 0, 'The Dark Lady watches over you. Make haste!', 12, 0, 100, 0, 0, 17043, 37384, 0, 'sylvanas SAY_INTRO_6');
INSERT INTO `creature_text` VALUES (38112, 0, 0, 'Men, women, and children... None were spared the master\'s wrath. Your death will be no different.', 14, 0, 100, 0, 0, 16710, 37931, 0, 'falric SAY_AGGRO');
INSERT INTO `creature_text` VALUES (38112, 1, 0, 'Sniveling maggot!', 14, 0, 100, 0, 0, 16711, 37941, 0, 'falric SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (38112, 1, 1, 'The children of Stratholme fought with more ferocity!', 14, 0, 100, 0, 0, 16712, 37942, 0, 'falric SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (38112, 2, 0, 'Marwyn, finish them...', 14, 0, 100, 0, 0, 16713, 37943, 0, 'falric SAY_DEATH');
INSERT INTO `creature_text` VALUES (38112, 3, 0, 'Despair... so delicious...', 14, 0, 100, 0, 0, 16715, 37945, 0, 'falric SAY_IMPENDING_DESPAIR');
INSERT INTO `creature_text` VALUES (38112, 4, 0, 'Fear... so exhilarating...', 14, 0, 100, 0, 0, 16716, 37946, 0, 'falric SAY_DEFILING_HORROR');
INSERT INTO `creature_text` VALUES (37221, 3, 0, 'Stand back! Touch that blade and your soul will be scarred for all eternity! I must attempt to commune with the spirits locked away within Frostmourne. Give me space. Back up, please. ', 14, 0, 100, 0, 0, 16634, 37594, 0, 'Jaina SAY_JAINA_INTRO_4');
INSERT INTO `creature_text` VALUES (37221, 2, 0, 'Frostmourne: the blade that destroyed our kingdom...', 14, 0, 100, 0, 0, 16633, 37593, 0, 'Jaina SAY_JAINA_INTRO_3');
INSERT INTO `creature_text` VALUES (37221, 1, 0, 'What is that! Up ahead! Could it be? Heroes, at my side!', 14, 0, 100, 0, 0, 16632, 37592, 0, 'Jaina SAY_JAINA_INTRO_2');
INSERT INTO `creature_text` VALUES (37221, 0, 0, 'The chill of this place... I can feel my blood freezing.', 14, 0, 100, 0, 0, 16631, 37591, 0, 'Jaina SAY_JAINA_INTRO_1');
INSERT INTO `creature_text` VALUES (37221, 4, 0, 'Uther! Dear Uther! I... I\'m so sorry.', 12, 0, 100, 0, 0, 16635, 37595, 0, 'Jaina SAY_JAINA_INTRO_5');
INSERT INTO `creature_text` VALUES (37221, 5, 0, 'Arthas is here? Maybe I...', 12, 0, 100, 0, 0, 16636, 37596, 0, 'Jaina SAY_JAINA_INTRO_6');
INSERT INTO `creature_text` VALUES (37221, 6, 0, 'But Uther, if there\'s any hope of reaching Arthas. I... I must try.', 12, 0, 100, 0, 0, 16637, 37597, 0, 'Jaina SAY_JAINA_INTRO_7');
INSERT INTO `creature_text` VALUES (37221, 7, 0, 'Tell me how, Uther? How do I destroy my prince? My...', 12, 0, 100, 0, 0, 16638, 37598, 0, 'Jaina SAY_JAINA_INTRO_8');
INSERT INTO `creature_text` VALUES (37221, 8, 0, 'You\'re right, Uther. Forgive me. I... I don\'t know what got a hold of me. We will deliver this information to the King and the knights that battle the Scourge within Icecrown Citadel.', 12, 0, 100, 0, 0, 16639, 37599, 0, 'Jaina SAY_JAINA_INTRO_9');
INSERT INTO `creature_text` VALUES (37221, 9, 0, 'Who could bear such a burden?', 12, 0, 100, 0, 0, 16640, 37600, 0, 'Jaina SAY_JAINA_INTRO_10');
INSERT INTO `creature_text` VALUES (37221, 10, 0, 'Then maybe there is still hope...', 12, 0, 100, 0, 0, 16641, 37601, 0, 'Jaina SAY_JAINA_INTRO_11');
INSERT INTO `creature_text` VALUES (37221, 11, 0, 'You won\'t deny me this, Arthas! I must know... I must find out... ', 14, 0, 100, 0, 0, 16642, 37602, 0, 'Jaina SAY_JAINA_INTRO_END');
INSERT INTO `creature_text` VALUES (37226, 0, 0, 'SILENCE, PALADIN!', 14, 0, 100, 0, 0, 17225, 37613, 0, 'Lich King SAY_LK_INTRO_1');
INSERT INTO `creature_text` VALUES (37226, 1, 0, 'So you wish to commune with the dead? You shall have your wish.', 14, 0, 100, 0, 0, 17226, 37614, 0, 'Lich King SAY_LK_INTRO_2');
INSERT INTO `creature_text` VALUES (37226, 2, 0, 'Falric. Marwyn. Bring their corpses to my chamber when you are through.', 14, 0, 100, 0, 0, 17227, 37615, 0, 'Lich King SAY_LK_INTRO_3');
INSERT INTO `creature_text` VALUES (38113, 0, 0, 'Death is all that you will find here!', 14, 0, 100, 0, 0, 16734, 37948, 0, 'marwyn SAY_AGGRO');
INSERT INTO `creature_text` VALUES (38112, 5, 0, 'As you wish, my lord.', 14, 0, 100, 0, 0, 16717, 37953, 0, 'Falric SAY_FALRIC_INTRO_1');
INSERT INTO `creature_text` VALUES (38112, 6, 0, 'Soldiers of Lordaeron, rise to meet your master\'s call!', 14, 0, 100, 0, 0, 16714, 37944, 0, 'Falric SAY_FALRIC_INTRO_2');
INSERT INTO `creature_text` VALUES (37223, 0, 0, 'I... I don\'t believe it! Frostmourne stands before us - unguarded - just as the gnome claimed. Come, heroes!', 14, 0, 100, 0, 0, 17049, 37568, 0, 'Sylvanas SAY_SYLVANAS_INTRO_1');
INSERT INTO `creature_text` VALUES (37223, 1, 0, 'Standing this close to the blade that ended my life... The pain... It is renewed.', 14, 0, 100, 0, 0, 17050, 37569, 0, 'Sylvanas SAY_SYLVANAS_INTRO_2');
INSERT INTO `creature_text` VALUES (37223, 2, 0, 'I dare not touch it. Stand back! Stand back as I attempt to commune with the blade! Perhaps our salvation lies within...', 14, 0, 100, 0, 0, 17051, 37570, 0, 'Sylvanas SAY_SYLVANAS_INTRO_3');
INSERT INTO `creature_text` VALUES (37223, 3, 0, 'Uther... Uther the Lightbringer. How...', 12, 0, 100, 0, 0, 17052, 37571, 0, 'Sylvanas SAY_SYLVANAS_INTRO_4');
INSERT INTO `creature_text` VALUES (37223, 4, 0, 'The Lich King is here? Then my destiny shall be fulfilled on this day! ', 12, 0, 100, 0, 0, 17053, 37572, 0, 'Sylvanas SAY_SYLVANAS_INTRO_5');
INSERT INTO `creature_text` VALUES (37223, 5, 0, 'There must be a way...', 12, 0, 100, 0, 0, 17054, 37573, 0, 'Sylvanas SAY_SYLVANAS_INTRO_6');
INSERT INTO `creature_text` VALUES (37223, 6, 0, 'Who could bear such a burden?', 12, 0, 100, 0, 0, 17055, 37600, 0, 'Sylvanas SAY_SYLVANAS_INTRO_7');
INSERT INTO `creature_text` VALUES (37223, 7, 0, 'The Frozen Throne...', 12, 0, 100, 0, 0, 17056, 37575, 0, 'Sylvanas SAY_SYLVANAS_INTRO_8');
INSERT INTO `creature_text` VALUES (37223, 8, 0, 'You will not escape me that easily, Arthas! I will have my vengeance! ', 14, 0, 100, 0, 0, 17057, 37576, 0, 'Sylvanas SAY_SYLVANAS_INTRO_END');
INSERT INTO `creature_text` VALUES (37225, 0, 0, 'Jaina! Could it truly be you?', 14, 0, 100, 0, 0, 16666, 37603, 0, 'Uther SAY_UTHER_INTRO_A2_1');
INSERT INTO `creature_text` VALUES (37225, 1, 0, 'Jaina, you haven\'t much time. The Lich King sees what the sword sees. He will be here shortly. ', 12, 0, 100, 0, 0, 16667, 37604, 0, 'Uther SAY_UTHER_INTRO_A2_2');
INSERT INTO `creature_text` VALUES (37225, 2, 0, 'No, girl. Arthas is not here. Arthas is merely a presence within the Lich King\'s mind. A dwindling presence...', 12, 0, 100, 0, 0, 16668, 37605, 0, 'Uther SAY_UTHER_INTRO_A2_3');
INSERT INTO `creature_text` VALUES (37225, 3, 0, 'Jaina, listen to me. You must destroy the Lich King. You cannot reason with him. He will kill you and your allies and raise you all as powerful soldiers of the Scourge.', 12, 0, 100, 0, 0, 16669, 37606, 0, 'Uther SAY_UTHER_INTRO_A2_4');
INSERT INTO `creature_text` VALUES (37225, 4, 0, 'Snap out of it, girl. You must destroy the Lich King at the place where he merged with Ner\'zhul - atop the spire, at the Frozen Throne. It is the only way.', 12, 0, 100, 0, 0, 16670, 37607, 0, 'Uther SAY_UTHER_INTRO_A2_5');
INSERT INTO `creature_text` VALUES (37225, 5, 0, 'There is... something else that you should know about the Lich King. Control over the Scourge must never be lost. Even if you were to strike down the Lich King, another would have to take his place. For without the control of its master, the Scourge would run rampant across the world - destroying all living things.', 12, 0, 100, 0, 0, 16671, 37608, 0, 'Uther SAY_UTHER_INTRO_A2_6');
INSERT INTO `creature_text` VALUES (37225, 6, 0, 'A grand sacrifice by a noble soul...', 12, 0, 100, 0, 0, 16672, 37609, 0, 'Uther SAY_UTHER_INTRO_A2_7');
INSERT INTO `creature_text` VALUES (37225, 7, 0, 'I do not know, Jaina. I suspect that the piece of Arthas that might be left inside the Lich King is all that holds the Scourge from annihilating Azeroth.', 12, 0, 100, 0, 0, 16673, 37610, 0, 'Uther SAY_UTHER_INTRO_A2_8');
INSERT INTO `creature_text` VALUES (37225, 8, 0, 'No, Jaina! ARRRRRRGHHHH... He... He is coming. You... You must...', 12, 0, 100, 0, 0, 16674, 37611, 0, 'Uther SAY_UTHER_INTRO_A2_9');
INSERT INTO `creature_text` VALUES (37225, 9, 0, 'Careful, girl. I\'ve heard talk of that cursed blade saving us before. Look around you and see what has been born of Frostmourne.', 12, 0, 100, 0, 0, 16659, 37583, 0, 'Uther SAY_UTHER_INTRO_H2_1');
INSERT INTO `creature_text` VALUES (37225, 10, 0, 'You haven\'t much time. The Lich King sees what the sword sees. He will be here shortly. ', 12, 0, 100, 0, 0, 16660, 37584, 0, 'Uther SAY_UTHER_INTRO_H2_2');
INSERT INTO `creature_text` VALUES (37225, 11, 0, 'You cannot defeat the Lich King. Not here. You would be a fool to try. He will kill those that follow you and raise them as powerful soldiers of the Scourge. But for you, Sylvanas, his reward for you would be worse than the last.', 12, 0, 100, 0, 0, 16661, 37585, 0, 'Uther SAY_UTHER_INTRO_H2_3');
INSERT INTO `creature_text` VALUES (37225, 12, 0, 'Perhaps, but know this: there must always be a Lich King. Even if you were to strike down Arthas, another would have to take his place, for without the control of the Lich King, the Scourge would wash over this world like locusts, destroying all that they touched.', 12, 0, 100, 0, 0, 16662, 37586, 0, 'Uther SAY_UTHER_INTRO_H2_4');
INSERT INTO `creature_text` VALUES (37225, 13, 0, 'I do not know, Banshee Queen. I suspect that the piece of Arthas that might be left inside the Lich King is all that holds the Scourge from annihilating Azeroth.', 12, 0, 100, 0, 0, 16663, 37587, 0, 'Uther SAY_UTHER_INTRO_H2_5');
INSERT INTO `creature_text` VALUES (37225, 14, 0, 'Alas, the only way to defeat the Lich King is to destroy him at the place where he was created.', 12, 0, 100, 0, 0, 16664, 37588, 0, 'Uther SAY_UTHER_INTRO_H2_6');
INSERT INTO `creature_text` VALUES (37225, 15, 0, 'Aye. ARRRRRRGHHHH... He... He is coming. You... You must...', 12, 0, 100, 0, 0, 16665, 37589, 0, 'Uther SAY_UTHER_INTRO_H2_7');
INSERT INTO `creature_text` VALUES (36477, 0, 0, 'Our work must not be interrupted! Ick, take care of them!', 14, 0, 100, 0, 0, 16926, 37222, 0, 'Krick SAY_AGGRO');
INSERT INTO `creature_text` VALUES (36477, 1, 0, 'We could probably use these parts.', 14, 0, 100, 0, 0, 16927, 37223, 0, 'Krick SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (36477, 1, 1, 'Arms and legs are in short supply! Thanks for your contribution!', 14, 0, 100, 0, 0, 16928, 37224, 0, 'Krick SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (36477, 2, 0, 'Enough moving around! Hold still while I blow them all up!', 14, 0, 100, 0, 0, 16929, 36538, 0, 'Krick SAY_BARRAGE_1');
INSERT INTO `creature_text` VALUES (36477, 3, 0, '%s begins rapidly conjuring explosive mines!', 41, 0, 100, 0, 0, 0, 36550, 0, 'Krick SAY_BARRAGE_2');
INSERT INTO `creature_text` VALUES (36477, 4, 0, 'Quickly! Poison them all while they\'re still close!', 14, 0, 100, 0, 0, 16930, 36537, 0, 'Krick SAY_POISON_NOVA');
INSERT INTO `creature_text` VALUES (36477, 5, 0, 'No, that one! That one! Get that one!', 14, 0, 100, 0, 0, 16931, 36536, 0, 'Krick SAY_CHASE_1');
INSERT INTO `creature_text` VALUES (36477, 5, 1, 'I\'ve changed my mind, go get that one instead!', 14, 0, 100, 0, 0, 16932, 37225, 0, 'Krick SAY_CHASE_2');
INSERT INTO `creature_text` VALUES (36477, 5, 2, 'What are you attacking him for? The dangerous one is over there!', 14, 0, 100, 0, 0, 16933, 37226, 0, 'Krick SAY_CHASE_3');
INSERT INTO `creature_text` VALUES (36477, 6, 0, 'Wait! Stop! Don\'t kill me, please! I\'ll tell you everything!', 14, 0, 100, 0, 0, 16934, 36841, 0, 'Krick SAY_KRICK_OUTRO_1');
INSERT INTO `creature_text` VALUES (36477, 7, 0, 'What you seek is in the master\'s lair, but you must destroy Tyrannus to gain entry. Now, within the Halls of Reflection you will find Frostmourne. It... It holds the truth.', 14, 0, 100, 0, 0, 16935, 36842, 0, 'Krick SAY_KRICK_OUTRO_3');
INSERT INTO `creature_text` VALUES (36477, 8, 0, 'I swear it is true! Please, don\'t kill me!!', 14, 0, 100, 0, 0, 16936, 36843, 0, 'Krick SAY_KRICK_OUTRO_5');
INSERT INTO `creature_text` VALUES (36477, 9, 0, 'Urg... no!!', 14, 0, 100, 0, 0, 16937, 36844, 0, 'Krick SAY_KRICK_OUTRO_8');
INSERT INTO `creature_text` VALUES (36476, 0, 0, '%s begins to unleash a toxic poison cloud!', 41, 0, 100, 0, 0, 0, 36531, 0, 'Ick SAY_ICK_POISON_NOVA');
INSERT INTO `creature_text` VALUES (36476, 1, 0, '%s is chasing you. Run!', 41, 0, 100, 0, 0, 0, 36529, 0, 'Ick SAY_ICK_CHASE_1');
INSERT INTO `creature_text` VALUES (36658, 0, 0, 'Another shall take his place. You waste your time.', 14, 0, 100, 0, 0, 16752, 36765, 0, 'Tyrannus SAY_TYRANNUS_DEATH');
INSERT INTO `creature_text` VALUES (36794, 1, 0, 'Worthless gnat! Death is all that awaits you!', 14, 0, 100, 0, 0, 16753, 36852, 0, 'Tyrannus SAY_TYRANNUS_OUTRO_7');
INSERT INTO `creature_text` VALUES (36794, 2, 0, 'Do not think that I shall permit you entry into my master\'s sanctum so easily. Pursue me if you dare.', 14, 0, 100, 0, 0, 16754, 36853, 0, 'Tyrannus SAY_TYRANNUS_OUTRO_9');
INSERT INTO `creature_text` VALUES (36658, 3, 0, 'Your pursuit shall be in vain, intruders, for the Lich King has placed an army of undead at my command! Behold!', 14, 0, 100, 0, 0, 16755, 36708, 0, 'Tyrannus SAY_AMBUSH_1');
INSERT INTO `creature_text` VALUES (36658, 4, 0, 'Persistent whelps! You will not reach the entrance of my lord\'s lair! Soldiers, destroy them!', 14, 0, 100, 0, 0, 16756, 36709, 0, 'Tyrannus SAY_AMBUSH_2');
INSERT INTO `creature_text` VALUES (36658, 5, 0, 'Rimefang, trap them within the tunnel! BURY THEM ALIVE!', 14, 0, 100, 0, 0, 16757, 36714, 0, 'Tyrannus SAY_GAUNTLET_START');
INSERT INTO `creature_text` VALUES (36658, 6, 0, 'Alas, brave, brave adventurers, your meddling has reached its end. Do you hear the clatter of bone and steel coming up the tunnel behind you? That is the sound of your impending demise.', 14, 0, 100, 0, 0, 16758, 37233, 0, 'Tyrannus SAY_TYRANNUS_INTRO_1');
INSERT INTO `creature_text` VALUES (36658, 7, 0, 'Ha, such an amusing gesture from the rabble. When I have finished with you, my master\'s blade will feast upon your souls. Die.', 14, 0, 100, 0, 0, 16759, 37234, 0, 'Tyrannus SAY_TYRANNUS_INTRO_3');
INSERT INTO `creature_text` VALUES (36658, 8, 0, 'I shall not fail the Lich King. Come and meet your end!', 14, 0, 100, 0, 0, 16760, 38718, 0, 'Tyrannus SAY_AGGRO');
INSERT INTO `creature_text` VALUES (36658, 9, 0, 'Such a shameful display. You are better off dead.', 14, 0, 100, 0, 0, 16761, 38715, 0, 'Tyrannus SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (36658, 9, 1, 'Perhaps you should have stayed... in the mountains!', 14, 0, 100, 0, 0, 16762, 38716, 0, 'Tyrannus SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (36658, 10, 0, 'Impossible.... Rimefang.... warn....', 14, 0, 100, 0, 0, 16763, 38714, 0, 'Tyrannus SAY_DEATH');
INSERT INTO `creature_text` VALUES (36658, 11, 0, 'Rimefang, destroy this fool!', 14, 0, 100, 0, 0, 16764, 36648, 0, 'Tyrannus SAY_MARK_RIMEFANG_1');
INSERT INTO `creature_text` VALUES (36658, 12, 0, 'The frostwyrm Rimefang gazes at $n and readies an icy attack!', 41, 0, 100, 0, 0, 0, 36649, 0, 'Tyrannus SAY_MARK_RIMEFANG_2');
INSERT INTO `creature_text` VALUES (36658, 13, 0, 'Power... overwhelming!', 14, 0, 100, 0, 0, 16765, 38717, 0, 'Tyrannus SAY_DARK_MIGHT_1');
INSERT INTO `creature_text` VALUES (36658, 14, 0, '%s roars and swells with dark might!', 41, 0, 100, 0, 0, 0, 36628, 0, 'Tyrannus SAY_DARK_MIGHT_2');
INSERT INTO `creature_text` VALUES (37592, 0, 0, 'Heroes! We will hold off the undead as long as we can, even to our dying breath. Deal with the Scourgelord!', 14, 0, 100, 0, 0, 17150, 37232, 0, 'Gorkun SAY_GORKUN_INTRO_2');
INSERT INTO `creature_text` VALUES (37592, 1, 0, 'Brave champions, we owe you our lives, our freedom.... Though it be a tiny gesture in the face of this enormous debt, I pledge that from this day forth, all will know of your deeds, and the blazing path of light you cut through the shadow of this dark citadel. ', 14, 0, 100, 0, 0, 0, 37320, 0, 'Gorkun SAY_GORKUN_OUTRO_1');
INSERT INTO `creature_text` VALUES (37592, 2, 0, 'This day will stand as a testament not only to your valor, but to the fact that no foe, not even the Lich King himself, can stand when Alliance and Horde set aside their differences and---', 14, 0, 100, 0, 0, 0, 37321, 0, 'Gorkun SAY_GORKUN_OUTRO_2');
INSERT INTO `creature_text` VALUES (36993, 0, 0, 'I\'m not so naive as to believe your appeal for clemency, but I will listen.', 14, 0, 100, 0, 0, 16611, 36854, 0, 'Jaina SAY_JAINA_OUTRO_2');
INSERT INTO `creature_text` VALUES (36993, 1, 0, 'Frostmourne lies unguarded? Impossible!', 14, 0, 100, 0, 0, 16612, 36856, 0, 'Jaina SAY_JAINA_OUTRO_4');
INSERT INTO `creature_text` VALUES (36993, 2, 0, 'What a cruel end. Come, heroes. We must see if the gnome\'s story is true. If we can separate Arthas from Frostmourne, we might have a chance at stopping him.', 14, 0, 100, 0, 0, 16613, 36855, 0, 'Jaina SAY_JAINA_OUTRO_10');
INSERT INTO `creature_text` VALUES (36993, 3, 0, 'Heroes, to me!', 14, 0, 100, 0, 0, 16614, 37479, 0, 'Jaina SAY_JAYNA_OUTRO_3');
INSERT INTO `creature_text` VALUES (36993, 4, 0, 'The Frost Queen is gone. We must keep moving - our objective is near.', 12, 0, 100, 0, 0, 16615, 37478, 0, 'Jaina SAY_JAYNA_OUTRO_4');
INSERT INTO `creature_text` VALUES (36993, 5, 0, 'I... I could not save them... Damn you, Arthas! DAMN YOU!', 12, 0, 100, 0, 0, 16616, 37480, 0, 'Jaina SAY_JAYNA_OUTRO_5');
INSERT INTO `creature_text` VALUES (36990, 0, 0, 'Why should the Banshee Queen spare your miserable life?', 14, 0, 100, 0, 0, 17033, 36845, 0, 'Sylvanas SAY_SYLVANAS_OUTRO_2');
INSERT INTO `creature_text` VALUES (36990, 1, 0, 'Frostmourne? The Lich King is never without his blade. If you are lying to me...', 14, 0, 100, 0, 0, 17034, 36846, 0, 'Sylvanas SAY_SYLVANAS_OUTRO_4');
INSERT INTO `creature_text` VALUES (36990, 2, 0, 'A fitting end for a traitor. Come, we must free the slaves and see what is within the Lich King\'s chamber for ourselves.', 14, 0, 100, 0, 0, 17035, 36847, 0, 'Sylvanas SAY_SYLVANAS_OUTRO_10');
INSERT INTO `creature_text` VALUES (36990, 3, 0, 'Take cover behind me! Quickly!', 14, 0, 100, 0, 0, 17037, 37540, 0, 'Sylvanas SAY_SYLVANAS_OUTRO_3');
INSERT INTO `creature_text` VALUES (36990, 4, 0, 'I thought he\'d never shut up. At last, Sindragosa silenced that long-winded fool. To the Halls of Reflection, champions! Our objective is near... I can sense it.', 12, 0, 100, 0, 0, 17036, 37541, 0, 'Sylvanas SAY_SYLVANAS_OUTRO_4');
INSERT INTO `creature_text` VALUES (26588, 0, 0, 'Um... I think one of those wolves is back...', 12, 0, 100, 0, 0, 0, 25803, 0, 'emily SAY_WORGHAGGRO1');
INSERT INTO `creature_text` VALUES (26588, 1, 0, 'He\'s going for Mr. Floppy!', 12, 0, 100, 0, 0, 0, 25804, 0, 'emily SAY_WORGHAGGRO2');
INSERT INTO `creature_text` VALUES (26588, 2, 0, 'Oh, no! Look, it\'s another wolf, and it\'s a biiiiiiig one!', 12, 0, 100, 0, 0, 0, 25806, 0, 'emily SAY_WORGRAGGRO3');
INSERT INTO `creature_text` VALUES (26588, 3, 0, 'He\'s gonna eat Mr. Floppy! You gotta help Mr. Floppy! You just gotta!', 12, 0, 100, 0, 0, 0, 26892, 0, 'emily SAY_WORGRAGGRO4');
INSERT INTO `creature_text` VALUES (26588, 4, 0, 'There\'s a big meanie attacking Mr. Floppy! Help!', 12, 0, 100, 0, 0, 0, 26893, 0, 'emily SAY_RANDOMAGGRO');
INSERT INTO `creature_text` VALUES (26588, 5, 0, 'Let\'s get out of here before more wolves find us!', 12, 0, 100, 0, 0, 0, 25805, 0, 'emily SAY_VICTORY1');
INSERT INTO `creature_text` VALUES (26588, 6, 0, 'Don\'t go toward the light, Mr. Floppy!', 12, 0, 100, 0, 0, 0, 25809, 0, 'emily SAY_VICTORY2');
INSERT INTO `creature_text` VALUES (26588, 7, 0, 'Mr. Floppy, you\'re ok! Thank you so much for saving Mr. Floppy!', 12, 0, 100, 0, 0, 0, 25810, 0, 'emily SAY_VICTORY3');
INSERT INTO `creature_text` VALUES (26588, 8, 0, 'I think I see the camp! We\'re almost home, Mr. Floppy! Let\'s go!', 12, 0, 100, 0, 0, 0, 26894, 0, 'emily SAY_VICTORY4');
INSERT INTO `creature_text` VALUES (26588, 9, 0, 'Mr. Floppy revives', 15, 0, 100, 0, 0, 0, 0, 0, 'emily TEXT_EMOTE_WP1');
INSERT INTO `creature_text` VALUES (26588, 10, 0, 'The Ravenous Worg chomps down on Mr. Floppy', 15, 0, 100, 0, 0, 0, 0, 0, 'emily TEXT_EMOTE_AGGRO');
INSERT INTO `creature_text` VALUES (26588, 11, 0, 'Are you ready, Mr. Floppy? Stay close to me and watch out for those wolves!', 12, 0, 100, 0, 0, 0, 25802, 0, 'emily SAY_QUEST_ACCEPT');
INSERT INTO `creature_text` VALUES (26588, 12, 0, 'Thank you for helping me to get back to the camp. Go tell Walter that I\'m safe now!', 12, 0, 100, 0, 0, 0, 26891, 0, 'emily SAY_QUEST_COMPLETE');
INSERT INTO `creature_text` VALUES (27570, 0, 0, 'We\'re all gonna die!', 12, 0, 100, 0, 0, 0, 26848, 0, 'venture co straggler SAY1');
INSERT INTO `creature_text` VALUES (27570, 0, 1, 'Gotta get out of here!', 12, 0, 100, 0, 0, 0, 26847, 0, 'venture co straggler SAY2');
INSERT INTO `creature_text` VALUES (27570, 0, 2, 'No way I\'m stickin\' around!', 12, 0, 100, 0, 0, 0, 26851, 0, 'venture co straggler SAY3');
INSERT INTO `creature_text` VALUES (27570, 0, 3, 'Forget this! I\'m going home!', 12, 0, 100, 0, 0, 0, 26850, 0, 'venture co straggler SAY4');
INSERT INTO `creature_text` VALUES (27570, 0, 4, 'I didn\'t sign up for this!', 12, 0, 100, 0, 0, 0, 26849, 0, 'venture co straggler SAY5');
INSERT INTO `creature_text` VALUES (27463, 0, 0, 'Ahh.... better.', 12, 0, 100, 0, 0, 0, 26657, 0, 'wounded skirmisher RANDOM_SAY_1');
INSERT INTO `creature_text` VALUES (27463, 0, 1, 'Whoa - I nearly died there. Thank you, $r!', 12, 0, 100, 0, 0, 0, 26659, 0, 'wounded skirmisher RANDOM_SAY_2');
INSERT INTO `creature_text` VALUES (27463, 0, 2, 'Thank you, $c.', 12, 0, 100, 0, 0, 0, 26656, 0, 'wounded skirmisher RANDOM_SAY_3');
INSERT INTO `creature_text` VALUES (29305, 0, 0, 'We fought back da Scourge. What chance joo thinkin\' JOO got?', 14, 0, 100, 0, 0, 14721, 31427, 0, 'Moorabi - Aggro');
INSERT INTO `creature_text` VALUES (29304, 6, 0, 'As %s falls, the altar set into the floor on his balcony begins to glow faintly.', 16, 0, 100, 0, 0, 0, 31199, 0, 'Slad\'ran - Activate Altar');
INSERT INTO `creature_text` VALUES (29304, 5, 0, '%s begins to cast Poison Nova!', 41, 0, 100, 0, 0, 0, 30757, 0, 'Slad\'ran - Poison Nova');
INSERT INTO `creature_text` VALUES (29304, 4, 0, 'A thousssand fangsss gonna rend your flesh!', 14, 0, 100, 0, 0, 14445, 30370, 0, 'Slad\'ran - Summon Constrictors');
INSERT INTO `creature_text` VALUES (29304, 3, 0, 'Minionsss of the scale, heed my call!', 14, 0, 100, 0, 0, 14444, 30369, 0, 'Slad\'ran - Summon Snakes');
INSERT INTO `creature_text` VALUES (29304, 0, 0, 'Drakkari gonna kill anybody who tressspasss on these landsss!', 14, 0, 100, 0, 0, 14443, 31711, 0, 'Slad\'ran - Aggro');
INSERT INTO `creature_text` VALUES (29304, 1, 0, 'You not breathin\'? Good.', 14, 0, 100, 0, 0, 14446, 31712, 0, 'Slad\'ran - Slay 1');
INSERT INTO `creature_text` VALUES (29304, 1, 1, 'Ssscared now?', 14, 0, 100, 0, 0, 14447, 31713, 0, 'Slad\'ran - Slay 2');
INSERT INTO `creature_text` VALUES (29304, 1, 2, 'I eat you next, mon.', 14, 0, 100, 0, 0, 14448, 31714, 0, 'Slad\'ran - Slay 3');
INSERT INTO `creature_text` VALUES (29304, 2, 0, 'I sssee now... Ssscourge wasss not... our greatessst enemy....', 14, 0, 100, 0, 0, 14449, 31715, 0, 'Slad\'ran - Death');
INSERT INTO `creature_text` VALUES (29305, 3, 0, 'Get ready for somethin\'... much... BIGGAH!', 14, 0, 100, 0, 0, 14722, 31428, 0, 'Moorabi - Transform');
INSERT INTO `creature_text` VALUES (29305, 1, 1, 'Who gonna stop me? You?', 14, 0, 100, 0, 0, 14726, 31431, 0, 'Moorabi - Slay 2');
INSERT INTO `creature_text` VALUES (29305, 1, 2, 'Not so tough now!!', 14, 0, 100, 0, 0, 14727, 31432, 0, 'Moorabi - Slay 3');
INSERT INTO `creature_text` VALUES (29305, 2, 0, 'If our gods can die... den so can we....', 14, 0, 100, 0, 0, 14728, 31433, 0, 'Moorabi - Death');
INSERT INTO `creature_text` VALUES (29305, 7, 0, 'As %s falls, the altar set into the floor on his balcony begins to glow faintly.', 16, 0, 100, 0, 0, 0, 31199, 0, 'Moorabi - Activate Altar');
INSERT INTO `creature_text` VALUES (29305, 6, 0, '%s transforms into a Mammoth!', 16, 0, 100, 0, 0, 0, 30388, 0, 'Moorabi - Transformed');
INSERT INTO `creature_text` VALUES (29305, 5, 0, '%s begins to transform!', 41, 0, 100, 0, 0, 0, 30385, 0, 'Moorabi - Begin Transform');
INSERT INTO `creature_text` VALUES (37955, 12, 0, '%s goes into a berserker rage!', 41, 0, 100, 0, 0, 0, 34057, 0, 'blood queen lana thel EMOTE_BERSERK_RAID');
INSERT INTO `creature_text` VALUES (36853, 11, 0, '%s goes into a berserker rage!', 41, 0, 100, 0, 0, 0, 34057, 0, 'sindragosa EMOTE_BERSERK_RAID');
INSERT INTO `creature_text` VALUES (15956, 0, 0, 'Just a little taste...', 14, 0, 100, 0, 0, 8785, 13000, 0, 'anubrekhan SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (15956, 0, 1, 'There is no way out.', 14, 0, 100, 0, 0, 8786, 13002, 0, 'anubrekhan SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (15956, 0, 2, 'Yes, run! It makes the blood pump faster!', 14, 0, 100, 0, 0, 8787, 13003, 0, 'anubrekhan SAY_AGGRO3');
INSERT INTO `creature_text` VALUES (15956, 1, 0, 'Ahh... welcome to my parlor.', 14, 0, 100, 0, 0, 8788, 13004, 0, 'anubrekhan SAY_GREET1');
INSERT INTO `creature_text` VALUES (15956, 1, 1, 'I hear little hearts beating. Yes... beating faster now... soon the beating will stop.', 14, 0, 100, 0, 0, 8790, 13006, 0, 'anubrekhan SAY_GREET2');
INSERT INTO `creature_text` VALUES (15956, 1, 2, 'Where to go? What to do? So many choices that all end in pain, end in death.', 14, 0, 100, 0, 0, 8791, 13007, 0, 'anubrekhan SAY_GREET3');
INSERT INTO `creature_text` VALUES (15956, 1, 3, 'Which one shall I eat first? So difficult to choose. They all smell so delicious...', 14, 0, 100, 0, 0, 8792, 13008, 0, 'anubrekhan SAY_GREET4');
INSERT INTO `creature_text` VALUES (15956, 1, 4, 'Closer now. Tasty morsels. I\'ve been too long without food, without blood to drink.', 14, 0, 100, 0, 0, 8793, 13009, 0, 'anubrekhan SAY_GREET5');
INSERT INTO `creature_text` VALUES (15956, 2, 0, 'Shhh... it will all be over soon.', 14, 0, 100, 0, 0, 8789, 13005, 0, 'anubrekhan SAY_SLAY');
INSERT INTO `creature_text` VALUES (15953, 0, 0, 'Your old lives, your mortal desires mean nothing... you are acolytes of the master now, and you will serve the cause without question! The greatest glory is to die in the master\'s service!', 14, 0, 100, 0, 0, 8799, 12852, 0, 'faerlina SAY_GREET');
INSERT INTO `creature_text` VALUES (15953, 1, 0, 'Slay them in the master\'s name!', 14, 0, 100, 0, 0, 8794, 12856, 0, 'faerlina SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (15953, 1, 1, 'You cannot hide from me!', 14, 0, 100, 0, 0, 8795, 12857, 0, 'faerlina SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (15953, 1, 2, 'Kneel before me, worm!', 14, 0, 100, 0, 0, 8796, 12858, 0, 'faerlina SAY_AGGRO3');
INSERT INTO `creature_text` VALUES (15953, 1, 3, 'Run while you still can!', 14, 0, 100, 0, 0, 8797, 61582, 0, 'faerlina SAY_AGGRO4');
INSERT INTO `creature_text` VALUES (15953, 2, 0, 'You have failed!', 14, 0, 16, 0, 0, 8800, 20094, 0, 'faerlina SAY_SLAY1');
INSERT INTO `creature_text` VALUES (15953, 2, 1, 'Pathetic wretch!', 14, 0, 16, 0, 0, 8801, 12855, 0, 'faerlina SAY_SLAY2');
INSERT INTO `creature_text` VALUES (15953, 3, 0, 'The master will avenge me!!', 14, 0, 100, 0, 0, 8798, 12853, 0, 'faerlina SAY_DEATH');
INSERT INTO `creature_text` VALUES (16064, 0, 0, 'C\'mon an\' fight ye wee ninny!', 14, 0, 100, 0, 0, 8899, 13034, 0, 'korthazz SAY_KORT_AGGRO');
INSERT INTO `creature_text` VALUES (16064, 5, 0, 'To arms, ye roustabouts! We\'ve got company!', 14, 0, 100, 0, 0, 8903, 13038, 3, 'korthazz SAY_KORT_TAUNT1');
INSERT INTO `creature_text` VALUES (16064, 6, 0, 'I\'ve heard about enough a\' yer snivelin\'! Shut yer flytrap before I shut it for ye\'!', 14, 0, 100, 0, 0, 8904, 13039, 3, 'korthazz SAY_KORT_TAUNT2');
INSERT INTO `creature_text` VALUES (16064, 1, 2, 'I\'m gonna\' enjoy killin\' these slack-jawed daffodils!', 14, 0, 100, 0, 0, 8905, 13040, 0, 'korthazz SAY_KORT_TAUNT3');
INSERT INTO `creature_text` VALUES (16064, 2, 0, 'I like my meat extra crispy!', 14, 0, 100, 0, 0, 8902, 13037, 0, 'korthazz SAY_KORT_SPECIAl');
INSERT INTO `creature_text` VALUES (16064, 3, 0, 'Next time, bring more friends!', 14, 0, 30, 0, 0, 8901, 13036, 0, 'korthazz SAY_KORT_SLAY');
INSERT INTO `creature_text` VALUES (16064, 4, 0, 'What a bloody waste this is!', 14, 0, 100, 0, 0, 8900, 13035, 0, 'korthazz SAY_KORT_DEATH');
INSERT INTO `creature_text` VALUES (16065, 0, 0, 'Defend yourself!', 14, 0, 100, 0, 0, 8892, 13010, 0, 'blaumeux SAY_BLAU_AGGRO');
INSERT INTO `creature_text` VALUES (16065, 5, 0, 'Come, Zeliek, do not drive them out. Not before we\'ve had our fun!', 14, 0, 100, 0, 0, 8896, 13014, 3, 'blaumeux SAY_BLAU_TAUNT1');
INSERT INTO `creature_text` VALUES (16065, 6, 0, 'I do hope they stay alive long enough for me to... introduce myself.', 14, 0, 100, 0, 0, 8897, 13015, 3, 'blaumeux SAY_BLAU_TAUNT2');
INSERT INTO `creature_text` VALUES (16065, 1, 2, 'The first kill goes to me! Anyone care to wager?', 14, 0, 100, 0, 0, 8898, 13016, 0, 'blaumeux SAY_BLAU_TAUNT3');
INSERT INTO `creature_text` VALUES (16065, 2, 0, 'Your life is mine!', 14, 0, 100, 0, 0, 8895, 13013, 0, 'blaumeux SAY_BLAU_SPECIAL');
INSERT INTO `creature_text` VALUES (16065, 3, 0, 'Who\'s next?', 14, 0, 30, 0, 0, 8894, 21654, 0, 'blaumeux SAY_BLAU_SLAY');
INSERT INTO `creature_text` VALUES (16065, 4, 0, 'Touche...', 14, 0, 100, 0, 0, 8893, 13011, 0, 'blaumeux SAY_BLAU_DEATH');
INSERT INTO `creature_text` VALUES (30549, 0, 0, 'You seek death?', 14, 0, 100, 0, 0, 14571, 13051, 0, 'rivendare SAY_RIVE_AGGRO1');
INSERT INTO `creature_text` VALUES (30549, 0, 1, 'None shall pass!', 14, 0, 100, 0, 0, 14572, 34133, 0, 'rivendare SAY_RIVE_AGGRO2');
INSERT INTO `creature_text` VALUES (30549, 0, 2, 'Be still!', 14, 0, 100, 0, 0, 14573, 13053, 0, 'rivendare SAY_RIVE_AGGRO3');
INSERT INTO `creature_text` VALUES (30549, 5, 0, 'Enough prattling. Let them come. We shall grind their bones to dust.', 14, 0, 100, 0, 0, 14577, 13058, 3, 'rivendare SAY_RIVE_TAUNT1');
INSERT INTO `creature_text` VALUES (30549, 6, 0, 'Conserve your anger. Harness your rage. You will all have outlets for your frustrations soon enough.', 14, 0, 100, 0, 0, 14578, 13059, 3, 'rivendare SAY_RIVE_TAUNT2');
INSERT INTO `creature_text` VALUES (30549, 1, 2, 'Life is meaningless. It is in death that we are truly tested.', 14, 0, 100, 0, 0, 14579, 13060, 0, 'rivendare SAY_RIVE_TAUNT3');
INSERT INTO `creature_text` VALUES (30549, 2, 0, 'Bow to the might of the Scourge!', 14, 0, 100, 0, 0, 14576, 13057, 0, 'rivendare SAY_RIVE_SPECIAL');
INSERT INTO `creature_text` VALUES (30549, 3, 0, 'You will find no peace in death.', 14, 0, 30, 0, 0, 14574, 13055, 0, 'rivendare SAY_RIVE_SLAY1');
INSERT INTO `creature_text` VALUES (30549, 3, 1, 'The master\'s will is done.', 14, 0, 30, 0, 0, 14575, 13056, 0, 'rivendare SAY_RIVE_SLAY2');
INSERT INTO `creature_text` VALUES (30549, 4, 0, 'Death... will not stop me.', 14, 0, 100, 0, 0, 14580, 33116, 0, 'rivendare SAY_RIVE_DEATH');
INSERT INTO `creature_text` VALUES (16063, 0, 0, 'Flee, before it\'s too late!', 14, 0, 100, 0, 0, 8913, 13097, 0, 'zeliek SAY_ZELI_AGGRO');
INSERT INTO `creature_text` VALUES (16063, 5, 0, 'Invaders, cease this foolish venture at once! Turn away while you still can!', 14, 0, 100, 0, 0, 8917, 13101, 3, 'zeliek SAY_ZELI_TAUNT1');
INSERT INTO `creature_text` VALUES (16063, 6, 0, 'Perhaps they will come to their senses... and run away as fast as they can.', 14, 0, 100, 0, 0, 8918, 13102, 3, 'zeliek SAY_ZELI_TAUNT2');
INSERT INTO `creature_text` VALUES (16063, 1, 2, 'Do not continue! Turn back while there\'s still time!', 14, 0, 100, 0, 0, 8919, 13103, 0, 'zeliek SAY_ZELI_TAUNT3');
INSERT INTO `creature_text` VALUES (16063, 2, 0, 'I have no choice but to obey!', 14, 0, 100, 0, 0, 8916, 13100, 0, 'zeliek SAY_ZELI_SPECIAL');
INSERT INTO `creature_text` VALUES (16063, 3, 0, 'Forgive me!', 14, 0, 30, 0, 0, 8915, 13099, 0, 'zeliek SAY_ZELI_SLAY');
INSERT INTO `creature_text` VALUES (16063, 4, 0, 'It is... as it should be. ', 14, 0, 100, 0, 0, 8914, 13098, 0, 'zeliek SAY_ZELI_DEATH');
INSERT INTO `creature_text` VALUES (16060, 2, 0, 'You have fought hard to invade the realm of the harvester.', 14, 0, 100, 0, 0, 0, 13032, 3, 'Gothik SAY_INTRO_3');
INSERT INTO `creature_text` VALUES (16060, 3, 0, 'Now there is only one way out- to walk the lonely path of the damned.', 14, 0, 100, 0, 0, 0, 13033, 3, 'Gothik SAY_INTRO_4');
INSERT INTO `creature_text` VALUES (16060, 4, 0, 'I have waited long enough. Now you face the harvester of souls.', 14, 0, 100, 0, 0, 8808, 13028, 3, 'Gothik SAY_PHASE_TWO');
INSERT INTO `creature_text` VALUES (16060, 1, 0, 'Brazenly you have disregarded powers beyond your understanding.', 14, 0, 100, 0, 0, 0, 13031, 3, 'Gothik SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (16060, 0, 0, 'Foolishly you have sought your own demise.', 14, 0, 100, 0, 0, 8807, 13030, 3, 'Gothik SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (15936, 0, 0, 'You are mine now.', 14, 0, 100, 0, 0, 8825, 13041, 0, 'heigan SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (15936, 0, 1, 'I see you!', 14, 0, 100, 0, 0, 8826, 73351, 0, 'heigan SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (15936, 0, 2, 'You... are next.', 14, 0, 100, 0, 0, 8827, 13043, 0, 'heigan SAY_AGGRO3');
INSERT INTO `creature_text` VALUES (15936, 1, 0, 'Close your eyes. Sleep.', 14, 0, 20, 0, 0, 8829, 13045, 0, 'heigan SAY_SLAY');
INSERT INTO `creature_text` VALUES (15936, 2, 0, 'The races of the world will perish. It is only a matter of time.', 14, 0, 100, 0, 0, 8830, 13046, 0, 'heigan SAY_TAUNT1');
INSERT INTO `creature_text` VALUES (15936, 2, 1, 'I see endless suffering. I see torment. I see rage. I see everything...', 14, 0, 100, 0, 0, 8831, 13047, 0, 'heigan SAY_TAUNT2');
INSERT INTO `creature_text` VALUES (15936, 2, 2, 'Soon the world will tremble.', 14, 0, 100, 0, 0, 8832, 13048, 0, 'heigan SAY_TAUNT3');
INSERT INTO `creature_text` VALUES (15936, 2, 3, 'The end is upon you.', 14, 0, 100, 0, 0, 8833, 13049, 0, 'heigan SAY_TAUNT4');
INSERT INTO `creature_text` VALUES (15936, 2, 4, 'Hungry worms will feast on your rotting flesh.', 14, 0, 100, 0, 0, 8834, 13050, 0, 'heigan SAY_TAUNT5');
INSERT INTO `creature_text` VALUES (15936, 3, 0, 'Noo... o...', 14, 0, 100, 0, 0, 8828, 0, 0, 'heigan SAY_DEATH');
INSERT INTO `creature_text` VALUES (15954, 0, 0, 'Glory to the master!', 14, 0, 100, 0, 0, 8845, 13061, 0, 'noth SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (15954, 0, 1, 'Your life is forfeit!', 14, 0, 100, 0, 0, 8846, 29730, 0, 'noth SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (15954, 0, 2, 'Die, trespasser!', 14, 0, 100, 0, 0, 8847, 13063, 0, 'noth SAY_AGGRO3');
INSERT INTO `creature_text` VALUES (15954, 1, 0, 'Rise, my soldiers! Rise and fight once more!', 14, 0, 100, 0, 0, 8851, 13067, 0, 'noth SAY_SUMMON');
INSERT INTO `creature_text` VALUES (15954, 2, 0, 'My task is done!', 14, 0, 20, 0, 0, 8849, 29070, 0, 'noth SAY_SLAY1');
INSERT INTO `creature_text` VALUES (15954, 2, 1, 'Breathe no more!', 14, 0, 20, 0, 0, 8850, 31472, 0, 'noth SAY_SLAY2');
INSERT INTO `creature_text` VALUES (15954, 3, 0, 'I will serve the master... in death!', 14, 0, 100, 0, 0, 8848, 13064, 0, 'noth SAY_DEATH');
INSERT INTO `creature_text` VALUES (16028, 0, 0, 'Patchwerk want to play!', 14, 0, 100, 0, 0, 8909, 13068, 0, 'patchwerk SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (16028, 0, 1, 'Kel\'thuzad make Patchwerk his avatar of war!', 14, 0, 100, 0, 0, 8910, 13069, 0, 'patchwerk SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (16028, 1, 0, 'No more play?', 14, 0, 100, 0, 0, 8912, 28241, 0, 'patchwerk SAY_SLAY');
INSERT INTO `creature_text` VALUES (16028, 2, 0, 'What... happen to-', 14, 0, 100, 0, 0, 8911, 13070, 0, 'patchwerk SAY_DEATH');
INSERT INTO `creature_text` VALUES (16028, 3, 0, '%s goes into a berserker rage!', 16, 0, 100, 0, 0, 0, 4428, 0, 'patchwerk EMOTE_BERSERK');
INSERT INTO `creature_text` VALUES (16028, 4, 0, '%s goes into a frenzy!', 41, 0, 100, 0, 0, 0, 38630, 0, 'patchwerk EMOTE_ENRAGE');
INSERT INTO `creature_text` VALUES (15929, 0, 0, 'Stalagg crush you!', 14, 0, 100, 0, 0, 8864, 13083, 0, 'stalagg SAY_STAL_AGGRO');
INSERT INTO `creature_text` VALUES (15929, 1, 0, 'Stalagg kill!', 14, 0, 20, 0, 0, 8866, 13085, 0, 'stalagg SAY_STAL_SLAY');
INSERT INTO `creature_text` VALUES (15929, 2, 0, 'Master, save me!!', 14, 0, 100, 0, 0, 8865, 13084, 0, 'stalagg SAY_STAL_DEATH');
INSERT INTO `creature_text` VALUES (15930, 0, 0, 'Feed you to master!', 14, 0, 100, 0, 0, 8802, 13023, 0, 'feugen SAY_FEUG_AGGRO');
INSERT INTO `creature_text` VALUES (15930, 1, 0, 'Feugen make master happy.', 14, 0, 20, 0, 0, 8804, 13025, 0, 'feugen SAY_FEUG_SLAY');
INSERT INTO `creature_text` VALUES (15930, 2, 0, 'No... more... Feugen.', 14, 0, 100, 0, 0, 8803, 13024, 0, 'feugen SAY_FEUG_DEATH');
INSERT INTO `creature_text` VALUES (15928, 0, 0, 'You are too late!! I... must... obey!!', 14, 0, 100, 0, 0, 8872, 13091, 0, 'thaddius SAY_GREET');
INSERT INTO `creature_text` VALUES (15928, 1, 0, 'Kill...', 14, 0, 100, 0, 0, 8867, 13086, 0, 'thaddius SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (15928, 1, 1, 'Eat... your... bones...', 14, 0, 100, 0, 0, 8868, 13087, 0, 'thaddius SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (15928, 1, 2, 'Break... you!!', 14, 0, 100, 0, 0, 8869, 13088, 0, 'thaddius SAY_AGGRO3');
INSERT INTO `creature_text` VALUES (15928, 2, 0, 'You... die now!!', 14, 0, 20, 0, 0, 8877, 13096, 0, 'thaddius SAY_SLAY');
INSERT INTO `creature_text` VALUES (15928, 3, 0, 'Now you feel pain...', 14, 0, 100, 0, 0, 8871, 13090, 0, 'thaddius SAY_ELECT');
INSERT INTO `creature_text` VALUES (15928, 4, 0, 'Thank... you...', 14, 0, 100, 0, 0, 8870, 39601, 0, 'thaddius SAY_DEATH');
INSERT INTO `creature_text` VALUES (15928, 5, 0, 'Hurry! Ple-e-ase hurry!!', 14, 0, 100, 0, 0, 8873, 13092, 0, 'thaddius SAY_SCREAM1');
INSERT INTO `creature_text` VALUES (15928, 5, 1, 'Sto-o-op! Make it stop!!', 14, 0, 100, 0, 0, 8874, 13093, 0, 'thaddius SAY_SCREAM2');
INSERT INTO `creature_text` VALUES (15928, 5, 2, 'Help me! Save me!', 14, 0, 100, 0, 0, 8875, 13094, 0, 'thaddius SAY_SCREAM3');
INSERT INTO `creature_text` VALUES (15928, 5, 3, 'No-o-o-o!!', 14, 0, 100, 0, 0, 8876, 13095, 0, 'thaddius SAY_SCREAM4');
INSERT INTO `creature_text` VALUES (26763, 0, 0, 'Chaos beckons.', 14, 0, 100, 0, 0, 13186, 29599, 0, 'anomalus SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26763, 1, 0, 'Of course.', 14, 0, 100, 0, 0, 13187, 29604, 0, 'anomalus SAY_DEATH');
INSERT INTO `creature_text` VALUES (26763, 2, 0, 'Reality... unwoven.', 14, 0, 100, 0, 0, 13188, 29601, 0, 'anomalus SAY_RIFT');
INSERT INTO `creature_text` VALUES (26763, 3, 0, 'Indestructible.', 14, 0, 100, 0, 0, 13189, 29602, 0, 'anomalus SAY_SHIELD');
INSERT INTO `creature_text` VALUES (26723, 0, 0, 'Preserve? Why? There\'s no truth in it. No no no... only in the taking! I see that now!', 14, 0, 100, 0, 0, 13450, 26176, 0, 'keristrasza SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26723, 1, 0, 'Now we\'ve come to the truth!', 14, 0, 100, 0, 0, 13453, 29620, 0, 'keristrasza SAY_SLAY');
INSERT INTO `creature_text` VALUES (26723, 2, 0, 'Finish it! Finish it! Kill me, or I swear by the Dragonqueen you\'ll never see daylight again!', 14, 0, 100, 0, 0, 13452, 29619, 0, 'keristrasza SAY_ENRAGE');
INSERT INTO `creature_text` VALUES (26723, 3, 0, 'Dragonqueen... Life-Binder... preserve... me.', 14, 0, 100, 0, 0, 13454, 29621, 0, 'keristrasza SAY_DEATH');
INSERT INTO `creature_text` VALUES (26723, 4, 0, 'Stay. Enjoy your final moments.', 14, 0, 100, 0, 0, 13451, 29618, 0, 'keristrasza SAY_CRYSTAL_NOVA');
INSERT INTO `creature_text` VALUES (26731, 0, 0, 'You know what they say about curiosity....', 14, 0, 100, 0, 0, 13319, 29593, 0, 'grand magus telestra SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26731, 1, 0, 'Death becomes you.', 14, 0, 100, 0, 0, 13324, 29597, 0, 'grand magus telestra SAY_KILL');
INSERT INTO `creature_text` VALUES (26731, 2, 0, 'Damn the... luck.', 14, 0, 100, 0, 0, 13320, 29598, 0, 'grand magus telestra SAY_DEATH');
INSERT INTO `creature_text` VALUES (26731, 3, 0, 'Now to finish the job!', 14, 0, 100, 0, 0, 13323, 29596, 0, 'grand magus telestra SAY_MERGE');
INSERT INTO `creature_text` VALUES (26731, 4, 0, 'There\'s plenty of me to go around.', 14, 0, 100, 0, 0, 13321, 29595, 0, 'grand magus telestra SAY_SPLIT_1');
INSERT INTO `creature_text` VALUES (26731, 4, 1, 'I\'ll give you more than you can handle!', 14, 0, 100, 0, 0, 13322, 29594, 0, 'grand magus telestra SAY_SPLIT_2');
INSERT INTO `creature_text` VALUES (27654, 0, 0, 'The prisoners shall not go free! The word of Malygos is law!', 14, 0, 100, 0, 0, 13594, 31793, 0, 'drakos SAY_AGGRO');
INSERT INTO `creature_text` VALUES (27654, 1, 0, 'A fitting punishment.', 14, 0, 100, 0, 0, 13602, 31801, 0, 'drakos SAY_KILL_1');
INSERT INTO `creature_text` VALUES (27654, 1, 1, 'Sentence executed!', 14, 0, 100, 0, 0, 13603, 31802, 0, 'drakos SAY_KILL_2');
INSERT INTO `creature_text` VALUES (27654, 1, 2, 'Another casualty of war!', 14, 0, 100, 0, 0, 13604, 31803, 0, 'drakos SAY_KILL_3');
INSERT INTO `creature_text` VALUES (27654, 2, 0, 'The war... goes on.', 14, 0, 100, 0, 0, 13605, 31804, 0, 'drakos SAY_DEATH');
INSERT INTO `creature_text` VALUES (27654, 3, 0, 'It is too late to run!', 14, 0, 100, 0, 0, 13598, 31797, 0, 'drakos SAY_PULL_1');
INSERT INTO `creature_text` VALUES (27654, 3, 1, 'Gather \'round....', 14, 0, 100, 0, 0, 13599, 31798, 0, 'drakos SAY_PULL_2');
INSERT INTO `creature_text` VALUES (27654, 3, 2, 'None shall escape!', 14, 0, 100, 0, 0, 13600, 31799, 0, 'drakos SAY_PULL_3');
INSERT INTO `creature_text` VALUES (27654, 3, 3, 'I condemn you to death!', 14, 0, 100, 0, 0, 13601, 31800, 0, 'drakos SAY_PULL_4');
INSERT INTO `creature_text` VALUES (27654, 4, 0, 'Tremble, worms!', 14, 0, 100, 0, 0, 13595, 31794, 0, 'drakos SAY_STOMP_1');
INSERT INTO `creature_text` VALUES (27654, 4, 1, 'I will crush you!', 14, 0, 100, 0, 0, 13596, 31795, 0, 'drakos SAY_STOMP_2');
INSERT INTO `creature_text` VALUES (27654, 4, 2, 'Can you fly?', 14, 0, 100, 0, 0, 13597, 31796, 0, 'drakos SAY_STOMP_3');
INSERT INTO `creature_text` VALUES (27655, 2, 0, 'Still you fight. Still you cling to misguided principles. If you survive, you\'ll find me in the center ring.', 14, 0, 100, 1, 0, 13637, 27414, 0, 'Mage-Lord Urom - SAY_SUMMON_3');
INSERT INTO `creature_text` VALUES (27655, 1, 0, 'Clearly my pets failed. Perhaps another demonstration is in order.', 14, 0, 100, 1, 0, 13636, 27413, 0, 'Mage-Lord Urom - SAY_SUMMON_2');
INSERT INTO `creature_text` VALUES (27655, 0, 0, 'What do we have here... those that would defy the Spell-Weaver? Those without foresight or understanding. How can I make you see? Malygos is saving the world from itself! Bah! You are hardly worth my time!', 14, 0, 100, 1, 0, 13635, 27411, 0, 'Mage-Lord Urom - SAY_SUMMON_1');
INSERT INTO `creature_text` VALUES (28217, 2, 0, 'Home not far. Come!', 12, 0, 100, 0, 0, 0, 27753, 0, 'Injured Rainspeaker Oracle');
INSERT INTO `creature_text` VALUES (28217, 3, 0, 'Thanks!', 12, 0, 100, 0, 0, 0, 27752, 0, 'Injured Rainspeaker Oracle');
INSERT INTO `creature_text` VALUES (28217, 1, 0, 'You tell us when it home time.', 12, 0, 100, 0, 0, 0, 27826, 0, 'Injured Rainspeaker Oracle');
INSERT INTO `creature_text` VALUES (28217, 0, 0, 'The Injured Rainspeaker Oracle rises clumsily to its feet.', 16, 0, 100, 0, 0, 0, 27785, 0, 'Injured Rainspeaker Oracle');
INSERT INTO `creature_text` VALUES (28315, 0, 0, 'Frenzyheart kill you if you come back. You no welcome here no more!', 12, 0, 100, 0, 0, 0, 27837, 0, 'vekjik SAY_TEXTID_VEKJIK1');
INSERT INTO `creature_text` VALUES (28787, 6, 1, 'We made it! Thank you for getting me out of that hell hole. Tell Hemet to expect me!', 12, 0, 100, 0, 0, 0, 28865, 0, 'engineer helice SAY_WP_7');
INSERT INTO `creature_text` VALUES (28787, 5, 1, 'You really shouldn\'t play with this stuff. Someone could get hurt.', 12, 0, 100, 0, 0, 0, 28864, 0, 'engineer helice SAY_WP_6');
INSERT INTO `creature_text` VALUES (28787, 4, 1, 'Oh, look, it\'s another cartload of explosives! Let\'s help them dispose of it.', 12, 0, 100, 0, 0, 0, 28862, 0, 'engineer helice SAY_WP_5');
INSERT INTO `creature_text` VALUES (28787, 3, 0, 'It\'s getting a little hot over here. Shall we move on?', 12, 0, 100, 0, 0, 0, 28860, 0, 'engineer helice SAY_WP_4');
INSERT INTO `creature_text` VALUES (28787, 2, 1, 'Or THIS is what you get.', 12, 0, 100, 0, 0, 0, 28859, 0, 'engineer helice SAY_WP_3');
