INSERT INTO `creature_text` VALUES (27655, 5, 1, 'So much unstable energy... but worth the risk to destroy you!', 14, 0, 100, 0, 0, 13640, 31830, 0, 'Mage-Lord Urom - SAY_ARCANE_EXPLOSION');
