INSERT INTO `creature_text` VALUES (15990, 0, 0, 'Your forces are nearly marshaled to strike back against your enemies, my liege.', 14, 0, 100, 0, 0, 14467, 12990, 3, 'kelthuzad SAY_SAPP_DIALOG1');
INSERT INTO `creature_text` VALUES (15990, 1, 0, 'It is good that you serve me so faithfully. Soon, all will serve the Lich King and in the end, you shall be rewarded...so long as you do not falter.', 14, 0, 100, 0, 0, 8881, 0, 0, 'kelthuzad SAY_SAPP_DIALOG2_LICH');
INSERT INTO `creature_text` VALUES (15990, 2, 0, 'Yes, master. The time of their ultimate demise draws close.... What is this?', 14, 0, 100, 0, 0, 14468, 12991, 3, 'kelthuzad SAY_SAPP_DIALOG3');
INSERT INTO `creature_text` VALUES (15990, 3, 0, 'Your security measures have failed! See to this interruption immediately!', 14, 0, 100, 0, 0, 8882, 0, 0, 'kelthuzad SAY_SAPP_DIALOG4_LICH');
INSERT INTO `creature_text` VALUES (15990, 4, 0, 'As you command, master!', 14, 0, 100, 0, 0, 14469, 12992, 3, 'kelthuzad SAY_SAPP_DIALOG5');
INSERT INTO `creature_text` VALUES (15990, 5, 0, 'No!!! A curse upon you, interlopers! The armies of the Lich King will hunt you down. You will not escape your fate...', 14, 0, 100, 0, 0, 14484, 13150, 3, 'kelthuzad SAY_CAT_DIED');
INSERT INTO `creature_text` VALUES (15990, 16, 0, 'Who dares violate the sanctity of my domain? Be warned, all who trespass here are doomed.', 14, 0, 100, 0, 0, 14463, 0, 3, 'kelthuzad SAY_TAUNT1');
INSERT INTO `creature_text` VALUES (15990, 17, 0, 'Fools! You think yourselves triumphant? You have only taken one step closer to the abyss!', 14, 0, 100, 0, 0, 14464, 12985, 3, 'kelthuzad SAY_TAUNT2');
INSERT INTO `creature_text` VALUES (15990, 18, 0, 'I grow tired of these games. Proceed, and I will banish your souls to oblivion!', 14, 0, 100, 0, 0, 14465, 12986, 3, 'kelthuzad SAY_TAUNT3');
INSERT INTO `creature_text` VALUES (15990, 19, 0, 'You have no idea what horrors lie ahead. You have seen nothing! The frozen heart of Naxxramas awaits you!', 14, 0, 100, 0, 0, 14466, 27382, 3, 'kelthuzad SAY_TAUNT4');
INSERT INTO `creature_text` VALUES (15990, 7, 0, 'Pray for mercy!', 14, 0, 100, 0, 0, 14475, 12995, 0, 'kelthuzad SAY_AGGRO1');
