INSERT INTO `creature_text` VALUES (15990, 8, 1, '%s cackles maniacally.', 16, 0, 100, 0, 0, 14479, 29805, 0, 'kelthuzad SAY_SLAY2');
INSERT INTO `creature_text` VALUES (15990, 9, 0, 'Agghhhh! Do... not... rejoice! Your victory is a hollow one, for I shall return with powers beyond your imagining!', 14, 0, 100, 0, 0, 14480, 13019, 0, 'kelthuzad SAY_DEATH');
INSERT INTO `creature_text` VALUES (15990, 10, 0, 'Your soul is bound to me, now!', 14, 0, 100, 0, 0, 14472, 13017, 0, 'kelthuzad SAY_CHAIN1');
INSERT INTO `creature_text` VALUES (15990, 10, 1, 'There will be no escape!', 14, 0, 100, 0, 0, 14473, 13018, 0, 'kelthuzad SAY_CHAIN2');
INSERT INTO `creature_text` VALUES (15990, 11, 0, 'I will freeze the blood in your veins!', 14, 0, 100, 0, 0, 14474, 13020, 0, 'kelthuzad SAY_FROST_BLAST');
INSERT INTO `creature_text` VALUES (15990, 12, 0, 'Master, I require aid!', 14, 0, 100, 0, 0, 14470, 12998, 0, 'kelthuzad SAY_REQUEST_AID');
INSERT INTO `creature_text` VALUES (15990, 20, 0, 'Come, heroes... By the will of the Lich King, you shall be destroyed.', 14, 0, 100, 0, 0, 0, 12993, 3, 'Kel\'Thuzad SAY_DIALOGUE_SAPPHIRON_KELTHUZAD4');
INSERT INTO `creature_text` VALUES (15990, 14, 0, 'Minions, servants, soldiers of the cold dark! Obey the call of Kel\'Thuzad!', 14, 0, 100, 0, 0, 14471, 12999, 0, 'kelthuzad SAY_SUMMON_MINIONS');
INSERT INTO `creature_text` VALUES (15990, 15, 0, 'Your petty magics are no challenge to the might of the Scourge!', 14, 0, 100, 0, 0, 14481, 13492, 0, 'kelthuzad SAY_SPECIAL1_MANA_DET');
