INSERT INTO `creature_text` VALUES (16807, 4, 0, 'I\'m already bored!', 14, 0, 100, 0, 0, 10271, 16864, 0, 'nethekurse SAY_AGGRO_1');
INSERT INTO `creature_text` VALUES (16807, 4, 1, 'Come on, show me a real fight!', 14, 0, 100, 0, 0, 10272, 15595, 0, 'nethekurse SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES (16807, 4, 2, 'I had more fun torturing the peons!', 14, 0, 100, 0, 0, 10273, 16863, 0, 'nethekurse SAY_AGGRO_3');
INSERT INTO `creature_text` VALUES (16807, 5, 0, 'You lose.', 14, 0, 100, 0, 0, 10274, 16865, 0, 'nethekurse SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (16807, 5, 1, 'Oh, just die!', 14, 0, 100, 0, 0, 10275, 16866, 0, 'nethekurse SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (16807, 6, 0, 'What... a shame.', 14, 0, 100, 0, 0, 10276, 16862, 0, 'nethekurse SAY_DIE');
INSERT INTO `creature_text` VALUES (19523, 0, 0, 'Smash!', 14, 0, 100, 0, 0, 10306, 30471, 0, 'omrogg GoCombat_1');
INSERT INTO `creature_text` VALUES (19523, 1, 0, 'If you nice me let you live.', 14, 0, 100, 0, 0, 10308, 14048, 0, 'omrogg GoCombat_2');
INSERT INTO `creature_text` VALUES (19523, 2, 0, 'Me hungry!', 14, 0, 100, 0, 0, 10309, 35064, 0, 'omrogg GoCombat_3');
INSERT INTO `creature_text` VALUES (19524, 0, 0, 'Why don\'t you let me do the talking?', 14, 0, 100, 0, 0, 10317, 14047, 0, 'omrogg GoCombatDelay_1');
INSERT INTO `creature_text` VALUES (19524, 1, 0, 'No, we will NOT let you live.', 14, 0, 100, 0, 0, 10318, 16916, 0, 'omrogg GoCombatDelay_2');
INSERT INTO `creature_text` VALUES (19524, 2, 0, 'You\'re always hungry.  That\'s why we so fat!', 14, 0, 100, 0, 0, 10319, 16919, 0, 'omrogg GoCombatDelay_3');
INSERT INTO `creature_text` VALUES (19523, 3, 0, 'You stay here.  Me go kill someone else!', 14, 0, 100, 0, 0, 10303, 16898, 0, 'omrogg Threat_1');
INSERT INTO `creature_text` VALUES (19524, 3, 0, 'What are you doing?', 14, 0, 100, 0, 0, 10315, 16901, 0, 'omrogg Threat_2');
INSERT INTO `creature_text` VALUES (19523, 4, 0, 'We kill someone else!', 14, 0, 100, 0, 0, 10302, 16895, 0, 'omrogg Threat_3');
INSERT INTO `creature_text` VALUES (19523, 5, 0, 'Me not like this one...', 14, 0, 100, 0, 0, 10300, 14043, 0, 'omrogg Threat_4');
INSERT INTO `creature_text` VALUES (19524, 4, 0, 'That\'s not funny!', 14, 0, 100, 0, 0, 10314, 16899, 0, 'omrogg ThreatDelay1_1');
INSERT INTO `creature_text` VALUES (19523, 6, 0, 'Me get bored.', 14, 0, 100, 0, 0, 10305, 16902, 0, 'omrogg ThreatDelay1_2');
INSERT INTO `creature_text` VALUES (19524, 5, 0, 'I\'m not done yet, idiot!', 14, 0, 100, 0, 0, 10313, 16896, 0, 'omrogg ThreatDelay1_3');
INSERT INTO `creature_text` VALUES (19524, 6, 0, 'Hey, you numbskull!', 14, 0, 100, 0, 0, 10312, 14044, 0, 'omrogg ThreatDelay1_4');
INSERT INTO `creature_text` VALUES (19523, 7, 0, 'Ha ha ha.', 14, 0, 100, 0, 0, 10304, 16900, 0, 'omrogg ThreatDelay2_1');
INSERT INTO `creature_text` VALUES (19524, 7, 0, 'Bored? He was almost dead!', 14, 0, 100, 0, 0, 10316, 16903, 0, 'omrogg ThreatDelay2_2');
INSERT INTO `creature_text` VALUES (19523, 8, 0, 'Hey!', 14, 0, 100, 0, 0, 10307, 16917, 0, 'omrogg ThreatDelay2_3');
INSERT INTO `creature_text` VALUES (19523, 9, 0, 'We kill his friend!', 14, 0, 100, 0, 0, 10301, 14045, 0, 'omrogg ThreatDelay2_4');
INSERT INTO `creature_text` VALUES (19523, 10, 0, 'This one die easy!', 14, 0, 100, 0, 0, 10310, 16922, 0, 'omrogg Killing_1');
INSERT INTO `creature_text` VALUES (19524, 8, 0, 'I\'m tired.  You kill next one!', 14, 0, 100, 0, 0, 10320, 16921, 0, 'omrogg Killing_2');
INSERT INTO `creature_text` VALUES (19524, 9, 0, 'That\'s because I do all the hard work!', 14, 0, 100, 0, 0, 10321, 16923, 0, 'omrogg KillingDelay_1');
INSERT INTO `creature_text` VALUES (19523, 11, 0, 'That\'s because I do all the hard work!', 14, 0, 100, 0, 0, 10321, 16923, 0, 'omrogg KillingDelay_2');
INSERT INTO `creature_text` VALUES (16809, 0, 0, 'This all... your fault!', 14, 0, 100, 0, 0, 10311, 16924, 0, 'omrogg YELL_DIE_L');
INSERT INTO `creature_text` VALUES (16809, 1, 0, 'I... hate... you.', 14, 0, 100, 0, 0, 10322, 16925, 0, 'omrogg YELL_DIE_R');
INSERT INTO `creature_text` VALUES (16809, 2, 0, '%s enrages', 16, 0, 100, 0, 0, 0, 0, 0, 'omrogg EMOTE_ENRAGE');
INSERT INTO `creature_text` VALUES (16808, 0, 0, 'Ours is the TRUE Horde! The only Horde!', 14, 0, 100, 0, 0, 10323, 17614, 0, 'kargath SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (16808, 0, 1, 'I\'ll carve the meat from your bones!', 14, 0, 100, 0, 0, 10324, 17616, 0, 'kargath SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (16808, 0, 2, 'I am called Bladefist for a reason. As you will see.', 14, 0, 100, 0, 0, 10325, 17617, 0, 'kargath SAY_AGGRO3');
INSERT INTO `creature_text` VALUES (16808, 1, 0, 'For the real Horde!', 14, 0, 100, 0, 0, 10326, 17619, 0, 'kargath SAY_SLAY1');
INSERT INTO `creature_text` VALUES (16808, 1, 1, 'I am the ONLY warchief!', 14, 0, 100, 0, 0, 10327, 17620, 0, 'kargath SAY_SLAY2');
INSERT INTO `creature_text` VALUES (16808, 2, 0, 'The true Horde... will prevail.', 14, 0, 100, 0, 0, 10328, 17621, 0, 'kargath SAY_DEATH');
INSERT INTO `creature_text` VALUES (17085, 0, 0, 'Avruu\'s magic... it still controls me.  You must fight me, mortal.  It\'s the only way to break the spell!', 12, 0, 100, 0, 0, 0, 13448, 0, 'aeranas SAY_SUMMON');
INSERT INTO `creature_text` VALUES (17085, 1, 0, 'Avruu\'s magic is broken!  I\'m free once again!', 12, 0, 100, 0, 0, 0, 13447, 0, 'aeranas SAY_FREE');
INSERT INTO `creature_text` VALUES (17077, 0, 0, '%s lifts its head into the air, as if listening for something.', 16, 0, 100, 0, 0, 0, 13430, 0, 'ancestral wolf EMOTE_WOLF_LIFT_HEAD');
INSERT INTO `creature_text` VALUES (17077, 1, 0, '%s lets out a howl that rings across the mountains to the north and motions for you to follow.', 16, 0, 100, 0, 0, 0, 13446, 0, 'ancestral wolf EMOTE_WOLF_HOWL');
INSERT INTO `creature_text` VALUES (17023, 0, 0, 'Gul\'dan speaks the truth! We should return at once to tell our brothers of the news! Retreat back through the portal!', 14, 0, 100, 0, 0, 0, 19911, 0, 'ancestral wolf SAY_WOLF_WELCOME');
INSERT INTO `creature_text` VALUES (16993, 0, 0, 'Thank you for agreeing to help. Now, let\'s get out of here, $n!', 12, 0, 100, 0, 0, 0, 13289, 0, 'wounded elf SAY_ELF_START');
INSERT INTO `creature_text` VALUES (16993, 1, 0, 'Over there! They\'re following us!', 12, 0, 100, 0, 0, 0, 13255, 0, 'wounded elf SAY_ELF_SUMMON1');
INSERT INTO `creature_text` VALUES (16993, 2, 0, 'Allow me a moment to rest. The journey taxes what little strength I have.', 12, 0, 100, 0, 0, 0, 13290, 0, 'wounded elf SAY_ELF_RESTING');
INSERT INTO `creature_text` VALUES (16993, 3, 0, 'Did you hear something?', 12, 0, 100, 0, 0, 0, 27926, 0, 'wounded elf SAY_ELF_SUMMON2');
INSERT INTO `creature_text` VALUES (16993, 4, 0, 'Falcon Watch, at last! Now, where\'s my... Oh no! My pack, it\'s missing! Where has - ', 12, 0, 100, 0, 0, 0, 13292, 0, 'wounded elf SAY_ELF_COMPLETE');
INSERT INTO `creature_text` VALUES (16993, 5, 0, 'You won\'t keep me from getting to Falcon Watch!', 12, 0, 100, 0, 0, 0, 13659, 0, 'wounded elf SAY_ELF_AGGRO');
INSERT INTO `creature_text` VALUES (18210, 4, 0, 'It is best that we split up now, in case they send more after us. Hopefully one of us will make it back to Garrosh. Farewell, stranger.', 12, 1, 100, 0, 0, 0, 15001, 0, 'maghar captive SAY_MAG_COMPLETE');
INSERT INTO `creature_text` VALUES (18445, 0, 0, 'Thanks, $n! How many times have you saved my life now? Three? That makes us blood in my book!', 12, 0, 100, 0, 0, 0, 15424, 0, 'corki NPC_CORKI');
INSERT INTO `creature_text` VALUES (20812, 0, 0, 'This is the last time I get caught! I promise! Bye!', 12, 0, 100, 0, 0, 0, 15308, 0, 'corki NPC_CORKI_2');
INSERT INTO `creature_text` VALUES (18369, 0, 0, 'Thanks, $r! I\'m sure my dad will reward you greatly! Bye!', 12, 0, 100, 0, 0, 0, 15297, 0, 'corki NPC_CORKI_3');
