INSERT INTO `creature_text` VALUES (19830, 0, 0, 'You cannot be serious!  We are severely understaffed and can barely keep this manaforge functional!', 12, 0, 100, 6, 0, 0, 18166, 0, 'dawnforge SAY_ARCANIST_ARDONIS_1');
INSERT INTO `creature_text` VALUES (19830, 1, 0, 'Yes, my lord.', 12, 0, 100, 0, 0, 0, 19189, 0, 'dawnforge SAY_ARCANIST_ARDONIS_2');
INSERT INTO `creature_text` VALUES (21504, 0, 0, 'Indeed, it is not a request.', 12, 0, 100, 1, 0, 0, 19185, 0, 'dawnforge SAY_PATHALEON_CULATOR_IMAGE_1');
INSERT INTO `creature_text` VALUES (21504, 1, 0, 'Duro will be reinforced!  Ultris was a complete disaster.  I will NOT have that mistake repeated!', 12, 0, 100, 5, 0, 0, 19187, 0, 'dawnforge SAY_PATHALEON_CULATOR_IMAGE_2');
INSERT INTO `creature_text` VALUES (21504, 2, 0, 'We\'ve had too many setbacks along the way: Hellfire Citadel, Fallen Sky Ridge, Firewing Point...  Prince Kael\'thas will tolerate no further delays.  I will tolerate nothing other than complete success!', 12, 0, 100, 1, 0, 0, 19188, 0, 'dawnforge SAY_PATHALEON_CULATOR_IMAGE_2_1');
INSERT INTO `creature_text` VALUES (21504, 3, 0, 'I am returning to Tempest Keep.  See to it that I do not have reason to return!', 12, 0, 100, 1, 0, 0, 19191, 0, 'dawnforge SAY_PATHALEON_CULATOR_IMAGE_2_2');
INSERT INTO `creature_text` VALUES (19831, 0, 0, 'We need you to send reinforcements to Manaforge Duro, Ardonis.  This is not a request, it\'s an order.', 12, 0, 100, 1, 0, 0, 18165, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_1');
INSERT INTO `creature_text` VALUES (19831, 1, 0, 'You will do as ordered.  Manaforge Duro has come under heavy attack by mana creatures and the situation is out of control.  Failure to comply will not be tolerated!', 12, 0, 100, 0, 0, 0, 18167, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_2');
INSERT INTO `creature_text` VALUES (19831, 2, 0, 'My lord!', 12, 0, 100, 0, 0, 0, 19186, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_3');
INSERT INTO `creature_text` VALUES (19831, 3, 0, 'Yes, my lord.', 12, 0, 100, 0, 0, 0, 19189, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_4');
INSERT INTO `creature_text` VALUES (19831, 4, 0, 'See to it, Ardonis!', 12, 0, 100, 5, 0, 0, 19190, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_5');
INSERT INTO `creature_text` VALUES (20415, 0, 0, 'Bessy, is that you?', 12, 0, 100, 0, 0, 0, 0, 0, 'bessy SAY_THADELL_1');
INSERT INTO `creature_text` VALUES (20415, 1, 0, 'Thank you for bringing back my Bessy, $N. I couldn\'t live without her!', 12, 0, 100, 0, 0, 0, 0, 0, 'bessy SAY_THADELL_2');
INSERT INTO `creature_text` VALUES (21648, 0, 0, 'Thank you, mortal.', 12, 11, 100, 0, 0, 0, 19849, 0, 'mature netherwing drake SAY_JUST_EATEN');
INSERT INTO `creature_text` VALUES (23139, 5, 0, 'But... My lord, I do not understand. $n... He is the orc that has...', 12, 0, 100, 0, 0, 0, 21805, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 3, 0, 'All hail Lord Illidan!', 14, 0, 100, 0, 0, 0, 21802, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 4, 0, 'Lord Illidan, this is the Dragonmaw that I, and others, have told you about. He will lead us to victory!', 12, 0, 100, 0, 0, 0, 21803, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 2, 0, 'Lord Illidan will be here shortly.', 12, 0, 100, 0, 0, 0, 21800, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 1, 0, 'Warriors of Dragonmaw, gather \'round! One among you has attained the rank of highlord! Bow your heads in reverence! Show your respect and allegiance to Highlord $n!', 14, 0, 100, 0, 0, 0, 21799, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23146, 1, 0, 'Yes... Yes we move now... Please no hurt us...', 12, 0, 100, 0, 0, 0, 21302, 0, 'Dragonmaw Enforcer');
INSERT INTO `creature_text` VALUES (23139, 0, 0, 'Come, $n. Lord Stormrage awaits.', 12, 0, 100, 0, 0, 0, 21798, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23146, 0, 0, 'Who... Who is you?? What you want?', 12, 0, 100, 0, 0, 0, 21300, 0, 'Dragonmaw Enforcer');
INSERT INTO `creature_text` VALUES (22083, 0, 0, 'What is the meaning of this, Mor\'ghor?', 12, 0, 100, 0, 0, 0, 21804, 0, 'overlord morghor LORD_ILLIDAN_SAY_1');
INSERT INTO `creature_text` VALUES (22083, 1, 0, 'Silence!', 12, 1, 100, 0, 0, 0, 32240, 0, 'overlord morghor LORD_ILLIDAN_SAY_2');
INSERT INTO `creature_text` VALUES (22083, 2, 0, 'Blathering idiot. You incomprehensibly incompetent buffoon...\n', 12, 0, 100, 0, 0, 0, 21808, 0, 'overlord morghor LORD_ILLIDAN_SAY_3');
INSERT INTO `creature_text` VALUES (22083, 3, 0, 'THIS is your hero?', 12, 0, 100, 0, 0, 0, 21809, 0, 'overlord morghor LORD_ILLIDAN_SAY_4');
INSERT INTO `creature_text` VALUES (22083, 4, 0, 'You have been deceived, imbecile.', 12, 0, 100, 0, 0, 0, 21810, 0, 'overlord morghor LORD_ILLIDAN_SAY_5');
INSERT INTO `creature_text` VALUES (22083, 5, 0, 'This... whole... operation... HAS BEEN COMPROMISED!', 12, 0, 100, 0, 0, 0, 21811, 0, 'overlord morghor LORD_ILLIDAN_SAY_6');
INSERT INTO `creature_text` VALUES (22083, 6, 0, 'I expect to see this insect\'s carcass in pieces in my lair within the hour. Fail and you will suffer a fate so much worse than death.', 12, 0, 100, 0, 0, 0, 21812, 0, 'overlord morghor LORD_ILLIDAN_SAY_7');
INSERT INTO `creature_text` VALUES (23141, 0, 0, 'You will not harm the $g boy:girl;, Mor\'ghor! Quickly, $n, climb on my back!', 12, 0, 100, 0, 0, 0, 21825, 0, 'Yarzill');
INSERT INTO `creature_text` VALUES (21027, 0, 0, 'I sense the tortured spirits, $n. They are this way. Come quickly!', 12, 0, 100, 0, 0, 0, 18668, 0, 'wilda SAY_WIL_START');
INSERT INTO `creature_text` VALUES (21027, 1, 0, 'Watch out!', 12, 0, 100, 0, 0, 0, 32401, 0, 'wilda SAY_WIL_AGGRO1');
INSERT INTO `creature_text` VALUES (21027, 1, 1, 'Naga attackers! Defend yourself!', 12, 0, 100, 0, 0, 0, 18688, 0, 'wilda SAY_WIL_AGGRO2');
INSERT INTO `creature_text` VALUES (21027, 2, 0, 'Grant me protection, $n. I must break through their foul magic!', 12, 0, 100, 0, 0, 0, 18669, 0, 'wilda SAY_WIL_PROGRESS1');
INSERT INTO `creature_text` VALUES (21027, 3, 0, 'The naga of Coilskar are exceptionally cruel to their prisoners. It is a miracle that I survived inside that watery prison for as long as I did. Earthmother be praised.', 12, 0, 100, 0, 0, 0, 18685, 0, 'wilda SAY_WIL_PROGRESS2');
INSERT INTO `creature_text` VALUES (21027, 3, 1, 'Lady Vashj must answer for these atrocities. She must be brought to justice!', 12, 0, 100, 0, 0, 0, 18684, 0, 'wilda SAY_WIL_PROGRESS4');
INSERT INTO `creature_text` VALUES (21027, 3, 2, 'The tumultuous nature of the great waterways of Azeroth and Draenor are a direct result of tormented water spirits.', 12, 0, 100, 0, 0, 0, 18675, 0, 'wilda SAY_WIL_PROGRESS5');
INSERT INTO `creature_text` VALUES (21027, 4, 0, 'Now we must find the exit.', 12, 0, 100, 0, 0, 0, 18670, 0, 'wilda SAY_WIL_FIND_EXIT');
INSERT INTO `creature_text` VALUES (21027, 5, 0, 'It shouldn\'t be much further, $n. The exit is just up ahead.', 12, 0, 100, 0, 0, 0, 18671, 0, 'wilda SAY_WIL_JUST_AHEAD');
INSERT INTO `creature_text` VALUES (21027, 6, 0, 'Thank you, $n. Please return to my brethren at the Altar of Damnation, near the Hand of Gul\'dan, and tell them that Wilda is safe. May the Earthmother watch over you...', 12, 0, 100, 0, 0, 0, 18672, 0, 'wilda SAY_WIL_END');
INSERT INTO `creature_text` VALUES (19685, 0, 0, 'Follow me, stranger.  This won\'t take long.', 12, 0, 100, 0, 0, 0, 17239, 0, 'kservant SAY_KHAD_SERV_0');
INSERT INTO `creature_text` VALUES (19685, 1, 0, 'Shattrath was once the draenei capital of this world. Its name means \"dwelling of light.\"', 15, 0, 100, 0, 0, 0, 17240, 0, 'kservant SAY_KHAD_SERV_1');
INSERT INTO `creature_text` VALUES (19685, 2, 0, 'When the Burning Legion turned the orcs against the draenei, the fiercest battle was fought here. The draenei fought tooth and nail, but in the end the city fell.', 15, 0, 100, 0, 0, 0, 17268, 0, 'kservant SAY_KHAD_SERV_2');
INSERT INTO `creature_text` VALUES (19685, 3, 0, 'The city was left in ruins and darkness... until the Sha\'tar arrived.', 15, 0, 100, 0, 0, 0, 17269, 0, 'kservant SAY_KHAD_SERV_3');
INSERT INTO `creature_text` VALUES (19685, 4, 0, 'Let us go into the Lower City.  I will warn you that as one of the only safe havens in Outland, Shattrath has attracted droves of refugees from all wars, current and past.', 15, 0, 100, 0, 0, 0, 17270, 0, 'kservant SAY_KHAD_SERV_4');
INSERT INTO `creature_text` VALUES (19685, 5, 0, 'The Sha\'tar, or \"born from light\" are the naaru that came to Outland to fight the demons of the Burning Legion.', 15, 0, 100, 0, 0, 0, 17242, 0, 'kservant SAY_KHAD_SERV_5');
INSERT INTO `creature_text` VALUES (19685, 6, 0, 'They were drawn to the ruins of Shattrath City where a small remnant of the draenei priesthood conducted its rites inside a ruined temple on this very spot.\n', 15, 0, 100, 0, 0, 0, 17271, 0, 'kservant SAY_KHAD_SERV_6');
INSERT INTO `creature_text` VALUES (19685, 7, 0, 'The priesthood, known as the Aldor, quickly regained its strength as word spread that the naaru had returned and reconstruction soon began.  The ruined temple is now used as an infirmary for injured refugees.', 15, 0, 100, 0, 0, 0, 17272, 0, 'kservant SAY_KHAD_SERV_7');
INSERT INTO `creature_text` VALUES (19685, 8, 0, 'It wouldn\'t be long, however, before the city came under attack once again.  This time, the attack came from Illidan\'s armies. A large regiment of blood elves had been sent by Illidan\'s ally, Kael\'thas Sunstrider, to lay waste to the city.', 15, 0, 100, 0, 0, 0, 17245, 0, 'kservant SAY_KHAD_SERV_8');
INSERT INTO `creature_text` VALUES (19685, 9, 0, 'As the regiment of blood elves crossed this very bridge, the Aldor\'s exarchs and vindicators lined up to defend the Terrace of Light.  But then the unexpected happened.\n', 15, 0, 100, 0, 0, 0, 17274, 0, 'kservant SAY_KHAD_SERV_9');
INSERT INTO `creature_text` VALUES (19685, 10, 0, 'The blood elves laid down their weapons in front of the city\'s defenders; their leader, a blood elf elder known as Voren\'thal, stormed into the Terrace of Light and demanded to speak to A\'dal.', 15, 0, 100, 0, 0, 0, 17247, 0, 'kservant SAY_KHAD_SERV_10');
INSERT INTO `creature_text` VALUES (19685, 11, 0, 'As the naaru approached him, Voren\'thal kneeled before him and uttered the following words: \"I\'ve seen you in a vision, naaru. My race\'s only hope for survival lies with you. My followers and I are here to serve you.\"', 15, 0, 100, 0, 0, 0, 17275, 0, 'kservant SAY_KHAD_SERV_11');
INSERT INTO `creature_text` VALUES (19685, 12, 0, 'The defection of Voren\'thal and his followers was the largest loss ever incurred by Kael\'s forces. And these weren\'t just any blood elves.  Many of the best and brightest amongst Kael\'s scholars and magisters had been swayed by Voren\'thal\'s influence.\n', 15, 0, 100, 0, 0, 0, 17250, 0, 'kservant SAY_KHAD_SERV_12');
INSERT INTO `creature_text` VALUES (19685, 13, 0, 'The naaru accepted the defectors, who would be known as the Scryers; their dwelling lies in the platform above.  Only those initiated with the Scryers are allowed there.', 15, 0, 100, 0, 0, 0, 17276, 0, 'kservant SAY_KHAD_SERV_13');
INSERT INTO `creature_text` VALUES (19685, 14, 0, 'The Aldor are followers of the Light and forgiveness and redemption are values they understand. However, they found hard to forget the deeds of the blood elves while under Kael\'s command.', 15, 0, 100, 0, 0, 0, 17253, 0, 'kservant SAY_KHAD_SERV_14');
INSERT INTO `creature_text` VALUES (19685, 15, 0, 'Many of the priesthood had been slain by the same magisters who now vowed to serve the naaru.  They were not happy to share the city with their former enemies.', 15, 0, 100, 0, 0, 0, 17277, 0, 'kservant SAY_KHAD_SERV_15');
INSERT INTO `creature_text` VALUES (19685, 16, 0, 'The Aldor\'s most holy temple and its surrounding dwellings lie on the terrace above.  As a holy site, only the initiated are welcome inside.\n', 15, 0, 100, 0, 0, 0, 17278, 0, 'kservant SAY_KHAD_SERV_16');
INSERT INTO `creature_text` VALUES (19685, 17, 0, 'The attacks against Shattrath continued, but the city did not fall, as you can see. On the contrary, the naaru known as Xi\'ri led a successful incursion into Shadowmoon Valley - Illidan\'s doorstep.', 15, 0, 100, 0, 0, 0, 17257, 0, 'kservant SAY_KHAD_SERV_17');
INSERT INTO `creature_text` VALUES (19685, 18, 0, 'There he continues to wage war on Illidan with the assistance of the Aldor and the Scryers.  The two factions have not given up on their old feuds, though.', 15, 0, 100, 0, 0, 0, 17279, 0, 'kservant SAY_KHAD_SERV_18');
INSERT INTO `creature_text` VALUES (19685, 19, 0, 'Such is their animosity that they vie for the honor of being sent to assist the naaru there. Each day, that decision is made here by A\'dal.  The armies gather here to receive A\'dal\'s blessing before heading to Shadowmoon.', 15, 0, 100, 0, 0, 0, 17280, 0, 'kservant SAY_KHAD_SERV_19');
INSERT INTO `creature_text` VALUES (19685, 20, 0, 'Khadgar should be ready to see you again.  Just remember that to serve the Sha\'tar you will most likely have to ally with the Aldor or the Scryers. And seeking the favor of one group will cause the others\' dislike.\n', 15, 0, 100, 0, 0, 0, 17262, 0, 'kservant SAY_KHAD_SERV_20');
INSERT INTO `creature_text` VALUES (19685, 21, 0, 'Good luck stranger, and welcome to Shattrath City.', 15, 0, 100, 0, 0, 0, 17281, 0, 'kservant SAY_KHAD_SERV_21');
