INSERT INTO `creature_text` VALUES (19622, 10, 1, 'Bow to my will.', 14, 0, 100, 0, 0, 11269, 18259, 0, 'kaelthas SAY_MINDCONTROL2');
INSERT INTO `creature_text` VALUES (19622, 11, 0, 'Let us see how you fare when your world is turned upside down.', 14, 0, 100, 0, 0, 11264, 20198, 0, 'kaelthas SAY_GRAVITYLAPSE1');
INSERT INTO `creature_text` VALUES (19622, 11, 1, 'Having trouble staying grounded?', 14, 0, 100, 0, 0, 11265, 20199, 0, 'kaelthas SAY_GRAVITYLAPSE2');
