INSERT INTO `creature_text` VALUES (35005, 2, 0, 'The Silver Covenant is pleased to present their contenders for this event, Highlord.', 14, 0, 100, 396, 0, 8574, 35259, 0, 'Arelas Brightstar - SAY_ARELAS_GRAND_CHAMPIONS_INTRO_1');
INSERT INTO `creature_text` VALUES (35005, 3, 0, 'Coming out of the gate is Eressea Dawnsinger, skilled mage and Grand Champion of Silvermoon!\n', 12, 0, 100, 0, 0, 8573, 35338, 0, 'Arelas Brightstar - SAY_ARELAS_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35005, 4, 0, 'Entering the arena is the lean and dangerous Zul\'tore, Grand Champion of Sen\'jin!\n', 12, 0, 100, 0, 0, 8573, 35335, 0, 'Arelas Brightstar - SAY_ARELAS_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35005, 5, 0, 'Presenting the fierce Grand Champion of Orgrimmar, Mokra the Skullcrusher!\n', 12, 0, 100, 0, 0, 8573, 35334, 0, 'Arelas Brightstar - SAY_ARELAS_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35005, 6, 0, 'Representing the tenacity of the Forsaken, here is the Grand Champion of the Undercity, Deathstalker Visceri!\n', 12, 0, 100, 0, 0, 8573, 35337, 0, 'Arelas Brightstar - SAY_ARELAS_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35005, 7, 0, 'Tall in the saddle of his kodo, here is the venerable Runok Wildmane, Grand Champion of Thunder Bluff!\n', 12, 0, 100, 0, 0, 8573, 35336, 0, 'Arelas Brightstar - SAY_ARELAS_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35005, 8, 0, 'What\'s that, up near the rafters?', 12, 0, 100, 25, 0, 0, 35545, 0, 'Arelas Brightstar - SAY_ARELAS_KNIGHT_INTRO');
INSERT INTO `creature_text` VALUES (35004, 0, 0, 'Entering the arena, a paladin who is no stranger to the battlefield or tournament ground, the Grand Champion of the Argent Crusade, Eadric the Pure!', 12, 0, 100, 0, 0, 8574, 35542, 0, 'Jaeren Sunsworn - SAY_JAEREN_EADRIC_INTRO');
INSERT INTO `creature_text` VALUES (35004, 1, 0, 'The next combatant is second to none in her passion for upholding the Light. I give you Argent Confessor Paletress!', 14, 0, 100, 0, 0, 8574, 35543, 0, 'Jaeren Sunsworn - SAY_JAEREN_PALETRESS_INTRO');
INSERT INTO `creature_text` VALUES (35004, 2, 0, 'The Sunreavers are proud to present their representatives in this trial by combat.', 14, 0, 100, 396, 0, 8574, 35260, 0, 'Jaeren Sunsworn - SAY_JAEREN_GRAND_CHAMPIONS_INTRO_1');
INSERT INTO `creature_text` VALUES (35004, 3, 0, 'Coming out of the gate is Colosos, the towering Grand Champion of the Exodar!', 12, 0, 100, 0, 0, 8573, 35247, 0, 'Jaeren Sunsworn - SAY_JAEREN_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35004, 4, 0, 'Entering the arena is the Grand Champion of Darnassus, the skilled sentinel Jaelyne Evensong!', 12, 0, 100, 0, 0, 8573, 35249, 0, 'Jaeren Sunsworn - SAY_JAEREN_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35004, 5, 0, 'Here comes the small but deadly Ambrose Boltspark, Grand Champion of Gnomeregan!', 12, 0, 100, 0, 0, 8573, 35248, 0, 'Jaeren Sunsworn - SAY_JAEREN_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35004, 6, 0, 'Proud and strong, give a cheer for Marshal Jacob Alerius, the Grand Champion of Stormwind!', 12, 0, 100, 0, 0, 8573, 35245, 0, 'Jaeren Sunsworn - SAY_JAEREN_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35004, 7, 0, 'The might of the dwarves is represented today by the Grand Champion of Ironforge, Lana Stouthammer!', 12, 0, 100, 0, 0, 8573, 35246, 0, 'Jaeren Sunsworn - SAY_JAEREN_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35004, 8, 0, 'What\'s that, up near the rafters?', 12, 0, 100, 25, 0, 0, 35545, 0, 'Jaeren Sunsworn - SAY_JAEREN_KNIGHT_INTRO');
INSERT INTO `creature_text` VALUES (34996, 50, 0, 'Welcome, champions. Today, before the eyes of your leaders and peers, you will prove yourselves worthy combatants.', 14, 0, 100, 1, 0, 0, 35321, 0, 'Highlord Tirion Fordring - SAY_TIRION_GRAND_CHAMPION_INTRO_1');
INSERT INTO `creature_text` VALUES (34996, 51, 0, 'You will first be facing three of the Grand Champions of the Tournament! These fierce contenders have beaten out all others to reach the pinnacle of skill in the joust.', 14, 0, 100, 0, 0, 0, 35330, 0, 'Highlord Tirion Fordring - SAY_TIRION_GRAND_CHAMPION_INTRO_2');
INSERT INTO `creature_text` VALUES (34996, 52, 0, 'Begin!', 14, 0, 100, 0, 0, 8574, 35331, 0, 'Highlord Tirion Fordring - SAY_TIRION_GRAND_CHAMPION_INTRO_3');
INSERT INTO `creature_text` VALUES (34996, 53, 0, 'Well fought! Your next challenge comes from the Crusade\'s own ranks. You will be tested against their considerable prowess.', 14, 0, 100, 0, 0, 15882, 35541, 0, 'Highlord Tirion Fordring - SAY_TIRION_PALETRESS_INTRO_1');
INSERT INTO `creature_text` VALUES (34996, 54, 0, 'You may begin!', 14, 0, 100, 22, 0, 8574, 35677, 0, 'Highlord Tirion Fordring - SAY_TIRION_PALETRESS_INTRO_2');
INSERT INTO `creature_text` VALUES (34996, 55, 0, 'Well done. You have proven yourself today-', 14, 0, 100, 0, 0, 0, 35544, 0, 'Highlord Tirion Fordring - SAY_TIRION_KNIGHT_INTRO_2');
INSERT INTO `creature_text` VALUES (34996, 56, 0, 'What is the meaning of this?', 14, 0, 100, 0, 0, 0, 35547, 0, 'Highlord Tirion Fordring - SAY_TIRION_KNIGHT_INTRO_2');
INSERT INTO `creature_text` VALUES (34996, 57, 0, 'My congratulations, champions. Through trials both planned and unexpected, you have triumphed.', 14, 0, 100, 0, 0, 15882, 35796, 0, 'Highlord Tirion Fordring - SAY_TIRION_KNIGHT_OUTRO_1');
INSERT INTO `creature_text` VALUES (34996, 58, 0, 'Go now and rest; you\'ve earned it.', 14, 0, 100, 0, 0, 0, 35797, 0, 'Highlord Tirion Fordring - SAY_TIRION_KNIGHT_OUTRO_2');
INSERT INTO `creature_text` VALUES (34994, 0, 0, 'Fight well, Horde! Lok\'tar Ogar!', 14, 0, 100, 22, 0, 0, 35326, 0, 'Thrall - SAY_THRALL_GRAND_CHAMPIONS_INTRO');
INSERT INTO `creature_text` VALUES (34994, 1, 0, 'Well done, Horde!', 14, 0, 100, 66, 0, 0, 35794, 0, 'Thrall - SAY_THRALL_KNIGHT_OUTRO');
INSERT INTO `creature_text` VALUES (34994, 2, 0, 'Garrosh, enough.', 12, 0, 100, 396, 0, 0, 35325, 0, 'Thrall');
INSERT INTO `creature_text` VALUES (34990, 50, 0, 'I have no taste for these games, Tirion. Still... I trust they will perform admirably.', 12, 0, 100, 1, 0, 0, 35322, 0, 'King Varian Wrynn');
INSERT INTO `creature_text` VALUES (34990, 51, 0, 'Don\'t just stand there; kill him!', 14, 0, 100, 22, 0, 0, 35550, 0, 'King Varian Wrynn');
INSERT INTO `creature_text` VALUES (34990, 52, 0, 'I did not come here to watch animals tear at each other senselessly, Tirion.', 12, 0, 100, 1, 0, 0, 35328, 0, 'King Varian Wrynn');
INSERT INTO `creature_text` VALUES (34990, 53, 0, 'You fought well.', 14, 0, 100, 66, 0, 0, 35795, 0, 'King Varian Wrynn - SAY_KNIGHT_OUTRO');
INSERT INTO `creature_text` VALUES (34995, 50, 0, 'Finally, a fight worth watching.', 12, 0, 100, 396, 0, 0, 35327, 0, 'Garrosh Hellscream - SAY_GARROSH_GRAND_CHAMPIONS_INTRO');
INSERT INTO `creature_text` VALUES (34995, 51, 0, 'Tear him apart!', 14, 0, 100, 22, 0, 0, 35551, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (34995, 52, 0, 'Admirably? Hah! I will enjoy watching your weak little champions fail, human.', 14, 0, 100, 22, 0, 0, 35324, 0, 'Garrosh Hellscream');
