INSERT INTO `creature_text` VALUES (17073, 0, 0, '<Sobbing> I hate graveyards.  So many dead.  And my sweet Luhanaa....', 12, 0, 100, 1, 0, 0, 13413, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 1, 'It should have been me!', 12, 7, 100, 1, 0, 0, 13419, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 2, 'Be at peace, my sweet, sweet, Luhanaa.', 12, 7, 100, 1, 0, 0, 13425, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 3, 'I\'ll never forget you, my love.', 12, 7, 100, 1, 0, 0, 13420, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 4, 'If I ever find the ones responsible for the crash...!', 12, 7, 100, 1, 0, 0, 13423, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 5, 'We should never have come along.', 12, 7, 100, 1, 0, 0, 13421, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 6, 'Why, Luhanaa?  Why?!', 12, 7, 100, 6, 0, 0, 13418, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 7, 'I miss you so much!', 12, 7, 100, 1, 0, 0, 13422, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 8, 'You\'ll always be here, with me.', 12, 7, 100, 1, 0, 0, 13424, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 9, '%s weeps softly.', 16, 0, 100, 18, 0, 0, 13414, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 10, '%s bows his head and sighs, clearly exhausted.', 16, 0, 100, 2, 0, 0, 13417, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 11, '%s breaks down into huge, wracking sobs.', 16, 0, 100, 18, 0, 0, 13416, 0, 'Draenei Mourner - Random Say');
