INSERT INTO `creature_text` VALUES (29384, 0, 4, 'Thank you, $r. I will join your struggle against the stormforged.', 12, 0, 0, 0, 0, 0, 30763, 0, '');
INSERT INTO `creature_text` VALUES (25416, 0, 0, 'Who dares?!', 12, 0, 0, 0, 0, 0, 24675, 0, '');
