INSERT INTO `creature_text` VALUES (36939, 0, 0, 'Rise up, sons and daughters of the Horde! Today we battle a hated enemy of the Horde! LOK\'TAR OGAR!', 14, 0, 100, 0, 0, 17087, 37689, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_1');
