INSERT INTO `creature_text` VALUES (36939, 5, 0, 'You will know our business soon. Kor\'kron, ANNIHILATE THEM!', 14, 0, 100, 0, 0, 17092, 37698, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_6');
