INSERT INTO `creature_text` VALUES (36948, 0, 0, 'Fire up the engines! We got a meetin\' with destiny, lads!', 14, 0, 100, 0, 0, 16962, 37677, 0, 'Muradin Bronzebeard - SAY_MURADIN_INTRO_1');
