INSERT INTO `creature_text` VALUES (36948, 5, 0, 'Cowardly dogs! Ye blindsided us!', 14, 0, 100, 0, 0, 16967, 37682, 0, 'Muradin Bronzebeard - SAY_MURADIN_INTRO_6');
INSERT INTO `creature_text` VALUES (36948, 6, 0, 'Not me battle? I dunnae who ye think ye are, mister, but I got a score to settle with Arthas and yer not gettin\' in me way! FIRE ALL GUNS! FIRE! FIRE!', 14, 0, 100, 0, 0, 16968, 37683, 0, 'Muradin Bronzebeard - SAY_MURADIN_INTRO_7');
