INSERT INTO `creature_text` VALUES (23224, 0, 1, 'Yes, unless I have anything to do with it.', 15, 0, 100, 0, 0, 0, 21157, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 2, 'I see that happening sometime between tomorrow and the next decade. Definitely.', 15, 0, 100, 0, 0, 0, 21158, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 3, 'Looks good for you...and bad for me.', 15, 0, 100, 0, 0, 0, 21160, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 4, 'I\'ve consulted my fellow imps, and we think YES, except for that one imp.', 15, 0, 100, 0, 0, 0, 21161, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 5, 'The outlook is positive, but I\'m still negative.', 15, 0, 100, 0, 0, 0, 21162, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 6, 'It pains me to say this, but \"Yes\".', 15, 0, 100, 0, 0, 0, 21163, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 7, 'When dwarves fly! They do? Then yes!', 15, 0, 100, 0, 0, 0, 21164, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 8, 'Sure, but you\'re not going to like it.', 15, 0, 100, 0, 0, 0, 21165, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 9, 'Be quiet \'bout what you hear and see around here, $r.', 15, 0, 100, 0, 0, 0, 21166, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 10, 'Unfortunately... yes.', 15, 0, 100, 0, 0, 0, 21169, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 11, 'I can\'t see why not, although, I can\'t see a lot of things right now.', 15, 0, 100, 0, 0, 0, 21170, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 12, 'I would bet your soul on it.', 15, 0, 100, 0, 0, 0, 21171, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 13, 'Yes, but if anyone asks... It wasn\'t me who told you.', 15, 0, 100, 0, 0, 0, 21172, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 14, 'Didn\'t you already ask that once? Yes already!', 15, 0, 100, 0, 0, 0, 21173, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 15, 'Please... Is Kil\'jaeden red?', 15, 0, 100, 0, 0, 0, 21175, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 16, 'Yeah, sure. You just keep thinking that.', 15, 0, 100, 0, 0, 0, 21176, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 17, 'I suppose.', 15, 0, 100, 0, 0, 0, 21177, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 18, 'Definitely.', 15, 0, 100, 0, 0, 0, 21178, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 19, 'Jump three times and dance for ten minutes and it will definitely happen!', 15, 0, 100, 0, 0, 0, 21179, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 20, 'Word on the peninsula is YES!', 15, 0, 100, 0, 0, 0, 21180, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 21, 'Oh, that one\'s for sure.', 15, 0, 100, 0, 0, 0, 21181, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 22, 'Yes, but not in the way you imagine.', 15, 0, 100, 0, 0, 0, 21182, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 23, 'Yes, yes, a thousand times, yes already!', 15, 0, 100, 0, 0, 0, 21183, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 24, 'Yes, now stop pestering me!', 15, 0, 100, 0, 0, 0, 21184, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 25, 'The answer will be a yes if you let me out of here.', 15, 0, 100, 0, 0, 0, 21185, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 26, 'It\'s as sure as the warts on my backside!', 15, 0, 100, 0, 0, 0, 21186, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 27, 'The answer is yes in here, I don\'t see why it would be any different out there.', 15, 0, 100, 0, 0, 0, 21187, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 28, 'Three words - \"ab - so - lutely\"!', 15, 0, 100, 0, 0, 0, 21188, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 29, 'Yes, but I hoped I would never have to answer that.', 15, 0, 100, 0, 0, 0, 21189, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 30, 'Ask me again later - I\'m trying to scratch my nose and it\'s hard to concentrate.', 15, 0, 100, 0, 0, 0, 21190, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 31, 'Why should I answer that?  Do you tell fortunes when people shake YOU?', 15, 0, 100, 0, 0, 0, 21191, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 32, 'Yes, it will rain.  That\'s not what you asked?  Too bad!', 15, 0, 100, 0, 0, 0, 21192, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 33, 'I\'m sorry, I can only speak Demonic.', 15, 0, 100, 0, 0, 0, 21193, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 34, 'Yes!  I mean no!  I mean... which answer gets me out of here?', 15, 0, 100, 0, 0, 0, 21194, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 35, 'Yes, No, Maybe so.', 15, 0, 100, 0, 0, 0, 21195, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 36, 'It won\'t matter, you\'ll be dead by tomorrow.', 15, 0, 100, 0, 0, 0, 21196, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 37, 'You should be asking \"Is that rogue behind me going to kill me?\"', 15, 0, 100, 0, 0, 0, 21197, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 38, 'It\'s times like these that I wish I had a longer cooldown.', 15, 0, 100, 0, 0, 0, 21198, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 39, '%s shrugs. Who knows?', 15, 0, 100, 0, 0, 0, 21199, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 40, 'It\'s like my mother always said: [Demonic] \"Razxx khaj jhashxx xashjx.\"', 15, 0, 100, 0, 0, 0, 21205, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 41, '%s is ignoring you.', 15, 0, 100, 0, 0, 0, 21206, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 42, 'Why me?  Why not a goblin, or a gnome...', 15, 0, 100, 0, 0, 0, 21207, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 43, 'What happens in the twisting nether, stays in the twisting nether.', 15, 0, 100, 0, 0, 0, 21208, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 44, 'Avoid taking unnecessary gambles. Your lucky numbers are two, two and half, and eleven-teen.', 15, 0, 100, 0, 0, 0, 21209, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 45, 'Wouldn\'t you like to know?', 15, 0, 100, 0, 0, 0, 21210, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 46, 'Oh, oh, oh!  I can see this one clearly...  Nah, lost it.', 15, 0, 100, 0, 0, 0, 21211, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 47, 'This was NOT in my contract!', 15, 0, 100, 0, 0, 0, 21212, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 48, 'XRA RAHKI MAZIZRA!', 15, 0, 100, 0, 0, 0, 21213, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 49, '4 8 15 16 23 42', 15, 0, 100, 0, 0, 0, 21214, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 50, 'Are you making fun of me?', 15, 0, 100, 0, 0, 0, 21215, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 51, 'What kind of imp do you think I am?', 15, 0, 100, 0, 0, 0, 21216, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 52, 'Say please.', 15, 0, 100, 0, 0, 0, 21217, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 53, 'Want to trade places?', 15, 0, 100, 0, 0, 0, 21218, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 54, 'Do you ask this question to everything that\'s trapped in a ball?', 15, 0, 100, 0, 0, 0, 21219, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 55, 'Hey! You try arranging furniture with some jerk shaking your house!', 15, 0, 100, 0, 0, 0, 21220, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 56, 'I can make that happen. Just sign below the dotted line...', 15, 0, 100, 0, 0, 0, 21221, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 57, 'Reply hazy and slightly damp. Dry thoroughly and try again.', 15, 0, 100, 0, 0, 0, 21222, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 58, 'Concentrate (on releasing me from this infernal prison) and try again later.', 15, 0, 100, 0, 0, 0, 21223, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 59, 'Please insert 25 silver pieces and try again.', 15, 0, 100, 0, 0, 0, 21224, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 60, 'Are you my pal, Danny?', 15, 0, 100, 0, 0, 0, 21225, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 61, 'You remember the time you tried to drill that hole in your head?', 15, 0, 100, 0, 0, 0, 21226, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 62, 'Oh that\'s right, don\'t make any effort to make your own fortune!', 15, 0, 100, 0, 0, 0, 21227, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 63, 'Two words - imp-possible!', 15, 0, 100, 0, 0, 0, 21228, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 64, 'You need Arcane Intellect, because that answer is obvious!  NO!', 15, 0, 100, 0, 0, 0, 21229, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 65, 'Not on your life!', 15, 0, 100, 0, 0, 0, 21230, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 66, 'I don\'t have to be a fortune-telling imp to know the answer to that one - NO!', 15, 0, 100, 0, 0, 0, 21231, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 67, 'The odds are 32.33 (repeating of course) percent chance of success.', 15, 0, 100, 0, 0, 0, 21232, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 68, 'When Blackrock freezes over!', 15, 0, 100, 0, 0, 0, 21233, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 69, 'Hahahaha, you\'re kidding right?', 15, 0, 100, 0, 0, 0, 21234, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 70, 'Inconceivable!', 15, 0, 100, 0, 0, 0, 21235, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 71, 'My sources say \"no\". Before the torture, that is.', 15, 0, 100, 0, 0, 0, 21236, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 72, 'That\'s about as likely as me getting out of this ball.', 15, 0, 100, 0, 0, 0, 21237, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 73, 'You picked the wrong time to shake me today, buddy!  Prepare for disappointment.', 15, 0, 100, 0, 0, 0, 21238, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 74, 'Not unless you\'re some kind of super-person.  And don\'t kid yourself, you\'re not.', 15, 0, 100, 0, 0, 0, 21239, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 75, 'That\'s about as likely as me getting a date with a succubus.', 15, 0, 100, 0, 0, 0, 21240, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 76, 'My fortune telling powers are immeasurable - your chances are though: NO CHANCE', 15, 0, 100, 0, 0, 0, 21241, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 77, 'NO - and don\'t try shaking me again for a better answer!', 15, 0, 100, 0, 0, 0, 21242, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 78, 'Yes is my answer...', 15, 0, 100, 0, 0, 0, 21243, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 79, '...NOT!', 15, 0, 100, 0, 0, 0, 21244, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 80, 'I\'m gonna have to give this one the big N-O.', 15, 0, 100, 0, 0, 0, 21245, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 81, 'The outlook is very bad - for YOU that is!  Haha, take it!', 15, 0, 100, 0, 0, 0, 21246, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 82, 'Survey says: BZZZT!', 15, 0, 100, 0, 0, 0, 21247, 0, '');
INSERT INTO `creature_text` VALUES (14467, 0, 0, 'Kroshius live? Kroshius crush!', 14, 0, 100, 0, 0, 0, 9633, 0, 'Kroshius');
INSERT INTO `creature_text` VALUES (25334, 0, 0, '%s gains fuel.', 41, 0, 100, 0, 0, 0, 26171, 0, 'Horde Siege Tank');
INSERT INTO `creature_text` VALUES (29368, 0, 0, 'How predictable! But then, who would expect a rock to think like anything other than one?', 12, 0, 100, 11, 0, 0, 30895, 0, '');
INSERT INTO `creature_text` VALUES (29368, 1, 0, 'If you\'re so eager to fight, I\'ll oblige you. But know that nothing you do here can prevent the completion of the iron colossus!', 12, 0, 100, 274, 0, 0, 30896, 0, '');
INSERT INTO `creature_text` VALUES (29801, 0, 0, 'At last, the tyranny of the stormforged is at its end!', 12, 0, 100, 25, 0, 0, 30891, 0, '');
INSERT INTO `creature_text` VALUES (29801, 1, 0, 'We\'ve defeated Valduran and we\'ll fell his colossus.', 12, 0, 100, 0, 25, 0, 33052, 0, '');
INSERT INTO `creature_text` VALUES (30152, 0, 0, 'No more will your minions assail the creatures of stone and their allies.', 12, 0, 100, 1, 0, 0, 30893, 0, '');
INSERT INTO `creature_text` VALUES (30152, 1, 0, 'I am your doom, Valduran!', 12, 0, 100, 15, 0, 0, 30894, 0, '');
INSERT INTO `creature_text` VALUES (30152, 2, 0, 'Well fought! The day is ours, but the war goes on!', 12, 0, 100, 4, 0, 0, 33053, 0, '');
INSERT INTO `creature_text` VALUES (27238, 0, 0, 'My beautiful mill. You! You\'ll die for this!', 12, 0, 100, 0, 0, 0, 26418, 0, 'Foreman Kaleiki');
INSERT INTO `creature_text` VALUES (26287, 0, 0, 'Thel\'zan\'s phylactery drops to the ground beneath Icestorm', 41, 0, 100, 0, 0, 0, 27125, 0, 'Icestorm');
INSERT INTO `creature_text` VALUES (27843, 0, 0, 'I\'ll flush her out, $N! You just be ready for her when she comes in!', 12, 0, 100, 1, 0, 0, 27120, 0, 'Wyrmbait');
INSERT INTO `creature_text` VALUES (27844, 0, 0, 'FIRE! FIRE! BRING HER DOWN!', 14, 0, 100, 0, 0, 0, 27123, 0, 'Legion Commander Tyralion');
INSERT INTO `creature_text` VALUES (25802, 0, 0, 'You challenge Kaw, destroyer of mammoths? Then face me and feel my thunder!', 14, 0, 100, 0, 0, 0, 25071, 0, 'Kaw the Mammoth Destroyer');
INSERT INTO `creature_text` VALUES (27383, 0, 0, 'I was... once like you. Mortal. Soft. Only flesh and bone. WEAK AND TIMID!', 14, 0, 100, 0, 0, 0, 27157, 0, 'Thel zan the Duskbringer');
