INSERT INTO `creature_text` VALUES (27858, 0, 0, 'Honor, courage, compassion and justice! Those were once the virtues you fought for as a cleric of the Argent Dawn, Thel\'zan.', 14, 0, 100, 0, 0, 0, 27173, 0, 'Highlord Bolvar Fordragon');
