INSERT INTO `creature_text` VALUES (27858, 2, 0, 'So how, then, did Father Inigo Montoy, bastion of virtue for the Argent Dawn, become the embodiment of that which he hated most?', 12, 0, 100, 0, 0, 0, 27175, 0, 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES (27858, 3, 0, 'Now, be free and join the battle! Let us end this together!', 12, 0, 100, 0, 0, 0, 27182, 0, 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES (27858, 4, 0, 'Victory for the Alliance!', 14, 0, 100, 0, 0, 0, 27180, 0, 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES (27858, 5, 0, 'You have saved Wintergarde! With Thel\'zan\'s filth washed away, the 7th Legion will easily clean up the remaining Scourge. When you are done here, report to Angrathar!', 12, 0, 100, 0, 0, 0, 27181, 0, 'Highlord Bolvar Fordragon');
