INSERT INTO `creature_text` VALUES (23990, 0, 0, 'I spit on you!', 12, 0, 100, 0, 0, 13538, 30503, 0, 'Gjalerbron Rune-Caster');
INSERT INTO `creature_text` VALUES (23990, 0, 1, 'YAAARRRGH!', 12, 0, 100, 0, 0, 0, 22700, 0, 'Gjalerbron Rune-Caster');
INSERT INTO `creature_text` VALUES (23990, 0, 2, 'Look what\'s come to play.', 12, 0, 100, 0, 0, 0, 22820, 0, 'Gjalerbron Rune-Caster');
INSERT INTO `creature_text` VALUES (23990, 0, 3, 'Your entrails will make a fine necklace.', 12, 0, 100, 0, 0, 13535, 30500, 0, 'Gjalerbron Rune-Caster');
INSERT INTO `creature_text` VALUES (24123, 0, 0, 'These are all excellent. It\'s going to be hard to choose just one.', 12, 0, 100, 1, 0, 0, 22854, 0, 'Nokoma Snowseer');
INSERT INTO `creature_text` VALUES (24123, 1, 0, 'I think I\'ve found the one I\'ll use. Now I have to begin the task of carving it. I don\'t think it\'s going to be easy. I don\'t even know what a female yeti\'s mating call sounds like!', 12, 0, 100, 396, 0, 0, 22855, 0, 'Nokoma Snowseer');
INSERT INTO `creature_text` VALUES (24123, 2, 0, 'I\'ve got most of the basic carving done, but it needs to be tested before I can continue. Are you willing to help me with that, $n?', 12, 0, 100, 396, 0, 0, 22856, 0, 'Nokoma Snowseer');
