INSERT INTO `creature_text` VALUES (27159, 3, 0, 'I have heard whisperings from the east that our esteemed Scarab Lord has joined the battle, as have entire batallions from Shattrath: heroes blessed by A\'dal himself, granted rank as Hand of A\'dal!', 12, 0, 100, 1, 0, 0, 0, 0, 'Text 4');
