INSERT INTO `creature_text` VALUES (25197, 0, 0, 'There, $n, now you will be able to speak with, and understand, the Winterfin murlocs.', 12, 0, 100, 1, 0, 0, 24428, 0, 'King Mrgl-Mrgl');
INSERT INTO `creature_text` VALUES (29860, 0, 0, 'SMASH!', 12, 0, 100, 0, 0, 0, 30471, 0, 'Vile');
INSERT INTO `creature_text` VALUES (29859, 0, 0, 'MRMRFRMRFRMRRRR!', 12, 0, 100, 0, 0, 0, 30472, 0, 'The Leaper');
INSERT INTO `creature_text` VALUES (29858, 0, 0, 'My dear general, your time has come!', 12, 0, 100, 0, 0, 0, 30470, 0, 'Lady Nightswood');
INSERT INTO `creature_text` VALUES (29851, 0, 0, 'You\'re the one that\'s been disrupting things? This should be easy.', 12, 0, 100, 0, 0, 0, 30465, 0, 'General Lightsbane');
INSERT INTO `creature_text` VALUES (29840, 0, 0, 'Mrrfrmrfrmrrrrr!', 12, 0, 100, 0, 0, 0, 30463, 0, 'The Leaper');
INSERT INTO `creature_text` VALUES (29840, 1, 0, 'Mrmrmmrmrmrmrm... mrmrmrmr?!', 12, 0, 100, 1, 0, 12937, 30459, 0, 'The Leaper');
INSERT INTO `creature_text` VALUES (29770, 0, 0, 'Who intrudes upon my ritual?', 12, 0, 100, 0, 0, 0, 30462, 0, 'Lady Nightswood');
INSERT INTO `creature_text` VALUES (29770, 1, 0, 'You dare?! Where is Baron Sliver? I would have words with him!', 12, 0, 100, 34, 0, 1173, 30458, 0, 'Lady Nightswood');
INSERT INTO `creature_text` VALUES (29769, 0, 0, 'Crush... maim... DESTROY!', 12, 0, 100, 0, 0, 0, 30461, 0, 'Vile');
INSERT INTO `creature_text` VALUES (29769, 1, 0, 'Vile free? Vile love $n!', 12, 0, 100, 34, 0, 1446, 30460, 0, 'Vile');
INSERT INTO `creature_text` VALUES (32239, 0, 0, 'It is time.  May the Light give us strength.', 12, 0, 100, 0, 0, 0, 32578, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (32239, 1, 0, 'Keep your heads down and follow my lead.', 12, 0, 100, 0, 0, 0, 32616, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (32239, 2, 0, 'Here it comes.  Stand ready.', 12, 0, 100, 0, 0, 0, 32619, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (32239, 3, 0, 'Something\'s wrong... I sense a dark presence.', 12, 0, 100, 0, 0, 0, 32620, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (32239, 4, 0, 'The Lich King is here.  May the Light guide our blades.', 12, 0, 100, 0, 0, 0, 32621, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (32239, 5, 0, 'You sound a little too confident.  Especially considering the way our last encounter ended.', 12, 0, 100, 25, 0, 0, 32592, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (32239, 6, 0, 'That might be, but I don\'t need to stand on holy ground to run that disembodied heart of yours with the Ashbringer.', 12, 0, 100, 1, 0, 0, 32589, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (32239, 7, 0, 'The heart... the last remaining vestige of your humanity.  I had to stop it from being destroyed.  I had to see for myself.  And at last I\'m sure...', 12, 0, 100, 1, 0, 0, 32590, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (32239, 8, 0, 'Only shadows from the past remain.  There\'s nothing left to redeem!', 12, 0, 100, 0, 0, 0, 32595, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (32311, 0, 0, 'Take courage, crusaders.  You do not fight alone!', 14, 0, 100, 0, 0, 0, 32708, 0, 'Koltira Deathweaver');
INSERT INTO `creature_text` VALUES (32311, 1, 0, 'The Lich King is badly hurt.  We ought to stay behind and finish him.', 12, 0, 100, 0, 0, 0, 32725, 0, 'Koltira Deathweaver');
INSERT INTO `creature_text` VALUES (32312, 0, 0, 'Quick, through the portal!  He won\'t stay down for long.', 12, 0, 100, 0, 0, 0, 32707, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (32312, 1, 0, 'Patience... we will get our chance soon enough.  Be content that for once, it is Tirion who is in our debt.', 12, 0, 100, 0, 0, 0, 32726, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (32184, 0, 0, 'Uninvited guests!  Did you think you\'d go unnoticed inside my dominion?', 12, 0, 100, 1, 0, 0, 32582, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (32184, 1, 0, 'I must confess... you were not altogether unexpected.  I hope you find your final resting place... to your liking.', 12, 0, 100, 1, 0, 0, 32585, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (32184, 2, 0, 'Last time we met, you had the advantage of fighting on holy ground.  You\'ll find that our situation has been... reversed.', 12, 0, 100, 11, 0, 0, 32588, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (32184, 3, 0, 'I call your bluff.  You\'re a paladin after all.  Your obsession with redemption goes beyond the inane.', 12, 0, 100, 1, 0, 0, 32593, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (32184, 4, 0, 'You surely wouldn\'t destroy humanity\'s only chance to redeem its most wayward son.  You\'d sooner die!', 12, 0, 100, 1, 0, 0, 32594, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (32184, 5, 0, 'Arrrrggggggggggh!!!!', 14, 0, 100, 34, 0, 0, 32694, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (32184, 6, 0, 'You... will pay for that, old man.  Slay them all!', 14, 0, 100, 0, 0, 0, 32698, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (32241, 0, 0, 'Tirion\'s down!  Defend him with your lives!', 14, 0, 100, 0, 0, 0, 32687, 0, 'Disguised Crusader');
INSERT INTO `creature_text` VALUES (30215, 1, 0, 'Sons of Hodir! I humbly present to you....', 42, 0, 100, 0, 0, 0, 30906, 0, 'Player - Read Pronouncement');
INSERT INTO `creature_text` VALUES (30215, 2, 0, 'The Helm of Hodir!', 42, 0, 100, 0, 0, 0, 30907, 0, 'Player - Read Pronouncement');
INSERT INTO `creature_text` VALUES (21024, 0, 0, 'Stand back, $n. The spirits may lash out.', 12, 0, 100, 1, 0, 0, 18749, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21129, 0, 0, 'Why have you captured us?', 12, 0, 100, 0, 0, 0, 18750, 0, 'Scorned Spirit of Earth');
INSERT INTO `creature_text` VALUES (21130, 0, 0, 'What is it that you hope to gain from this?', 12, 0, 100, 0, 0, 0, 18751, 0, 'Scorned Spirit of Fire');
INSERT INTO `creature_text` VALUES (21024, 1, 0, 'We wish to help you, spirit. We seek your wisdom.', 12, 0, 100, 1, 0, 0, 18753, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21131, 0, 0, 'Wisdom? It was your people that caused the insufferable wrath of Cyrukh.', 12, 0, 100, 0, 0, 0, 18754, 0, 'Scorned Spirit of Water');
INSERT INTO `creature_text` VALUES (21132, 0, 0, 'Why should we help you? The world is ruined. Destroyed.', 12, 0, 100, 0, 0, 0, 18755, 0, 'Scorned Spirit of Air');
INSERT INTO `creature_text` VALUES (21024, 2, 0, 'You are right, spirit. It was my people that did this to you. Allow us to make it right.', 12, 0, 100, 1, 0, 0, 18757, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21024, 3, 0, 'Please tell us this: Who or what is Cyrukh?', 12, 0, 100, 1, 0, 0, 18758, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21129, 1, 0, 'Fool! Cyrukh the fire lord! When the tie was severed, Cyrukh awoke. It is the tears of Cyrukh that fill this land with molten rock.', 12, 0, 100, 0, 0, 0, 18759, 0, 'Scorned Spirit of Earth');
INSERT INTO `creature_text` VALUES (21024, 4, 0, 'The... The fire lord? An elemental lord was unearthed? Of course... Gul\'dan you were a damned fool!', 12, 0, 100, 6, 0, 0, 18760, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21130, 1, 0, 'He is insane now. Driven mad during the shattering. There is no rest for the elements.', 12, 0, 100, 0, 0, 0, 18761, 0, 'Scorned Spirit of Fire');
INSERT INTO `creature_text` VALUES (21024, 5, 0, 'There must be a way.', 12, 0, 100, 0, 0, 0, 18762, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21131, 1, 0, 'Yes, a way. Destroy Cyrukh. Destroy an elemental lord.', 12, 0, 100, 0, 0, 0, 18763, 0, 'Scorned Spirit of Water');
INSERT INTO `creature_text` VALUES (21024, 6, 0, 'Do not doubt my comrades and I, spirit. Tell us how to conjure the fire lord. My allies will destroy him.', 12, 0, 100, 1, 0, 0, 18764, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21132, 1, 0, 'You must utter the words...', 12, 0, 100, 0, 0, 0, 18765, 0, 'Scorned Spirit of Air');
INSERT INTO `creature_text` VALUES (21129, 2, 0, 'Yes... Yes... the words. The words of damnation. The words spoken by the dark one. Your master.', 12, 0, 100, 0, 0, 0, 18766, 0, 'Scorned Spirit of Earth');
INSERT INTO `creature_text` VALUES (21130, 2, 0, 'Yes... Gul\'dan...', 12, 0, 100, 0, 0, 0, 18767, 0, 'Scorned Spirit of Fire');
INSERT INTO `creature_text` VALUES (21024, 7, 0, 'We will find these words. We will summon your master and we will destroy him.', 12, 0, 100, 1, 0, 0, 18768, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21129, 3, 0, 'Talk. More talk... Now release us, mortal.', 12, 0, 100, 0, 0, 0, 18769, 0, 'Scorned Spirit of Earth');
INSERT INTO `creature_text` VALUES (21024, 8, 0, '%s nods.', 16, 0, 100, 273, 0, 0, 18756, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21024, 9, 0, 'Now to recover the lost words!', 12, 0, 100, 5, 0, 0, 18778, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21024, 10, 0, '%s bows.', 16, 0, 100, 2, 0, 0, 18752, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (23364, 0, 0, 'The %s screams out for its mother.', 16, 0, 100, 0, 0, 431, 21420, 0, 'Black Dragon Whelpling');
INSERT INTO `creature_text` VALUES (23282, 0, 0, 'I believe that I shall feast upon both $r and ogre flesh tonight.', 14, 0, 100, 0, 0, 7274, 21718, 0, 'Obsidia');
INSERT INTO `creature_text` VALUES (20802, 0, 0, 'Let\'s do this... Just keep me covered and I\'ll deliver the package.', 12, 0, 100, 0, 0, 0, 18432, 0, 'Protectorate Demolitionist - Comienza escort');
INSERT INTO `creature_text` VALUES (19544, 1, 0, 'There it is! I could\'ve sworn it wasn\'t here last time I checked...', 12, 0, 100, 0, 0, 0, 16929, 0, 'Conjurer Luminrath');
INSERT INTO `creature_text` VALUES (19543, 0, 0, 'I don\'t know what I was thinking, going out without my sword. I would\'ve put it on if I\'d seen it here...', 12, 0, 100, 0, 0, 0, 16931, 0, 'Battle-Mage Dathric');
INSERT INTO `creature_text` VALUES (19546, 0, 0, 'I can\'t sleep without a good bedtime story. Now I\'m certain to rest well.', 12, 0, 100, 0, 0, 0, 16932, 0, 'Abjurist Belmara');
INSERT INTO `creature_text` VALUES (26097, 0, 0, 'Thank you for saving us. We accidentally uncovered the pieces of Gearmaster Mechazod and reassembled him.', 12, 0, 100, 0, 0, 0, 25205, 0, 'Fizzcrank Gnome Ghost');
INSERT INTO `creature_text` VALUES (26097, 1, 0, 'In return, he \'de-cursed\' us, saying that we were being \'fixed\' by returning us to the state of being mechanical constructs.', 12, 0, 100, 0, 0, 0, 25207, 0, 'Fizzcrank Gnome Ghost');
INSERT INTO `creature_text` VALUES (26097, 2, 0, 'He claims that all creations of the Titans fall prey to what he calls, \'The Curse of Flesh\'. He says that the original gnomes were once mechagnomes.', 12, 0, 100, 0, 0, 0, 25208, 0, 'Fizzcrank Gnome Ghost');
INSERT INTO `creature_text` VALUES (26097, 3, 0, 'He\'s quite insane! Someone must put a stop to his madness before it\'s too late for everyone!', 12, 0, 100, 0, 0, 0, 25209, 0, 'Fizzcrank Gnome Ghost');
INSERT INTO `creature_text` VALUES (25602, 0, 0, 'Now, $n, let us release their souls and hear what they have to say.', 12, 0, 100, 1, 0, 0, 25204, 0, 'Greatmother Taiga');
INSERT INTO `creature_text` VALUES (25602, 1, 0, 'This is dire news indeed!', 12, 0, 100, 1, 0, 0, 25211, 0, 'Greatmother Taiga');
INSERT INTO `creature_text` VALUES (25380, 0, 0, 'And remember, $n, the bloodspore powder can be used to weaken all magnataur in Gammoth.', 15, 0, 100, 0, 0, 0, 24994, 0, 'Primal Mighthorn');
INSERT INTO `creature_text` VALUES (25834, 0, 0, 'You were looking for me, child? Why do you come to kill me, $n? I only wish to help.', 12, 0, 100, 6, 0, 0, 25024, 0, 'Gearmaster Mechazod');
INSERT INTO `creature_text` VALUES (25834, 1, 0, 'Now that I have been reassembled, we can return to a time of perfection... the time of the Titans!', 12, 0, 100, 1, 0, 0, 25025, 0, 'Gearmaster Mechazod');
INSERT INTO `creature_text` VALUES (25834, 2, 0, 'But, I can see it in your eyes, hear it in your pulse rate. You would destroy me despite my offer of immortality!', 12, 0, 100, 25, 0, 0, 25026, 0, 'Gearmaster Mechazod');
INSERT INTO `creature_text` VALUES (25834, 3, 0, 'Very well. It saddens me that it has come to this. I look upon all of you as if you were my children. I will slay you if I must!', 12, 0, 100, 25, 0, 0, 25027, 0, 'Gearmaster Mechazod');
INSERT INTO `creature_text` VALUES (23837, 1, 0, 'There is a noise from behind you!', 41, 0, 100, 0, 0, 0, 25215, 0, 'ELM General Purpose Bunny');
INSERT INTO `creature_text` VALUES (25849, 0, 0, 'It\'s really going to work this time... right?', 12, 0, 100, 6, 0, 0, 25143, 0, 'Fezzix Geartwist');
INSERT INTO `creature_text` VALUES (25849, 1, 0, 'It works, Dorain! Look! It actually works...', 12, 0, 100, 4, 0, 0, 25148, 0, 'Fezzix Geartwist');
INSERT INTO `creature_text` VALUES (25849, 2, 0, 'Here I goooooooooo!', 12, 0, 100, 0, 0, 0, 25150, 0, 'Fezzix Geartwist');
INSERT INTO `creature_text` VALUES (25849, 3, 0, 'OUCH!', 12, 0, 100, 0, 0, 0, 25151, 0, 'Fezzix Geartwist');
INSERT INTO `creature_text` VALUES (25849, 4, 0, 'I give up! So much for goblin ingenuity!', 12, 0, 100, 5, 0, 0, 25152, 0, 'Fezzix Geartwist');
INSERT INTO `creature_text` VALUES (25983, 0, 0, 'Fezzix, I think we\'ve solved your little engine problem.', 12, 0, 100, 1, 0, 0, 25132, 0, 'Dorain Frosthoof');
