INSERT INTO `creature_text` VALUES (26608, 0, 0, 'Give in, Taunka! Your will cannot hold out against the Scourge!', 14, 0, 100, 15, 0, 0, 25952, 0, 'Under-King Anub\'et\'kan');
INSERT INTO `creature_text` VALUES (26608, 0, 1, 'Leave no survivors!', 14, 0, 100, 15, 0, 0, 25951, 0, 'Under-King Anub\'et\'kan');
