INSERT INTO `creature_text` VALUES (26608, 1, 0, 'Your freedom will be short lived, Icemist!', 14, 0, 100, 15, 0, 0, 25959, 0, 'Under-King Anub\'et\'kan');
INSERT INTO `creature_text` VALUES (26608, 2, 0, 'Insolent mortal! You will be consumed!', 14, 0, 100, 15, 0, 0, 25964, 0, 'Under-King Anub\'et\'kan');
INSERT INTO `creature_text` VALUES (26608, 3, 0, 'ARRGGH!! It cannot be! How...', 14, 0, 100, 15, 0, 0, 25965, 0, 'Under-King Anub\'et\'kan');
