INSERT INTO `creature_text` VALUES (26810, 0, 0, 'Gather, brothers and sisters.', 12, 1, 100, 1, 0, 0, 26136, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26810, 1, 0, '%s reads from the scroll as the other Taunka gather around him.', 16, 1, 100, 0, 0, 0, 26130, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26810, 2, 0, 'Lok\'tar ogar! Victory or death - it is these words that bind me to the Horde! For they are the most sacred and fundamental of truths to any warrior of the Horde.', 12, 1, 100, 1, 0, 0, 26137, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26810, 3, 0, 'I give my flesh and blood freely to the Warchief. I am the instrument of my Warchief\'s desire. I am a weapon of my Warchief\'s command!', 12, 1, 100, 1, 0, 0, 26138, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26810, 4, 0, 'From this moment until the end of days I live and die - For the Horde!', 12, 1, 100, 1, 0, 0, 26139, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26810, 5, 0, 'For my Taunka brothers and sisters! FOR THE HORDE!', 14, 1, 100, 388, 0, 0, 26140, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26379, 0, 0, 'We will walk together, $n.', 12, 1, 100, 1, 0, 0, 26131, 0, 'Overlord Agmar');
INSERT INTO `creature_text` VALUES (26379, 1, 0, 'Welcome to the Horde, High Chief. Lok\'tar ogar!', 12, 1, 100, 2, 0, 0, 26141, 0, 'Overlord Agmar');
INSERT INTO `creature_text` VALUES (26379, 2, 0, 'FOR THE HORDE!', 14, 1, 100, 15, 0, 0, 26142, 0, 'Overlord Agmar');
INSERT INTO `creature_text` VALUES (26437, 0, 0, 'Amidst the tragedy and chaos, this place stands defiantly against the Scourge! It is good to be Horde.', 12, 1, 100, 113, 0, 0, 25727, 0, 'Taunka Soldier');
INSERT INTO `creature_text` VALUES (29677, 0, 0, 'The %s sniffs the ground to pickup the trail.', 16, 0, 100, 0, 0, 0, 30366, 0, 'Frosthound to Player');
